drop FUNCTION dbi_insert_utente(character varying, character varying, integer, integer, integer, boolean, integer, character varying, character varying, character varying, text, text, character varying, character varying, timestamp with time zone, integer, text, integer, text, text);
  
CREATE OR REPLACE FUNCTION public.dbi_insert_utente(
    usr character varying,
    password_ character varying,
    role_id integer,
    enteredby integer,
    modifiedby integer,
    enabled boolean,
    site_id integer,
    namefirst character varying,
    namelast character varying,
    cf character varying,
    notes text,
    luogo text,
    nickname character varying,
    email character varying,
    expires timestamp with time zone,
    clinica integer,
    luogo_vam text,
    id_provincia integer,
    nr_iscrizione text,
    input_telefono text)
  RETURNS text AS
$BODY$
   DECLARE
msg text ;
us_id int ;
us_id2 int ;
t timestamp without time zone;
asl_val int;
provincia text;
cat_name text;
BEGIN

	IF (site_id=-1) THEN
	   asl_val=null;
	ELSE
	   asl_val=site_id;
	END IF;

	IF (role_id=-1) THEN
		enabled:=false;
	ELSE
		enabled:=true;
	END IF;

	IF (clinica=-1) THEN
		clinica:=null;
	END IF;

	t=now();

	--Gestione Province
        provincia:='';
	IF (id_provincia=61) THEN
	   provincia:='CE';
	ELSIF(id_provincia=62) THEN
	   provincia:='BN';
	ELSIF(id_provincia=63) THEN
	   provincia:='NA';
	ELSIF(id_provincia=64) THEN
	   provincia:='AV';
	ELSIF(id_provincia=65) THEN
	   provincia:='SA';
	END IF;
	--Fine Gestione Province

	
	us_id := (select us.id from utenti_super us where us.username = usr and us.trashed_date is null);
	IF (us_id is null) THEN	
		us_id=nextVal('utenti_super_id_seq');
		INSERT INTO utenti_super (id, data_scadenza,enabled,entered,modified,note,password,username,num_iscrizione_albo, sigla_provincia,luogo) 
		VALUES (us_id,expires::timestamp without time zone,true,current_timestamp,current_timestamp,notes,password_,usr,nr_iscrizione,provincia,luogo_vam);
	END IF;

	IF (clinica is not null or role_id = 18) THEN
		us_id2=nextVal('utenti_id_seq');
		INSERT INTO utenti (id,codice_fiscale,cognome,data_scadenza,email,enabled,entered,nome,note,password,ruolo,username,clinica,superutente,asl_referenza,telefono1) 
		VALUES (us_id2,cf,namelast,expires::timestamp without time zone,email,true,current_timestamp,namefirst,notes,password_,role_id,usr,clinica,us_id,asl_val,input_telefono); 

		INSERT INTO secureobject (name) VALUES (us_id2::text);
		
		cat_name := (select nome from permessi_ruoli where id=role_id);
		INSERT INTO category_secureobject (categories_name,secureobjects_name) 
		VALUES (cat_name,us_id2::text);
	END IF;
	
	msg = 'OK';
	RETURN msg;

END
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100;
