CREATE OR REPLACE FUNCTION public.chk_bns_get_domande_v7(
	_codicespecie integer,
	_numallegato integer,
	_versione integer,
	_idcontrollo integer)
    RETURNS TABLE(id integer, level integer, irr_id integer, dettirrid integer, idmod integer, descrizione text, titolo text, sottotitolo text, quesito text, esito text, evidenze text) 
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE STRICT PARALLEL UNSAFE
    ROWS 1000

AS $BODY$

DECLARE
BEGIN
	FOR id, level, irr_id , dettirrid , idmod , descrizione , titolo , sottotitolo, quesito , esito , evidenze
	in

	select distinct c.id, c.level, c.irr_id , c.dettirrid , c.idmod , c.descrizione , c.titolo , c.sottotitolo, c.quesito , c.esito , c.evidenze

from chk_bns_domande_v7 c where c.idmod in 
(select code from lookup_chk_bns_mod where codice_specie = _codiceSpecie and versione = _versione and num_allegato = _numAllegato and start_date < 
(select assigned_date from ticket where ticketid = _idcontrollo) and end_date >(select assigned_date from ticket where ticketid = _idcontrollo) ) 
order by c.level asc

LOOP
        RETURN NEXT;
     END LOOP;
     RETURN;
 END;
$BODY$;

ALTER FUNCTION public.chk_bns_get_domande_v7(integer, integer, integer, integer)
    OWNER TO postgres;

