insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,117,1032,'PERSONALE','Numero di addetti che si occupano degli Animali','1. Numero di addetti che si occupano degli animali 146/2001 All. Personale Punto 1 "Gli animali devono essere accuditi da un numero sufficiente di addetti" Il numero di addetti deve essere sufficiente per garantire la salute e il benessere di tutti gli animali in allevamento: verificare rapporto n. addetti - n. animali. Sono fornite indicazioni differenti in base al tipo di popolazione e alla tipologia di allevamento (es. se allevati al chiuso in stalla, o al pascolo). I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. BOVINA DA LATTE Stabulazione libera: Numero non sufficiente di addetti: un operatore per più di 300 animali totali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per più di 120 animali totali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 60 capi in lattazione) Livello della non conformita'': no; NO Numero accettabile di addetti: un operatore per 200-300 animali totali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per 80-120 animali totali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 50 capi in lattazione) Livello della non conformita'': SI Numero ottimale di addetti: un operatore per meno di 200 animali totali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per meno di 80 animali totali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 40 capi lattazione) Livello della non conformita'': Ottimale BOVINA DA LATTE Stabulazione fissa: Numero non sufficiente di addetti: un operatore per più di 120 animali totali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per più di 100 animali totali e mungitura con lattodotto dei relativi 50 capi in lattazione; oppure un operatore per più di 80 animali totali e mungitura a secchio dei relativi 40 capi in lattazione. Livello della non conformita'': no; NO Numero accettabile di addetti: un operatore per 80-120 animali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per circa 50-100 animali totali e mungitura con lattodotto dei relativi 25-50 capi in lattazione; oppure un operatore per circa 40-80 animali totali e mungitura a secchio dei relativi 20-40 capi in lattazione. Livello della non conformita'': SI Numero ottimale di addetti: un operatore per meno di 80 animali (nel caso in cui la mungitura sia eseguita da altri); oppure operatore per meno di 50 animali totali e mungitura con lattodotto dei relativi 25 capi in lattazione; oppure un operatore per meno di 40 animali totali e mungitura a secchio dei relativi 20 capi in lattazione. Livello della non conformita'': Ottimale BUFALE DA LATTE Numero non sufficiente di addetti: un operatore per più di 300 animali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per più di 120 animali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 60 capi in lattazione); Livello della non conformita'': no; NO Numero accettabile di addetti: un operatore per 200-300 animali (nel caso in cui la mungitura sia eseguita da altri); oppure operatore per 100-120 animali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 50 capi in lattazione); Livello della non conformita'': SI Numero ottimale di addetti: un operatore per meno di 200 animali (nel caso in cui la mungitura sia eseguita da altri); oppure u operatore per meno di 100 animali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 50 capi in lattazione) Livello della non conformita'': Ottimale LINEA VACCA-VITELLO: Numero non sufficiente di addetti: STALLA: un operatore per più di 70 animali; PASCOLO: un operatore per più di 120 animali Livello della non conformita'': no; NO Numero sufficiente di addetti: STALLA: un operatore per 30-70 animali; PASCOLO: un operatore per 80-120 animali Livello della non conformita'': SI Numero ottimale di addetti: STALLA: un operatore per meno di 30 animali; PASCOLO: un operatore per meno di 80 animali Livello della non conformita'': Ottimale BOVINO DA CARNE: Numero non sufficiente di addetti: un operatore per più di 800 animali Livello della non conformita'': no; NO Numero accettabile di addetti: un operatore per 400-800 animali Livello della non conformita'': SI Numero ottimale di addetti: un operatore per meno di 400 animali Livello della non conformita'': Ottimale','1) Gli animali sono accuditi da un numero sufficiente di addetti.','S, NA, NB, NC, -, O','(*) Indicare il numero degli addetti',1);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,118,1033,'PERSONALE','Formazione degli Addetti','2. Formazione degli addetti 2. Formazione degli addetti 146/2001 All. Personale Punto 1 "Gli addetti devono avere capacità e conoscenze adeguate" Valutare le competenze complessive del personale addetto agli animali, che possono essere sia di origine pratica (perché fornite dall''esperienza) sia di origine teorica (es.corso di formazione/ titolo di studio). Si considera accettabile una lunga esperienza pratica nel settore oppure, in assenza di essa (es. giovani allevatori), il conseguimento di titoli di studio inerenti (diploma o laurea in agraria, veterinaria e lauree brevi simili) o la partecipazione a corsi di formazione specifici sul benessere animale. Per assegnare un giudizio ottimale è necessario possedere entrambi i requisiti: esperienza prolungata e titolo di studio/formazione specifica. I corsi di formazione o aggiornamento dovrebbero essere ripetuti con cadenza regolare durante il periodo lavorativo (almeno 1 corso ogni 3 anni). Se l''allevamento è seguito da più operatori, si considera sufficiente la partecipazione ai corsi di formazione anche di un solo addetto (sia esso il titolare o il dipendente assunto). I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Capacità e conoscenze non adeguate: esperienza indicativamente minore di 10 anni per i bovini o minore di 7 anni per i bufalini e nessun corso di formazione sul benessere animale Livello della non conformita'': no; NO Capacità e conoscenze adeguate: esperienza indicativamente di almeno 10 anni per i bovini o 7 anni per i bufalini e nessun corso di formazione sul benessere animale/titolo di studio (o combinazione opposta) Livello della non conformita'': SI Capacità e conoscenze ottimali: esperienza indicativamente di almeno 10 anni per i bovini o 7 anni per i bufalini con titolo di studio o corso di formazione attinente, seguito negli ultimi 3 anni Livello della non conformita'': Ottimale','2) Il personale addetto agli animali dispone di capacità e conoscenze adeguate e ha ricevuto istruzioni pratiche sulle pertinenti diposizioni normative.normative e ha frequentato corsi di formazione specifici in materia di benessere degli animali.','S, NA, NB, NC, -, O','*) Indicare esperienza nel settore, presenza di istruzioni pratiche scritte o correttamente comunicate ed estremi dei corsi di formazione frequentati, con periodicità e ricaduta a livello aziendale, ecc.',2);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,119,1034,'ISPEZIONE E CONTROLLO DEGLI ANIMAL','Numero di Ispezioni','3. Numero di ispezioni 146/2001 All. Controllo Punto 2. "Tutti gli animali tenuti in sistemi di allevamento, il cui benessere richieda un''assistenza frequente dell''uomo, sono ispezionati almeno una volta al giorno. Gli animali allevati o custoditi in altri sistemi sono ispezionati a intervalli sufficienti al fine di evitare sofferenze." Si considerano le visite giornaliere in stalla al di là di quelle eseguite per le attività di routine, qualora le stesse siano altament specializzate o compiute con particolare impiego di attenzione da parte dell''operatore (es. mungitura con assegnazione di compiti specifici o distribuzione dell''alimentazione con carro unifeed). L''ispezione si intende relativa a tutti i gruppi di animali presenti in allevamento (fatta eccezione dei vitelli 0-6 mesi d''età per i quali vige il requisito del D. Lgs. 126/2011). Qualora sussista segnalazione scritta delle osservazioni (es. calori) o regis computerizzata, oltre ad un numero conforme di ispezioni, può essere assegnato il giudizio ottimale. Si considera non adeguato un numero di visite insufficienti: - STALLA: meno di 1 ispezione/giorno; - ALL''APERTO (PASCOLO): ispezione a intervalli insufficienti e compiuta con frequenze irregolari e non programmate, in relazione alla stagione al periodo produttivo/riproduttivo o ad eventi straordinari Livello della non conformita'': no; NO Si considera adeguato un numero di visite sufficienti: - STALLA: almeno 1 ispezione/giorno; - ALL''APERTO (PASCOLO): ispezione a intervalli sufficienti e compiuta con frequenze regolari e programmate, in relazione al stagione al periodo produttivo/riproduttivo o ad eventi straordinari Livello della non conformita'': SI Numero di visite superiori al requisito previsto: 2 o più ispezioni al giorno unitamente a segnalazione scritta delle osservazioni (es. calori) o registrazione computerizzata Livello della non conformita'': Ottimale','3) Gli animali nei locali di stabulazione sono ispezionati almeno una volta al giorno; mentre gli animali allevati o custoditi in altri sistemi sono ispezionati a intervalli sufficienti al fine di evitare sofferenze.','S, NA, NB, NC, -, O','(*) Indicare il numero di ispezioni e le modalità con le quali il dato viene raccolto e se esiste un programma per organizzare le ispezioni degli animali stabulati all''aperto',3);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,120,1035,'ISPEZIONE E CONTROLLO DEGLI ANIMAL','Illuminazione per L''ispezione','4. Illuminazione per l''ispezione 146/2001 All. Controllo Punto 3 "Per consentire l''ispezione completa degli animali in qualsiasi momento, deve essere disponibile un''adeguata illuminazione fissa o mobile" L''intensità luminosa e la durata del periodo di luce dovrebbero permettere agli operatori di ispezionare adeguatamente tutti gli animali nell''arco della giornata. In aggiunta un''illuminazione fissa o mobile dovrebbe essere presente per consentire l''ispezione degli animali in qualsiasi momento, anche di notte, cosicché gli addetti possano intervenire in modo attento e senza rischi. Illuminazione per l''ispezione insufficiente: assenza di illuminazione artificiale per l''ispezione Livello della non conformita'': no; NO Illuminazione per l''ispezione sufficiente: presenza di illuminazione artificiale corretta e funzionante Livello della non conformita'': SI','4) È disponibile un''adeguata illuminazione che consente l''ispezione completa degli animali.','S, NA, NB, NC, -','(*) Descrivere le modalità di illuminazione durante le ore diurne o notturne',4);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,121,1036,'ISPEZIONE E CONTROLLO DEGLI ANIMAL','Trattamento degli animali malati o Feriti','5. Trattamento degli animali malati o feriti 146/2001 All. Controllo Punto 4 "Gli animali malati o feriti devono ricevere immediatamente un trattamento appropriato e, qualora un animale non reagisca alle cure in questione, deve essere consultato un medico veterinario. Ove necessario gli animali malati o feriti vengono isolati in appositi locali muniti, se del caso, di lettiere asciutte o confortevoli." Per avere evidenza che gli animali malati o feriti ricevano un trattamento adeguato (non necessariamente di tipo farmacologico) verificare tramite intervista presso gli addetti se sono state impartite precise istruzioni, anche di tipo orale. Punti da verificare: 1. la verifica dell''immediato trattamento: si assolve con due possibili condizioni, la prima è che l''animale malato o ferito possa essere spostato o sia già stato spostato nel reparto infermeria (area, box o posta, eventualmente isolato) o, in alternativa, che l''animale malato (che non necessita dell''infermeria) sia stato identificato e abbia ricevuto un trattamento appropriato pur rimanendo nel gruppo con gli altri animali (valutare le dichiarazioni e/o la presenza di trattamenti farmacologici in atto sul registro dei tratta farmacologici o nelle registrazioni in Vetinfo farmacosorveglianza); 2. il consulto con un veterinario: verificare se è dichiarata o documentata la presenza del veterinario aziendale o di un veterinario libero professionista o privato che segue l''azienda. Per assegnare il giudizio conforme tutte le condizioni devono essere soddisfatte. Si considera non adeguato: evidenza di personale non istruito e/o presenza di animali che necessitano di un trattamento e non lo hanno ancora ricevuto (n.b. valutare con attenzione la possibile insorgenza iperacuta dei casi di malattia) e/o assenza di un veterinario che segue l''azienda Livello della non conformita'': no; NO Si considera adeguato: presenza di personale istruito ed evidenza che i due punti di verifica sopra enunciati siano soddisfatti Livello della non conformita'': SI Il requisito superiore (ottimale) prevede, oltre ai criteri per l''adeguatezza, la presenza delle relative procedure scritte di trattamento degli animali (es. piano di gestione della mastite o della zoppia o specifici protocolli terapeutici) Livello della non conformita'': Ottimale','5) Gli animali malati o feriti ricevono immediatamente un trattamento appropriato.','S, NA, NB, NC, -, O','(*) Indicare se sono presenti animali mal gestiti (es. assenza prolungata di pareggi podali), ammalati o feriti e le modalità di detenzione; se l''infermeria o altro settore sono, all''occorrenza, adattabili ad accogliere ed isolare animali deboli, malati, feriti o aggressivi',5);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,122,1037,'ISPEZIONE E CONTROLLO DEGLI ANIMAL','Abbattimento','6. Abbattimento D. Lgs. 146/2001, allegato, punto 4 "Gli animali malati o feriti devono ricevere immediatamente un trattamento appropriato [...]." Reg. 1099/2009 Art. 2 Definizioni a) «abbattimento»: qualsiasi processo applicato intenzionalmente che determini la morte dell''animale; d) «abbattimento d''emergenza»: l''abbattimento di animali feriti o affetti da una malattia procurante dolori o sofferenze acuti, qualora non esista altra possibilità pratica per alleviare tali dolori o sofferenze; e secondo il considerando n.12 è da intendersi "emergenza" in circostanze eccezionali, come nel caso di incidenti avvenuti in luoghi isolati dove gli animali non possono essere raggiunti da personale competente e con attrezzature idonee. Poiché in questi frangenti, il rispetto di misure ottimali in materia di benessere potrebbe protrarre le sofferenze degli animali, nel loro interesse è opportun escludere l''abbattimento di emergenza dall''applicazione di determinate disposizioni del regolamento. L''abbattimento di animali sofferenti in allevamento, in circostanze routinarie, non è da intendersi abbattimento d''emergenza. j) «macellazione»: l''abbattimento di animali destinati all''alimentazione umana. Capo II Articolo 4 Metodi di stordimento 1. Gli animali sono abbattuti esclusivamente previo stordimento, conformemente ai metodi e alle relative prescrizioni di applicazione di cui all''allegato I. La perdita di coscienza e di sensibilità è mantenuta fino alla morte dell''animale. Capo II Articolo 7 Livello di competenze e certificato di idoneità 1. L''abbattimento e le operazioni correlate sono effettuati esclusivamente da persone che abbiano un adeguato livello di competenze per l''esecuzione di dette operazioni senza causare agli animali dolori, ansia o sofferenze evitabili. 2. Gli operatori provvedono affinché le seguenti operazioni di macellazione siano eseguite esclusivamente da persone che dispongano del relativo certificato di idoneità, come previsto dall''articolo 21, che ne attesti la capacità di eseguirle conformemente alle norme stabilite dal presente regolamento [...] Capo IV Articolo 19 Abbattimento di emergenza Nel caso di abbattimenti di emergenza, la persona che ha in custodia gli animali interessati adotta tutti i provvedimenti necessari per abbattere gli animali nel più breve tempo possibile. Reg. 1/2005 Allegato I, Capo I, Paragrafo 1 "Non può essere trasportato nessun animale che non sia idoneo al viaggio previsto, né le condizioni di trasporto possono essere tali da esporre l''animale a lesioni o a sofferenze inutili." Nel caso in cui la condizione di un animale richieda l''abbattimento in azienda, è necessario che vengano rispettate le metodologie contemplate dalla specifica normativa (Regolamento CE 1099/2009). Valutare che tali pratiche siano svolte da personale competente (es. eutanasia effettuata da un veterinario, oppure ricorso a personale provvisto del certificato d''idoneità alla macellazione, oppure proprietario/detentore adeguatamente formato) e che siano presenti delle istruzioni (es. numeri da contattare in caso d''emergenza, materiale didattico del corso frequentato dal proprietario/detentore, modalità di manutenzione degli strumenti, ecc.). È tuttavia possibile, in casi eccezionali, effettuare l''abbattimento di animali che presentino patologie gravi, causa di sofferenze acute, anche senza possesso di un certificato di idoneità né di un''adeguata competenza. In tali situazioni, infatti, l''intervento di personale competente dotato di attrezzature idonee, non essendo immediato, potrebbe determinare un prolungamento delle soffere dell''animale. Questi eventi non devono avere tuttavia caratteristiche di routinarietà. Si considera non adeguato: Mancato ricorso a personale competente e/o assenza di istruzioni in caso l''abbattimento sia eseguito da parte di personale aziendale e/o uso di strumentazione non adeguata o non sottoposta a regolare manutenzione Livello della non conformita'': no; NO Si considera adeguato: Abbattimento effettuato da un veterinario, oppure ricorso a personale provvisto del certificato d''idoneità alla macellazione, oppure, in caso di abbattimento da parte di personale aziendale, si valuta la presenza di istruzioni e di formazione degli addetti al riguardo (es. presenza di un corso di formazione frequentato da chi in allevamento è addetto all''abbattimento, con tematica specifica trattata) e presenza di strumentazione adeguata e sottoposta a regolare manutenzione. Livello della non conformita'': SI Il requisito superiore previsto prevede, oltre ai criteri per l''adeguatezza, la presenza di procedure scritte per il corretto abbattimento, indicando responsabilità, strumenti e verifiche periodiche per agevolare una corretta gestione dell''emergenza Livello della non conformita'': Ottimale','6) Gli animali malati o feriti ricevono immediatamente un trattamento appropriato in caso di abbattimento o MSU','S, NA, NB, NC, -, O','(*) Fare riferimento a procedure o istruzioni sull''abbattimento e ai piani di emergenza e alla formazione degli operatori. Indicare se ci sono evidenze di animali ammalati o feriti che non hanno ricevuto in maniera tempestiva un trattamento adeguato in caso di MSU o abbattimento. Le valutazioni ABM (lesioni, body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',6);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,123,1038,'TENUTA DEI REGISTRI (Registrazione dei dati)','Tenuta delle registrazioni dei Dati','7. Tenuta delle registrazioni dei dati 146/2001 All. Registrazione Punti 5-6. (i riferimenti al D.P.R. 8 febbraio 1954, n. 320, attualmente abrogato, si intendono fatti corrispondenti articoli dei decreti legislativi 5 agosto 2022, n.134 e n.136 ) "Le mortalità sono denunciate ai sensi del D.P.R. 8 febbraio 1954 n. 320. I registri sono conservati per un periodo di almeno tre anni e sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta." Dlgs 134 art. 9 comma 10 "L''operatore provvede alla registrazione in BDN di tutte le informazioni inerenti agli animali identificati singolarmente, alle partite, agli insiemi e ai gruppi di animali, nonché agli eventi che li riguardano, con i tempi di cui al presente articolo e con i modi previsti nel manuale operativo anche ai fini della generazione del registro della propria attività nella stessa BDN. Tale registro sostituisce qualsiasi altro registro aziendale cartaceo o su altro supporto concernente l''identificazion registrazione degli animali." D. Lgs. 136/2022, art. 6 "Notifica e comunicazione delle malattie all''Autorità competente"; art. 11 comma 4 "gli operatori nel caso in cui non siano ancora disponibili nel sistema «ClassyFarm.it»,le apposite funzionalità informatiche per la tipologia di stabilimento di cui sono responsabili, adempiono agli obblighi del presente articolo tenendo in modalità elettronica o cartacea, per cinque anni, la documentazione riguardante almeno le informazioni di cui all''allegato 3 del presente decreto"; Allegato 3 "individuazione registrazione e segnalazione delle mortalità anomale" Verificare in BDN la presenza del registro di carico e scarico e la corretta registrazione delle informazioni, al fine di valutare se si siano verificate eventuali mortalità anomale. Nel caso si siano verificate mortalità anomale, verificare che siano state correttamente individuate, registrate e segnalate. Si considera non conforme: assenza in BDN del registro di carico e scarico o mancata o non corretta registrazione delle informazioni e/ o evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': NO Si considera conforme: presenza in BDN del registro di carico e scarico e corretta registrazione delle informazioni e mancata evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': S','7) In BDN è presente il registro di carico e scarico, le informazioni vengono correttamente registrate e non sono presenti episodi di mortalità anomale non registrate','S, NC, -','(*) Annotare eventuali rilievi di mortalità anomale non registrate o di mancanza delle registrazioni delle informazioni in BDN',7);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,124,1039,'TENUTA DEI REGISTRI (Registrazione dei dati)','Tenuta del registro dei trattamenti Farmacologici','8. Tenuta del registro dei trattamenti farmacologici 146/2001 All. Registrazione Punti 5-6. "Il proprietario o il custode ovvero il detentore degli animali tiene un registro dei trattamenti terapeutici effettuati. La registrazione e le relative modalità di conservazione sono effettuate secondo quanto previsto dal decreto legislativo 27 gennaio 1992, n. 119 successive modificazioni ed integrazioni e dal decreto legislativo 4 agosto 1999, n. 336. I registri sono conservati per un periodo di almeno tre anni e sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta" Sono presenti le registrazioni informatizzate dei trattamenti su Vetinfo Farmacosorveglianza, ai sensi della Legge 20 novembre 2017, n. 167 e s.m.i. Assenza delle registrazioni in Vetinfo farmacosorveglianza o non corretta registrazione dei trattamenti. Livello della non conformita'': NO Corretta registrazione dei trattamenti in Vetinfo farmacosorveglianza. Livello della non conformita'': SI','8) Le registrazioni in Vetinfo farmacosorveglianza vengono effettuate nei tempi stabiliti e i trattamenti sono regolarmente prescritti da un medico veterinario.','S, NC, -','(*) Segnalare i trattamenti controllati a campione', 8);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,125,1040,'LIBERTA'' DI MOVIMENTO','Superficie disponibile per il decubito bovine/bufale adulte (ovvero in lattazione, in asciutta, al parto e vacche nutrici in linea vacca- Vitello)','9. Superficie disponibile per il decubito bovine/bufale adulte (ovvero in lattazione, in asciutta, al parto e vacche nutrici in linea vacca-vitello) 146/2001 All. Libertà di movimento punto 7; "La libertà di movimento propria dell''animale, in funzione della sua specie e secondo l''esperienza acquisita e le conos scientifiche, non deve essere limitata in modo tale da causargli inutili sofferenze o lesioni. Allorché continuamente o regolarmente legato, incatenato o trattenuto, l''animale deve poter disporre di uno spazio adeguato alle sue esigenze fisiologiche ed etologiche, secondo l''esperienza acquisita e le conoscenze scientifiche." L''area di stabulazione degli animali deve essere correttamente dimensionata al fine di garantire il massimo comfort ed igiene, nonché il completo utilizzo da parte di tutti gli animali, senza causare loro inutili sofferenze o lesioni. Se la parte di decubito e di alimentazione coincidono, escludere la zona di accesso all''alimentazione (circa 1 m). Nel caso della stabulazione fissa, ogni bovina deve avere a disposizione un''impronta di riposo, una mangiatoia e un abbeveratoio. I limiti indicati si riferiscono all''area di decubito coperta (cuccette, poste o lettiera permanente) e sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. BOVINE/BUFALE ADULTE Si considera non adeguato: Libertà di movimento limitata da una superficie inferiore a 6 m²/capo (bovine adulte e al parto), 5 m²/capo (bufale adulte), 7 m2/capo (bufale al parto) o numero di cuccette utilizzabili inferiore al 90% del numero totale degli animali o cuccette con misure errate - in caso di stabulazione fissa, presenza di uno o più animali legati che non dispongono di una posta strutturata, ma sono collocati in aree non previste e non idonee Livello della non conformita'': no; NO Si considera adeguato: Libertà di movimento assicurata da una superficie compresa tra 6 e 7 m²/capo (bovine adulte e al parto), tra 5 e 7 m²/capo (bufale adulte) tra 7 e 8 m2/capo (bufale al parto) o numero di cuccette utilizzabili pari al 90-110% del numero degli animali presenti - in caso di stabulazione fissa, tutti gli animali legati dispongono di una posta strutturata e nessuno di essi è collocato in aree non previste e non idonee Livello della non conformita'': SI Si considera ottimale: Tutti gli animali sono liberi con superficie disponibile superiore a 7 m²/capo (bovine/bufale adulte) e a 8 m2/capo (bufale al parto) o un numero di cuccette utilizzabili superiore del 10% rispetto al numero totale degli animali; Oppure, oltre ai criteri per l''adeguatezza, gli animali possono accedere al pascolo idoneo (almeno 60 gg/anno per i bovini da latte e almeno 4-5 mesi/anno per i bovini in linea vacca-vitello) Livello della non conformita'': Ottimale','9) Lo spazio a disposizione di ogni animale è sufficiente a consentirgli un''adeguata libertà di movimento ed è tale da non causargli inutili sofferenze o lesioni I locali di stabulazione sono costruiti in modo da permettere agli animali di coricarsi, giacere in decubito, alzarsi ed accudire se stessi senza difficoltà Per rispondere adeguato entrambe le condizioni devono essere soddisfatte','S, NA, NB, NC, -, O','(*) in caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. dimensioni delle cuccette o delle poste; animali senza adeguato spazio; presenza di lesioni cutanee, ecc.). Le valutazioni ABM (lesioni, body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',9);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,126,1041,'LIBERTA'' DI MOVIMENTO','Superficie disponibile per il decubito manze bovine/bufaline e tori da rimonta','10. Superficie disponibile per il decubito manze bovine/bufaline e tori da rimonta 146/2001 All. Libertà di movimento punto 7; "La libertà di movimento propria dell''animale, in funzione della sua specie e secondo l''esperienza acquisita e le conos scientifiche, non deve essere limitata in modo tale da causargli inutili sofferenze o lesioni. Allorché continuamente o regolarmente legato, incatenato o trattenuto, l''animale deve poter disporre di uno spazio adeguato alle sue esigenze fisiologiche ed etologiche, secondo l''esperienza acquisita e le conoscenze scientifiche." L''area di stabulazione degli animali deve essere correttamente dimensionata al fine di garantire il massimo comfort ed igiene, nonché il completo utilizzo da parte di tutti gli animali, senza causar loro inutili sofferenze o lesioni. Se la parte di decubito e alimentazione coincidono, escludere la zona di accesso all''alimentazione (circa 1 m). Nel caso della stabulazione fissa, ogni animale deve avere a disposizione un''impronta di riposo, una mangiatoia e un abbeveratoio. I limiti indicati si riferiscono all''area di decubito coperta (cuccette, poste o lettiera permanente) e sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. MANZE BOVINE/BUFALINE E TORI DA RIMONTA Si considera non adeguato: Libertà di movimento limitata da una superficie inferiore 3,5 m²/capo (manze bovine), 2,5 m²/capo (manze bufaline) e 9 m²/capo (tori da rimonta) o numero di cuccette utilizzabili inferiore al 90% del numero totale degli animali o cuccette con misure errate - in caso di stabulazione fissa, presenza di uno o più animali legati che non dispongono di una posta strutturata, ma sono collocati in aree non previste e non idonee Livello della non conformita'': no; NO Si considera adeguato: Libertà di movimento assicurata da una superficie compresa tra 3,5-4 m²/capo (manze bovine), tra 2,5- m²/capo (manze bufaline) e 9-10 m²/capo (tori da rimonta) o numero di cuccette utilizzabili pari al 90-110% del numero degli animali presenti - in caso di stabulazione fissa, tutti gli animali legati dispongono di una posta strutturata e nessuno di essi è collocato in aree non previste e non idonee Livello della non conformita'': SI Si considera ottimale: Tutti gli animali sono liberi con una superficie disponibile superiore a 4 m²/capo (manze bovine/bufaline) e 10 m²/capo (tori da rimonta) o un numero di cuccette utilizzabili superiore del 10% rispetto al numero totale degli animali Oppure, oltre ai criteri per l''adeguatezza, gli animali possono accedere al pascolo idoneo (almeno 4-5 mesi/anno) Livello della non conformita'': Ottimale','10) Lo spazio a disposizione di ogni animale è sufficiente a consentirgli un''adeguata libertà di movimenti ed è tale da non causargli inutili sofferenze o lesioni I locali di stabulazione sono costruiti in modo da permettere agli animali di coricarsi, giacere in decubito, alzarsi ed accudire se stessi senza difficoltà Per rispondere adeguato entrambe le condizioni devono essere Soddisfatte','S, NA, NB, NC, -, O','(*) in caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. dimensioni delle cuccette o delle poste; animali senza adeguato spazio; presenza di lesioni cutanee, ecc.). Le valutazioni ABM (lesioni, body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',10);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,127,1042,'LIBERTA'' DI MOVIMENTO','Superficie disponibile per il decubito bovini da ingrasso (> 6 mesi d''età)','11. Superficie disponibile per il decubito bovini da ingrasso (> 6 mesi d''età) 146/2001 All. Libertà di movimento punto 7; "La libertà di movimento propria dell''animale, in funzione della sua specie e secondo l''esperienza acquisita e le conos scientifiche, non deve essere limitata in modo tale da causargli inutili sofferenze o lesioni. Allorché'' continuamente o regolarmente legato, incatenato o trattenuto, l''animale deve poter disporre di uno spazio adeguato alle sue esigenze fisiologiche ed etologiche, secondo l''esperienza acquisita e le conoscenze scientifiche." L''area di stabulazione degli animali deve essere correttamente dimensionata al fine di garantire il massimo comfort ed igiene, nonché il completo utilizzo da parte di tutti gli animali, senza causar loro inutili sofferenze o lesioni. Se la parte di decubito e alimentazione coincidono, escludere la zona di accesso all''alimentazione (circa 1,5 m). Nel caso della stabulazione fissa, ogni animale deve avere a disposizione un''impronta di riposo, una mangiatoia e un abbeveratoio. I limiti indicati si riferiscono all''area di decubito coperta (cuccette, poste o lettiera permanente) e sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. BOVINI DA INGRASSO (> 6 mesi d''età) Tabella riepilogativa superficie/capo (escludendo la zona di accesso all''alimentazione): Categoria di peso animale Superficie minima di decubito (m²/capo) Superficie ottimale di decubito (m²/capo) 400 kg 2,5 4,5 500 kg 3,0 5,0 600 kg 3,5 5,5 700 kg 4,0 6,0 800 kg 4,5 6,5 900 kg 5,0 7,0 1000 kg 5,5 7,5 Si considera non adeguato: Libertà di movimento limitata da una superficie inferiore a quella minima indicata in tabella / presenza di uno o più animali legati che non dispongono di una posta strutturata, ma sono collocati in aree non previste e non idonee Livello della non conformita'': no; NO Si considera adeguato: Libertà di movimento assicurata da una superficie compresa tra quella minima e quella ottimale indicate in tabella / tutti gli animali legati dispongono di una posta strutturata e nessuno di essi è collocato in aree non previste e non idonee Livello della non conformita'': SI Si considera ottimale: Tutti gli animali sono liberi con superficie disponibile superiore o uguale alla superficie ottimale indicata Oppure, oltre ai criteri per l''adeguatezza, gli animali possono accedere al pascolo idoneo (almeno 4-5 mesi/anno) Livello della non conformita'': Ottimale','11) Lo spazio a disposizione di ogni animale è sufficiente a consentirgli un''adeguata libertà di movimenti ed è tale da non causargli inutili sofferenze o lesioni I locali di stabulazione sono costruiti in modo da permettere agli animali di coricarsi, giacere in decubito, alzarsi ed accudire se stessi senza difficoltà Per rispondere adeguato entrambe le condizioni devono essere Soddisfatte','S, NA, NB, NC, -, O','(*) in caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. dimensioni delle cuccette o delle poste; animali senza adeguato spazio; presenza di lesioni cutanee, ecc.). Le valutazioni ABM (lesioni, body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',11);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,128,1043,'LIBERTA'' DI MOVIMENTO','Caratteristiche delle poste e degli attacchi nella Stabulazione fissa','12. Caratteristiche delle poste e degli attacchi nella stabulazione fissa 146/2001 All. Libertà di movimento punto 7 "Allorché continuamente o regolarmente legato, incatenato o trattenuto, l''animale deve poter disporre di uno spazio adeguato alle sue esigenze fisiologiche ed etologiche, secondo l''esperienza acquisita e le conoscenze scientifiche" Nel caso di animali allevati a stabulazione fissa, è necessario osservare la tipologia dei dispositivi di attacco utilizzati, che non devono essere nocivi, non devono causare lesioni agli animali e devono consentirgli di alzarsi e coricarsi senza difficoltà. Si considera non adeguato: Presenza di poste costruite non correttamente in relazione agli animali presenti e/o con attacchi troppo corti o nocivi Livello della non conformita'': no; NO Si considera adeguato: Presenza di poste e di attacchi conformati in modo da consentire a tutti gli animali di coricarsi in modo corretto e contemporaneamente, senza presentare posture anomale o deformazioni scheletrico-articolari Livello della non conformita'': SI','12) Allorché gli animali siano continuamente o regolarmente legati, incatenati o trattenuti, gli attacchi eventualmente utilizzati non provocano lesioni e consentono agli animali di assumere una posizione confortevole, di giacere ed alzarsi, non provocano strangolamenti o ferite, sono regolarmente esaminati, riparati o sostituiti se Danneggiati','S, NA, NB, NC, -','(*) in caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. poste troppe corte che costringono gli animali a decubitare nella fossa posteriore di raccolta del letame (c.d. zanella); animali che decubitano obliquamente e non consentono agli altri di sdraiarsi; attacchi, corde o catene troppo corte che non consentono minimi movimenti o conformate in modo da causare strangolamenti o ferite. Le valutazioni ABM (lesioni, body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',12);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,129,1044,'EDIFICI E LOCALI DI STABULAZIONE','Igiene, pulizia e gestione degli ambienti di stabulazione e della lettiera bovine/bufale adulte (ovvero in lattazione, in asciutta, al parto e vacche nutrici in linea vacca- Vitello)','13. Igiene, pulizia e gestione degli ambienti di stabulazione e della lettiera bovine/bufale adulte (ovvero in lattazione, in asciutta, al parto e vacche nutrici in linea vacca-vitello) 146/2001 All. Fabbricati e locali di stabulazione punto 8. "I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati." Considerare l''igiene, la pulizia e la gestione degli ambienti di stabulazione, delle pavimentazioni e della lettiera (cuccette, poste o lettiera permanente) e la frequenza degli interventi di ripristino e ricambio del materiale. Nel caso di grigliato pulito, usato come area di decubito, la valutazione è al massimo adeguata. Solo nel caso degli allevamenti bufalini, date le caratteristiche di specie, condizione adeguata o ottimale non esclude la presenza di ambienti bagnati. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: - STALLA: Ambienti di stabulazione e lettiera sporchi, non gestiti e/o dannosi per gli animali - PASCOLO: prevalenza di ambienti sporchi, paludosi e fangosi Livello della non conformita'': no; NO Si considera adeguato: - STALLA: Ambienti di stabulazione e/o lettiera discretamente puliti e gestiti sufficientemente e/o grigliato pulito in quasi tutti i gruppi - PASCOLO: prevalenza di ambienti asciutti e puliti; presenza di ambienti bagnati e fangosi solo nei pressi dei punti di abbeverata Livello della non conformita'': SI Si considera ottimale: - STALLA: Ambienti di stabulazione e lettiera puliti, asciutti e gestiti in modo ottimale con ricambio frequente del materiale - PASCOLO: tutti gli ambienti sono asciutti e ben gestiti, anche nei pressi dei punti di abbeverata (presenza di fontane/vasche i cemento), copertura uniforme da coltre erbosa e/o rimozione regolare del materiale organico prodotto e aggiunta di lettiera pulita e asciutta all''interno del ricovero Livello della non conformita'': Ottimale','13) I materiali utilizzati per la costruzione dei locali di stabulazione che possono venire a contatto (es. lettiere) con gli animali non sono nocivi e devono poter essere puliti e disinfettati. Gli escrementi, l''urina e i foraggi non mangiati o caduti sono rimossi con regolarità','S, NA, NB, NC, -, O','(*)In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. condizioni igieniche dei locali e dei materiali di stabulazione, frequenza di ripristino e ricambio, condizioni igieniche degli animali). Le valutazioni ABM (pulizia animali, zoppie, lesioni cutanee) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',13);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,130,1045,'EDIFICI E LOCALI DI STABULAZIONE','Igiene, pulizia e gestione degli ambienti di stabulazione e della lettiera manze bovine/bufaline e tori da rimonta','14. Igiene, pulizia e gestione degli ambienti di stabulazione e della lettiera manze bovine/bufaline e tori da rimonta 146/2001 All. Fabbricati e locali di stabulazione punto 8. "I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati." Considerare l''igiene, la pulizia e la gestione degli ambienti di stabulazione, delle pavimentazioni e della lettiera (cuccette, poste o lettiera permanente) e la frequenza degli interventi di ripristino e ricambio del materiale. Nel caso di grigliato pulito, usato come area di decubito, la valutazione è al massimo adeguata. Solo nel caso degli allevamenti bufalini, date le caratteristiche di specie, condizione adeguata o ottimale non esclude la presenza di ambienti bagnati. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: - STALLA: Ambienti di stabulazione e lettiera sporchi, non gestiti e/o dannosi per gli animali - PASCOLO: Prevalenza di ambienti sporchi, paludosi e fangosi Livello della non conformita'': no; NO Si considera adeguato: - STALLA: Ambienti di stabulazione e/o lettiera discretamente puliti e gestiti sufficientemente e/o grigliato pulito in quasi tutti i gruppi - PASCOLO: Prevalenza di ambienti asciutti e puliti; presenza di ambienti bagnati e fangosi solo nei pressi dei punti di abbeverata Livello della non conformita'': SI Si considera ottimale: - STALLA: Ambienti di stabulazione e lettiera puliti, asciutti e gestiti in modo ottimale con ricambio frequente del materiale - PASCOLO: Tutti gli ambienti sono asciutti e ben gestiti, anche nei pressi dei punti di abbeverata (presenza di fontane/vasche in cemento), copertura uniforme da coltre erbosa e/o rimozione regolare del materiale organico prodotto e aggiunta di lettiera pulita e asciutta all''interno del ricovero Livello della non conformita'': Ottimale','14) I materiali utilizzati per la costruzione dei locali di stabulazione che possono venire a contatto (es. lettiere) con gli animali non sono nocivi e devono poter essere puliti e disinfettati. Gli escrementi, l''urina e i foraggi non mangiati o caduti sono rimossi con regolarità','S, NA, NB, NC, -, O','(*) In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. condizioni igieniche dei locali e dei materiali di stabulazione, frequenza di ripristino e ricambio, condizioni igieniche degli animali). Le valutazioni ABM (pulizia animali, zoppie, lesioni cutanee) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',14);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,131,1046,'EDIFICI E LOCALI DI STABULAZIONE','Igiene, pulizia e gestione degli ambienti di stabulazione e della lettiera bovini da ingrasso (> 6 mesi d''età)','15. Igiene, pulizia e gestione degli ambienti di stabulazione e della lettiera bovini da ingrasso (> 6 mesi d''età) 146/2001 All. Fabbricati e locali di stabulazione punto 8. "I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati." Considerare l''igiene, la pulizia e la gestione degli ambienti di stabulazione, delle pavimentazioni e della lettiera (cuccette, poste o lettiera permanente) e la frequenza degli interventi di ripristino e ricambio del materiale. Nel caso di grigliato pulito, usato come area di decubito, la valutazione è al massimo adeguata. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: - STALLA: Ambienti di stabulazione e lettiera sporchi, non gestiti e/o dannosi per gli animali - PASCOLO: prevalenza di ambienti sporchi, paludosi e fangosi Livello della non conformita'': no; NO Si considera adeguato: - STALLA: Ambienti di stabulazione e/o lettiera discretamente puliti e gestiti sufficientemente e/o grigliato pulito in quasi tutti i gruppi - PASCOLO: prevalenza di ambienti asciutti e puliti; presenza di ambienti bagnati e fangosi solo nei pressi dei punti di abbeverata Livello della non conformita'': SI Si considera ottimale: - STALLA: Ambienti di stabulazione e lettiera puliti, asciutti e gestiti in modo ottimale con ricambio frequente del materiale - PASCOLO: tutti gli ambienti sono asciutti e ben gestiti, anche nei pressi dei punti di abbeverata (presenza di fontane/vasche i cemento), copertura uniforme da coltre erbosa e/o rimozione regolare del materiale organico prodotto e aggiunta di lettiera pulita e asciutta all''interno del ricovero Livello della non conformita'': Ottimale','15) I materiali utilizzati per la costruzione dei locali di stabulazione che possono venire a contatto (es. lettiere) con gli animali non sono nocivi e devono poter essere puliti e disinfettati. Gli escrementi, l''urina e i foraggi non mangiati o caduti sono rimossi con regolarità','S, NA, NB, NC, -, O','(*)In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. condizioni igieniche dei locali e dei materiali di stabulazione, frequenza di ripristino e ricambio, condizioni igieniche degli animali). Le valutazioni ABM (pulizia animali, zoppie, lesioni cutanee) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',15);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,132,1047,'EDIFICI E LOCALI DI STABULAZIONE','Igiene dei locali e delle attrezzature per la mungitura','16. Igiene dei locali e delle attrezzature per la mungitura 146/2001 All. Fabbricati e locali di stabulazione punto 8. "I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati." BOVINE E BUFALE DA LATTE Valutare l''igiene generale dei locali e delle attrezzature per la mungitura (es. sala di mungitura) senza confonderlo con il loro livello tecnologico. In presenza del robot di mungitura valutare l''igiene dell''ambiente in cui è inserito e quella del robot stesso. Nel caso di allevamenti a stabulazione fissa, valutare l''igiene dei gruppi e delle attrezzature utilizzate per la mungitura. I limiti indicati son solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: Presenza di feci sui gruppi, su pavimenti e muri (o, nella stabulazione fissa, sulle altre attrezzature per la mungitura), o presenza di materiali difficilmente lavabili e disinfettabili Livello della non conformita'': no; NO Si considera adeguato: Pulizia adeguata dei gruppi ma feci su pavimenti e muri (o, nella stabulazione fissa, su altre superfici non a diretto contatto con le bovine) Livello della non conformita'': SI Si considera ottimale: Assenza di feci e buona igiene generale Livello della non conformita'': Ottimale','16) I locali e le attrezzature adibite alla mungitura sono pulite regolarmente e frequentemente e soddisfano i requisiti minimi dal punto di vista igienico-sanitario Gli escrementi e l''urina sono rimossi con regolarità','S, NA, NB, NC, -, O','(*) Indicare le condizioni igieniche dei locali e delle attrezzature adibite alla mungitura',16);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,133,1048,'EDIFICI E LOCALI DI STABULAZIONE','Assenza di fabbricati e locali di stabulazione nocivi (tutti i Gruppi)','17. Assenza di fabbricati e locali di stabulazione nocivi (tutti i gruppi) 146/2001 All. Fabbricati e locali di stabulazione punto 8 e 9. "8. I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati. 9. I locali di stabulazione e i dispositivi di attacco degli animali devono essere costruiti e mantenuti in modo che non vi siano spigoli taglienti o sporgenze tali da provocare lesioni agli animali." Tutti i materiali e le attrezzature utilizzati nei locali di stabulazione, nonché le superfici stesse, con cui gli animali possono venire a contatto, e i dispositivi di attacco eventualmente utilizzati non devono essere nocivi per gli animali e non devono avere spigoli taglienti o sporgenze in grado di provocare lesioni o traumi. Si considera non adeguato: Presenza di locali, ambienti o attrezzature nocivi per gli animali: es. lettiera dannosa, percorsi, accessi, delimitazioni, attacchi ed educatori elettrici che causano lesioni agli animali Livello della non conformita'': no; NO Si considera adeguato: Presenza di locali, ambienti e attrezzature idonei e non nocivi per gli animali Livello della non conformita'': SI','17) I materiali di costruzione, i recinti e le attrezzature con le quali gli animali possono venire a contatto non sono nocivi per gli animali stessi, non vi sono spigoli taglienti o sporgenze, le superfici sono facilmente lavabili e disinfettabili','S, NA, NB, NC, -','(*)In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. superficie di decubito o lettiera dannosa e gravemente lesiva per gli animali; evidenza di molti materassini rotti e deformati; presenza di spigoli, rastrelliere, passaggi, attacchi ed educatori elettrici ecc. taglienti e/o lesivi, ecc.). Le valutazioni ABM (lesioni cutanee, zoppie) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',17);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,134,1049,'EDIFICI E LOCALI DI STABULAZIONE','Pavimentazione (tutti i gruppi)','18. Pavimentazione (tutti i gruppi) 146/2001 All. Fabbricati e locali di stabulazione punto 8 e 9. "8. I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati. 9. I locali di stabulazione e i dispositivi di attacco degli animali devono essere costruiti e mantenuti in modo che non vi siano spigoli taglienti o sporgenze tali da provocare lesioni agli animali." Valutare le superfici di camminamento (corridoi, passaggi, accessi, corsia di alimentazione, aree di esercizio, ecc.) e di decubito che devono essere sufficientemente ruvide, sicure e libere da ostacoli consentendo agli animali di alzarsi, coricarsi e muoversi, anche rapidamente, senza scivolare. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: - STALLA: Presenza di pavimento - pieno o fessurato - non idoneo, liscio e scivoloso, o che impedisce i movimenti; - PASCOLO: Sentieri scivolosi per presenza di fango oppure presenza di ostacoli naturali e/o artificiali che non consentono il passaggio in completa sicurezza o che siano causa di ansia o eccitazione evitabili Livello della non conformita'': no; NO Si considera adeguato: - STALLA: Presenza di pavimento - pieno o fessurato - idoneo e rugoso (es. per la presenza di idonea rigatura, o rivestimento in gomma o quantità lieve di lettiera) per almeno la maggior parte delle superfici su cui camminano gli animali - PASCOLO: Il fondo dei sentieri di accesso è idoneo e ben gestito, gli animali possono camminare in sicurezza senza scivolare o subire lesioni, ansia o eccitazione evitabili Livello della non conformita'': SI Si considera ottimale: - STALLA: Solo se gli animali sono stabulati liberi ed hanno un pavimento - pieno o fessurato - idoneo e rugoso su tutte le superfici su cui camminano (compresa la presenza di lettiera permanente con materiale organico adeguato, perché abbondante, non abrasivo, ben conservato, assorbente); - PASCOLO: I sentieri di accesso presentano fondo idoneo, non scivoloso e non abrasivo, con assenza di ostacoli e/o pericoli per tutta la loro lunghezza Livello della non conformita'': Ottimale','18) La pavimentazione, le superfici di camminamento sono conformi alla descrizione adeguata','S, NA, NB, NC, -, O','(*)In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. pavimentazione, superfici di camminamento dannose e gravemente lesive per gli animali). Le valutazioni ABM (zoppie, lesioni cutanee) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',18);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,135,1050,'EDIFICI E LOCALI DI STABULAZIONE','Infermeria','19. Infermeria 146/2001 All. Controllo punto 4 "Ove necessario gli animali malati o feriti vengono isolati in appositi locali muniti, se del caso, di lettiere asciutte o confortevoli." Per curare i soggetti con problemi sanitari e/o lesioni traumatiche, è opportuno che ogni allevamento abbia a disposizione un''area di isolamento-infermeria o, in caso di necessità e in base alle caratteristiche dell''azienda (es. mandrie di piccole dimensioni) poss servirsene. Gli animali in infermeria devono poter disporre di alimento ed acqua fresca ad libitum. Nel reparto infermeria devono essere presenti solo animali con patologie ben identificate. In caso di infermeria con cuccette o poste fisse il giudizio può essere solo intermedio. Se la zona è un recinto individuale di isolamento, esso deve avere dimensioni adeguate, permettere all''animale di girarsi facilmente, di avere contatti visivi ed olfattivi con gli altri animali, salvo nel caso in cui ciò non sia in contraddizione con specifiche prescrizion veterinarie. La zona di isolamento-infermeria deve essere fornita di lettiera confortevole, paglia o materiali similari, tappetini di gomma morbida e/o altre pavimentazioni che possano migliorare le condizioni degli animali nel caso in cui la condizione clinica lo richieda, come ad esempio per animali in stato di decubito prolungato o zoppi. Si considera requisito adeguato l''esistenza di un''infermeria identificata e circoscritta adibita a tale funzione. Quest''area deve essere segnalata tramite apposita cartellonistica o altra indicazione scritta (se è soggetta a rotazione) o segnalata sulla planimetria aziendale (se è a localizzazione fissa). Tuttavia, in caso di allevamenti di piccole dimensioni o alla posta per "infermeria identificata" si intende che: - in caso di presenza di animali feriti o che necessitano isolamento, il box o posta o altro spazio o settore deve essere identificato; - in caso non ci siano animali feriti o bisognosi di isolamento, il box o posta o altro spazio o settore può essere solo "identificabile", ma l''allevatore deve dare prova della possibilità di ricavare tale zona in qualunque momento, in caso di necessità. Per motivazioni sanitarie, si considera ottimale la presenza di una zona di isolamento-infermeria con capienza complessiva no inferiore al 3% del numero medio di animali quotidianamente presenti in allevamento e con spazi disponibili molto abbondanti (es. più di 8-10 m2/capo), facili da ispezionare da parte dell''operatore, eventualmente suddivisi su più box, con lettiera perman ottimamente gestita, abbeveratoi e facile accesso al cibo. Negli allevamenti a stabulazione fissa, per posta specifica si intende una posta separata dalle altre o (se la condizione clinica consente) la posta dell''animale stesso purchè delimitata da sbarre e con autoalimentatore in modo da proteggere l''animale malato o infermo da quelli vicini. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: Assenza di qualsiasi locale / posta specifico ed identificato nel quale, in caso di necessità, possano essere isolati animali feriti o malati Livello della non conformita'': no; NO Si considera adeguato: Presenza di locale / posta identificato ed appositamente preparato per accogliere animali malati o feriti munito di lettiera asciutta o tappetino confortevole, ove la condizioni clinica lo richieda Livello della non conformita'':SI Si considera ottimale: Animali liberi in locale specifico ed identificato, con lettiera permanente asciutta e confortevole, in grado di ospitare almeno il 3% del numero medio di animali quotidianamente presenti in allevamento e con spazi disponibili molto abbondanti (es. più di 8-10 m2/capo) Livello della non conformita'': Ottimale','19) In caso di necessità, gli animali possono essere isolati in appositi locali muniti, se del caso, di lettiera asciutta o tappetino Confortevole','S, NA, NB, NC, -, O','(*) Descrivere, se del caso, le caratteristiche del recinto di isolamento-infermeria',19);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,136,1051,'EDIFICI E LOCALI DI STABULAZIONE','Temperatura ed umidità (tutti i Gruppi)','20. Temperatura ed umidità (tutti i gruppi) 146/2001 All. Fabbricati e locali di stabulazione Punto 10 "10. La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas devono essere mantenute entro limiti non dannosi per gli animali." Si valutano la tipologia di stalla e le attrezzature di raffrescamento. Si considera stalla aperta quando non presenta muri su nessuno dei 4 lati oppure quando è presente un solo lato chiuso, senza che questo limiti una buona aerazione della stalla. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: la presenza di condizioni microclimatiche dannose per gli animali: es. ambienti chiusi o polverosi o stalle semi-aperte senza un''adeguata aerazione Livello della non conformita'': no; NO Si considera adeguato: la presenza di condizioni microclimatiche idonee per gli animali: es. ventilazione naturale (stalla aperta) o impianti di ventilazione/aerazione senza sistemi di controllo Livello della non conformita'': SI Il requisito superiore prevede, oltre ai criteri per l''adeguatezza, la presenza di condizioni microclimatiche ottimali per gli animali: es. presenza nelle aree di stabulazione più importanti (asciutta, zona parto e lattazione) di impianti di condizionamento del microclima con sistemi di controllo automatizzati / pascolo idoneo Livello della non conformita'': Ottimale','20) La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria sono mantenute entro limiti non dannosi per gli animali All''atto dell''ispezione, T° e UR sono adeguate alle esigenze etologiche della specie e all''età degli animali','S, NA, NB, NC, -, O','(*) Descrivere come vengono garantite idonee condizioni microclimatiche',20);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,137,1052,'EDIFICI E LOCALI DI STABULAZIONE','Presenza di gas nocivi (tutti i Gruppi)','21. Presenza di gas nocivi (tutti i gruppi) 146/2001 All. Fabbricati e locali di stabulazione Punto 10 "10. La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas devono essere mantenute entro limiti non dannosi per gli animali." Durante la valutazione dell''allevamento, sono considerati accettabili tenori di ammoniaca inferiori a 20 ppm e tenori di anidrid Carbonica inferiori a 3.000 ppm. Nel caso di concentrazioni di gas non adeguate (es. NH3>20 ppm) può essere rilevata la seguente sintomatologia negli anima rossore delle congiuntive e lacrimazione; inoltre tali sintomi possono essere percepiti anche dal valutatore, con il forte tipico odore pungente negli ambienti di stabulazione. In caso di sospetto di condizione inadeguata, è necessario confermare la condizione, misurando la concentrazione dei gas nocivi in allevamento mediante un rilevatore di gas portatile da posizionare al centro della stalla all''altezza degli animali. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato l''evidenza di una concentrazione di gas dannosa per gli animali (indicazioni di riferimento dei limiti: NH3 > 20 ppm; CO2 > 3000 ppm) Livello della non conformita'': no; NO Si considera adeguato l''evidenza di una concentrazione dei gas non dannosa per gli animali (indicazioni di riferimento dei limiti: NH3 < 20 ppm; CO2 < 3000 ppm) Livello della non conformita'': SI Si considera ottimale l''evidenza di una concentrazione dei gas ottimali per gli animali (indicazioni di riferimento dei limiti: NH3 < 10 ppm; CO2 < 3000 ppm) Livello della non conformita'': Ottimale','21) Le concentrazioni di gas sono mantenute entro limiti non dannosi per gli animali','S, NA, NB, NC, -, O','(*) Se del caso, registrare i valori misurati NH3... CO2... a campione in tutti i settori e tipologie, per confermare un sospetto di condizione inadeguata',21);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,138,1053,'EDIFICI E LOCALI DI STABULAZIONE','Illuminazione minima - ciclo di luce per gli animali (tutti i Gruppi)','22. Illuminazione minima - ciclo di luce per gli animali (tutti i gruppi) 146/2001 All. Fabbricati e locali di stabulazione Punto 11 "11. Gli animali custoditi nei fabbricati non devono essere tenuti costantemente al buio o esposti ad illuminazione artificiale senza un adeguato periodo di riposo. Se la luce naturale disponibile è insufficiente a soddisfare esigenze comportamentali e fisiologiche degli animali, occorre prevedere un''adeguata illuminazione artificiale." I bovini stabulati che non hanno accesso alla luce naturale dispongono di un periodo di luce ininterrotto non inferiore alle 8 ore (con intensità minima di almeno 40 lux) e di un periodo di buio ininterrotto (o debole illuminazione notturna) non inferiore alle 8 or Indicativamente, in condizioni di routine, si può considerare come riferimento per i 40 lux, una luce che permette di leggere la checklist senza sforzo. Le fonti di luce artificiale dovrebbero essere installate in modo da non causare disagio agli animali. In caso di sospetto di condizione inadeguata, è necessario confermare con luxometro. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: assenza o insufficienza di illuminazione naturale o artificiale / assente o insufficiente periodo di riposo Livello della non conformita'': no; NO Si considera adeguato: presenza di illuminazione naturale o artificiale adeguata per almeno 8 ore al giorno / presenza di un periodo di riposo adeguato per almeno 8 ore al giorno Livello della non conformita'': SI','22) Gli animali custoditi nei fabbricati non sono tenuti costantemente al buio, ad essi sono garantiti un adeguato periodo di luce (naturale o artificiale) ed un adeguato periodo di riposo','S, NA, NB, NC, -','(*) Registrare il tipo di illuminazione naturale/artificiale/assente e in caso di rilievo non conforme, confermare con luxometro.',22);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,139,1054,'EDIFICI E LOCALI DI STABULAZIONE','Presenza di ripari nelle aree esterne per gli animali custoditi al di fuori dei fabbricati (tutti i gruppi)','23. Presenza di ripari nelle aree esterne per gli animali custoditi al di fuori dei fabbricati (tutti i gruppi) 146/2001 All. Animali custoditi al di fuori dei fabbricati Punto 12 "12. Agli animali custoditi al di fuori dei fabbricati deve essere fornito, in funzione delle necessità e delle possibilità, un riparo adeguato dalle intemperie, dai predatori e da rischi per la salute." Si considerano i ripari sia di tipo naturale (alberi, anfratti, grotte, ecc.) che di tipo artificiale (tettoie, ricoveri, ecc.) a condizione che offrano un riparo adeguato, in relazione alla stagione e alla località. Se nell''allevamento non ci sono animali custoditi al di fuori dei fabbricati, il requisito si considera non applicabile. Si considera non adeguato: In funzione della valutazione del rischio, delle necessità e delle possibilità, l''assenza, insufficienza presenza di ripari non adeguati anche per un solo gruppo di animali Livello della non conformita'': no; NO Si considera adeguato: Presenza di ripari sufficienti ed adeguati per tutti gli animali Livello della non conformita'': SI Si considera ottimale: Presenza di ripari di tipo artificiale facilmente accessibili, sufficienti ed idonei a proteggere tutti gli animali dai pericoli ambientali Livello della non conformita'': Ottimale','23) Gli animali custoditi al di fuori dei fabbricati hanno ripari adeguati e sufficienti','S, NA, NB, NC, -, O','(*) Descrivere i ripari, la loro localizzazione e le modalità con le quali gli animali li raggiungono',23);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,140,1055,'ATTREZZATURA AUTOMATICA E MECCANICA','Ispezione delle attrezzature automatiche e Meccaniche','24. Ispezione delle attrezzature automatiche e meccaniche 146/2001 All. Impianti automatici o meccanici Punto 13 "13. Ogni impianto automatico o meccanico indispensabile per la salute ed il benessere degli animali deve essere ispezionato almeno una volta al giorno. Gli eventuali difetti riscontrati devono essere eliminati immediatamente; se ciò non è possibile, occorre prendere le misure adeguate per salvaguardare la salute ed il benessere degli animali." La macchina mungitrice, l''autoalimentatore, ecc., possono essere considerati impianti automatici o meccanici indispensabili per la salute degli animali; per questo devono essere ispezionati una volta al giorno e i difetti riscontrati eliminati immediatamente. Se ciò non è possibile occorre prendere misure adeguate per salvaguardare la salute ed il benessere degli animali (es. possedere un generatore di corrente). Si considera non adeguato: mancanza di regolari ispezioni giornaliere e/o rilievo di una mancanza di interventi tempestivi Livello della non conformita'': no; NO Si considera adeguato: Almeno 1 ispezione al giorno Livello della non conformita'': SI Si considera ottimale: Presenza di procedure o manuali di buone pratiche comprensivi di istruzioni operative per gli operatori, che garantiscano il controllo giornaliero e la conservazione delle relative registrazioni Livello della non conformita'': Ottimale','24) Gli impianti automatici o meccanici sono ispezionati almeno una volta al giorno Sono prese misure adeguate per salvaguardare la salute e il benessere degli animali in caso di non funzionamento degli impianti (es. metodi alternativi di alimentazione; generatore di corrente; ecc.)','S, NA, NB, NC, -, O','(*) Raccolta di informazioni tramite interviste agli operatori, consultazione di manuali di buone pratiche e relative registrazioni',24);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,141,1056,'ATTREZZATURA AUTOMATICA E MECCANICA','Allarme impianto di ventilazione','25. Allarme impianto di ventilazione 146/2001 All. Impianti automatici o meccanici Punto 13 "13. Se la salute ed il benessere degli animali dipendono da un impianto di ventilazione artificiale, deve essere previsto un adeguato impianto di riserva per garantire un ricambio di aria sufficiente a salvaguardare la salute e il benessere degli animali. In caso di guasto all''impianto deve essere previsto un sistema di allarme che segnali il guasto. Detto sistema d''allarme deve essere sottoposto controlli regolari." Qualora per la salute ed il benessere degli animali sia presente e necessario un sistema di aerazione artificiale con ventilazion unicamente forzata, occorre prevedere un sistema di allarme che segnali eventuali guasti all''allevatore, nonché un opportuno sistema sostitutivo che all''occorrenza consenta un ricambio d''aria sufficiente per gli animali presenti, in attesa che il guasto venga corretto (es. apertura d''emergenza di finestre/generatore di corrente, ecc.). Il sistema di allarme e il sistema sostitutivo devono essere verificati regolarmente, soprattutto se il loro malfunzionamento metterebbe in serio rischio la salute ed il benessere degli animali. Se l''allevamento non necessita di un impianto di ventilazione forzata (es. idonea circolazione naturale dell''aria, pascolo, ecc.) requisito si considera non applicabile. Si considera non adeguato: Assenza di un sistema di allarme e di sostituzione all''impianto di ventilazione artificiale e/o assenza di controlli regolari del sistema di allarme Livello della non conformita'': no; NO Si considera adeguato: Presenza di un sistema di allarme e di sostituzione all''impianto di ventilazione artificiale regolarme controllato Livello della non conformita'': SI','25) Se la salute e il benessere degli animali dipendono da un impianto di ventilazione artificiale, è previsto un adeguato impianto di riserva per garantire un ricambio di aria sufficiente a salvaguardare la salute e il benessere degli animali in caso di guasto all¿impianto stesso È previsto un sistema di allarme che segnali eventuali guasti','S, NA, NB, NC, -','(*) Registrare la verifica del corretto funzionamento del sistema di allarme e del sistema sostitutivo effettuata durante il controllo ufficiale; annotare la frequenza degli interventi di verifica e manutenzione effettuati dall''allevatore',25);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,142,1057,'ALIMENTAZIONE, ABBEVERAGGIO ED ALTRE SOSTANZE','Gestione degli alimenti e della razione Giornaliera','26. Gestione degli alimenti e della razione giornaliera 146/2001 All. Mangimi, acqua e altre sostanze Punto 14 "14. Agli animali deve essere fornita un''alimentazione sana adatta alla loro età e specie e in quantità sufficiente a mantenerli in buona salute e a soddisfare le loro esigenze nutrizionali. Gli alimenti o i liquidi sono somministrati agli animali in modo da non causare loro inutili sofferenze o lesioni e non contengono sostanze che possano causare inutili sofferenze o lesioni." I sistemi di alimentazione dovrebbero permettere ad ogni individuo di soddisfare i propri fabbisogni per quantità e qualità deg alimenti. La dieta dovrebbe fornire energia sufficiente, nutrienti e fibra alimentare tali da soddisfare i requisiti nutrizionali e rispettare la fisiologia digestiva e metabolica degli animali. Per poter soddisfare le esigenze nutrizionali degli animali e far sì che l''alimentazione sia adeguata al loro sviluppo corporeo, all''ordine di parto, allo stato fisiologico e al livello produttivo è necessario che, almeno ognuno dei gruppi base previsti dall''allevamen disponga di una razione specifica. Tale razione specifica può essere scritta o registrata su pesa o comunque, anche se non scritta, nota all''allevatore o al personale che si occupa della preparazione degli alimenti per gli animali, ed è buona prassi che sia mantenuta il più possibile stabile nel tempo. La corretta alimentazione degli animali è anche collegata alla qualità degli alimenti e dell''acqua che la compongono, che devono essere di origine conosciuta e conservati in ambienti idonei (es. silos, magazzini, fienili, sistema di distribuzione idrica) per evitare alterazioni e contaminazioni con sostanze tossico-nocive. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: - STALLA: presenza di una razione non adatta agli animali perché non adeguata alle loro esigenze, mai stata calcolata, non in rapporto ai fabbisogni o composta da alimenti non sani; - PASCOLO: es. nessuna tecnica di pascolamento e assenza di integrazione aggiuntiva all''occorrenza Livello della non conformita'': no; NO Si considera adeguato: - STALLA: presenza di una razione adatta agli animali, perché specifica per ogni gruppo di base (manze-vacche-tori-ingrassi) composta da alimenti sani; - PASCOLO: es. alimentazione basata sul pascolamento turnato e in determinati periodi è prevista un''integrazione alimentare Livello della non conformita'': SI Si considera ottimale: - STALLA: presenza di una razione ottimale per gli animali, perché calcolata da un alimentarista, revisionata di frequente o comunque ad ogni cambio di alimenti, e composta da alimenti sani; - PASCOLO: es. animali al pascolo (rigoglioso o con integrazione) per un periodo di almeno 4-5 mesi e integrazione specifica per gli animali che hanno bisogno (es. per le fattrici in allattamento) Livello della non conformita'': Ottimale','26) l''alimentazione è adeguata in rapporto all''età, al peso e alle esigenze comportamentali e fisiologiche degli animali Gli alimenti o i liquidi sono somministrati agli animali in modo da non causare loro inutili sofferenze o lesioni e non contengono sostanze che possano causare inutili sofferenze o lesioni','S, NA, NB, NC, -, O','(*) Descrivere eventualmente la razione o le razioni (se specifiche per i diversi gruppi di animali), le modalità di calcolo, il nominativo dell''alimentarista o del veterinario che l''ha calcolata, ecc. Le valutazioni ABM (body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',26);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,143,1058,'ALIMENTAZIONE, ABBEVERAGGIO ED ALTRE SOSTANZE','Tipologia di Alimentazione','27. Tipologia di alimentazione 146/2001 All. Mangimi, acqua e altre sostanze Punto 15 "15. Tutti gli animali devono avere accesso ai mangimi ad intervalli adeguati alle loro necessità fisiologiche." L''alimentazione dovrebbe essere somministrata ad libitum per garantire ad ogni animale di alimentarsi secondo esigenza durante le 24 h. Nel caso di alimentazione frazionata gli alimenti dovrebbero essere garantiti quasi costantemente nell''arco di 24 h, comprensivi sia della frazione fibrosa che di quella concentrata (quest''ultima deve essere somministrata in almeno due occasioni). In tal senso è auspicabile l''utilizzo del carro unifeed. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: Accesso all''alimentazione ad intervalli non corretti perchè non garantita nelle 24h e frazionata c intervalli non adeguati alle necessità fisiologiche degli animali Livello della non conformita'': no; NO Si considera adeguato: Accesso all''alimentazione ad intervalli corretti perché garantita nelle 24h e frazionata correttam (concentrati somministrati almeno in 2 volte) Livello della non conformita'': SI Si considera ottimale: Accesso agli alimenti in modo costante nelle 24 ore: es. carro unifeed disponibile per 24h/giorno Livello della non conformita'': Ottimale','27) Se non viene praticata l''alimentazione ad libitum o con sistemi automatici è assicurato l''accesso agli alimenti a tutti gli animali contemporaneamente per evitare competizioni a intervalli adeguati alle loro necessità fisiologiche','S, NA, NB, NC, -, O','(*) Descrivere le modalità di somministrazione dell''alimento ed eventualmente gli intervalli tra le varie somministrazioni. Le valutazioni ABM (body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',27);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,144,1059,'ALIMENTAZIONE, ABBEVERAGGIO ED ALTRE SOSTANZE','Disponibilità di acqua e numero di abbeveratoi (tutti i gruppi)','28. Disponibilità di acqua e numero di abbeveratoi (tutti i gruppi) 146/2001 All. Mangimi, acqua e altre sostanze Punti 16 e 17 "16. Tutti gli animali devono avere accesso ad un''appropriata quantità di acqua, di qualità adeguata [...] 17. Le attrezzature per la somministrazione di mangimi e di acqua devono essere concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua e le conseguenze negative derivanti da rivalità tra gli animali." Per quanto riguarda il numero di abbeveratoi, si considera adeguato: BOVINE E BUFALE ADULTE DA LATTE: almeno 1 abbeveratoio ogni 10 animali o se a vasca 6-7 cm/capo MANZE (bovine e bufale) DA LATTE: almeno 1 abbeveratoio ogni 15 animali o se a vasca 5 cm/capo BOVINI DA CARNE: almeno 1 abbeveratoio ogni 13 animali o se a vasca 6 cm/capo ALTRI BOVINI (es. linea vacca-vitello): almeno 1 abbeveratoio ogni 14 animali o se a vasca 5 cm/capo AL PASCOLO: se il tipo di animali non è forte produttore di latte, almeno una vasca da 1000 litri per 20 animali (oppure garantire 50 litri/capo/giorno), collocata in prossimità della zona di alimentazione. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio Per quanto riguarda la qualità dell''acqua si considera adeguato che gli abbeveratoi siano mantenuti in buone condizioni di pulizia, liberi da feci e residui di alimento inveterato; in aggiunta al requisito adeguato si considera ottimale, l''esecuzione su base almeno annuale di esami per la potabilità dell''acqua. Si considera non adeguato: - - STALLA: Assenza di acqua di abbeverata o acqua razionata (non ad libitum) o acqua insalubre per uno o più animali particolare riguardo agli animali malati o sottoposti a condizioni atmosferiche di grande calore / presenza di attrezzature non idonee per la somministrazione di acqua (es. lesive o palesemente insufficienti) - - PASCOLO: distanze eccessive tra le fonti di acqua e le aree pascolative o acqua insalubre Livello della non conformita'': no; NO Si considera adeguato: - - STALLA: Presenza di abbeveratoi funzionanti in tutti i gruppi, con somministrazione di acqua pulita e in numero sufficiente - - PASCOLO: presenza di abbeveratoi artificiali o fonti naturali posizionati vicino alle aree pascolative e disponibili per tutti i gruppi e/o presenza di abbeveratoi mobili in sostituzione di quelli fissi e che non siano pericolosi per gli animali Livello della non conformita'': SI - Il requisito superiore prevede, oltre ai criteri per l''adeguatezza, la presenza di abbeveratoi funzionanti del tipo "a livello" in tutti i gruppi e deve essere prevista l''esecuzione di esami annuali per la potabilità dell''acqua o l''approvvigionamento dall''acquedotto in maniera diretta (senza vasche di raccolta intermedia) Livello della non conformita'': Ottimale','28) La modalità di somministrazione dell''acqua consente un''adeguata idratazione degli animali anche nei periodi di intenso calore. Il numero di punti di abbeverata è in linea con le indicazioni fornite? E'' garantito per tutti gli animali l''accesso ad una appropriata quantità di acqua di qualità adeguata. Le attrezzature per la somministrazione di mangimi e di acqua sono concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua e le conseguenze negative derivanti da rivalità tra gli Animali','S, NA, NB, NC, -, O','(*) Registrare modalità di accesso all''acqua e numero di abbeveratoi, descrivere la frequenza di pulizia di abbeveratoi, riportare gli estremi di eventuali esami dell''acqua, ecc. Le valutazioni ABM (body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',28);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,145,1060,'ALIMENTAZIONE, ABBEVERAGGIO ED ALTRE SOSTANZE','Numero di posti disponibili in mangiatoia (tutti i gruppi)','29. Numero di posti disponibili in mangiatoia (tutti i gruppi) 146/2001 All. Mangimi, acqua e altre sostanze Punto 17 "17. Le attrezzature per la somministrazione di mangimi e di acqua devono essere concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua e le conseguenze negative derivanti da rivalità tra gli animali." Verificare quanti animali possono accedere contemporaneamente alla mangiatoia. Nel caso di allevamento a stabulazione fiss valutare che tutti gli animali possano accedere all''alimentazione, senza conseguenze negative. Di seguito sono forniti gli spazi ritenuti adeguati per ogni tipologia di animale: BOVINE ADULTE: per ogni capo garantire uno spazio di almeno 68 cm lineari; MANZE BOVINE: per ogni capo garantire uno spazio di almeno 50 cm lineari; BOVINI DA CARNE: per animali sotto 200 kg p.v. garantire >= 40 cm/capo; per animali tra 200 e 300 kg p.v. garantire >= 50 cm/capo; per animali tra 300 e 400 kg p.v. garantire >= 60 cm/capo; per animali tra 400 e 500 kg p.v. garantire >= 65 cm/capo; per animali oltre 600 kg p.v. garantire >= 70 cm/capo. TORI ADULTI: come i bovini da carne; se con le corna garantire almeno 1 m/capo. BUFALE ADULTE: per ogni capo garantire uno spazio di almeno 75 cm lineari; MANZE BUFALINE: per ogni capo garantire uno spazio di almeno 55 cm lineari. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera accesso agli alimenti non adeguato: - - per razioni frazionate (non unifeed): meno del 100% degli animali può alimentarsi contemporaneamente - - per razioni con unifeed: meno del 70% degli animali può alimentarsi contemporaneamente Livello della non conformita'': no; NO Si considera accesso agli alimenti adeguato: - per razioni frazionate (non unifeed): il 100% degli animali può alimentarsi contemporaneamente - per razioni con unifeed: più del 70% degli animali può alimentarsi contemporaneamente Livello della non conformita'': SI Si considera accesso agli alimenti ottimale: - STALLA: presenza di 2 accessi differenziati, con numero totale di spazi maggiore (superiore del 20%) rispetto al numero di animali oppure possibilità di accesso ad un pascolo idoneo (anche periodica 60 gg/anno per gli animali da latte) - PASCOLO: con presenza di ampie zone pascolative e/o presenza di punti di integrazione adeguati al numero di animali Livello della non conformita'': Ottimale','29) Tutti gli animali hanno accesso all''alimento con una frequenza adeguata alle loro necessità riducendo al minimo le competizioni derivanti dalla rivalità tra animali','S, NA, NB, NC, -, O','(*) Descrivere le modalità di accesso all''alimento, riportare gli spazi disponibili per l''alimentazione ... Le valutazioni ABM (body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la Conformità',29);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,146,1061,'ALIMENTAZIONE, ABBEVERAGGIO ED ALTRE SOSTANZE','Somministrazione di sostanze Illecite','30. Somministrazione di sostanze illecite 146/2001 All. Mangimi, acqua e altre sostanze punto 18. "18. Nessuna altra sostanza, ad eccezione di quelle somministrate a fini terapeutici o profilattici o in vista di trattamenti zootecnici come previsto nell''art. 1, paragrafo 2, lettera c) della direttiva 96/22/CE, deve essere somministrata ad un animale, a meno che gli studi scientifici sul benessere degli animali e l''esperienza acquisita ne abbiano dimostrato l''innocuità per la sua salute e il benessere" Verificare il registro o le registrazioni elettroniche dei trattamenti e l''armadietto o eventuali confezioni o giacenze di farmaci. - Si considera non adeguato: evidenza di somministrazione di sostanze illecite Livello della non conformita'': NO - Si considera adeguato: nessuna evidenza di somministrazione di sostanze illecite Livello della non conformita'':SI','30) Non viene somministrata alcuna sostanza, ad eccezione di quelle somministrate a fini terapeutici o profilattici o in vista di trattamenti zootecnici come previsto dalla normativa vigente I trattamenti terapeutici e profilattici sono regolarmente prescritti da un medico veterinario','S, NC, -','(*) Descrivere le verifiche condotte',30);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,147,1062,'MUTILAZIONI','Mutilazioni e altre Pratiche','31. Mutilazioni e altre pratiche 146/2001 All. Mutilazioni Punto 19 "19. E'' vietato [...] il taglio di [...] di code per i bovini se non a fini terapeutici certificati. La cauterizzazione dell''abbozzo corneale e'' ammessa al di sotto delle tre settimane di vita. [...] La castrazione e'' consentita per mantenere la qualita'' dei prodotti e le pratiche tradizionali di produzione a condizione che tali operazioni siano effettuate prima del raggiungimento della matura sessuale personale qualificato, riducendo al minimo ogni sofferenza per gli animali. [...] Le pratiche di cui al presente punto sono effettuate sotto il controllo del medico veterinario dell''azienda." Per mutilazione si intende una pratica non effettuata per fini terapeutici o diagnostici, che si manifesta quale danno o perdita di una parte sensibile del corpo o quale alterazione della struttura dell''osso. Verificare se sono presenti animali con mutilazioni (es. taglio della coda, castrazione, mozzamento delle corna in animali adulti). Se queste sono eseguite e sono riconducibili al periodo di permanenza dell''animale nell''azienda ispezionata, deve essere verificato il registro dei trattamenti (cartaceo o elettronico) per controllare se contestualmente è stato effettuato/prescritto dal veterinario un trattamento anestetico e analgesico. Tutti i trattamenti, che prevedono operazioni cruente, devono essere eseguiti con materiali sterili o a perdere ed espletati in modo da evitare all''animale dolore o sofferenza prolungata o non necessaria Si considera ottimale la mancanza di qualsiasi mutilazione e/o castrazione su tutti gli animali. Si considera non adeguato: Presenza di animali con incisioni o con mutilazioni/castrazioni eseguite senza il rispetto dei tempi e delle modalità previste dalla normativa Livello della non conformita'': NO Si considera adeguato: Presenza di animali con mutilazioni/castrazioni eseguite nel rispetto dei tempi e delle modalità previste dalla normativa Livello della non conformita'': SI Si considera ottimale: Presenza di tutti gli animali integri e che non presentano incisioni o mutilazioni/castrazioni Livello della non conformita'': Ottimale','31) Sono rispettate le disposizioni pertinenti in caso di mutilazioni/castrazioni effettuate sugli animali?','S, NC, -, O','(*) Descrivere l''eventuale tipo di mutilazione/incisione/castrazione, le categorie e le marche auricolari degli animali interessati e i box/ricoveri dove sono alloggiati. Descrivere le modalità ed i medicinali utilizzati, riportare il nominativo del veterinario, ecc.',31);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,148,1063,'PROCEDURE D''ALLEVAMENTO','Disposizioni Generali','32. Disposizioni generali 32. Disposizioni generali 146/2001 Art. 2 Comma 1 Obblighi dei proprietari, dei custodi dei detentori degli animali 1. Il proprietario o il custode ovvero il detentore deve: a) adottare misure adeguate per garantire il benessere dei propri animali e affinché'' non vengano loro provocati dolore, sofferenze o lesioni inutili; b) allevare e custodire gli animali [omissis] in conformità alle disposizioni di cui all''allegato. 146/2001 All. Procedure di allevamento punto 20 "Non devono essere praticati l''allevamento naturale o artificiale o procedimenti di allevamento che provochino o possano provocare agli animali in questione sofferenze o lesioni. Questa disposizione non impedisce il ricorso a taluni procedimenti che possono causare sofferenze o ferite minime o momentanee o richiedere interventi che non causano lesioni durevoli, se consentiti dalle disposizioni nazionali." Valutare se vengono praticate procedure d''allevamento in contrasto con una o più delle 5 libertà, tali da causare evidenze negative sullo stato degli animali. Si considera non adeguato: la gestione delle fasi produttive in allevamento ha evidenze negative sullo stato degli animali Livello della non conformita'': no, NO Si considera adeguato: la gestione delle fasi produttive in allevamento non evidenzia alcun effetto negativo sullo stato degli animali Livello della non conformita'': SI','32) Non sono praticati procedimenti di allevamento che provocano o possano provocare agli animali sofferenze o lesioni. Nel caso si ricorra a taluni procedimenti che possono causare sofferenze o ferite minime o momentanee o richiedere interventi che non causano lesioni durevoli, sono consentiti dalle disposizioni vigenti','S, NA, NB, NC, -','(*) In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate. Le valutazioni ABM, di seguito elencate, possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità.',32);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,149,1064,'PROCEDURE D''ALLEVAMENTO','Biosicurezza - Lotta agli Infestanti','33. Biosicurezza - Lotta agli infestanti Reg. CE 852/2004 All. I parte A cap. II punto 4 f) "Gli operatori del settore alimentare che allevano, raccolgono o cacciano animali o producono prodotti primari di origine animale devono, se del caso, adottare misure adeguate per evitare la contaminazione da parte di animali e altri insetti nocivi" Verificare la presenza di azioni volte al controllo degli infestanti (mosche, roditori e parassiti). Se l''ambiente in cui vivono gli animali è ritenuto tale da non richiedere alcuna azione di controllo, il requisito si considera applicabile. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: Completa insussistenza di qualsiasi azione volta al controllo degli infestanti o piani approssimativi (es. dichiarata la presenza di gatti come unico metodo di lotta ai roditori) No, NO Si considera adeguato: Presenza di procedure approssimative e non formalizzate (assenza di piani scritti) ma giudicate idonee a contenimento dei principali infestanti Livello della non conformita'': SI Si considera ottimale: Presenza di azioni strutturate volte al controllo degli infestanti (es. piani di derattizzazione con descrizione dei principi attivi utilizzati, schede di sicurezza e planimetria con localizzazione delle esche, definizione dei limiti critici e delle azion conseguenti al loro superamento, la registrazione dei trattamenti e delle azioni correttive) Livello della non conformita'': Ottimale','33) Vengono messe in atto azioni preventive e vengono eseguiti interventi contro mosche, roditori e parassiti','S, NA, NB, NC, -, O','(*) Descrivere le condizioni rilevate (es. presenza di mosche o evidenza della presenza di roditori o loro tracce). Descrivere, nel caso siano presenti perché ritenute necessarie, le modalità e periodicità di disinfestazione, derattizzazione e pulizia dell''allevamento (presenza di una ditta esterna o procedure interne aziendali), annotare gli estremi di alcune registrazioni e delle postazioni ispezionate. In caso N.A. specificare il motivo.',33);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,150,1065,'ABM','Stato di nutrizione misurato tramite body condition score (BCS)','34. Stato di nutrizione misurato tramite body condition score (BCS) Valutare la condizione corporea degli animali, utilizzando una scala di BCS da 1 a 5 per i bovini e una scala da 1 a 10 per i bufali. Sono da registrare gli animali molto magri o molto grassi (eccetto nei bovini da ingrasso). BOVINE (ADULTE E MANZE): valori di BCS minori di 2 e maggiori di 4,25 sono oltre i limiti accettati BOVINI DA INGRASSO: valori di BCS minori di 2 sono oltre i limiti accettati BUFALE ADULTE: Valori di BCS minori di 4,5 e maggiori di 7,5 sono oltre i limiti accettati Più del 10% di animali con BCS oltre i limiti Livello della non conformita'': Insufficiente Tra il 5% e il 10% di animali con BCS oltre i limiti (tra il 2% e il 10% per i bovini da ingrasso) Livello della non conformita'': Adeguato Meno del 5% di animali con BCS oltre i limiti (meno del 2% per i bovini da ingrasso) Livello della non conformita'': Ottimale','34) Animali con BCS oltre i limiti','A, I, -, O','(*) Registrare il numero di animali con BCS oltre i limiti e il numero di animali controllati.',34);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,151,1066,'ABM','Pulizia degli Animali','35. Pulizia degli animali -Valutare la condizione di pulizia degli animali; per ogni animale considerare l''osservazione di un solo fianco. Questa valutazione non deve essere eseguita nella popolazione di bufale da latte in quanto non è indicativa di discomfort Più del 20% di animali sporchi (40% nel caso dei bovini da ingrasso) Livello della non conformita'': Insufficiente Tra il 10% e il 20% di animali sporchi (tra il 10% e il 40% nel caso dei bovini da ingrasso) Livello della non conformita'': Adeguato Meno del 10% di animali sporchi Livello della non conformita'': Ottimale','35) Grado di pulizia del corpo degli Animali','A, I, -, O','(*) Registrare il numero di animali sporchi e il numero di animali controllati.',35);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,152,1067,'ABM','Lesioni cutanee','36. Lesioni cutanee I seguenti parametri rappresentano degli utili indicatori per valutare le condizioni e lo stato di salute e benessere degli animali allevati Numero stimato BOVINE/BUFALE IN LATTAZIONE Numero stimato BOVINE/BUFALE IN ASCIUTTA Numero stimato MANZE (>6 MESI FINO AL 1° PARTO) Numero stimato BOVINI DA INGRASSO (MASCHI E FEMMINE > 6 MESI) Numero stimato TORI DA RIPRODUZIONE Stima della produzione latte (kg Tipologia di stabulazione (stabulazione libera, stabulazione fissa, al pascolo): STIMA DEI DATI AZIENDALI (da compilare in base alle informazioni fornite dall''allevatore): Questi dati rappresentano una stima fornita dallallevatore della consistenza delle diverse popolazioni animali presenti in allevamento il giorno dellispezione. Compilare questa tabella è un ausilio necessario ai fini della valutazione delle animal-based measures, nonché della categorizzazione del rischio in ClassyFarm. Numero stimato FATTRICI (linea vacca-vitello) Valutare la frequenza di animali con presenza di lesioni cutanee (e loro gravità) su garretti, tuberosità ossee e tessuti molli. Le lesioni si valutano osservando in senso cranio-caudale gli animali: testa, collo, sterno, spalla, arto anteriore, bacino, coscia, arto posteriore (faccia laterale e faccia mediale dell''arto controlaterale) e mammella. Per la sola specie bovina, una lesione cutanea grave equivale a 3 lesioni cutanee lievi. Per la specie bufalina, le lesioni osservate non sono distinte in lievi e gravi, ma sono considerate tutte come lesioni cutanee. BOVINE DA LATTE: Più del 30% di animali con lesioni cutanee lievi ALTRI BOVINI: Più del 20% di animali con lesioni cutanee lievi BUFALE DA LATTE: Più del 20% di animali con lesioni cutanee Livello della non conformita'': Insufficiente BOVINE DA LATTE: Tra il 15% e il 30% di animali con lesioni cutanee lievi ALTRI BOVINI: Tra il 10% e il 20% di animali con lesioni cutanee lievi BUFALA DA LATTE: Tra il 5% e il 20% di animali con lesioni cutanee Livello della non conformita'': Adeguato BOVINE DA LATTE: Meno del 15% di animali con lesioni cutanee lievi ALTRI BOVINI: Meno del 10% di animali con lesioni cutanee lievi BUFALA DA LATTE: Meno del 5% di animali con lesioni cutanee Livello della non conformita'': Ottimale','36) Animali con lesioni al corpo','A, I, -, O','(*) Registrare il numero di animali con lesioni lievi e gravi, il gruppo e il numero di animali controllati.',36);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,153,1068,'ABM','Zoppie','37. Zoppie Valutare unicamente gli animali con evidente zoppia tramite uno score di locomozione che va da 0 a 3; animale zoppo = score 2 e score 3. (Score 2 = L''animale zoppica visibilmente, minimo carico sull''arto interessato, ha una camminata asimmetrica; score 3 = Animale che non appoggia il peso su un arto o non in grado di camminare). Nel caso delle bufale da latte, data la loro particolare conformazione ed abilità a modellare l''andatura, può essere più indicativ rilevare la condizione degli unghioni, se lunghi e deformi, anziché la zoppia. BOVINE DA LATTE (stabulazione libera): Più dell''8% di animali zoppi BOVINE DA LATTE (stabulazione fissa): Più del 15% di animali zoppi LINEA VACCA-VITELLO: Più del 10% di animali zoppi BOVINI DA INGRASSO: Più del 6% di animali zoppi BUFALE DA LATTE: Più del 10% di animali con unghioni lunghi e/o deformi Livello della non conformita'': Insufficiente BOVINE DA LATTE (stabulazione libera): Tra 4% e 8% di animali zoppi BOVINE DA LATTE (stabulazione fissa): Tra il 10 e il 15% di animali zoppi LINEA VACCA-VITELLO: Tra 5% e 10% di animali zoppi BOVINI DA INGRASSO: Tra 2% e 6% di animali zoppi BUFALE DA LATTE: Tra 5% e 10% di animali con unghioni lunghi e/o deformi Livello della non conformita'': Adeguato BOVINE DA LATTE (stabulazione libera): Meno del 4% di animali zoppi BOVINE DA LATTE (stabulazione fissa): Meno del 10 di animali zoppi LINEA VACCA-VITELLO: Meno del 5% di animali zoppi BOVINI DA INGRASSO: Meno del 2% di animali zoppi BUFALE DA LATTE: Meno del 5% di animali con unghioni lunghi e/o deformi Livello della non conformita'': Ottimale','37) Animali con evidenti segni di Zoppia','A, I, -, O','(*) Registrare il numero di animali con zoppia di grado 2 e 3, il gruppo e il numero di animali controllati.',37);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,155,1070,'ABM','a. Patologie respiratorie ed enteriche nei bovini da carne arrivati da 8-40 Giorni','38. Patologie respiratorie ed enteriche nel bovino da carne Valutare il numero di animali che presentano grave scolo nasale, tosse, dispnea (forma grave) o che presentano lieve scolo nasale o importante scolo oculare o regione perineale sporca per diarrea (forma lieve). In particolare, l''osservazione dovrebbe interessare gli animali che hanno superato il periodo di condizionamento (arrivo in allevamento da almeno 41 giorni) fino alla macellazione. 38. a. Patologie respiratorie ed enteriche nei bovini da carne arrivati da 8-40 giorni Più del 15% di animali con patologia in forma grave e/o più del 40% di animali con patologia in forma lieve Livello della non conformita'': Insufficiente Tra il 5% e 15% di animali con patologia in forma grave e/o tra il 20% e 40% di animali con patologia in forma lieve Livello della non conformita'': Adeguato Meno del 5% di animali con patologia in forma grave e meno del 20% di animali con patologia in forma lieve Livello della non conformita'': Ottimale','38a) Animali tra 8-40 giorni dall''arrivo con evidenti segni di patologia respiratoria e/o enterica','A, I, -, O','(*) Registrare il numero di animali con patologia in forma grave o lieve, il gruppo e il numero di animali controllati.',38);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,156,1071,'ABM','b. Patologie respiratorie ed enteriche nei bovini da carne arrivati da 41 giorni fino alla macellazione','38. b. Patologie respiratorie ed enteriche nei bovini da carne arrivati da 41 giorni fino alla macellazione Più del 4% di animali con patologia in forma grave e/o più del 20% di animali con patologia in forma lieve Livello della non conformita'': Insufficiente Tra il 2% e 4% di animali con patologia in forma grave e/o tra il 10% e 20% di animali con patologia in forma lieve Livello della non conformita'': Adeguato Meno del 2% di animali con patologia in forma grave e meno del 10% di animali con patologia in forma lieve Livello della non conformita'': Ottimale','38b) Animali arrivati da almeno 41 giorni in allevamento (fino alla macellazione) con evidenti segni di patologia respiratoria e/o enterica','A, I, -, O','(*) Registrare il numero di animali con patologia in forma grave o lieve, il gruppo e il numero di animali controllati.',39);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,157,1072,'ABM','Mortalità annuale degli animali adulti (oltre 6 mesi di vita)','39. Mortalità annuale degli animali adulti (oltre 6 mesi di vita) Considerare il numero di bovini morti spontaneamente in allevamento, macellati d''urgenza (MSU), eutanasizzati negli ultimi 12 mesi rispetto al numero di bovini adulti (oltre i 6 mesi d''età) presenti il giorno della valutazione (o rispetto al numero di animali circolanti in allevamento in 1 anno, nel caso dei bovini da carne con ristallo). BOVINI/BUFALINI Più del 5% Livello della non conformita'': Insufficiente BOVINI Tra il 2% e 5% BUFALINI Tra il 3% e 5% Livello della non conformita'': Adeguato BOVINI Meno del 2% BUFALINI Meno del 3% Livello della non conformita'': Ottimale','39) Mortalità annuale animali Adulti','A, I, -, O','(*) Registrare il numero di animali morti e la tipologia di morte',40);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (41,158,1073,'ABM','Sanità della Mammella','40. Sanità della mammella Negli allevamenti da latte, verificare il valore di media geometrica delle cellule somatiche rilevato dall''ultimo controllo disponibile sul latte di massa Media geometrica cellule somatiche maggiore di 400.000 cell/ml Livello della non conformita'': Insufficiente Media geometrica cellule somatiche tra 300.000 e 400.000 cell/ml Livello della non conformita'': Adeguato Media geometrica SCC minore di 300.000 cell/ml Livello della non conformita'': Ottimale','40) Media geometrica cellule somatiche nel latte di massa','A, I, -, O','(*) Registrare il valore di media geometrica delle cellule somatiche rilevato dall''ultimo controllo disponibile sul latte di massa',41);


alter table ticket add column descrizione_preavviso_ba text;
CREATE TABLE IF NOT EXISTS public.chk_bns_risposte_v7
(
    id serial,
    id_chk_bns_mod_ist integer,
    id_domanda integer,
    risposta text COLLATE pg_catalog."default",
    evidenze text COLLATE pg_catalog."default",
    trashed_date timestamp without time zone,
    entered_by integer,
    entered timestamp without time zone DEFAULT now()
)

TABLESPACE pg_default;
DROP VIEW public.bdn_cu_chiusi_benessere_animale;

CREATE OR REPLACE VIEW public.bdn_cu_chiusi_benessere_animale
 AS
 SELECT DISTINCT ist.id_alleg,
    ist.id_bdn,
    specie.short_description_ba AS codice_specie_chk_bns,
    o.name AS ragione_sociale,
    o.org_id,
    ticket.ticketid AS id_controllo,
    ticket.assigned_date AS data_controllo,
    concat('R', ticket.site_id) AS codice_asl,
    upper(o.account_number::text) AS codice_azienda,
    COALESCE(btrim(o.partita_iva::text), btrim(o.codice_fiscale_rappresentante::text)) AS id_fiscale_allevamento,
        CASE
            WHEN ROW(nucleo.nucleo_ispettivo, nucleo.nucleo_ispettivo_due, nucleo.nucleo_ispettivo_tre, nucleo.nucleo_ispettivo_quattro, nucleo.nucleo_ispettivo_cinque, nucleo.nucleo_ispettivo_sei, nucleo.nucleo_ispettivo_sette, nucleo.nucleo_ispettivo_otto, nucleo.nucleo_ispettivo_nove, nucleo.nucleo_ispettivo_dieci)::text ~~* '%nas_%'::text THEN '001'::text
            WHEN ROW(nucleo.nucleo_ispettivo, nucleo.nucleo_ispettivo_due, nucleo.nucleo_ispettivo_tre, nucleo.nucleo_ispettivo_quattro, nucleo.nucleo_ispettivo_cinque, nucleo.nucleo_ispettivo_sei, nucleo.nucleo_ispettivo_sette, nucleo.nucleo_ispettivo_otto, nucleo.nucleo_ispettivo_nove, nucleo.nucleo_ispettivo_dieci)::text ~~* '%forestale%'::text THEN '002'::text
            ELSE '003'::text
        END AS occodice,
    false AS flag_cee,
        CASE
            WHEN ticket.closed IS NULL THEN 'APERTO'::text
            WHEN ticket.closed_nolista THEN 'CHIUSO SENZA LISTA PER ASSENZA DI NON CONFORMITA'''::text
            ELSE 'CHIUSO'::text
        END AS stato,
    o.cf_correntista::text AS id_fiscale_detentore,
    op.tipo_produzione_ba,
        CASE
            WHEN op.codice_orientamento_ba IS NOT NULL THEN op.codice_orientamento_ba
            ELSE ''::text
        END AS codice_orientamento_ba,
    ist.data_import,
    ist.esito_import,
    ist.descrizione_errore,
    o.specie_allev,
    lcm.tipo_allegato_bdn AS tipo_allegato,
        CASE
            WHEN o.specie_allev::text !~~* 'pesci'::text THEN '0'::text || o.specie_allevamento::text
            ELSE 'PES'::text
        END AS specie_allevamento,
        CASE
            WHEN lcm.codice_checklist = 'isVitelli'::text THEN 'S'::text
            ELSE 'N'::text
        END AS flag_vitelli,
        CASE
            WHEN (o.specie_allev::text ~~* 'gallus'::text OR o.specie_allev::text ~~* 'oche'::text OR o.specie_allev::text ~~* 'fagiani'::text OR o.specie_allev::text ~~* 'conigli'::text OR o.specie_allev::text ~~* 'quaglie'::text) AND o.codice_tipo_allevamento IS NOT NULL THEN o.codice_tipo_allevamento
            ELSE '11'::text
        END AS tipo_allevamento_codice,
        CASE
            WHEN ticket.flag_preavviso IS NULL OR ticket.flag_preavviso = '-1'::text THEN 'N'::text
            ELSE ticket.flag_preavviso
        END AS flag_preavviso,
    ticket.data_preavviso_ba AS data_preavviso,
	ticket.descrizione_preavviso_ba AS descrizione_preavviso,
    ist.id AS id_chk_bns_mod_ist,
    ticket.flag_checklist,
    lcm.description AS nome_checklist,
    o.codice_fiscale_rappresentante AS id_fiscale_proprietario,
    lcm.codice_checklist
   FROM ticket
     JOIN organization o ON ticket.org_id = o.org_id AND o.tipologia = 2 AND o.account_number IS NOT NULL AND o.trashed_date IS NULL
     LEFT JOIN lookup_site_id asl ON asl.code = o.asl_old
     LEFT JOIN orientamenti_produttivi op ON op.specie_allevata = o.specie_allev::text AND op.descrizione_tipo_produzione = o.tipologia_strutt AND o.orientamento_prod = op.descrizione_codice_orientamento
     LEFT JOIN lookup_specie_allevata specie ON specie.description::text = o.specie_allev::text
     JOIN tipocontrolloufficialeimprese tipi ON ticket.ticketid = tipi.idcontrollo AND tipi.enabled AND tipi.pianomonitoraggio > 0
     JOIN lookup_piano_monitoraggio lp ON lp.code = tipi.pianomonitoraggio
     JOIN rel_motivi_eventi_cu rel ON rel.cod_raggrup_ind = lp.codice_interno_univoco
     JOIN lookup_eventi_motivi_cu ev ON ev.code = rel.id_evento_cu AND ev.codice_evento::text = 'isBenessereAnimale'::text
     LEFT JOIN nucleo_ispettivo_mv nucleo ON nucleo.id_controllo_ufficiale = ticket.ticketid
     JOIN chk_bns_mod_ist ist ON ist.idcu = ticket.ticketid AND ist.trashed_date IS NULL AND ist.bozza IS FALSE AND (ist.esito_import IS NULL OR ist.esito_import = ''::text OR ist.esito_import ~~* '%KO%'::text)
     JOIN lookup_chk_bns_mod lcm ON lcm.code = ist.id_alleg AND (lcm.codice_checklist IS NULL OR (lcm.codice_checklist = ANY (ARRAY['isBoviniBufalini'::text, 'isBroiler'::text, 'isVitelli'::text, 'isSuini'::text, 'isGallus'::text, 'isOviCaprini'::text, 'isConigli'::text, 'isAltreSpecie'::text])))
  WHERE ticket.tipologia = 3 AND (ticket.closed IS NOT NULL OR ticket.closed_nolista) AND ticket.trashed_date IS NULL AND ticket.assigned_date > (now() - '2 years'::interval);


