CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_ovicaprini(
	_username_ws_bdn text,
	_password_ws_bdn text,
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE STRICT PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
BEGIN

  msg := concat('<?xml version="1.0" encoding="UTF-8"?>
<S:Envelope xmlns:S="http://schemas.xmlsoap.org/soap/envelope/">','
',
(select get_chiamata_ws_insert_ba_sa_header from get_chiamata_ws_insert_ba_sa_header(_username_ws_bdn, _password_ws_bdn)),'
',
'<S:Body>
      <ns2:insertControlliallevamentiBA xmlns:ns2="http://ws.izs.it">','
      ',(select get_chiamata_ws_insert_ba_v7_ovicaprini_controlli_allevamenti from get_chiamata_ws_insert_ba_v7_ovicaprini_controlli_allevamenti(_id)),'
      ',(select get_chiamata_ws_insert_ba_v7_ovicaprini_dettaglio_checklist from get_chiamata_ws_insert_ba_v7_ovicaprini_dettaglio_checklist(_id)),'
      </ns2:insertControlliallevamentiBA>
      </S:Body> 
      </S:Envelope>');
    
    
 RETURN msg;
 END;
$BODY$;

