    chk_bns_mod_ist_v7_suini.data_chiusura_relazione,
    chk_bns_mod_ist_v7_suini.extrapiano,
    chk_bns_mod_ist_v7_suini.intenzionalita
   FROM chk_bns_mod_ist_v7_suini
  WHERE chk_bns_mod_ist_v7_suini.trashed_date IS NULL;

ALTER TABLE public.bdn_checklist_v7_suini
    OWNER TO postgres;

-- FUNCTION: public.get_chiamata_ws_insert_ba_v7_suini(text, text, integer)

-- DROP FUNCTION IF EXISTS public.get_chiamata_ws_insert_ba_v7_suini(text, text, integer);

CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_suini(
	_username_ws_bdn text,
	_password_ws_bdn text,
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE STRICT PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
BEGIN

  msg := concat('<?xml version="1.0" encoding="UTF-8"?>
<S:Envelope xmlns:S="http://schemas.xmlsoap.org/soap/envelope/">','
',
(select get_chiamata_ws_insert_ba_sa_header from get_chiamata_ws_insert_ba_sa_header(_username_ws_bdn, _password_ws_bdn)),'
',
'<S:Body>
      <ns2:insertControlliallevamentiBA xmlns:ns2="http://ws.izs.it">','
      ',(select get_chiamata_ws_insert_ba_v7_suini_controlli_allevamenti from get_chiamata_ws_insert_ba_v7_suini_controlli_allevamenti(_id)),'
      ',(select get_chiamata_ws_insert_ba_v7_suini_dettaglio_checklist from get_chiamata_ws_insert_ba_v7_suini_dettaglio_checklist(_id)),'
      </ns2:insertControlliallevamentiBA>
      </S:Body> 
      </S:Envelope>');
    
 RETURN msg;
 END;
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_suini(text, text, integer)
    OWNER TO postgres;

-- FUNCTION: public.get_chiamata_ws_insert_ba_v7_suini_controlli_allevamenti(integer)

-- DROP FUNCTION IF EXISTS public.get_chiamata_ws_insert_ba_v7_suini_controlli_allevamenti(integer);

CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_suini_controlli_allevamenti(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
tag_preavviso text;
BEGIN

select 
concat('<controlliallevamenti>
            <allevIdFiscale>',id_fiscale_allevamento,'</allevIdFiscale>
            <aslCodice>',codice_asl,'</aslCodice>
            <aziendaCodice>',codice_azienda,'</aziendaCodice>
            <caId>0</caId>
            <detenIdFiscale>',id_fiscale_detentore,'</detenIdFiscale>
            <dtControllo>', to_char(data_controllo, 'YYYY-MM-DD"T"00:00:00.000'),'</dtControllo>
            <flagEsito>',suini.esito_controllo,'</flagEsito>
            <flagExtrapiano>',suini.extrapiano,'</flagExtrapiano>
            <flagPreavviso>',bdn.flag_preavviso,'</flagPreavviso>
	    ',case when bdn.flag_preavviso <> 'N' then concat('<dataPreavviso>',to_char(bdn.data_preavviso, 'YYYY-MM-DD"T"00:00:00.000'),'</dataPreavviso>') else '' end,'            
	   	',case when bdn.flag_preavviso = 'A' then concat('<descAltroPreavviso>', bdn.descrizione_preavviso,'</descAltroPreavviso>') else '' end,'
            <flagVitelli>',bdn.flag_vitelli,'</flagVitelli>
            <ocCodice>',bdn.occodice,'</ocCodice>
            <primoControllore>',suini.nome_controllore,'</primoControllore>
            <speCodice>',bdn.codice_specie_chk_bns,'</speCodice>
            <tipoControllo>BA</tipoControllo>
            <flagCopiaCheckList>',bdn.flag_checklist,'</flagCopiaCheckList>
	    ',case when suini.esito_controllo = 'N' then concat(
	    '<sanzBloccoMov>',suini.sanz_blocco,'</sanzBloccoMov>
            <sanzAmministrativa>',suini.sanz_amministrativa,'</sanzAmministrativa>
            <sanzSequestro>',suini.sanz_sequestro,'</sanzSequestro>
            <sanzAbbattimentoCapi>',suini.sanz_abbattimento,'</sanzAbbattimentoCapi>
            <sanzInformativa>',suini.sanz_informativa,'</sanzInformativa>
            <sanzAltro>',suini.sanz_altro,'</sanzAltro>
            ',case when suini.sanz_altro <> '0' then concat('<sanzAltroDesc>',suini.sanz_altro_desc,'</sanzAltroDesc>')
	    else '' end,'
            <noteControllore>',suini.note_controllore,'</noteControllore>
            <noteDetentore>',suini.note_proprietario,'</noteDetentore>')
	    else '' end,'             
	    ',case when suini.esito_controllo <> 'A' then 
	    concat('<flagEvidenze>',suini.evidenze,'</flagEvidenze>')
	    else '' end,
	    case when suini.evidenze = 'S' then 
	    concat('<evidenzaIr>',suini.evidenze_ir,'</evidenzaIr>
            <evidenzaSv>',suini.evidenze_sv,'</evidenzaSv>
            <evidenzaSa>',suini.evidenze_tse,'</evidenzaSa>')
            else '' end,'
            <flagCondizionalita>',suini.appartenente_condizionalita,'</flagCondizionalita>
             ',case when suini.data_chiusura_relazione <> '' then concat('<dataChiusura>',concat(suini.data_chiusura_relazione,'T00:00.000'),'</dataChiusura>')
	    else '' end,'    
         </controlliallevamenti>') into msg
from bdn_cu_chiusi_benessere_animale bdn
left join bdn_checklist_v7_suini suini on suini.id_chk_bns_mod_ist = bdn.id_chk_bns_mod_ist
where bdn.id_chk_bns_mod_ist = _id;
     
    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_suini_controlli_allevamenti(integer)
    OWNER TO postgres;

-- FUNCTION: public.get_chiamata_ws_insert_ba_v7_suini_dettaglio_checklist(integer)

-- DROP FUNCTION IF EXISTS public.get_chiamata_ws_insert_ba_v7_suini_dettaglio_checklist(integer);

CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_suini_dettaglio_checklist(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
BEGIN
select 
concat(
'<dettagliochecklist>
	<clbId>0</clbId>
	<flagVitelli>',bdn.flag_vitelli,'</flagVitelli>
	<critCodice>',suini.criteri_utilizzati,'</critCodice>
	',
	case when suini.criteri_utilizzati = 'CF13' then concat('<criterioControlloAltro>',suini.criteri_utilizzati_altro_descrizione,'</criterioControlloAltro>
	') else '' end,
	case when suini.esito_controllo = 'N' then concat('<flagIntenzionalita>',suini.intenzionalita,'</flagIntenzionalita>
	') else '' end,
	'<nomeRappresentante>',suini.nome_proprietario,'</nomeRappresentante>
	',
	'<tipoAllegato>',bdn.tipo_allegato,'</tipoAllegato>
	',
	case when suini.esito_controllo = 'N' then 
		concat('<prescrizioni>',suini.prescrizioni_descrizione,'</prescrizioni>
		', 
		case when suini.prescrizioni_descrizione <> '' then 
			concat('<dataScadPrescrizioni>',concat(suini.data_prescrizioni,'T00:00:00.000'),'</dataScadPrescrizioni>
			        <flagPrescrizioneEsitoBa>',suini.eseguite_prescrizioni,'</flagPrescrizioneEsitoBa>
			        ',	
				case when suini.data_verifica <> '' then 
					concat('<dataVerificaBa>',concat(suini.data_verifica,'T00:00:00.000'),'</dataVerificaBa>
						<secondoControllore>',suini.nome_controllore,'</secondoControllore>
						<nomeRappresentanteVer>',suini.nome_proprietario_prescrizioni_eseguite,'</nomeRappresentanteVer>
						'
					       ) else '' end
		               ) end
		      )else '' end,
	'<parametro>2024</parametro>
	<numClb>',suini.num_checklist,'</numClb>
	',
	case when suini.esito_controllo <> 'A' then concat('<veterinario>',suini.veterinario_ispettore,'</veterinario>
	',
	case when trim(suini.scrofe_morte_anno) <> '' then concat('<rScrofeMorteAnno>',suini.scrofe_morte_anno,'</rScrofeMorteAnno>
	') else '' end,
	case when trim(suini.suinetti_svezzati_anno) <> '' then concat('<rSuinettiSvezzatiAnno>',suini.suinetti_svezzati_anno,'</rSuinettiSvezzatiAnno>
	') else '' end,
	case when trim(suini.svezzamento_suini_presenti) <> '' then concat('<sSuiniPresenti>',suini.svezzamento_suini_presenti,'</sSuiniPresenti>
	') else '' end,
	case when trim(suini.svezzamento_suini_morti) <> '' then concat('<sSuiniMorti>',suini.svezzamento_suini_morti,'</sSuiniMorti>
	') else '' end,
	case when trim(suini.svezzamento_tutto) <> '' then concat('<sFlagTuttoPieno>',suini.svezzamento_tutto,'</sFlagTuttoPieno>
	') else '' end,
	case when trim(suini.svezzamento_num_cicli) <> '' then concat('<sNumCicli>',suini.svezzamento_num_cicli,'</sNumCicli>
	') else '' end,
	case when trim(suini.svezzamento_num_animali_ciclo) <> '' then concat('<sNumCapiCiclo>',suini.svezzamento_num_animali_ciclo,'</sNumCapiCiclo>
	') else '' end,
	case when trim(suini.svezzamento_fessurato) <> '' then concat('<sBoxTipo1>',suini.svezzamento_fessurato,'</sBoxTipo1>
	') else '' end,
	case when trim(suini.svezzamento_pieno) <> '' then concat('<sBoxTipo2>',suini.svezzamento_pieno,'</sBoxTipo2>
	') else '' end,
	case when trim(suini.svezzamento_pieno) <> '' then concat('<sBoxTipo2>',suini.svezzamento_pieno,'</sBoxTipo2>
	') else '' end,
	case when trim(suini.svezzamento_parzialmente_fessurato) <> '' then concat('<sBoxTipo3>',suini.svezzamento_parzialmente_fessurato,'</sBoxTipo3>
	') else '' end,
	case when trim(suini.svezzamento_parzialmente_grigliato) <> '' then concat('<sBoxTipo4>',suini.svezzamento_parzialmente_grigliato,'</sBoxTipo4>
	') else '' end,
	case when trim(suini.svezzamento_lettiera) <> '' then concat('<sBoxTipo5>',suini.svezzamento_lettiera,'</sBoxTipo5>
	') else '' end,
	case when trim(suini.ingrasso_suini_presenti) <> '' then concat('<iSuiniPresenti>',suini.ingrasso_suini_presenti,'</iSuiniPresenti>
	') else '' end,
	case when trim(suini.ingrasso_suini_morti) <> '' then concat('<iSuiniMorti>',suini.ingrasso_suini_morti,'</iSuiniMorti>
	') else '' end,
	case when trim(suini.ingrasso_tutto) <> '' then concat('<iFlagTuttoPieno>',suini.ingrasso_tutto,'</iFlagTuttoPieno>
	') else '' end,
	case when trim(suini.ingrasso_num_cicli) <> '' then concat('<iNumCicli>',suini.ingrasso_num_cicli,'</iNumCicli>
	') else '' end,
	case when trim(suini.ingrasso_num_animali_ciclo) <> '' then concat('<iNumCapiCiclo>',suini.ingrasso_num_animali_ciclo,'</iNumCapiCiclo>
	') else '' end,
	case when trim(suini.ingrasso_fessurato) <> '' then concat('<iBoxTipo1>',suini.ingrasso_fessurato,'</iBoxTipo1>
	') else '' end,
	case when trim(suini.ingrasso_pieno) <> '' then concat('<iBoxTipo2>',suini.ingrasso_pieno,'</iBoxTipo2>
	') else '' end,
	case when trim(suini.ingrasso_parzialmente_fessurato) <> '' then concat('<iBoxTipo3>',suini.ingrasso_parzialmente_fessurato,'</iBoxTipo3>
	') else '' end,
	case when trim(suini.ingrasso_parzialmente_grigliato) <> '' then concat('<iBoxTipo4>',suini.ingrasso_parzialmente_grigliato,'</iBoxTipo4>
	') else '' end,
	case when trim(suini.ingrasso_lettiera) <> '' then concat('<iBoxTipo5>',suini.ingrasso_lettiera,'</iBoxTipo5>
	') else '' end) else '' end,
	case when suini.esito_controllo <> 'A' then concat('<capiCodaTagliata>',suini.presenza_animali_coda_tagliata,'</capiCodaTagliata>
								<gruppiCodaTagliata>',suini.presenza_gruppi_animali_coda_tagliata,'</gruppiCodaTagliata>
								<produzioniTipiche>',suini.presenza_animali_tipiche,'</produzioniTipiche>
								<usoAnestetici>',suini.utilizzo_anestetici,'</usoAnestetici>
								<presenzaManuale>',suini.presenza_manuale,'</presenzaManuale>
							   ') else '' end,
	case when suini.esito_controllo <> 'A' then (select get_chiamata_ws_insert_ba_v7_requisiti from public.get_chiamata_ws_insert_ba_v7_requisiti(_id)) else '' END,
	CASE     when suini.esito_controllo <> 'A' then (select get_chiamata_ws_insert_ba_v7_suini_box from public.get_chiamata_ws_insert_ba_v7_suini_box(_id)) else ''
	end,'
            </dettagliochecklist>') into msg
from bdn_cu_chiusi_benessere_animale bdn
left join bdn_checklist_v7_suini suini on suini.id_chk_bns_mod_ist = bdn.id_chk_bns_mod_ist
where bdn.id_chk_bns_mod_ist = _id;
   
    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_suini_dettaglio_checklist(integer)
    OWNER TO postgres;

-- FUNCTION: public.get_chiamata_ws_insert_ba_v7_suini_box(integer)

-- DROP FUNCTION IF EXISTS public.get_chiamata_ws_insert_ba_v7_suini_box(integer);

CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_suini_box(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
BEGIN
     
   select string_agg(concat('<box>
               <clBoxId>0</clBoxId>
               <clbId>0</clbId>
               <boxNum>',numero,'</boxNum>
               <larghezza>',larghezza,'</larghezza>
               <lunghezza>',lunghezza,'</lunghezza>
               <numeroAnimali>',animali,'</numeroAnimali>
               <peso>',peso,'</peso>
               <categoria>',categoria,'</categoria>
               <flagPavimento>',pavimento,'</flagPavimento>
               <travetti>',travetti,'</travetti>
               <fessure>',fessure,'</fessure>
               <regolare>',regolare,'</regolare>
            </box>'),'
            ') into msg 
   from
  chk_bns_boxes_v7 where trashed_date  is null and id_chk_bns_mod_ist = _id
  and (numero <> '' or larghezza <> '' or lunghezza <> '' or animali <> '' or peso <> '' or categoria <> '' or pavimento <> '' or travetti <> '' or fessure <> '' or regolare <> '') ;
    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_suini_box(integer)
    OWNER TO postgres;
    
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,60,1156,'PERSONALE','Numero di addetti che si occupano degli animali','146/2001 All. Personale Punto 1. "Gli animali devono essere accuditi da un numero sufficiente di addetti" Il numero di addetti deve essere sufficiente tale da garantire la salute e il benessere per tutti gli animali in allevamento: verificare il rapporto n. addetti - n. animali (i limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio). Riproduzione: - Numero non sufficiente di addetti: indicativamente un operatore per più di 400 scrofe presenti al momento della visita - Numero sufficiente di addetti un operatore per 200 - 400 scrofe presenti al momento della visita - Numero di addetti superiore al requisito previsto: indicativamente un operatore per meno di 200 scrofe presenti al momento della visita Svezzamento-Ingrasso: - Numero non sufficiente di addetti: indicativamente un operatore per più di 4.000 animali presenti al momento della visita per i siti 3 (3.000 per i siti 2) - Numero sufficiente di addetti: indicativamente un operatore per 2.000 - 4.000 animali presenti al momento della visita per i siti 3 (1.500 - 3.000 per i siti 2) - Numero di addetti superiore al requisito previsto: indicativamente un operatore per meno di 2.000 animali presenti al momento della visita per i siti 3 (1500 per i siti 2)','a) Gli animali sono accuditi da un numero sufficiente di addetti.','S, NA, NB, NC, -, O','(*) Indicare il numero degli addetti',1);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,61,1157,'PERSONALE','Formazione degli addetti','146/2001 All. Personale Punto 1 e 122/2011 Art. 5 punto 1 "Gli addetti devono avere capacità e conoscenze adeguate" e "Qualsiasi persona che assume o comunque impiega personale addetto ai suini garantisce che gli addetti agli animali abbiano ricevuto istruzioni pratiche sulle disposizioni di cui all''articolo 3 e all''allegato I del D.lgs 122/2011". Valutare la formazione del personale. La formazione risulta adeguata quando tutto il personale ha ricevuto istruzioni pratiche e almeno il detentore (o suo delegato) ha 5 anni di esperienza nel settore per quanto riguarda i reparti dello svezzamento - ingrasso (10 anni di esperienza per i riproduttori) o possiede un titolo di studio inerente; inoltre è richiesto come requisito basilare l''aver frequentato un corso di formazione inerente al benessere del suino (sia il detentore o un suo delegato sia il proprietario). - Capacità e conoscenze non adeguate: esperienza indicativamente minore di 5 anni per il settore svezzamento-ingrasso (10 anni per i riproduttori) o nessun titolo formativo e nessun corso di formazione inerente e presenza anche di un solo addetto agli animali senza nessuna istruzione pratica ricevuta - Capacità e conoscenze adeguate: esperienza indicativamente di almeno 5 anni per il settore svezzamento-ingrasso (10 anni per riproduttori) o presenza di titolo di studio e di corso di formazione inerente e istruzioni pratiche fornite a tutti gli addetti (in forma scritta o da verificare tramite semplici quesiti) - Capacità e conoscenze superiori al requisito previsto: esperienza indicativamente di almeno 5 anni per il settore svezzamento-ingrasso (10 anni per i riproduttori) o presenza di titolo di studio e presenza di più corsi di formazione specifici e periodici con ricaduta i allevamento e presenza di istruzioni pratiche per tutti gli addetti. ','a) Il personale addetto agli animali dispone di capacità e conoscenze adeguate, ha ricevuto istruzioni pratiche sulle pertinenti diposizioni normative e ha frequentato corsi di formazione specifici in materia di benessere degli animali.','S, NA, NB, NC, -, O','*) Indicare esperienza nel settore, presenza di istruzioni pratiche scritte o correttamente comunicate ed estremi dei corsi di formazione frequentati, con periodicità e ricaduta a livello aziendale, ecc',2);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,62,1158,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Numero di ispezioni','a) 146/2001 All. Controllo Punto 2. a) "Tutti gli animali tenuti in sistemi di allevamento, il cui benessere richieda un''assistenza frequente dell''uomo, sono ispeziona almeno una volta al giorno".Si considerano le visite effettuate nei capannoni in funzione dello stato di benessere degli animali. - Numero di visite insufficienti: meno di 1 ispezione/giorno - Numero di visite sufficienti: almeno 1 ispezione/giorno - Numero di visite superiori al requisito previsto: 2 o più ispezioni al giorno','a) Gli animali nei locali di stabulazione sono ispezionati almeno una volta al giorno.','S, NA, NB, NC, -, O','(*) Indicare il numero di ispezioni e le modalità con le quali il dato viene raccolto.',3);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,63,1159,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Illuminazione per l''ispezione','b) 146/2001 All. Controllo Punto 3. b) "Per consentire l''ispezione completa degli animali in qualsiasi momento, deve essere disponibile un''adeguata illuminazione fissa o mobile". Verificare la disponibilità di un''illuminazione per l''ispezione, anche durante le ore notturne. - Illuminazione per l''ispezione insufficiente: assenza di illuminazione artificiale per l''ispezione - Illuminazione sufficiente: Presenza di illuminazione artificiale corretta e funzionante','b) E'' disponibile un''adeguata illuminazione che consente l''ispezione completa degli animali','S, NA, NB, NC, -','(*) Descrivere le modalità di illuminazione durante le ore diurne o notturne',4);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,101,1202,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Gestione degli animali - Gestione dei gruppi - Gestione delle scrofe - Trattamento antiparassitario scrofe - Pulizia delle scrofe sala parto','a) 146/2001 Art. 4 punto 1 lettera a. e All. Proced. di allevam. punto 21. ; a) "La conformità delle modalità di allevamento e custodia degli animali deve essere valutata tenendo conto della specie, del grado di sviluppo, adattamento e addomesticamento, nonché delle loro esigenze fisiologiche ed etologiche secondo l''esperienza acquisita e le conoscenze scientifiche". I suini allevati sono divisi in gruppi omogenei per sesso e/o età e/o categoria (verri, scrofe e scrofe lattonzoli, suinetti e suini all''ingrasso). Il criterio risulta insufficiente: nel caso in cui i suini siano allevati con una commistio indiscriminata in un ambiente intensivo (es.svezzati nello stesso box con suini grassi) senza l''evidenza di una comprovata necessità. Il criterio risulta sufficiente: nel caso i suini siano allevati in ambiente intensivo e siano suddivisi per gruppi omogenei o siano allevati Estensivamente.','a) I suini sono divisi in gruppi omogenei per sesso, età e categoria (verri, scrofe e scrofette, lattonzoli, suinetti e suini all''ingrasso).','S, NA, NB, NC, -','(*) Indicare le modalità di formazione dei gruppi e il periodo.',5);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,102,1203,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Gestione degli animali - Gestione dei gruppi - Gestione delle scrofe - Trattamento antiparassitario scrofe - Pulizia delle scrofe sala parto','b)122/2011 All. 1 parte II lettera D punto 1-4. - 122/2011 All. 1 parte II lettera B punto 1. ; b)"Quando i suini sono tenuti in gruppo occorre prendere misure per evitare lotte che vadano oltre il comportamento normale". Valutare quali accorgimenti vengono adottati e riportarne l''evidenza. Si considerano adeguate: le procedure atte ad evitare comportamen aggressivi come ad es. formazione dei gruppi nelle ore serali, utilizzo di sostanze odorose vaporizzate, aggiunta di alimento all''entrata nel box, aggiunta di materiale manipolabile in quantità maggiore o di diversa tipologia, utilizzo, in casi eccezionali, di tranquillanti dietro prescrizione veterinaria. Si considerano non adeguate: l''assenza di misure adottate.','b) SUINETTI E SUINI ALL''INGRASSO: - quando sono tenuti in gruppo vengono prese sufficienti misure per evitare lotte che vadano oltre il comportamento normale - la formazione dei gruppi avviene con il minimo possibile di commistione (mescolamento di suini che non si conoscono) - qualora necessaria la modificazione dei gruppi avviene di preferenza prima ','S, NA, NB, NC, -','(*) Indicare se vengono presi accorgimenti per evitare lotte e quali. Le valutazioni ABM (lesioni alla coda, lesioni alle orecchie, graffi e pulizia degli animali) possono supportare e dare un''evidenza della presenza di un''eventuale non conformità o confermare una conformità.',6);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,103,1204,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Gestione degli animali - Gestione dei gruppi - Gestione delle scrofe - Trattamento antiparassitario scrofe - Pulizia delle scrofe sala parto','c-d)122/2011 All. parte II lettera B punto 2. c) "Le scrofe gravide e le scrofette devono, se necessario, essere sottoposte a trattamento contro i parassiti interni od esterni". Si valuta la presenza/assenza dei trattamenti (visionare il registro dei trattamenti o le registrazioni in vetinfo farmacosorveglianza) presenza/assenza di evidenze di infestazioni (es. evidenti forme infestive: dermatiti da rogna; segnalazioni da parte del macello es. white spot). Si considera adeguato: la presenza di trattamenti o l''assenza di evidenze di infestazioni - Si considera non adeguat l''assenza di trattamenti in presenza di animali con evidenti segni di infestazione. ','c) Scrofe e scrofette : se necessario sono sottoposte a trattamenti contro parassiti esterni e interni','S, NA, NB, NC, -','(*) Indicare le modalità di trattamento delle scrofette e scrofe con antiparassitari ed i prodotti utilizzati.',7);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,64,1160,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Trattamento animali feriti - abbattimento - isolamento degli animali a rischio o aggressivi','a-b)146/2001 All. Controllo punto 4 e Reg CE 1099/2009 art. 19 e Allegato I, capo 1. ; a) "Gli animali malati o feriti devono ricevere immediatamente un trattamento appropriato e, qualora un animale non reagisca alle cure in questione, deve essere consultato un medico veterinario. Ove necessario gli animali malati o feriti vengono isolati in appositi locali muniti, se del caso, di lettiere asciutte o confortevoli." Per assegnare il giudizio conforme entrambe le condizioni devono essere soddisfatte. - Si considera non adeguato: l''assenza sia di procedure che di personale formato e/o la presenza di animali che necessitano trattamento e non lo hanno ricevuto (n.b. valutare con attenzione la possibile insorgenza iperacuta dei casi di malattia) e/o l''assenza di un veterinario che segue l''azienda. - Si considera adeguato: la presenza sia di procedure che di personale formato con evidenze di eventuali animali in infermeria o con trattamenti in atto e la presenza di un veterinario che segue l''azienda. Punti da verificare: 1. la verifica dell''immediato trattamento: si assolve con due possibili condizioni, la prima è che l''animale sia spostato nel reparto infermeria (eventualmente isolato), la seconda è che un animale malato all''interno del box (che non necessita dell''infermeria) sia stato identificato e abbia ricevuto un trattamento appropriato (valutare le dichiarazioni e/o la presenza di trattamenti farmacologici in atto sul a-b)146/2001 All. Controllo punto 4 e Reg CE 1099/2009 art. 19 e Allegato I, capo 1. ; Pagina 6 di 29 registro dei trattamenti farmacologici o nelle registrazioni in vetinfo farmacosorveglianza); 2. il consulto con un veterinario: verificare se è dichiarata o documentata la presenza del veterinario aziendale o d veterinario libero professionista o privato che segue l''azienda . - Il requisito superiore prevede, oltre ai criteri per l''adeguatezza, la presenza delle relative procedure scritte di trattamento deg animali (es. piano di gestione della morsicatura delle code).','a) Gli animali malati o feriti ricevono immediatamente un trattamento appropriato.','S, NA, NB, NC, -, O','(*) Indicare se sono presenti animali ammalati o feriti e le modalità di detenzione, se l''infermeria o altro settore sono, all''occorrenza, adattabili ad accogliere ed isolare suini deboli, malati o aggressivi.',8);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,64,1161,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Trattamento animali feriti - abbattimento - isolamento degli animali a rischio o aggressivi','b) Nel caso in cui la condizione di un animale richieda l''abbattimento in azienda, è necessario che vengano rispettate le metodologie contemplate dalla specifica normativa (Regolamento CE 1099/2009). Valutare che tali pratiche siano svolte da personale formato e che siano presenti delle procedure scritte. - Si considera non adeguato: l''assenza di procedure, di personale formato e di strumentazione adeguata e sottoposta a regol manutenzione - Si considera adeguato: in caso di abbattimento si valuta la formazione degli addetti sulle corrette procedure di abbattimento (es. presenza di un corso di formazione sul benessere frequentato da chi é addetto all''abbattimento, con tematica specifica trattata, oppure presenza attestato del corso da norcino,oppure abbattimento eseguito dal veterinario, presenza di strumentazione adeguata e sottoposta a regolare manutenzione - Il requisito superiore previsto prevede, oltre ai criteri per l''adeguatezza, la presenza delle procedure scritte per il co abbattimento, indicando responsabilità, strumenti e verifiche periodiche','b) Gli animali malati o feriti ricevono immediatamente un trattamento appropriato in caso di abbattimento','S, NA, NB, NC, -, O','(*) Fare riferimento a procedure o istruzioni sull''abbattimento e ai piani di emergenza e alla formazione degli operatori.',9);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,104,1205,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Trattamento animali feriti - abbattimento - isolamento degli animali a rischio o aggressivi','c)146/2001 All. Controllo punto 4 comma 2 e 122/2011 Art. 3 punto 8. c) "I suini che devono essere allevati in gruppo, che sono particolarmente aggressivi, che sono stati attaccati da altri suini [...] sono temporaneamente tenuti in recinto individuale. In tal caso, il recinto individuale deve permettere all''animale di girarsi facilmente se ciò non è in contraddizione con specifici pareri veterinari". Valutare la possibilità di mantenere gli animali temporaneamente in un recinto individuale con possibilità di vedere altri suini e con caratteristiche strutturali adeguate alle esigenze del caso. - Si considera non adeguato: l''impossibilità di isolare un suino (assenza di un box vuoto o di un settore dell''infermeria idoneo mantenere l''isolamento di uno o più animali o presenza in tutti i box, compresa l''infermeria, di più di un suino e nell''inferme impossibilità di mantenere animali isolati - Si considera adeguato: la presenza di un ricovero vuoto o di un settore dell''infermeria idoneo a mantenere l''isolamento di uno o più Animali.','c) Gli animali a rischio o particolarmente aggressivi sono tenuti separati dal gruppo - Sono presenti recinti individuali nei quali possono essere tenuti temporaneamente i suini (soggetti con problemi comportamentali, particolarmente aggressivi, che sono stati attaccati da altri suini, che sono malati, feriti ecc.) - I recinti individuali di isolamento hanno dimensioni adeguate e permettono agli animali di girarsi facilmente, di avere contatti visivi e olfattivi con gli altri suini, salvo nel caso in cui ciò sia in contraddizione con specifiche prescrizioni veterinarie','S, NA, NB, NC, -','(*) Indicare se sono presenti animali in recinti individuali, in caso di animali ricoverati in recinti che non permettono di girarsi se è presente idonea certificazione Veterinaria.',10);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,65,1162,'TENUTA DEI REGISTRI (Registrazione dei dati)','Tenuta delle registrazioni dei dati','a)146/2001 All. Registrazione Punti 5-6. ; (i riferimenti al D.P.R. 8 febbraio 1954, n. 320, attualmente abrogato, si intendono fatti ai corrispondenti articoli dei decreti legislativi 5 agosto 2022, n.134 e n.136 ) "Le mortalità sono denunciate ai sensi del D.P.R. 8 febbraio 1954 n. 320. I registri sono conservati per un periodo di almeno tre anni e a)146/2001 All. Registrazione Punti 5-6. ; Pagina 7 di 29 sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta." Dlgs 134 art. 9 comma 10 "L''operatore provvede alla registrazione in BDN di tutte le informazioni inerenti agli animali identificati singolarmente, alle partite, agli insiemi e ai gruppi di animali, nonché agli eventi che li riguardano, con i tempi di cui al presente articolo e con i modi previsti nel manuale operativo anche ai fini della generazione del registro della propria attività nella stessa BDN. Tale registro sostituisce qualsiasi altro registro aziendale cartaceo o su altro supporto concernente l''identificazione e registrazione degli animali." D. Lgs. 136/2022, art. 6 "Notifica e comunicazione delle malattie all''Autorità competente"; art. 11 comma 4 "gli operatori nel caso in cui non siano ancora disponibili nel sistema «ClassyFarm.it»,le apposite funzionalità informatiche per la tipologia di stabilimento di cui so responsabili, adempiono agli obblighi del presente articolo tenendo in modalità elettronica o cartacea, per cinque ann documentazione riguardante almeno le informazioni di cui all''allegato 3 del presente decreto"; Allegato 3 "...individuazione registrazione e segnalazione delle mortalità anomale" Verificare in BDN la presenza del registro di carico e scarico e la corretta registrazione delle informazioni, al fine di valutare se si siano verificate eventuali mortalità anomale. Nel caso si siano verificate mortalità anomale, verificare che siano state correttamente individuate, registrate e segnalate. Si considera non conforme: assenza in BDN del registro di carico e scarico o mancata o non corretta registrazione delle informazioni e/ o evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': NO Si considera conforme: presenza in BDN del registro di carico e scarico e corretta registrazione delle informazioni e mancata evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': SI','a) In BDN è presente il registro di carico e scarico, le informazioni vengono correttamente registrate e non sono presenti episodi di mortalità anomale non registrate','S, NC, -','(*) Annotare eventuali rilievi di mortalità anomale non registrate o di mancanza delle registrazioni delle informazioni in BDN',11);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,105,1206,'TENUTA DEI REGISTRI (Registrazione dei dati)','Tenuta dei registri dei trattamenti farmacologici','b)146/2001 All. Registrazione Punti 5-6. "Il proprietario o il custode ovvero il detentore degli animali tiene un registro dei trattamenti terapeutici effettuati. La registrazione e le relative modalità di conservazione sono effettuate secondo quanto previsto dal decreto legislativo 27 gennaio 1992, n. 119, e successive modificazioni ed integrazioni e dal decreto legislativo 4 agosto 1999, n. 336. I registri sono conservati per un periodo di almeno tre anni e sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta" Sono presenti le registrazioni informatizzate dei trattamenti su Vetinfo Farmacosorveglianza, ai sensi della Legge 20 novembre 2017, n. 167 e s.m.i. Assenza delle registrazioni in Vetinfo farmacosorveglianza o non corretta registrazione dei trattamenti. Livello della non conformita'': NO Corretta registrazione dei trattamenti in Vetinfo farmacosorveglianza. Livello della non conformita'': SI','b) Le registrazioni in Vetinfo farmacosorveglianza vengono effettuate nei tempi stabiliti e i trattamenti sono regolarmente prescritti da un medico veterinario.','S, NC, -','(*) Segnalare i trattamenti controllati a campione',12);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,66,1163,'LIBERTA'' DI MOVIMENTO','Spazio della gabbia di gestazione e sala parto - Spazio libero per il parto naturale o assistito - Stallo da parto - Conformità delle pavimentazioni suinetti lattonzoli e fonte di calore','a) 122/2011 Art. 3 punto 2 e All. parte I punto 3 lettera b. 146 All. Libertà di movimento punto 7; a) "È vietato costruire o convertire impianti in cui le scrofe e le scrofette sono tenute all''attacco, nonché il relativo utilizzo". I locali di stabulazione dei suini devono essere costruiti in modo da permettere agli animali di: - avere accesso ad una zona in cui coricarsi confortevole dal punto di vista fisico e termico e adeguatamente prosciugata e pulita, che consenta a tutti gli animali di stare distesi contemporaneamente; - riposare e alzarsi con movimenti normali. La libertà di movimento propria dell''animale, in funzione della sua specie e secondo l''esperienza acquisita e le conosce scientifiche, non deve essere limitata in modo tale da causargli inutili sofferenze o lesioni. Allorché continuamente o regolarmente legato, incatenato o trattenuto, l''animale deve poter disporre di uno spazio adeguato alle sue esigenze fisiologiche ed etologich secondo l''esperienza acquisita e le conoscenze scientifiche. Le gabbie devono essere di dimensioni tali per cui l''animale possa riposare e alzarsi senza impattare contro le strutture della gabbia. Si considera non adeguato: è praticato l''attacco delle scrofe o le dimensioni delle gabbie non sono sufficienti (impossibilità di praticare movimenti normali, quali ad es. alzarsi e sdraiarsi) e/o causano lesioni alle scrofe). - Si considera adeguato: non è praticato l''attacco delle scrofe e le dimensioni delle gabbie sono sufficienti (tali da consentir movimenti normali e da non causare lesioni alle scrofe). - Il requisito superiore prevede il non utilizzo delle gabbie di gestazione e gabbie parto. ','a) La libertà di movimento dell''animale non è limitata in modo tale da causargli inutili sofferenze o lesioni - è rispettato il divieto di utilizzo di attacchi per le scrofe e le strofette.','S, NA, NB, NC, -, O','(*) in caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. dimensioni delle gabbie in relazione a quelle degli animali, presenza di lesioni cutanee, dimensioni dello spazio oltre il posteriore della scrofa, dimensioni dell''area di pavimento pieno per i suinetti, ecc.)',13);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,106,1207,'LIBERTA'' DI MOVIMENTO','Spazio della gabbia di gestazione e sala parto - Spazio libero per il parto naturale o assistito - Stallo da parto - Conformità delle pavimentazioni suinetti lattonzoli e fonte di calore','b) 122/2011 All. parte II lettera B punto 4; b) "Dietro alla scrofa o alla scrofetta deve essere prevista una zona libera che renda agevole il parto naturale o assistito": verificare la presenza di uno spazio adeguato. - Si ritiene non adeguato: assenza di una zona libera per il parto (es. presenza di gabbie parto attaccate direttamente alle struttur perimetrali senza spazio sufficiente per il parto) - Si considera adeguato: la presenza di una zona libera per il parto (es. chiusura della gabbia parto che garantisca uno spazio sufficiente oltre il posteriore della scrofa','b) Dietro alla scrofa o alla scrofetta è prevista una zona libera che rende agevole il parto naturale o assistito','S, NA, NB, NC, -','*) in caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. dimensioni delle gabbie in relazione a quelle degli animali, presenza di lesioni cutanee, dimensioni dello spazio oltre il posteriore della scrofa, dimensioni dell''area di pavimento pieno per i suinetti, ecc.)',14);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,107,1208,'LIBERTA'' DI MOVIMENTO','Spazio della gabbia di gestazione e sala parto - Spazio libero per il parto naturale o assistito - Stallo da parto - Conformità delle pavimentazioni suinetti lattonzoli e fonte di calore','c)122/2011 All. parte II lettera B punto 5 e parte II lettera C punto 2; c)"Gli stalli da parto in cui le scrofe possono muoversi liberamente devono essere provvisti di strutture, quali ad esempio apposite sbarre, destinate a proteggere i lattonzoli" e "nel caso si usi uno stallo da parto i lattonzoli devono disporre di spazio sufficiente per poter essere allattati senza difficoltà". In sala parto devono essere presenti delle strutture atte alla salvaguardia dei suinetti e deve essere presente uno spazio sufficiente per l''allattamento. - Si considera non adeguato: l''assenza di protezioni per i suinetti e/o di spazio sufficiente per l''allattamento - Si ritiene adeguato: la presenza di protezioni per i suinetti e di spazio sufficiente per l''allattamento','c) Gli stalli da parto, in cui le scrofe possono muoversi liberamente, sono provvisti di strutture per proteggere i lattonzoli ad es. apposite sbarre','S, NA, NB, NC, -','(*) in caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. dimensioni delle gabbie in relazione a quelle degli animali, presenza di lesioni cutanee, dimensioni dello spazio oltre il posteriore della scrofa, dimensioni dell''area di pavimento pieno per i suinetti, ecc.)',15);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,108,1209,'LIBERTA'' DI MOVIMENTO','Spazio della gabbia di gestazione e sala parto - Spazio libero per il parto naturale o assistito - Stallo da parto - Conformità delle pavimentazioni suinetti lattonzoli e fonte di calore','d) 122/2011 All. parte II lettera C punto 1. d) "Una parte del pavimento, sufficientemente ampia per consentire agli animali di riposare insieme contemporaneamente, deve essere piena o ricoperta da un tappetino, da paglia o da altro materiale adeguato": valutare la presenza di una superficie piena per i suinetti sottoscrofa. - Si ritiene non adegata la seguente condizione: assenza di area piena sufficientemente ampia o mancanza di copertura (es. tappetino) - Si ritiene adeguata la seguente condizione: presenza di area piena sufficiente o ricoperta da materiale adeguato (es. tappetino) - Il requisito superiore prevede, oltre ai criteri per l''adeguatezza, che ci sia un idonea fonte di calore (es. lampade a infrarossi','d) LATTONZOLI: - una parte del pavimento è sufficientemente ampia da consentire agli animali di coricarsi e riposare contemporaneamente - questa superficie è piena o ricoperta da un tappetino, da paglia o da altro materiale adeguato - vi è una idonea fonte di calore - nel caso si usi uno stallo da parto i lattonzoli dispongono di spazio sufficiente per essere allattati senza difficoltà','S, NA, NB, NC, -, O','(*) in caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. dimensioni delle gabbie in relazione a quelle degli animali, presenza di lesioni cutanee, dimensioni dello spazio oltre il posteriore della scrofa, dimensioni dell''area di pavimento pieno per i suinetti, ecc.)',16);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,67,1164,'SPAZIO DISPONIBILE','Densità animale Scrofa in box - Densità animale - Allevamento in gruppo','a) 122/2011 Art. 3 punto 3 e 1 lettera a-b. e All. 1 parte generale punto 3 lettera b. ; a) Riproduzione: "Le superfici libere totali a disposizione di ciascuna scrofetta dopo la fecondazione e di ciascuna scrofa qualora dette scrofette o scrofe siano allevate in gruppi, devono essere rispettivamente di almeno 1,64 mq e 2,25 mq", inoltre "I lati del recinto [...] hanno una lunghezza maggiore di 2,8. Allorché sono allevati meno di 6 animali i lati del recinto dove viene allevato il gruppo devono avere una lunghezza superiore a 2,4 m" e " se i suini in questione sono allevati in gruppi di: 1) meno di sei animali, le superfici libere disponibili devono essere aumentate del 10 per cento (2,48 mq per le scrofe e 1,80 mq per le scrofette); 2) 40 o piu'' animali, le superfici libere disponibili possono essere ridotte del 10 per cento (2,03 mq per le scrofe e 1,48 mq per le scrofette)".La condizione non adeguata prevede le dimensioni inferiori al limite previsto e/o la presenza di strutture limitanti il movimento - La condizione adeguata prevede il rispetto delle dimensioni conformi al minimo previsto dalla normativa - Il requisito superiore prevede le dimensioni della libert ottimale. Svezzamento-Ingrasso: "Le superfici libere a disposizione di ciascun suinetto o suino all''ingrasso allevato in gruppo, de corrispondere ad almeno: 0,15 mq per i suini di peso vivo pari o inferiore a 10 kg; 0,20 mq per i suini di peso vivo compreso tra 10 e 20 kg; 0,30 mq per i suini di peso vivo compreso tra 20 e 30 kg; 0,40 mq per i suini di peso vivo compreso tra 30 e 50 kg; 0,55 mq per i suini di peso vivo compreso tra 50 e 85 kg; 0,65 mq per i suini di peso vivo compreso tra 85 e 110 kg; 1,00 mq per i suini di peso vivo superiore a 110 kg".Verificare la densità degli animali nei locali di stabulazione. La condizione non adeguata si configura con la libertà limitata - La condizione adeguata si configura con la libertà adeguata - Il requisito superiore si configura con la libertà ottimale. Dal calcolo della superficie disponibile bisogna escludere l''area occupata da mangiatoie, l''area occupata da abbeveratoi o truogoli e quella dei parchetti esterni, qualora non siano ricoperti da tettoia. (*) Vedere tabelle allegate','a) le superfici libere a disposizione di ciascun SUINETTO O SUINO ALL''INGRASSO ALLEVATO IN GRUPPO (escluse le scrofette dopo la fecondazione e le scrofe) corrispondono ad almeno: 0,15 mq per i suini di peso vivo pari o inferiore a 10 kg; 0,20 mq per i suini di peso vivo compreso tra 10 e 20 kg; 0,30 mq per i suini di peso vivo compreso tra 20 e 30 kg; 0,40 mq per i suini di peso vivo compreso tra 30 e 50 kg; 0,55 mq per i suini di peso vivo compreso tra 50 e 85 kg; 0,65 mq per i suini di peso vivo compreso tra 85 e 110 kg; 1,00 mq per i suini di peso vivo superiore a 110 kg; SCROFETTE DOPO LA FECONDAZIONE E SCROFE GRAVIDE: - una parte della superficie libera totale a disposizione per ciascuna è costituita da pavimento pieno continuo: 1) di almeno 0,95 mq per ogni scrofetta 2) di almeno 1,3 mq per ogni scrofa - una parte di tale pavimento (non superiore al 15%) è riservata alle aperture di scarico (griglie, tombini ecc.) Le superfici libere totali a disposizione di ciascuna SCROFETTA E SCROFA ALLEVATE IN GRUPPO sono di: 1) 1,64 mq per ciascuna scrofetta dopo la fecondazione 2) 2,25 mq per ciascuna scrofa Se le scrofette dopo la fecondazione e le scrofe sono allevate in: - gruppi di meno di sei animali le superfici libere disponibili devono essere aumentate del 10%; - 40 o più animali le superfici libere disponibili possono essere ridotte del 10 % - i lati del recinto dove viene allevato il gruppo di scrofe o di scrofette hanno una lunghezza superiore a 2,8 m Se sono allevati meno di 6 animali i lati del recinto hanno una lunghezza superiore a 2,4 m. Deroga per la aziende con meno di 10 scrofe: le scrofe e le scrofette sono allevate individualmente nel periodo compreso tra quattro settimane dopo la fecondazione e una settimana prima della data prevista per il parto in tal caso gli animali possono girarsi facilmente nel recinto.','S, NA, NB, NC, -, O','(*) Registrare per le varie tipologie di box presenti in azienda larghezza,Lunghezza, N° suini, Categoria e fascia di peso. Registrare gli esiti delle verifiche a campione su scrofe e scrofette.',17);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,109,1210,'SPAZIO DISPONIBILE','Densità animale Scrofa in box - Densità animale - Allevamento in gruppo','b) 122/2011 Art. 3 punto 3-4. b) "Le scrofe e le scrofette sono allevate in gruppo nel periodo compreso tra quattro settimane dopo la fecondazione e una settimana prima della data prevista per il parto [...]" e "In deroga [...] le scrofe e le scrofette allevate in aziende di meno di 10 scrofe possono essere allevate individualmente nel periodo indicato, a condizione che gli animali possano girarsi facilmente nel recinto". Verificare il numero di scrofe in produzione ed il numero di posti in box (in relazione alla percentuale di portata al parto) per verificar potenzialità strutturale dell''allevamento nei confronti del requisito - verificare a campione, su almeno 5 scrofe/scrofette in gabbia gestazione il tempo trascorso dalla fecondazione (lavagnette o annotazioni su schede annesse alle gabbie, registrazioni computerizzate dell''allevatore, ecc). In caso di deroga per animali in aziende con meno di 10 scrofe/scrofette verificare la possibilità degli animali di girarsi. La condizione non adeguata prevede: la presenza anche di un solo parametro non sufficiente - la condizione adeguata prevede: tutti i parametri sono sufficienti. (*) Vedere tabelle allegate','b) nel periodo compreso tra quattro settimane dopo la fecondazione e una settimana prima della data prevista per il parto le scrofe e le scrofette sono allevate in gruppo','S, NA, NB, NC, -','(*) Registrare per le varie tipologie di box presenti in azienda larghezza,Lunghezza, N° suini, Categoria e fascia di peso. Registrare gli esiti delle verifiche a campione su scrofe e scrofette',18);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,68,1165,'EDIFICI E LOCALI DI STABULAZIONE','Temperatura, Umidità Relativa e Polverosità - Presenza di gas nocivi','a-b) 146/2001 All. Fabbricati e locali di stabulazione punto 10. a) "La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria [...] devono essere mantenute entro limiti non dannosi per gli animali". Verificare il tipo di ventilazione presente e le condizioni microclimatiche presenti in allevamento con particolare attenzione a temperatura, umidità relativa e polverosità. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Condizioni microclimatiche non adeguate per gli animali: es. ambienti chiusi (assenza di aperture) senza adeguata ventilazione o polverosi. Condizioni microclimatiche idonee per gli animali: es. ventilazione naturale o impianti di ventilazione senza sistemi di controllo. Il requisito superiore prevede condizioni microclimatiche ottimali per gli animali: es. impianti di condizionamento automatizzato del microclima o ventilazione naturale con sistemi di controllo','a) la circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria) sono mantenute entro i limiti non dannosi per gli animali? All''atto dell''ispezione T° e UR sono adeguate alle esigenze etologiche della specie e all''età degli animali?','S, NA, NB, NC, -, O','(*) Registrare i valori misurati NH3.....CO2..... a campione in tutti i settori e tipologie',19);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,68,1166,'EDIFICI E LOCALI DI STABULAZIONE','Temperatura, Umidità Relativa e Polverosità - Presenza di gas nocivi','b) "Le concentrazioni di gas devono essere mantenute entro limiti non dannosi per gli animali". I limiti indicati devono essere rilevati al momento della verifica con apposita attrezzatura. In mancanza di specifica attrezzatura, in via eccezionale, si possono indicativamente considerare superati i limiti (NH3) quando è presente rossore delle congiuntive e lacrimazione negli animali e vengono avvertit sensazioni analoghe dal rilevatore, il quale dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. La condizione non adeguata prevede delle concentrazioni di gas nocivi dannose per gli animali (indicazione di riferimento dei limiti: NH3 > 20 ppm e/o CO2 > 3000 ppm) - La condizione adeguata prevede delle concentrazioni di gas nocivi non dannose per gli animali (indicazione di riferimento dei limiti: NH3 10-20 ppm e CO2 < 3000 ppm) - Il requisito superiore prevede le concentrazioni di gas nocivi ottimali per gli animali (indicazione di riferimento dei limiti: NH3 < 10 ppm; CO2 < 3000 ppm)','b) le concentrazioni di gas sono mantenuti entro i limiti non dannosi per gli animali?','S, NA, NB, NC, -, O','(*) Registrare i valori misurati NH3.....CO2..... a campione in tutti i settori e tipologie',20);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,69,1167,'EDIFICI E LOCALI DI STABULAZIONE','Rumori','122/2001 All. I parte I punto 1. "Nella parte del fabbricato dove sono stabulati i suini vanno evitati i rumori continui di intensità pari a 85 dBA nonchè i rumori costanti o improvvisi". Indicativamente, in condizioni di routine, si può considerare come riferimento per gli 85 dBA un rumore paragonabile a quello percepito nel traffico intenso. - La condizione non adeguata prevede: rumori continui superiori a 85dBA o improvvisi. - La condizione adeguata prevede: rumori continui inferiori a 85 dBA. Nei locali dove sono stabulati i suini sono evitati i rumori continui di intensità pari a 85 dBA.','Nei locali dove sono stabulati i suini sono evitati i rumori continui di intensità pari a 85 dBA, i rumori costanti ed improvvisi','S, NA, NB, NC, -','',21);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,70,1168,'EDIFICI E LOCALI DI STABULAZIONE','Area di decubito e possibilità di vedere altri animali','146/2001 All. Fabbricati e locali di stabul. punto 8 e 122/2011 All. 1 parte I punto 3 lettera a, b, c. "I locali di stabulazione dei suini devono essere costruiti in modo da permettere agli animali di: avere accesso ad una zona in cu coricarsi confortevole dal punto di vista fisico e termico e adeguatamente prosciugata e pulita, che consenta a tutti gli animali di stare distesi contemporaneamente; riposare e alzarsi con movimenti normali; vedere altri suini (scrofe e scrofette nella settimana che precede il parto e durante il parto stesso possono essere tenute fuori dalla vista degli altri animali)" Verificare le condizioni dell''area di decubito. La condizione non adeguata prevede: presenza anche di un solo parametro non sufficiente. - La condizione adeguata prevede: tutti i parametri sono sufficienti. - Verificare la possibilità per tutti gli animali (comprese le scrofe in gabbia gestazione) di vedere altri sui','I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati. I locali di stabulazione sono costruiti in modo di permettere agli animali di: - avere accesso ad una zona in cui coricarsi, confortevole dal punto di vista fisico e termico, adeguatamente prosciugata e pulita ed in cui tutti gli animali possono stare distesi contemporaneamente; - riposare ed alzarsi con movimenti normali, vedere altri suini (scrofe e scrofette nella settimana che precede il parto e durante il parto stesso possono essere tenute fuori dalla vista degli altri animali).','S, NA, NB, NC, -','Le valutazioni ABM (pulizia degli animali) possono supportare e dare un''evidenza della presenzadi una eventuale non conformità o confermare la conformità.',22);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,71,1169,'EDIFICI E LOCALI DI STABULAZIONE','Assenza di materiali e attrezzature nocivi per gli animali','146/2001 All. Fabbricati e locali di stabul. punti 8 e 9 "I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati. I locali di stabulazione e i dispositivi di attacco degli animali devono essere costruiti e mantenuti in modo che non vi siano spigoli taglienti o sporgenze tali da provocare lesioni agli animali"','I materiali e le attrezzature con i quali gli animali possono venire a contatto non sono nocivi per gli animali e non vi sono spigoli taglienti o sporgenze','S, NA, NB, NC, -','Le valutazioni ABM (lesioni degli animali) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità.',23);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,72,1170,'EDIFICI E LOCALI DI STABULAZIONE','Infermeria','146/2001 All. Controllo punto 4 comma 2 e 122/2011 Art. 3 punto 8. "Gli animali malati o feriti se necessario vengono isolati in appositi locali muniti, se del caso, di lettiere asciutte o confortevoli" e "I suini che devono essere allevati in gruppo [...] che sono malati o feriti, sono temporaneamente tenuti in recinto individuale". Nel reparto infermeria devono essere posti solo animali malati o feriti. Verificare la presenza, il numero e le condizioni dei locali infermeria. La condizione non adeguata prevede: nessun locale specifico ed identificabile - La condizione adeguata prevede: che ci sia un locale identificato, appositamente preparato per accogliere animali malati o feriti con lettiera asciutta e/o tappetino confortevole ove condizione clinica lo richieda. - Il requisito superiore prevede la seguente condizione: locale specifico ed identificato, con lettiera asciutta e/o tappetino, in grado di ospitare un numero sufficiente di animali e con un minimo di 2 aree dedicate e separate per singolo Edificio "Gli animali malati o feriti se necessario vengono isolati in appositi locali muniti, se del caso, di lettiere asciutte o confortevoli" e "I suini che devono essere allevati in gruppo [...] che sono malati o feriti, sono temporaneamente tenuti in recinto individuale". Nel reparto infermeria devono essere posti solo animali malati o feriti. Verificare la presenza, il numero e le condizioni dei locali infermeria. La condizione non adeguata prevede: nessun locale specifico ed identificabile - La condizione adeguata prevede: che ci sia un locale identificato, appositamente preparato per accogliere animali malati o feriti con lettiera asciutta e/o tappetino confortevole ove condizione clinica lo richieda. - Il requisito superiore prevede la seguente condizione: locale specifico ed identificato, con lettiera asciutta e/o tappetino, in grado di ospitare un numero sufficiente di animali e con un minimo di 2 aree dedicate e separate per singolo edificio','Il locale/recinto infermeria è chiaramente identificato e con presenza permanente di lettiera asciutta e/o tappetino nel caso la condizione clinica lo richieda e acqua fresca in quantità sufficiente','S, NA, NB, NC, -, O','Identificare la posizione del/dei box, segnalare le dimensioni larghezza, lunghezza, numero suini e fascia di peso.',24);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,73,1171,'EDIFICI E LOCALI DI STABULAZIONE','Gestione degli alimenti','146/2001 All. Mangimi, acqua e altre sostanze punto 17 "Le attrezzature per la somministrazione di mangimi e di acqua devono essere concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti". I locali adibiti alla preparazione/conservazione degli alimenti adeguatamente separati e soddisfano i requisiti minimi dal punto di vista igienico-sanitario. La condizione non adeguata prevede: locali adibiti alla preparazione/conservazione degli alimenti non idonei (es. mancata separazione e/o luoghi di preparazione degli alimenti sporchi, fatiscenti)- La condizione adeguata prevede: Locali adibiti alla preparazione/conservazione degli alimenti idonei (es. locali adeguatamente separati, puliti e correttamente gesti','I locali adibiti alla preparazione/conservazione degli alimenti sono adeguatamente separati e soddisfano i requisiti minimi dal punto di vista igienico- sanitario.','S, NA, NB, NC, -','Descrivere le modalità di preparazione e somministrazione dell''alimento',25);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,74,1172,'EDIFICI E LOCALI DI STABULAZIONE','Presenza di ripari nelle aree esterne per gli animali custoditi al di fuori dei fabbricati','146/2001 All. Fabbricati e locali di stabulazione punto 12 "Agli animali custoditi al di fuori dei fabbricati deve essere fornito, in funzione delle necessità e delle possibilità, un riparo adeguato dalle intemperie, dai predatori e da rischi per la salute". Si considerano i ripari sia di tipo naturale (alberi, anfratti, grotte ecc.) che di tipo artificiale (tettoie, ricoveri, ecc.) a condizione che offrano un riparo adeguato, in relazione alla stagione e alla località. La condizione non adeguata prevede: In funzione della valutazione del rischio, delle necessità e delle possibilità, l''assenza, insufficienza o presenza di ripari non adeguati anche per un solo gruppo di animali. La condizione adeguata prevede: la presenza di ripari sufficienti ed adeguati per tutti gli animali; La condizione per il requisito superiore prevede: la presenza di ripari artificiali facilmente accessibili, sufficienti ed idonei a proteggere tutti gli animali dai pericoli ambientali. Se nell''allevamento non ci sono animali custoditi al di fuori dei fabbricati, il requisito si considera non applicabile.','Gli animali custoditi al di fuori dei fabbricati hanno ripari adeguati e sufficienti o gli animali sono stabulati al chiuso','S, NA, NB, NC, -, O','Descrivere i ripari, la loro localizzazione, e le modalità con le quali gli animali li raggiungono.',26);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,75,1173,'ILLUMINAZIONE MINIMA','Illuminazione','122/2011 All. 1 parte generale punto 2 e 146/2001 All. Fabbricati e locali di stabulazione punto 11 "I suini devono essere tenuti alla luce di un''intensità di almeno 40 lux per un periodo minimo di 8 ore al giorno". Gli animali custoditi nei fabbricati non devono essere tenuti costantemente al buio o esposti ad illuminazione artificiale senza un adeguato periodo di riposo. Se la luce naturale disponibile è insufficiente a soddisfare esigenze comportamentali e fisiologiche degli animali, occorre prevedere un''adeguata illuminazione artificiale. Indicativamente, in condizioni di routine, si può considerare come riferimento per i 40 lux una luce che permette di leggere la check-list senza sforzo. La condizione non adeguata prevede: l''illuminazione naturale insufficiente e illuminazione artificiale assente/insufficiente. - La condizione adeguata prevede: l''illuminazione adeguata. In caso di sospetto condizione inadeguata è necessario confermare con luxom','Dove sono stabulati i suini è assicurata la luce di intensità di almeno 40 lux per un periodo minimo di 8 ore al giorno.','S, NA, NB, NC, -','(*) Registrare il tipo di illuminazione artificiale/naturale/assente e in caso di rilievo non conforme confermare con luxometro',27);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,76,1174,'PAVIMENTAZIONI','Caratteristiche pavimenti e strutture','122/2011 All. 1 parte I punto 5. "I pavimenti devono essere non sdrucciolevoli e senza asperità per evitare lesioni ai suini e progettati, costruiti e mantenuti in modo da non arrecare lesioni o sofferenze ai suini. Essi devono essere adeguati alle dimensioni e al peso dei suini e, se non è prevista un lettiera, costituire una superficie rigida, piana e stabile". I pavimenti non devono essere sdrucciolevoli o presentare asperità. condizione non adeguata prevede: la presenza anche di un solo parametro non sufficiente - La condizione adeguata prevede: tutti i parametri sono sufficienti -','I pavimenti: - non sono sdrucciolevoli e non hanno asperità che possono provocare lesioni ai suini - sono costruiti e mantenuti in modo da non arrecare lesioni o sofferenze agli animali - sono adeguati alle dimensioni e al peso dei suini - se non è prevista una lettiera i pavimenti sono a superficie rigida, piana e stabile - gli escrementi, l''urina e i foraggi non mangiati o caduti sono rimossi con regolarità per ridurre al minimo gli odori o la presenza di mosche o roditori.','S, NA, NB, NC, -','(*) Le valutazioni ABM ( lesioni cutanee per l''elemento di verifica n. 12 e zoppie per l''elemento di verifica 17 ) possono supportare e dare un''evidenza della presenza di un''eventuale non conformità o confermare una conformità.',28);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,77,1175,'PAVIMENTAZIONI','Conformità pavimentazione - conformità pavimentazione parchetto','122/2011 Art.3 Punto 1 lettera c sottopunto 1 e 2 "Qualora si utilizzano pavimenti fessurati in calcestruzzo per suini allevati in gruppo: l''ampiezza massima delle aperture deve essere di:11 mm per i lattonzoli, 14 mm per i suinetti, 18 mm per i suini all''ingrasso, 20 mm per le scrofette dopo la fecondazione e le scrofe; l''ampiezza minima dei travetti deve essere di: 50 mm per i lattonzoli e i suinetti, 80 mm per i suini all''ingrasso, le scrofette dopo la fecondazione e le scrofe" e "[...] una parte della superficie [...] pari ad almeno 0,95 mq per scrofetta e ad almeno 1,3 mq per scrofa, deve essere costituita da pavimento pieno continuo riservato per non oltre il 15 per cento alle aperture di scarico". a) Verificare il rispetto della normativa all''interno del box (N.B. alle misure deve essere applicato il range di tolleranza ± 3 mm come da C.M. 22766 del 12.12.2012 e norme ISO). - La condizione non adeguata prevede: una pavimentazione in calcestruzzo fessurato con ampiezza dei travetti o delle fessure non conformi alla normativa - La condizione adeguata prevede una pavimentazione in calcestruzzo fessurato con misure idonee; pavimento pieno; grigliato in plastica o in metallo; - Il requisito superiore prevede la seguente condizione: pavimento pieno con lettiera permanente','a) SCROFETTE DOPO LA FECONDAZIONE E SCROFE GRAVIDE - una parte della superficie libera totale a disposizione per ciascuna è costituita da pavimento pieno continuo: 1) di almeno 0,95 mq per ogni scrofetta 2) di almeno 1,3 mq per ogni scrofa. Una parte di tale pavimento (non superiore al 15%) è riservata alle aperture di scarico (griglie, tombini ecc.) - i pavimenti fessurati in calcestruzzo per SUINI ALLEVATI IN GRUPPO hanno: A) l''ampiezza massima delle aperture di: - 11 mm per i lattonzoli; - 14 mm per i suinetti; - 18 mm per i suini all''ingrasso; - 20 mm per le scrofette dopo la fecondazione e le scrofe; B) l''ampiezza minima dei travetti: - 50 mm per i lattonzoli e i suinetti; - 80 mm per i suini all''ingrasso, le scrofette dopo la fecondazione e le scrofe','S, NA, NB, NC, -, O','(*) Annotare le dimensioni travetto, fessura per le varie tipologie di box e per i vari reparti.',29);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,77,1176,'PAVIMENTAZIONI','Conformità pavimentazione - conformità pavimentazione parchetto','b) Verificare il rispetto della normativa anche all''interno del parchetto se lo stesso viene incluso nella superficie utile (parchet coperto) (N.B. alle misure deve essere applicato il range di tolleranza ± 3 mm come da C.M. e ISO). - La condizione non adeguata prevede: una pavimentazione in calcestruzzo fessurato con ampiezza dei travetti o delle fessure non conformi alla normativa - La condizione adeguata prevede: una pavimentazione in calcestruzzo fessurato con misure idonee o pavimento pieno','b) SCROFETTE DOPO LA FECONDAZIONE E SCROFE GRAVIDE - LATTONZOLI, SUINETTI E SUINI ALL''INGRASSO : Il parchetto esterno rispetta i requisiti previsti dalla normativa in materia di pavimentazione','S, NA, NB, NC, -','(*) Annotare le dimensioni travetto, fessura per le varie tipologie di box e per i vari reparti.',30);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,78,1177,'PAVIMENTAZIONI','Spazio a disposizione dei verri - Contatti tra animali VERRO','a) 122/2011 All. parte II lettera A punto 1 e 2 b) 122/2011 All. parte II lettera A punto 1 a) "Il verro adulto deve disporre di una superficie libera al suolo di almeno 6 mq" e "Qualora i recinti siano utiliz l''accoppiamento, il verro adulto deve disporre di una superficie al suolo di 10 mq e il recinto deve essere libero da ost - La condizione non adeguata prevede: spazio del box non sufficiente - La condizione adeguata prevede: spazio a disposizione sufficiente','a) la superficie libera al suolo minima del recinto per VERRO ADULTO è di 6 mq - se il recinto viene utilizzato anche per l''accoppiamento la superficie al suolo è di almeno 10 mq ed è libero da ostacoli','S, NA, NB, NC, -','(*) Registrare le dimensioni dei box: larghezza, lunghezza',31);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,78,1178,'PAVIMENTAZIONI','Spazio a disposizione dei verri - Contatti tra animali VERRO','b) "I recinti per i verri devono essere sistemati e costruiti in modo da permettere all''animale di girarsi e di avere il contatto uditivo, olfattivo e visivo con gli altri suini". Verificare le dimensioni dei box per i verri e la possibilità di contatto uditivo, olfattivo e visivo. - La condizione non adeguata prevede: l''impossibilità di contatto - La condizione adeguata prevede: la possibilità di contatto uditivo, olfattivo e visivo','b) nel recinto il verro si può girare ed avere contatti uditivi, olfattivi e visivi con altri suini','S, NA, NB, NC, -','(*) Registrare le dimensioni dei box: larghezza, lunghezza',32);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,79,1179,'MATERIALE MANIPOLABILE','Materiale per esplorazione e manipolazione - Materiale per la nidificazione','a) 122/2011 Art. 3 punto 5 e All. 1 parte I punto 4 e parte II lettera B punto 1 e lettera D e Raccomandazione (UE) 2016/336 a) "i suini devono avere accesso permanente a una quantità sufficiente di materiali che consentano loro adeguate attività di esplorazione e manipolazione, quali ad esempio paglia, fieno, legno, segatura, composti di funghi, torba o un miscuglio di questi, salvo che il loro uso possa comprometterne la salute e il benessere". Verificare la presenza di materiali che possano esplicare la funzione di esplorazione e manipolazione e che essi siano nella disponibilità degli animali in tutti i reparti. - La condizione non adeguata prevede: assenza di materiali manipolabili o presenza di materiali manipolabili di sola categ marginale (es. catena, oggetti di plastica) o un solo materiale di categoria sub-ottimale o non idoneo (es. copertoni) o materiali non disponibili o sporchi. Il mangime secco in mangiatoia nell''alimentazione ad libitum non é considerato materiale manipolabile adeguato - La condizione adeguata prevede: la presenza di più materiali manipolabili di categoria sub-ottimale o una commistione di materiali (almeno un marginale e un subottimale) complementari a garantire tutte le proprietà dei materiali idonei. - La condizione per il requisito superiore prevede: la presenza di materiali manipolabili di categoria ottimale o due di categoria sub ottimale complementari','a) i suini (fermo restando quanto previsto all''art. 3, comma 5 D.Lgs. 122/2011 per scrofe e scrofette) hanno accesso ad una quantità sufficiente di materiale che consente loro adeguate attività di esplorazione e manipolazione (ad es. paglia, fieno, legno, segatura, composti di funghi, torba o un loro miscuglio, etc.) - salvo che il loro uso possa compromettere la salute o il benessere degli animali - le SCROFE e SCROFETTE hanno accesso permanente al materiale','S, NA, NB, NC, -, O','(*) Indicare il materiale manipolabile utilizzato (paglia, fieno, segatura, composti di funghi, torba, materiale grossolano quale legno o altro);',33);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,110,1211,'MATERIALE MANIPOLABILE','Materiale per esplorazione e manipolazione - Materiale per la nidificazione','b) 122/2011 All. parte II lettera B punto 3 b) "Nella settimana precedente al momento previsto del parto, scrofe e scrofette devono disporre di una lettiera adeguata in quantità sufficiente, a meno che ciò non sia tecnicamente realizzabile per il sistema di eliminazione dei liquami utilizzato nello stabilimento". Verificare la presenza di materiali. - La condizione non adeguata prevede: lettiera assente o insufficiente - La condizione adeguata prevede: lettiera sufficientemente presente o assente con comprovata impossibilità di utilizzo per il sistema di eliminazione dei liquami - La condizione per il requisito superiore prevede: lettiera di qualità e quantità ottimale (paglia fornita come lettiera) b','b) SCROFE e SCROFETTE nella settimana precedente il parto dispongono di lettiera adeguata in quantità sufficiente (tranne nel caso in cui sia tecnicamente irrealizzabile per il sistema di eliminazione dei liquami)','S, NA, NB, NC, -, O','(*) Indicare il materiale manipolabile utilizzato (paglia, fieno, segatura, composti di funghi, torba, materiale grossolano quale legno o altro);',34);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,80,1180,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRA- ZIONE DI ALTRE SOSTANZE','Alimentazione: frequenza - Gestione degli alimenti e della razione giornaliera','a) 146/2001 All. Mangimi, acqua e altre sostanze punto 15 e 122/2011 All. 1 parte I punto 6  a) "Tutti suini devono essere nutriti almeno una volta al giorno" Verificare le dichiarazioni dell''allevatore e la situazione in allevamento: controllare le programmazioni per le somministrazioni di alimento a livello di gestionale o verificare se l''alimentazione è ad libitum. - La condizione non adeguata prevede: mancata somministrazione giornaliera - La condizione adeguata prevede: la somministrazione almeno una volta al giorno - La condizione per il requisito superiore prevede: che ci siano almeno 2 o più somministrazioni di cibo al giorno (compresi i giorni festivi) o che sia presente l''alimentazione ad libitum','a) Tutti suini devono essere nutriti almeno una volta al giorno','S, NA, NB, NC, -, O','(*) Visionare il cartellino/formula dell''alimento e prenderne i riferimenti',35);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,111,1212,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRA- ZIONE DI ALTRE SOSTANZE','Alimentazione: frequenza - Gestione degli alimenti e della razione giornaliera','b) 146/2001 All. Mangimi, acqua e altre sostanze punto 14 b) "Agli animali deve essere fornita un''alimentazione sana adatta alla loro età e specie e in quantità sufficiente a mantenerli in buona salute e a soddisfare le loro esigenze nutrizionali [...]". Agli animali deve essere fornita un''alimentazione qualitativame quantitativamente adeguata: i limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. - La condizione non adeguata prevede: una razione non adatta agli animali: es. razione empirica non in rapporto ai fabbisogni composta da alimenti non adeguati. - La condizione adeguata prevede: una razione adatta agli animali: es. razione specifica per ogni gruppo (Riproduttori: scrofe scrofette, suinetti lattonzoli- Svezzamento- Ingrasso: suinetti svezzati, suini all''ingrasso ). - La condizione per il requisito superiore prevede: una razione ottimale per gli animali: es. razione calcolata da un alimentaris composta da alimenti con la presenza di nutrienti essenzi','b) l''alimentazione è adeguata in rapporto all''età, al peso e alle esigenze comportamentali e fisiologiche delle diverse categorie animali','S, NA, NB, NC, -, O','(*) Visionare il cartellino/formula dell''alimento e prenderne i riferimenti',36);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,81,1181,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRA- ZIONE DI ALTRE SOSTANZE','Alimentazione: accesso agli alimenti - Alimentazione razionata: aggressioni scrofe e scrofette in box','a) 122/2011 Art. 3 punto 6. a)"Le scrofe e le scrofette allevate in gruppo devono essere alimentate utilizzando un sistema idoneo a garantire che ciascun animale ottenga mangime a sufficienza senza essere aggredito, anche in situazione di competitività" Il sistema utilizzato garantisce che si evitino aggressioni. - La condizione non adeguata prevede: la presenza di un sistema inefficace a garantire le aggressioni (es. ridotto spazio al truogolo) - La condizione adeguata prevede: che il sistema utilizzato garantisca che si evitino aggressioni (es. spazio al truogolo sufficiente o alimentatore automatico ad ingresso selettivo','a) le SCROFE e le SCROFETTE ALLEVATE in GRUPPO sono alimentate utilizzando un sistema idoneo a garantire che ciascun animale ottenga mangime a sufficienza senza essere aggredito, anche in situazione di competitività','S, NA, NB, NC, -','(*) Registrare la tipologia di alimentazione ad libitum/razionata e il numero di distributori (ad libitum)/cm capo a disposizione e la fascia di peso',37);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,112,1213,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRA- ZIONE DI ALTRE SOSTANZE','Alimentazione: accesso agli alimenti - Alimentazione razionata: aggressioni scrofe e scrofette in box','b) 122/2011 Art. 3 punto 6 e All. 1 parte I punto 6. b) "Le scrofe e le scrofette allevate in gruppo devono essere alimentate utilizzando un sistema idoneo a garantire che ciascun animale ottenga mangime a sufficienza" e "Se i suini sono alimentati in gruppo e non «ad libitum» o mediante un sistema automatico alimentazione individuale, ciascun suino deve avere accesso agli alimenti contemporaneamente agli altri suini del gruppo". Riproduttori - In caso di alimentazione razionata, la lunghezza del fronte mangiatoia per ogni scrofa e scrofetta deve essere di 50 cm, per permettere la contemporaneità dell''alimentazione. Svezzamento-Ingrasso i limiti indicati in tabella sono un ausilio per il valutatore che prima di esprimere un giudizio dovrà comunque considerare tutti i fattori di rischio dell''allevamento. Verificare il tipo di  alimentazione (razionata o ad libitum). - La condizione non adeguata prevede: l''impossibilità di accesso contemporaneo all''alimento nel caso di alimentazione razionata. - La condizione adeguata prevede: che ciascun suino ha accesso agli alimenti contemporaneamente o è presente l''alimentazione ad libitum - La condizione per il requisito superiore prevede: ciascun suino ha un ottimale accesso agli alimenti , in caso di alimentazion razionata: Riproduttori - dimensione minima al truogolo (50cm) con più punti di accesso (es. due truogoli)- Svezzamento-Ingrasso - uno spazio al truogolo maggiorato come da fronte mangiatoia ottimale; in caso di alimentazione ad libitum un punto di distribuzione ogni 10 animali','b) se sono alimentati in gruppo e non ad libitum o mediante un sistema automatico di alimentazione individuale, ciascun suino ha accesso agli alimenti contemporaneamente agli altri suini del gruppo','S, NA, NB, NC, -, O','(*) Registrare la tipologia di alimentazione ad libitum/razionata e il numero di distributori (ad libitum)/cm capo a disposizione e la fascia di peso',38);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,82,1182,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRA- ZIONE DI ALTRE SOSTANZE','Acqua di abbeverata: disponibilità - Distribuzione dell''alimento','146/2001 All. Mangimi, acqua e altre sostanze punto 16 e 122/2011 All. 1 parte I punto 7 a) "Tutti gli animali devono avere accesso ad un''appropriata quantità di acqua, di qualità adeguata, o devono poter soddisfare le loro esigenze di assorbimento di liquidi in altri modi" e "A partire dalla seconda settimana di età, ogni suino deve poter disporre permanenza di acqua fresca sufficiente".Verificare la possibilità che tutti i suini abbiano costante accesso ad una fonte di distribuzione sempre funzionante di acqua fresca. Valutare il numero di abbeveratoi presenti, l''accessibilità e il loro funzionamento. - La condizione non adeguata prevede: la distribuzione di acqua razionata o abbeverata non permanente o abbeveratoi non accessibili. - La condizione adeguata prevede: la presenza di abbeveratoio funzionante a imbocco, tazza (uno per box) o abbeveratoio permanente con rabbocco automatico (es sistema a galleggiante), accessibile. - La condizione per il requisito superiore prevede: la presenza di abbeveratoi funzionanti accessibili e puliti, in numero minimo di 2 per box o comunque 1 ogni 15 animali o abbeveratoio permanente con sistema automatico di controllo della presenza del flusso','a) a partire dalla seconda settimana di età, ogni suino dispone in permanenza di acqua fresca di qualità ed in quantità sufficiente','S, NA, NB, NC, -, O','(*) Registrare la tipologia di abbeverata razionata/succhiotto/tazza/truogolo e il numero di abbeveratoi',39);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,113,1214,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRA- ZIONE DI ALTRE SOSTANZE','Acqua di abbeverata: disponibilità - Distribuzione dell''alimento','b) 146/2001 All. Mangimi, acqua e altre sostanze punto 17 b) "Le attrezzature per la somministrazione di mangimi e di acqua devono essere concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua" Valutare la pulizia delle attrezzature per la somministrazione di mangimi e acqua. - La condizione non adeguata prevede che le attrezzature per la somministrazione di mangimi e acqua non risultino idonee (es. la somministrazione del mangime secco a terra in mancanza di adeguata mangiatoia, eccezione fatta per l''utilizzo temporaneo al momento dell''immissione degli animali nel box per abituarli a non defecare/urinare nel luogo di alimentazione o al momento della formazione dei gruppi di scrofe/scrofette/suinetti a scopo distrattivo). - La condizione adeguata prevede che le attrezzature per la somministrazione di mangimi e acqua risultino idonee (non presentano materiale fecale all''interno e si presentano pulite)','b) I secchi, i poppatoi, le mangiatoie sono puliti dopo ogni utilizzo e sottoposti a periodica disinfezione ogni alimento avanzato viene rimosso regolarmente - le attrezzature per l''alimentazione automatica sono pulite regolarmente e frequentemente, smontando le parti in cui si depositano residui di alimento','S, NA, NB, NC, -','(*) Registrare la tipologia di abbeverata razionata/succhiotto/tazza/truogolo e il numero di abbeveratoi',40);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,83,1183,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRA- ZIONE DI ALTRE SOSTANZE','Somministrazioni di sostanze illecite','146/2001 All. Mangimi, acqua e altre sostanze punto 18. "Nessuna altra sostanza, ad eccezione di quelle somministrate a fini terapeutici o profilattici o in vista di trattamenti zootecnici come previsto nell''art. 1, paragrafo 2, lettera c) della direttiva 96/22/CE, deve essere somministrata ad un animale, a meno che gli stud scientifici sul benessere degli animali e l''esperienza acquisita ne abbiano dimostrato l''innocuità per la sua salute e il suo benessere". Verificare il registro o le registrazioni elettroniche dei trattamenti e l''armadietto o eventuali confezioni o giacenze di farmaci. 146/2001 All. Mangimi, acqua e altre sostanze punto 18. La condizione non adeguata prevede: la somministrazione di sostanze illecite; La condizione adeguata prevede: nessuna somministrazione di sostanze illecite. ','Non viene somministrata alcuna sostanza, ad eccezione di quelle somministrate a fini terapeutici o profilattici o in vista di trattamenti zootecnici come previsto dalla normativa vigente','S, NA, NB, NC, -','',41);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,84,1184,'MANGIMI CONTENENTI FIBRE','Fibra nella razione delle scrofe gestanti - Energia nella razione delle scrofe gestanti','a-b) 146/2001 All. Mangimi, acqua e altre sostanze punto 14 e 122/2011 Art. 3 punto 7. a) Agli animali deve essere fornita un''alimentazione sana adatta alla loro età e specie e in quantità sufficiente a mantenerli in buona salute e a soddisfare le loro esigenze nutrizionali. Gli alimenti o i liquidi sono somministrati agli animali in modo da non causare loro inutili sofferenze o lesioni e non contengono sostanze che possano causare inutili sofferenze o lesioni. "Le scrofe e le scrofette asciutte gravide devono ricevere mangime riempitivo o ricco di fibre in quantità sufficiente [...]"Per sufficiente si intende un tenore di cellulosa grezza compreso tra il 2,23% e il 6% della razione o un quantitativo di almeno 100 g/giorno per scrofa di paglia o fieno: i limiti indicati sono un ausilio per il valutatore che prima di esprimere un giudizio dovrà comunque considerare tutti i fattori d dell''allevamento. - La condizione non adeguata prevede: una quantità di fibra nella razione non sufficiente - La condizione adeguata prevede: una quantità di fibra nella razione sufficiente','a) Per calmare la fame e tenuto conto del bisogno di masticare tutte le SCROFE e le SCROFETTE ASCIUTTE GRAVIDE ricevono mangime riempitivo o ricco di fibre in quantità sufficiente','S, NA, NB, NC, -','Registrare il tenore di fibra dichiarato o la presenza in % di alimenti con alto tenore di fibra. Registrare il tenore energetico del mangime o la presenza in % di alimenti Energetici.',42);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,84,1185,'MANGIMI CONTENENTI FIBRE','Fibra nella razione delle scrofe gestanti - Energia nella razione delle scrofe gestanti','b) "Le scrofe e le scrofette asciutte gravide devono ricevere mangime così come alimenti ad alto tenore energetico". La razione deve contenere alimenti con un alto tenore energetico: es. cereali (verificare il tenore energetico per kg di alimento o in base al fabbisogno giornaliero della scrofa). - La condizione non adeguata prevede: il tenore energetico nella razione non adeguato - La condizione adeguata prevede: il tenore energetico nella razione adeguato','b) Le SCROFE e le SCROFETTE ASCIUTTE GRAVIDE ricevono alimenti ad alto tenore energetico','S, NA, NB, NC, -','Registrare il tenore di fibra dichiarato o la presenza in % di alimenti con alto tenore di fibra. Registrare il tenore energetico del mangime o la presenza in % di alimenti Energetici.',43);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,85,1186,'MUTILAZIONI','Operazioni non consentite','146/2001 All. Mutilazioni punto 19 e 122/2011 All. 1 punto 8, 9 e 10. "Sono vietate tutte le operazioni effettuate per scopi diversi da quelli terapeutici o diagnostici o per l''identificazione dei suini (e incisione auricolare) e che possono provocare un danno o la perdita di una parte sensibile del corpo o un''alterazione della struttura ossea". Valutare se vengono eseguite procedure di mutilazione oltre a quelle derogate dalla normativa. La condizione non adeguata prevede: Sono eseguite operazioni che causano mutilazioni non consentite dalla normativa (es. incisione auricolare no identificazione) - La condizione adeguata prevede: sono eseguite operazioni che causano mutilazioni consentite dalla normativa o non sono effettuate operazioni che causano mutilazioni','Non sono effettuate delle operazioni, non derogate dalla normativa, che possono provocare un danno o la perdita di una parte sensibile del corpo o un''alterazione della struttura ossea.','S, NA, NB, NC, -','(*) Descrivere l''eventuale tipo di mutilazione, le categorie di animali interessati e i box/ricoveri dove sono alloggiati',44);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,86,1187,'MUTILAZIONI','Levigatura o troncatura incisivi (lattonzoli)','122/2011 All. 1 punto 10, 9 e 8 lettera a "sono vietate tutte le operazioni [...] ad eccezione: di una riduzione uniforme degli incisivi dei lattonzoli mediante levigatura troncatura, entro i primi sette giorni di vita, che lasci una superficie liscia intatta" e "[...] la riduzione degli incisivi dei lattonzoli non devono costituire operazioni di routine, ma devono essere praticati soltanto ove sia comprovata la presenza di ferite ai capezzoli delle scrofe o agli orecchi o alle code di altri suini" e "Tutte le operazioni sopra descritte devono essere praticate da un veterinario o da altra persona formata ai sensi dell''articolo 5" La riduzione degli incisivi è vietata per scopi diversi da quelli terapeutici. Come eccezione, è consentita in presenza di ferite, previa verifica della presenza delle idonee condizioni gestionali ambientali, a condizione che lasci una superfice liscia intatta e che sia eseguita dal veterinario o da personale formato entro i 7 giorni di vita. La condizione non adeguata prevede: presenza di suini con riduzione degli incisivi senza adempimento di uno dei requisiti richiesti - La condizione adegua prevede: l''assenza di suini con una riduzione degli incisivi o presenza di suini con riduzione degli incisivi con il rispetto di tutti Requisiti.','La riduzione uniforme degli incisivi dei lattonzoli é praticata entro i primi 7 giorni di vita, mediante levigatura o troncatura che lasci una superficie intatta - le operazioni di riduzione degli incisivi dei lattonzoli non costituiscono operazioni di routine, ma sono praticate soltanto se sono comprovate lesioni ai capezzoli delle scrofe, agli orecchi o alle code dei suinetti - tutte queste operazioni sono praticate da un veterinario o da altro personale specializzato (ai sensi dell''articolo 5) con tecniche e mezzi adeguati ed in condizioni igieniche','S, NA, NB, NC, -','(*) Controllo a campione di almeno 5 box contenenti suinetti di età superiore a 7 giorni e annotazione delle evidenze riscontrate',45);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,87,1188,'MUTILAZIONI','Riduzione delle zanne dei verri','122/2011 All. 1 punto 10 e 8 lettera a "[...] zanne dei verri che possono essere ridotte, se necessario, per evitare lesioni agli altri animali o per motivi di sicurezza" e "Tutte le operazioni sopra descritte devono essere praticate da un veterinario o da altra persona formata ai sensi dell''articolo 5". La riduzione delle zanne dei verri è vietata per scopi diversi da quelli terapeutici. Come eccezione, è ammessa per evitare le lesioni ad altri animali o per motivi di sicurezza, a condizione che sia eseguita dal veterinario o da personale formato. La condizione non adeguata prevede: la presenza di verri con zanne ridotte senza adempimento dei requisiti o effettuata da personale non formato - La condizione adeguata prevede: non viene effettuata la riduzione delle zanne o viene eseguita con il rispetto di tutti i requis','La riduzione delle zanne dei verri viene effettuata per evitare lesioni agli altri animali o per motivi di sicurezza e solamente da un veterinario o da personale formato - tutte queste operazioni sono praticate da un veterinario o da altro personale specializzato (ai sensi dell''articolo 5) con tecniche e mezzi adeguati ed in condizioni igieniche','S, NA, NB, NC, -','',46);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,88,1189,'MUTILAZIONI','Mozzamento della coda - Misure preventive al taglio della coda','a) 122/2011 All. 1 punto 10, 9 e 8 lettera b. a) "sono vietate tutte le operazioni [...] ad eccezione: del mozzamento di una parte della coda" e " il mozzamento della coda e [...] non devono costituire operazioni di routine, ma devono essere praticati soltanto ove sia comprovata la presenza di ferite [...] alle code di altri suini" e "Tutte le operazioni sopra descritte devono essere praticate da un veterinario o da altra persona formata ai dell''articolo 5 [...]" e "Qualora la castrazione o il mozzamento della coda siano praticati dopo il settimo giorno di vita, essi devono essere effettuati unicamente da parte di un veterinario sotto anestesia e con somministrazione prolungata di analgesici" Il mozzamento della coda è vietato per scopi diversi da quelli terapeutici. Come eccezione, è consentito in presenza di ferite sulle code, previa verifica e successivo miglioramento della situazione gestionale ed ambientale, agendo sul materiale di arricchimento, a condizione che venga eseguito da personale formato (entro i 7 giorni di vita) oppure dal veterinario con anestesia e analgesia prolungata (oltre i 7 giorni di vita). - La condizione non adeguata prevede: la presenza di suinetti con code mozzate senza adempimento di uno dei requisiti richiesti - La condizione adeguata prevede: la presenza di suini con code mozzate, ma previo adempimento di tutti i requisiti richiesti - La condizione per il requisito superiore prevede: l''assenza, nell''intero allevamento, di suini con code mozzate ','a) il mozzamento di una parte della coda viene effettuato entro i primi 7 giorni di vita. - se la castrazione o il mozzamento della coda sono praticati dopo il 7° giorno di vita, sono eseguiti sotto anestesia e con somministrazione prolungata di analgesici, unicamente da un medico veterinario - il mozzamento della coda e la riduzione degli incisivi dei lattonzoli non costituiscono operazioni di routine, ma sono praticati soltanto se sono comprovate lesioni ai capezzoli delle scrofe, agli orecchi o alle code dei suinetti e dopo aver adottato misure intese ad evitare le morsicature delle code ed altri comportamenti anormali (tenendo conto delle condizioni ambientali e della densità). - tutte queste operazioni sono praticate da un veterinario o da altro personale specializzato (ai sensi dell''articolo 5) con tecniche e mezzi adeguati ed in condizioni igieniche.','S, NA, NB, NC, -, O','(*) Allevamento con svezzamento e ingrasso: - Verificare la presenza dell''analisi del rischio e l''eventuale cronoprogramma del piano di miglioramento dei punti critici - Verificare, se del caso, la presenza di gruppi di animali con coda lunga e descriverne il numero e la localizzazione - Verificare, se del caso, l''ultima autovalutazione. Allevamento da riproduzione: - Verificare, se del caso, l''età di effettuazione del taglio della coda, controllando a campione almeno 5 covate di animali di più di 7 giorni.',47);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,114,1215,'MUTILAZIONI','Mozzamento della coda - Misure preventive al taglio della coda','b) D.Lgs 122/2011, All I parte I, par 9. b) "Il mozzamento della coda [...] non devono costituire operazioni di routine, ma devono essere praticati soltanto ove sia comprovata la presenza di ferite ai capezzoli delle scrofe o agli orecchi o alle code di altri suini. Prima di effettuare tali operazioni si devono adottare misure intese ad evitare le morsicature delle code e altri comportamenti anormali tenendo conto delle condizioni ambientali e della densità degli animali. È pertanto necessario modificare condizioni ambientali o sistemi di gestione inadeguati." Se vengono allevati suini a coda mozzata, sono state preventivamente adottate le misure di miglioramento delle condizioni ambientali e della densità degli animali negli alloggi. - La condizione non adeguata prevede: sono allevati suini a coda mozzata, non è stata effettuata alcuna valutazione del rischio, non sono state adottate misure di miglioramento . - La condizione adeguata prevede: sono allevati suini a coda mozzata ed è in corso il cronoprogramma di miglioramento in seguito a una valutazione del rischio, oppure é stato completato il cronoprogramma di miglioramento e sono allevati gruppi di animali a coda integra. - La condizione per il requisisto superiore prevede: tutti gli animali allevati presentano coda integra e non sono presenti lesioni alla coda o alle orecchie degli animali.','b) vi è documentazione della comprovata esigenza di tali pratiche (dichiarazione di un medico veterinario associata a valutazione del rischio).','S, NA, NB, NC, -, O','',48);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,89,1190,'MUTILAZIONI','Castrazione','146/2001 All. Mutilazioni punto 19 e 122/2011 All. 1 punto 10 e 8 lettera c. "Sono vietate tutte le operazioni [...] ad eccezione: della castrazione di suini di sesso maschile con mezzi diversi dalla lacerazione dei tessuti" e "Tutte le operazioni sopra descritte devono essere praticate da un veterinario o da altra persona formata ai sensi dell''articolo 5 [...] Qualora la castrazione o il mozzamento della coda siano praticati dopo il settimo giorno di vita, essi devono essere effettua unicamente da parte di un veterinario sotto anestesia e con somministrazione prolungata di analgesici" e "La castrazione è consentita per mantenere la qualità dei prodotti e le pratiche tradizionali di produzione a condizione che tali operazioni siano effettuate prima del raggiungimento della maturazione sessuale da personale qualificato, riducendo al minimo ogni sofferenza per gli animali. castrazione dei suini di sesso maschile è vietata per scopi diversi da quelli terapeutici. Come eccezione, è consentita tramite buone pratiche chirurgiche a condizione che sia eseguita da personale formato (entro i 7 giorni di vita) o dal veterinario con anestesia analgesia prolungata (oltre i 7 giorni di vita e comunque entro la maturità sessuale). - La condizione non adeguata prevede: la presenza di suini castrati senza adempimento di uno dei requisiti richiesti - La condizione adeguata prevede: la presenza di suini castrati con il rispetto di tutti i requisiti o di suini non castrati.','la castrazione dei suini di sesso maschile è effettuata con mezzi diversi dalla lacerazione dei tessuti entro i primi 7 giorni di vita - se la castrazione o il mozzamento della coda sono praticati dopo il 7° giorno di vita, sono eseguiti sotto anestesia e con somministrazione prolungata di analgesici, unicamente da un medico veterinario - tutte queste operazioni sono praticate da un veterinario o da altro personale specializzato (ai sensi dell''articolo 5) con tecniche e mezzi adeguati ed in condizioni igieniche','S, NA, NB, NC, -','(*) Se eseguita entro i 7 giorni registrare i nominativi degli addetti alla castrazione, gli estremi dei corsi di formazione specifici frequentati , registrare il numero di covate controllate per la verifica dei 7 giorni, verificare, se del caso, la tecnica di castrazione, per escludere la lacerazione dei tessuti; se eseguita oltre i 7 giorni registrare l''evidenza dei trattamenti farmacologici',49);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,90,1191,'MUTILAZIONI','Anello al naso','122/2011 All. 1 punto 10 e 8 lettera d. "L''apposizione di un anello al naso è ammessa soltanto quando gli animali sono detenuti in allevamenti all''aperto e nel rispetto della normativa nazionale" e "Tutte le operazioni sopra descritte devono essere praticate da un veterinario o da altra persona formata L''apposizione dell''anello al naso non è consentita, come deroga è ammessa solo negli allevamenti all''aperto. La condizione n adeguata prevede: la presenza di suini con anello al naso senza adempimento di uno dei requisiti richiesti - La condizione adeguata prevede: la presenza di suini con anello al naso con il rispetto di tutti i requisiti o di suini senza anello al naso','L''apposizione di un anello al naso avviene solo su animali stabulati all''aperto e viene effettuata da un veterinario o da personale formato','S, NA, NB, NC, -','',50);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,91,1192,'PROCEDURE D''ALLEVAMENTO','Disposizioni generali','146/2001 Al. Procedure di allevamento punto 20 "Non devono essere praticati l''allevamento naturale o artificiale o procedimenti di allevamento che provochino o possano provocare agli animali in questione sofferenze o lesioni. Questa disposizione non impedisce il ricorso a taluni procedimenti che possono causare sofferenze o ferite minime o momentanee o richiedere interventi che non causano lesioni durevoli, se consentiti dalle disposizioni nazionali." Valutare se vengono praticate procedure d''allevamento che ledono le 5 libertà. Condizione non adeguata: allevamento non consono alla specie suina con evidenti limitazioni a livello di 5 libertà. Condizione adeguata: allevamento consono alla specie suina senza limitazioni delle 5 libertà','Non sono praticati procedimenti di allevamento che provocano o possano provocare agli animali sofferenze o lesioni (questa disposizione non impedisce il ricorso a taluni procedimenti che possono causare sofferenze o ferite minime o momentanee o richiedere interventi che non causano lesioni durevoli, se consentiti dalle disposizioni vigenti)','S, NA, NB, NC, -','(*) Descrivere le modalità di contenimento degli animali per prelievi di sangue, vaccinazioni, fecondazioni ecc.',51);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,92,1193,'PROCEDURE D''ALLEVAMENTO','Biosicurezza - lotta agli infestanti','Reg. CE 852/2004 All. I parte A cap. II punto 4 f) "Gli operatori del settore alimentare che allevano, raccolgono o cacciano animali o producono prodotti primari di origine animale devono, se del caso, adottare misure adeguate per: per evitare la contaminazione da parte di animali e altri insetti nocivi". Verificare la presenza di azioni volte al controllo degli infestanti (mosche, roditori e parassiti). - La condizione non adeguata prevede: la completa insussistenza di qualsiasi azione volta al controllo degli infestanti o approssimativi (es. dichiarata la presenza di gatti come unico metodo di lotta ai roditori) - La condizione adeguata prevede: la presenza di azioni strutturate volte al controllo degli infestanti (es. piani di derattizzazione con descrizione dei principi attivi utilizzati, schede di sicurezza e planimetria con localizzazione delle esche, definizione dei limiti critici e delle azioni conseguenti al loro superamento, la registrazione dei trattamenti e delle azioni correttive','Vengono messe in atto azioni preventive e vengono eseguiti interventi contro mosche, roditori e parassiti','S, NA, NB, NC, -','*) Descrivere le modalità e periodicità di disinfestazione, derattizzazione e pulizia dell''allevamento (presenza di una ditta esterna o procedure interne aziendali), annotare gli estremi di alcune registrazioni e delle postazioni ispezionate',52);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,93,1194,'PROCEDURE D''ALLEVAMENTO','Età allo svezzamento - Procedure di pulizia negli impianti specializzati per lo svezzamento precoce da 21 a 27 giorni','a) 122/2011 All. 1 parte II lettera C punto 4. Riproduttori: a) "Nessun lattonzolo deve essere staccato dalla scrofa prima che abbia raggiunto un''età di 28 giorni, a meno che la permanenza presso la madre influenzi negativamente il benessere o la salute del lattonzolo o di quest''ultima. I lattonzoli possono tuttavia essere svezzati fino a sette giorni prima di tale eta'' qualora siano trasferiti in impianti specializzati" La condizione non adeguata prevede: età svezzamento non adeguata (inferiore ai 28 giorni in caso di assenza di impianti specializzati; inferiore ai 21 giorni in caso di impianti specializzati) - La condizione adeguata prevede:Età di svezzamento adeguata (dai 28 giorni in caso di assenza di impianti specializzati; dai 21 giorni in caso di impianti specializzati)','a) RIPRODUZIONE - LATTONZOLI: - nessuno di essi viene staccato dalla scrofa prima dei 28 giorni d''età (tranne vi sia influenza negativa per la madre o il lattonzolo stesso) - i lattonzoli sono svezzati prima dei 28 previsti max 7 giorni prima (21 gg) ma vengono trasferiti in impianti specializzati','S, NA, NB, NC, -','(*) Registrare l''età dello svezzamento e, se del caso, le modalità di pulizia, disinfezione, tutto pieno tutto vuoto, ecc.',53);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,115,1216,'PROCEDURE D''ALLEVAMENTO','Età allo svezzamento - Procedure di pulizia negli impianti specializzati per lo svezzamento precoce da 21 a 27 giorni','b) 122/2011 All. 1 parte II lettera C punto 4. b) "Tali impianti devono essere svuotati e accuratamente puliti e disinfettati prima dell''introduzione di un nuovo gruppo e devono essere separati dagli impianti in cui sono tenute le scrofe, in modo da ridurre al minimo i rischi di trasmissione di malattie ai piccoli". In caso di svezzamento precoce, i suinetti devono essere trasferiti in impianti che rispettano le condizioni igienico-sanitarie imposte dalla normativa. La condizione non adeguata prevede: gli impianti specializzati non vengono svuotati e/o puliti e/o disinfettati o non sono separati dalle scrofe - La condizione adeguata prevede: la presenza dell''impianto con tutto pieno tutto vuoto per settore (fisicamente separato) pulito e disinfettato a fine ciclo o vendita dei suinetti lattonzoli ai siti specializzati.','b) RIPRODUZIONE - LATTONZOLI: - gli impianti specializzati vengono svuotati, puliti e disinfettati prima dell''introduzione di un nuovo gruppo - gli impianti specializzati sono separati dagli impianti in cui sono tenute le scrofe (per ridurre i rischi di malattie ai piccoli)','S, NA, NB, NC, -','(*) Registrare le modalità di pulizia, disinfezione, tutto pieno tutto vuoto, ecc.',54);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,116,1217,'PROCEDURE D''ALLEVAMENTO','Età allo svezzamento - Procedure di pulizia negli impianti specializzati per lo svezzamento precoce da 21 a 27 giorni','c) 122/2011 All. 1 parte II lettera C punto 4. Svezzamento- Ingrasso c) "Tali impianti devono essere svuotati e accuratamente puliti e disinfettati prima dell''introduzione di un nuovo gruppo e devono essere separati dagli impianti in cui sono tenute le scrofe, in modo da ridurre al minimo i rischi di trasmissione di malattie ai piccoli". In caso di svezzamento precoce, i suinetti devono essere trasferiti in impianti che rispettano le condizioni igienico-sanitarie imposte dalla normativa. La condizione non adeguata prevede: che gli impianti specializzati non vengono svuotati e/o puliti e/o disinfettati o non sono separati dalle scrofe. - La condizione adeguata prevede: la presenza dell''impianto specializzato con tutto pieno tutto vuoto per settore (fisicamente separato) pulito e disinfettato a fine ciclo. - La condizione per il requisito superiore (possibile solo per la riproduzione) prevede: lo svezzamento oltre i 28 giorni di vita.','c) INGRASSO - LATTONZOLI: - nessuno di essi viene staccato dalla scrofa prima dei 28 giorni d''età (tranne vi sia influenza negativa per la madre o il lattonzolo stesso) - i lattonzoli sono svezzati prima dei 28 previsti max 7 giorni prima (21 gg) ma vengono trasferiti in impianti specializzati - gli impianti specializzati vengono svuotati, puliti e disinfettati prima dell''introduzione di un nuovo gruppo - gli impianti specializzati sono separati dagli impianti in cui sono tenute le scrofe (per ridurre i rischi di malattie ai piccoli)','S, NA, NB, NC, -, O','(*) Registrare l''età dello svezzamento e, se del caso, le modalità di pulizia, disinfezione, tutto pieno tutto vuoto, ecc.',55);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,94,1195,'ATTREZZATURE AUTOMATICHE E MECCANICHE  ','Allarme impianto di ventilazione e impianto di riserva  ','146/2001 All. Impianti automatici o meccanici punto 13. "Se la salute ed il benessere degli animali dipendono da un impianto di ventilazione artificiale, deve essere previsto un adegua impianto di riserva per garantire un ricambio di aria sufficiente a salvaguardare la salute e il benessere degli animali. In caso di guasto all''impianto deve essere previsto un sistema di allarme che segnali il guasto. Detto sistema d''allarme deve essere sottoposto a controlli regolari". Verificare il rispetto della normativa in caso l''areazione dei locali dipenda da un impianto di ventilazione forzata. L condizione non adeguata prevede: presenza unicamente di ventilazione forzata (locali chiusi, privi di finestre) con assenza di un sistema d''allarme e/o contemporaneamente di un generatore di corrente che, in caso di guasti dell''impianto e/o mancanza della corrente elettrica, consenta il ripristino della ventilazione); La condizione adeguata prevede: ventilazione naturale oppure ventilazione unicamente forzata con sistema d''allarme ed impianto di riserva.','Se la salute e il benessere degli animali dipendono da un impianto di ventilazione artificiale, è previsto un adeguato impianto di riserva per garantire un ricambio d''aria sufficiente a salvaguardare la salute e il benessere degli animali - in caso di guasto all''impianto è previsto un sistema di allarme che segnali il guasto  ','S, NA, NB, NC, -','*) Segnalare il tipo di ventilazione naturale/forzata e in caso di ventilazione unicamente forzata se è presente l''impianto d''allarme e di riserva. Eseguire una verifica sul funzionamento dell''impianto e annotare le modalità e la frequenza degli interventi di manutenzione.',56);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,95,1196,'ATTREZZATURE AUTOMATICHE E MECCANICHE  ','Ispezione','"Ogni impianto automatico o meccanico (es. alimentazione, areazione e sistemi di allarme) indispensabile per la salute ed il benessere degli animali deve essere ispezionato almeno una volta al giorno. Gli eventuali difetti riscontrati devono essere immediatamente; se ciò non è possibile, occorre prendere le misure adeguate per salvaguardare la salute ed il benessere degli animali" La condizione non adeguata prevede: la mancanza di regolari ispezioni giornaliere e/o rilievo di una mancanza di interventi tempestivi - La condizione adeguata prevede: almeno una ispezione al giorno . La condizione ottimale prevede la presenza di procedure o manuali di buone pratiche comprensivi di istruzioni operative per gli operatori, che garantiscano il controllo giornaliero e la conservazione delle relative registrazioni','Gli impianti automatici o meccanici sono ispezionati almeno 1 volta al giorno.  ','S, NA, NB, NC, -, O','(*) Raccolta di informazioni tramite interviste agli operatori- consultazione di manuali di buone pratiche e relative registrazioni',57);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,96,1197,'ABM','LESIONI ALLA CODA - Morsicatura Coda  ','Considerare le seguenti lesioni: sanguinamento visibile sulla coda; presenza di gonfiore e infezione; mancanza di parte del tessuto e presenza di un''escara (le percentuali cambiano se gli animali sono caudectomizzati). - INSUFFICIENTE: più del 2% di animali con lesioni alla coda (suini non caudectomizzati: più del 7% con lesioni). - MIGLIORABILE: tra il 1% e il 2% di animali con lesioni alla coda (suini non caudectomizzati: tra il 4% e il 7% con lesioni). - OTTIMALE: meno del 1% di animali con lesioni alla coda (suini non caudectomizzati: meno del 4% con lesioni)','Animali con lesioni alla coda  ','M, I, -, O','(*) Registrare il numero di suini con lesioni, il numero di box e il numero di animali controllati',58);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,97,1198,'ABM','SEGNI DI MALATTIA - Lesioni corpo  ','Valutare la frequenza di animali con la presenza di lesioni al corpo e la loro gravità. - INSUFFICIENTE: più del 26% di animali con la presenza di lesioni lievi e/o più del 17% con lesioni gravi. - MIGLIORABILE: Tra il 12% e il 26% di animali con la presenza di lesioni lievi e/o tra l''8% e il 17% con lesioni gravi. - OTTIMALE: Meno del 12% di animali con la presenza di lesioni lievi e meno dell''8% di lesioni gravi','Animali con lesioni al corpo','M, I, -, O','(*) Registrare il numero di suini con lesioni lievi e gravi, il numero di box e il numero di animali controllati.',59);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,98,1199,'ABM','SEGNI DI MALATTIA - Zoppie  ','Valutare unicamente gli animali con evidente zoppia (zoppia 1=L''animale zoppica visibilmente, minimo carico sull''arto interessato, ha una camminata asimmetrica; zoppia 2= Animale che non appoggia il peso su un arto o non in grado di camminare). - INSUFFICIENTE: Più del 9% di animali con zoppia 1 e/o più del 3% con zoppia 2. - MIGLIORABILE: Tra il 4% e il 9% di animali con zoppia 1 e/o tra l''1% e il 3% con zoppia 2 . - OTTIMALE: Meno del 4% di animali zoppia 1 e meno dell''1% con zoppia 2','Animali con evidenti segni di zoppia.','M, I, -, O','(*) Registrare il numero di suini con zoppia di grado 1 e 2, il numero di box e il numero di animali controllati.',60);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,99,1200,'ABM','CONDIZIONE CUTANEA - Pulizia degli animali  ','Per ogni animale considerare l''osservazione di un solo fianco e valutare l''animale sporco nei due gradi di imbrattamento (Riproduttori: 1=la superfice del corpo imbrattata è compresa tra il 10% e il 30%; 2=la superficie del corpo imbrattata supera il 30%. Svezzamento - Ingrasso: 1=La superficie del corpo imbrattata è compresa tra 20% e 50%; 2= La superficie del corpo imbrattata supera il 50%) . - INSUFFICIENTE: Più del 46% di animali con imbrattamento 1 e/o più del 13% con imbrattamento 2 - MIGLIORABILE: Tra il 20% e il 46% di animali con la presenza di imbrattamento 1 e/o tra l''6% e il 13% con imbrattamento 2. - OTTIMALE: Meno dello 20% di animali con la presenza di imbrattamento 1 e meno del 6% di imbrattamento 2','Grado di pulizia del corpo degli animali','M, I, -, O','(*) Registrare il numero di suini con imbrattamento di grado 1 e 2, il numero di box e il numero di animali controllati.',61);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (43,100,1201,'ABM','COMPORTAMENTO - Comportamento esplorativo materiale manipolabile  ','Osservare gli animali presenti nel box e suddividerli in due categorie: (A) numero di animali che stanno esplorando il materiale di arricchimento; (B) numero dei suini che interagiscono con altri suini e con accessori del recinto. Applicare la seguente formula per ricavare l''Indice di utilizzo (I)= A/(A+B)*100. INSUFFICIENTE: (I)= 0% - 18%: minimo comportamento esplo MIGLIORABILE: (I)= 18,1% - 86,3%: intermedio comportamento esplorativo. - OTTIMALE: (I)= 86,4% - 100%: mas comportamento esplorativ','Comportamento esplorativo con i materiali manipolabili','M, I, -, O','(*) Registrare il numero di suini con comportamento di categoria A e B, il numero di box e il numero di animali controllati',62);
    
  
    

