CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_vitelli(
	_username_ws_bdn text,
	_password_ws_bdn text,
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE STRICT PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
BEGIN

  msg := concat('<?xml version="1.0" encoding="UTF-8"?>
<S:Envelope xmlns:S="http://schemas.xmlsoap.org/soap/envelope/">','
',
(select get_chiamata_ws_insert_ba_sa_header from get_chiamata_ws_insert_ba_sa_header(_username_ws_bdn, _password_ws_bdn)),'
',
'<S:Body>
      <ns2:insertControlliallevamentiBA xmlns:ns2="http://ws.izs.it">','
      ',(select get_chiamata_ws_insert_ba_v7_vitelli_controlli_allevamenti from get_chiamata_ws_insert_ba_v7_vitelli_controlli_allevamenti(_id)),'
      ',(select get_chiamata_ws_insert_ba_v7_vitelli_dettaglio_checklist from get_chiamata_ws_insert_ba_v7_vitelli_dettaglio_checklist(_id)),'
      </ns2:insertControlliallevamentiBA>
      </S:Body> 
      </S:Envelope>');
    
 RETURN msg;
 END;
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_vitelli(text, text, integer)
    OWNER TO postgres;


CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_vitelli_controlli_allevamenti(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
tag_preavviso text;
BEGIN

select 
concat('<controlliallevamenti>
	  <caId>0</caId>
             <dtControllo>', to_char(data_controllo, 'YYYY-MM-DD"T"00:00:00.000'),'</dtControllo>
            <aslCodice>',codice_asl,'</aslCodice>
            <aziendaCodice>',codice_azienda,'</aziendaCodice>
            <speCodice>',bdn.codice_specie_chk_bns,'</speCodice>
            <allevIdFiscale>',id_fiscale_allevamento,'</allevIdFiscale>
            <ocCodice>',bdn.occodice,'</ocCodice>
            <flagEsito>',vitelli.esito_controllo,'</flagEsito>
            <flagVitelli>',bdn.flag_vitelli,'</flagVitelli>
            <flagExtrapiano>',vitelli.extrapiano,'</flagExtrapiano>
            <detenIdFiscale>',id_fiscale_detentore,'</detenIdFiscale>
            <flagPreavviso>',bdn.flag_preavviso,'</flagPreavviso>
	    ',case when bdn.flag_preavviso <> 'N' then concat('<dataPreavviso>',to_char(bdn.data_preavviso, 'YYYY-MM-DD"T"00:00:00.000'),'</dataPreavviso>') else '' end,'            
            <primoControllore>',vitelli.nome_controllore,'</primoControllore>
            <tipoControllo>BA</tipoControllo>
            <flagCopiaCheckList>',bdn.flag_checklist,'</flagCopiaCheckList>
	    ',case when vitelli.esito_controllo = 'N' then concat(
	    '<sanzBloccoMov>',vitelli.sanz_blocco,'</sanzBloccoMov>
            <sanzAmministrativa>',vitelli.sanz_amministrativa,'</sanzAmministrativa>
            <sanzSequestro>',vitelli.sanz_sequestro,'</sanzSequestro>
            <sanzAbbattimentoCapi>',vitelli.sanz_abbattimento,'</sanzAbbattimentoCapi>
            <sanzInformativa>',vitelli.sanz_informativa,'</sanzInformativa>
            <sanzAltro>',vitelli.sanz_altro,'</sanzAltro>
            ',case when vitelli.sanz_altro <> '0' then concat('<sanzAltroDesc>',vitelli.sanz_altro_desc,'</sanzAltroDesc>')
	    else '' end,'
            <noteControllore>',vitelli.note_controllore,'</noteControllore>
            <noteDetentore>',vitelli.note_proprietario,'</noteDetentore>')
	    else '' end,'             
	    ',case when vitelli.esito_controllo <> 'A' then 
	    concat('<flagEvidenze>',vitelli.evidenze,'</flagEvidenze>')
	    else '' end,
	    case when vitelli.evidenze = 'S' then 
	    concat('<evidenzaIr>',vitelli.evidenze_ir,'</evidenzaIr>
            <evidenzaSv>',vitelli.evidenze_sv,'</evidenzaSv>
            <evidenzaSa>',vitelli.evidenze_tse,'</evidenzaSa>')
            else '' end,'
            <flagCondizionalita>',vitelli.appartenente_condizionalita,'</flagCondizionalita>
             ',case when vitelli.data_chiusura_relazione <> '' then concat('<dataChiusura>',concat(vitelli.data_chiusura_relazione,'T00:00.000'),'</dataChiusura>')
	    else '' end,'    
         </controlliallevamenti>') into msg
from bdn_cu_chiusi_benessere_animale bdn
left join bdn_checklist_v7_vitelli vitelli on vitelli.id_chk_bns_mod_ist = bdn.id_chk_bns_mod_ist
where bdn.id_chk_bns_mod_ist = _id;
     
    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_vitelli_controlli_allevamenti(integer)
    OWNER TO postgres;
    
 
CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_vitelli_dettaglio_checklist(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
BEGIN
select 
concat(
'<dettagliochecklist>
	<clbId>0</clbId>
	<flagVitelli>',bdn.flag_vitelli,'</flagVitelli>
	<critCodice>',vitelli.criteri_utilizzati,'</critCodice>
	',
	case when vitelli.criteri_utilizzati = 'CF13' then concat('<criterioControlloAltro>',vitelli.criteri_utilizzati_altro_descrizione,'</criterioControlloAltro>
	') else '' end,
	case when vitelli.esito_controllo = 'N' then concat('<flagIntenzionalita>',vitelli.intenzionalita,'</flagIntenzionalita>
	') else '' end,
	'<nomeRappresentante>',vitelli.nome_proprietario,'</nomeRappresentante>
	',
	'<tipoAllegato>',bdn.tipo_allegato,'</tipoAllegato>
	',
	case when vitelli.esito_controllo = 'N' then 
		concat('<prescrizioni>',vitelli.prescrizioni_descrizione,'</prescrizioni>
		', 
		case when vitelli.prescrizioni_descrizione <> '' then 
			concat('<dataScadPrescrizioni>',concat(vitelli.data_prescrizioni,'T00:00:00.000'),'</dataScadPrescrizioni>
			        <flagPrescrizioneEsitoBa>',vitelli.eseguite_prescrizioni,'</flagPrescrizioneEsitoBa>
			        ',	
				case when vitelli.data_verifica <> '' then 
					concat('<dataVerificaBa>',concat(vitelli.data_verifica,'T00:00:00.000'),'</dataVerificaBa>
						<secondoControllore>',vitelli.nome_controllore,'</secondoControllore>
						<nomeRappresentanteVer>',vitelli.nome_proprietario_prescrizioni_eseguite,'</nomeRappresentanteVer>
						'
					       ) else '' end
		               ) end
		      )else '' end,
	'<parametro>2024</parametro>
	<numClb>',vitelli.num_checklist,'</numClb>
	',
	case when vitelli.esito_controllo <> 'A' then concat('<veterinario>',vitelli.veterinario_ispettore,'</veterinario>
	') else '' end, 
	case when vitelli.esito_controllo <> 'A' then concat('<presenzaManuale>',vitelli.presenza_manuale,'</presenzaManuale>
	') else '' end,
	case when vitelli.esito_controllo <> 'A' then concat('<flagImpiantiElettrici>',vitelli.flag_impianti_elettrici,'</flagImpiantiElettrici>
	',
	case when vitelli.flag_impianti_elettrici = 'S' then concat('<flagDichiarazioneConformita>',vitelli.flag_dichiarazione_conformita,'</flagDichiarazioneConformita>
	') else '' end) else '' end,
	case when vitelli.esito_controllo <> 'A' then (select get_chiamata_ws_insert_ba_v7_requisiti from public.get_chiamata_ws_insert_ba_v7_requisiti(_id)) else '' 
	END,'
            </dettagliochecklist>
            ') into msg
from bdn_cu_chiusi_benessere_animale bdn
left join bdn_checklist_v7_vitelli vitelli on vitelli.id_chk_bns_mod_ist = bdn.id_chk_bns_mod_ist
where bdn.id_chk_bns_mod_ist = _id;
   
    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_vitelli_dettaglio_checklist(integer)
    OWNER TO postgres;
    

insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,159,1074,'PERSONALE','Numero di addetti che si occupano degli animali - ELEMENTO DI VERIFICA COMUNE AD ANIMALI ADULTI E VITELLI','146/2001 All. Personale Punto 1. "Gli animali devono essere accuditi da un numero sufficiente di addetti" Il numero di addetti deve essere sufficiente per garantire la salute e il benessere di tutti gli animali in allevamento: verificare il rapporto n. addetti - n. animali. I limiti di seguito riportati si riferiscono solo agli allevamenti di vitelli a carne bianca, dove la gestione si serve di una certa meccanizzazione. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. VITELLO A CARNE BIANCA Numero non sufficiente di addetti: un operatore per più di 800 animali totali. Livello della non conformita'': no; NO Numero accettabile di addetti: un operatore per 400-800 animali totali. Livello della non conformita'': SI Numero ottimale di addetti: un operatore per meno di 400 animali totali. Livello della non conformita'': Ottimale Per le altre tipologie produttive (allevamento per produzione latte, linea vacca-vitello, ingrasso), qualora vengano eseguite ispezioni disgiunte sui vitelli e sugli animali adulti, valgono i limiti già indicati per l''intera mandria, a seconda della tipologia di allevamento (vedasi elenco sottostante e Manuale Esplicativo allegato alla presente). I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. BOVINA DA LATTE Stabulazione libera Numero non sufficiente di addetti: un operatore per più di 300 animali totali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per più di 120 animali totali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 60 capi in lattazione) Livello della non conformita'': no; NO Numero accettabile di addetti: un operatore per 200-300 animali totali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per 80-120 animali totali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 50 capi in lattazione) Livello della non conformita'': SI Numero ottimale di addetti: un operatore per meno di 200 animali totali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per meno di 80 animali totali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 40 capi in lattazione) Livello della non conformita'': Ottimale BOVINA DA LATTE Stabulazione fissa Numero non sufficiente di addetti: un operatore per più di 120 animali totali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per più di 100 animali totali e mungitura con lattodotto dei relativi 50 capi in lattazione; oppure un operatore per più di 80 animali totali e mungitura a secchio dei relativi 40 capi in lattazione. Livello della non conformita'':no; NO Numero accettabile di addetti: un operatore per 80-120 animali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per circa 50-100 animali totali e mungitura con lattodotto dei relativi 25-50 capi in lattazione; oppure un operatore per circa 40-80 animali totali e mungitura a secchio dei relativi 20-40 capi in lattazione. Livello della non conformita'': SI Numero ottimale di addetti:un operatore per meno di 80 animali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per meno di 50 animali totali e mungitura con lattodotto dei relativi 25 capi in lattazione; oppure un operatore per meno di 40 animali totali e mungitura a secchio dei relativi 20 capi in lattazione. Livello della non conformita'': Ottimale BUFALE DA LATTE Numero non sufficiente di addetti: un operatore per più di 300 animali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per più di 120 animali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 60 capi in lattazione); Livello della non conformita'': no; NO Numero accettabile di addetti: un operatore per 200-300 animali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per 100-120 animali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 50 capi in lattazione); Livello della non conformita'': SI Numero ottimale di addetti: un operatore per meno di 200 animali (nel caso in cui la mungitura sia eseguita da altri); oppure un operatore per meno di 100 animali (nel caso in cui la stessa persona svolga anche la mungitura dei relativi circa 50 capi in lattazione) Livello della non conformita'': Ottimale LINEA VACCA-VITELLO Numero non sufficiente di addetti: STALLA: un operatore per più di 70 animali; PASCOLO: un operatore per più di 120 animali Livello della non conformita'': no; NO Numero sufficiente di addetti: STALLA: un operatore per 30-70 animali; PASCOLO: un operatore per 80-120 animali Livello della non conformita'': SI Numero ottimale di addetti: STALLA: un operatore per meno di 30 animali; PASCOLO: un operatore per meno di 80 animali Livello della non conformita'': Ottimale BOVINO DA CARNE Numero non sufficiente di addetti: un operatore per più di 800 animali Livello della non conformita'': no; NO Numero accettabile di addetti: un operatore per 400-800 animali Livello della non conformita'': SI Numero ottimale di addetti: un operatore per meno di 400 animali Livello della non conformita'': Ottimale','1) Gli animali sono accuditi da un numero sufficiente di addetti','S, NA, NB, NC, -, O','(*) Indicare il numero degli addetti',1);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,160,1075,'PERSONALE','Formazione degli addetti','146/2001 All. Personale Punto 1. "Gli addetti devono avere capacità e conoscenze adeguate" Valutare le competenze complessive del personale addetto agli animali, che possono essere sia di origine pratica (perché fornite dall''esperienza) sia di origine teorica (es. corso di formazione/titolo di studio). Si considera accettabile una lunga esperienza pratica nel settore oppure, in assenza di essa (es. giovani allevatori), il conseguimento di titoli di studio inerenti (diploma o laurea in agraria, veterinaria e lauree brevi simili) o la partecipazione a corsi di formazione specifici sul benessere animale. Per assegnare un giudizio ottimale è necessario possedere entrambi i requisiti: esperienza prolungata e titolo di studio/formazione specifica. I corsi di formazione o aggiornamento dovrebbero essere ripetuti con cadenza regolare durante il periodo lavorativo (almeno 1 corso ogni 3 anni). Se l''allevamento è seguito da più operatori, si considera sufficiente la partecipazione ai corsi di formazione anche di un solo addetto (sia esso il titolare o il dipendente assunto). I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Capacità e conoscenze non adeguate: esperienza indicativamente minore di 10 anni per i bovini o minore di 7 anni per i bufalini e nessun corso di formazione sul benessere animale livello della non conformita'': no; NO Capacità e conoscenze adeguate: esperienza indicativamente di almeno 10 anni per i bovini o 7 anni per i bufalini e nessun corso di formazione sul benessere animale/titolo di studio (o combinazione opposta) Livello della non conformita'': SI Capacità e conoscenze ottimali: esperienza indicativamente di almeno 10 anni per i bovini o 7 anni per i bufalini con titolo di studio o corso di formazione attinente, seguito negli ultimi 3 anni Livello della non conformita'': Ottimale','2) Il personale addetto agli animali dispone di capacità e conoscenze adeguate e ha ricevuto istruzioni pratiche sulle pertinenti diposizioni normative','S, NA, NB, NC, -, O','(*) Indicare esperienza nel settore, presenza di istruzioni pratiche scritte o correttamente comunicate ed estremi dei corsi di formazione frequentati, con periodicità e ricaduta a livello aziendale, ecc.',2);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,161,1076,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Numero di ispezioni','126/2011 All. I Punto 6 "Tutti i vitelli allevati in locali di stabulazione devono essere controllati dal proprietario e dalla persona responsabile almeno due volte al giorno e quelli allevati all''esterno almeno una volta al giorno" L''ispezione si intende relativa a tutti i gruppi di vitelli presenti in allevamento (0-6 mesi). Si considerano le visite giornaliere in stalla al di là di quelle eseguite per le attività di routine, qualora le stesse siano altamente specializzate o compiute con particolare impiego di attenzione da parte dell''operatore (es. distribuzione dell''alimento con strumenti meccanici). Qualora sussista segnalazione scritta delle osservazioni o registrazione computerizzata, oltre ad un numero conforme di ispezioni, può essere assegnato il giudizio ottimale. Si considera non adeguato un numero di visite insufficienti: - 1 ispezione/giorno per gli animali stabulati; - meno di 1 ispezione/giorno per gli animali allevati all''esterno Livello della non conformita'': no; NO Si considera adeguato un numero di visite sufficienti: - almeno 2 ispezioni/giorno per gli animali stabulati; - almeno 1 ispezione/giorno per gli animali allevati all''esterno Livello della non conformita'': SI Numero di visite superiori al requisito previsto: - più di 2 ispezioni/giorno per gli animali stabulati e segnalazione scritta delle osservazioni o registrazione computerizzata; - più di 1 ispezione/giorno per gli animali allevati all''esterno Livello della non conformita'': Ottimale','3) Gli animali nei locali di stabulazione sono ispezionati almeno due volte al giorno; mentre gli animali allevati all''esterno sono ispezionati almeno una volta al giorno','S, NA, NB, NC, -, O','(*) Indicare il numero di ispezioni e le modalità con le quali il dato viene raccolto e se esiste un programma per organizzare le ispezioni degli animali stabulati all''aperto',3);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,162,1077,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Illuminazione per l''ispezione','126/2011 All. I Punto 5 "Dovrà, inoltre, essere disponibile un''illuminazione adeguata (fissa o mobile) di intensità sufficiente per consentire di controllare i vitelli in qualsiasi momento" 146/2001 All. Controllo Punto 3 "Per consentire l''ispezione completa degli animali in qualsiasi momento, deve essere disponibile un''adeguata illuminazione fissa o mobile" L''intensità luminosa e la durata del periodo di luce dovrebbero permettere agli operatori di ispezionare adeguatamente tutti gli animali nell''arco della giornata. In aggiunta un''illuminazione fissa o mobile dovrebbe essere presente per consentire l''ispezione degli animali in qualsiasi momento, anche di notte, cosicché gli addetti possano intervenire in modo attento e senza rischi. Illuminazione per l''ispezione insufficiente: assenza di illuminazione artificiale per l''ispezione Livello della non conformita'': no; NO Illuminazione per l''ispezione sufficiente: presenza di illuminazione artificiale corretta e funzionante Livello della non conformita'': SI','4) E'' disponibile un''adeguata illuminazione che consente l''ispezione completa degli animali','S, NA, NB, NC, -','(*) Descrivere le modalita'' di illuminazione durante le ore diurne o notturne',4);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,163,1078,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Trattamento degli animali malati o feriti','126/2011 All. I Punto 6 "I vitelli che presentano sintomi di malattie o ferite debbono ricevere immediatamente le opportune cure e, qualora un vitello non reagisca al trattamento dell''allevatore, deve essere consultato al più presto un veterinario. Se necessario, i vitelli malati o feriti debbono essere isolati in locali appropriati con lettiera asciutta e confortevole." 146/2001 All. Controllo Punto 4 "Gli animali malati o feriti devono ricevere immediatamente un trattamento appropriato e, qualora un animale non reagisca alle cure in questione, deve essere consultato un medico veterinario. Ove necessario gli animali malati o feriti vengono isolati in appositi locali muniti, se del caso, di lettiere asciutte o confortevoli." Per avere evidenza che gli animali malati o feriti ricevano un trattamento adeguato (non necessariamente di tipo farmacologico), verificare tramite intervista presso gli addetti se sono state impartite precise istruzioni, anche di tipo orale. Punti da controllare: 1. la verifica dell''immediato trattamento: si assolve con due possibili condizioni, la prima è che l''animale malato o ferito possa essere spostato o sia già stato spostato nel reparto infermeria (area o box, eventualmente isolato) o, in alternativa, che l''animale malato (che non necessita dell''infermeria) sia stato identificato e abbia ricevuto un trattamento appropriato pur rimanendo nel gruppo con gli altri animali (valutare le dichiarazioni e/o la presenza di trattamenti farmacologici in atto sul registro dei trattamenti farmacologici o nelle registrazioni in Vetinfo farmacosorveglianza); 2. il consulto con un veterinario: verificare se è dichiarata o documentata la presenza del veterinario aziendale o di un veterinario libero professionista o privato che segue l''azienda. Per assegnare il giudizio conforme tutte le condizioni devono essere soddisfatte. Si considera non adeguato: evidenza di personale non istruito e/o presenza di animali che necessitano di un trattamento e non lo hanno ancora ricevuto (n.b. valutare con attenzione la possibile insorgenza iperacuta dei casi di malattia) e/o assenza di un veterinario che seguel''azienda Livello della non conformita'':no; NO Si considera adeguato: presenza di personale istruito ed evidenza che i due punti di verifica sopra enunciati siano soddisfatti Livello della non conformita'': SI Il requisito superiore (ottimale) prevede, oltre ai criteri per l''adeguatezza, la presenza delle relative procedure scritte di trattamento degli animali (es. piano di gestione delle patologie infettive, traumatiche, o specifici protocolli terapeutici) Livello della non conformita'': Ottimale','5) Gli animali malati o feriti ricevono immediatamente un trattamento appropriato','S, NA, NB, NC, -, O','(*)Indicare se sono presenti animali ammalati o feriti e le modalita'' di detenzione; se l''infermeria o altro settore sono, all''occorrenza, adattabili ad accogliere ed isolare animali deboli, malati, feriti o con problemi di tipo comportamentale',5);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,164,1079,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Abbattimento','D. Lgs. 146/2001, allegato, punto 4 "Gli animali malati o feriti devono ricevere immediatamente un trattamento appropriato [...]" Reg. 1099/2009 Art. 2 Definizioni a) «abbattimento»: qualsiasi processo applicato intenzionalmente che determini la morte dell''animale; d) «abbattimento d''emergenza»: l''abbattimento di animali feriti o affetti da una malattia procurante dolori o sofferenze acuti, qualora non esista altra possibilità pratica per alleviare tali dolori o sofferenze; e secondo il considerando n.12 è da intendersi "emergenza" in circostanze eccezionali, come nel caso di incidenti avvenuti in luoghi isolati dove gli animali non possono essere raggiunti da personale competente e con attrezzature idonee. Poiché in questi frangenti, il rispetto di misure ottimali in materia di benessere potrebbe protrarre le sofferenze degli animali, nel loro interesse è opportuno escludere l''abbattimento di emergenza dall''applicazione di determinate disposizioni del regolamento. L''abbattimento di animali sofferenti in allevamento, in circostanze routinarie, non è da intendersi abbattimento d''emergenza. j) «macellazione»: l''abbattimento di animali destinati all''alimentazione umana. Capo II Articolo 4 Metodi di stordimento 1. Gli animali sono abbattuti esclusivamente previo stordimento, conformemente ai metodi e alle relative prescrizioni di applicazione di cui all''allegato I. La perdita di coscienza e di sensibilità è mantenuta fino alla morte dell''animale. Capo II Articolo 7 Livello di competenze e certificato di idoneità 1. L''abbattimento e le operazioni correlate sono effettuati esclusivamente da persone che abbiano un adeguato livello di competenze per l''esecuzione di dette operazioni senza causare agli animali dolori, ansia o sofferenze evitabili. 2. Gli operatori provvedono affinché le seguenti operazioni di macellazione siano eseguite esclusivamente da persone che dispongano del relativo certificato di idoneità, come previsto dall''articolo 21, che ne attesti la capacità di eseguirle conformemente alle norme stabilite dal presente regolamento [...] Capo IV Articolo 19 Abbattimento di emergenza Nel caso di abbattimenti di emergenza, la persona che ha in custodia gli animali interessati adotta tutti i provvedimenti necessari per abbattere gli animali nel più breve tempo possibile. Reg. 1/2005 Allegato I, Capo I, Paragrafo 1 "Non può essere trasportato nessun animale che non sia idoneo al viaggio previsto, né le condizioni di trasporto possono essere tali da esporre l''animale a lesioni o a sofferenze inutili." Nel caso in cui la condizione di un animale richieda l''abbattimento in azienda, è necessario che vengano rispettate le metodologie contemplate dalla specifica normativa (Regolamento CE 1099/2009). Valutare che tali pratiche siano svolte da personale competente (es. eutanasia effettuata da un veterinario, oppure ricorso a personale provvisto del certificato d''idoneità alla macellazione, oppure proprietario/detentore adeguatamente formato) e che siano presenti delle istruzioni (es. numeri da contattare in caso d''emergenza, materiale didattico del corso frequentato dal proprietario/detentore, modalità di manutenzione degli strumenti, ecc.). È tuttavia possibile, in casi eccezionali, effettuare l''abbattimento di animali che presentino patologie gravi, causa di sofferenze acute, anche senza possesso di un certificato di idoneità né di un''adeguata competenza. In tali situazioni, infatti, l''intervento di personale competente dotato di attrezzature idonee, non essendo immediato, potrebbe determinare un prolungamento delle sofferenze dell''animale. Questi eventi non devono avere tuttavia caratteristiche di routinarietà. Si considera non adeguato: Mancato ricorso a personale competente e/o assenza di istruzioni in caso l''abbattimento sia eseguito da parte di personale aziendale e/o uso di strumentazione non adeguata o non sottoposta a regolare manutenzione Livello della non conformita'':no; NO Si considera adeguato: Abbattimento effettuato da un veterinario, oppure ricorso a personale provvisto del certificato d''idoneità alla macellazione, oppure, in caso di abbattimento da parte di personale aziendale, si valuta la presenza di istruzioni e di formazione degli addetti al riguardo (es. presenza di un corso di formazione frequentato da chi in allevamento è addetto all''abbattimento, con tematica specifica trattata) e presenza di strumentazione adeguata e sottoposta a regolare manutenzione Livello della non conformita'': SI Il requisito superiore previsto prevede, oltre ai criteri per l''adeguatezza, la presenza di procedure scritte per il corretto abbattimento, indicando responsabilità, strumenti e verifiche periodiche per agevolare una corretta gestione dell''emergenza Livello della non conformita'': Ottimale','6) Gli animali malati o feriti ricevono immediatamente un trattamento appropriato in caso di abbattimento o MSU','S, NA, NB, NC, -, O','(*)Fare riferimento a procedure o istruzioni sull''abbattimento e ai piani di emergenza e alla formazione degli operatori.Indicare se ci sono evidenze di animali ammalati o feritiche non hanno ricevuto in maniera tempestiva un trattamento adeguato in caso di MSU o abbattimento. Riportare e descrivere eventuale presenza di lesioni cutanee. Le valutazioni ABM in calce alla checklist (es. body condition score, zoppie, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformita'' o confermare la Conformità',6);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,165,1080,'TENUTA DEI REGISTRI(Registra zione dei dati)','Tenuta delle registrazioni dei dati','146/2001 All. Registrazione Punti 5-6. (i riferimenti al D.P.R. 8 febbraio 1954, n. 320, attualmente abrogato, si intendono fatti ai corrispondenti articoli dei decreti legislativi 5 agosto 2022, n.134 e n.136 ) "Le mortalità sono denunciate ai sensi del D.P.R. 8 febbraio 1954 n. 320. I registri sono conservati per un periodo di almeno tre anni e sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta." Dlgs 134 art. 9 comma 10 "L''operatore provvede alla registrazione in BDN di tutte le informazioni inerenti agli animali identificati singolarmente, alle partite, agli insiemi e ai gruppi di animali, nonché agli eventi che li riguardano, con i tempi di cui al presente articolo e con i modi previsti nel manuale operativo anche ai fini della generazione del registro della propria attività nella stessa BDN. Tale registro sostituisce qualsiasi altro registro aziendale cartaceo o su altro supporto concernente l''identificazione e registrazione degli animali." D. Lgs. 136/2022, art. 6 "Notifica e comunicazione delle malattie all''Autorità competente"; art. 11 comma 4 "gli operatori nel caso in cui non siano ancora disponibili nel sistema «ClassyFarm.it»,le apposite funzionalità informatiche per la tipologia di stabilimento di cui sono responsabili, adempiono agli obblighi del presente articolo tenendo in modalità elettronica o cartacea, per cinque anni, la documentazione riguardante almeno le informazioni di cui all''allegato 3 del presente decreto"; Allegato 3 "...individuazione e registrazione e segnalazione delle mortalità anomale" Verificare in BDN la presenza del registro di carico e scarico e la corretta registrazione delle informazioni, al fine di valutare se si siano verificate eventuali mortalità anomale. Nel caso si siano verificate mortalità anomale, verificare che siano state correttamente individuate, registrate e segnalate. Si considera non conforme: assenza in BDN del registro di carico e scarico o mancata o non corretta registrazione delle informazioni e/ o evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': NO Si considera conforme: presenza in BDN del registro di carico e scarico e corretta registrazione delle informazioni e mancata evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': SI','7) In BDN è presente il registro di carico e scarico, le informazioni vengono correttamente registrate e non sono presenti episodi di mortalità anomale non registrate','S, NC, -','(*) Annotare eventuali rilievi di mortalità anomale non registrate o di mancanza delle registrazioni delle informazioni in BDN',7);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,166,1081,'TENUTA DEI REGISTRI(Registra zione dei dati)','Tenuta del registro dei trattamenti farmacologici','146/2001 All. Registrazione Punti 5-6. "Il proprietario o il custode ovvero il detentore degli animali tiene un registro dei trattamenti terapeutici effettuati. La registrazione e le relative modalità di conservazione sono effettuate secondo quanto previsto dal decreto legislativo 27 gennaio 1992, n. 119, e successive modificazioni ed integrazioni e dal decreto legislativo 4 agosto 1999, n. 336. I registri sono conservati per un periodo di almeno tre anni e sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta" Sono presenti le registrazioni informatizzate dei trattamenti su Vetinfo Farmacosorveglianza, ai sensi della Legge 20 novembre 2017, n. 167 e s.m.i. Assenza delle registrazioni in Vetinfo farmacosorveglianza o non corretta registrazione dei trattamenti. Livello della non conformita'': NO Corretta registrazione dei trattamenti in Vetinfo farmacosorveglianza. Livello della non conformita'': SI','8) Le registrazioni in Vetinfo farmacosorveglianza vengono effettuate nei tempi stabiliti e i trattamenti sono regolarmente prescritti da un medico veterinario.','S, NC, -','(*) Segnalare i trattamenti controllati a campione',8);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,167,1082,'LIBERTA'' DI MOVIMENTO','Locali di stabulazione e attacchi','126/2011 All. I Punto 7; "I locali di stabulazione devono essere costruiti in modo da consentire ad ogni vitello di coricarsi, giacere, alzarsi ed accudire a se stesso senza difficoltà" 126/2011 All. I Punto 8 "I vitelli non debbono essere legati, ad eccezione di quelli stabulati in gruppo che possono essere legati per un periodo massimo di un''ora al momento della somministrazione di latte e succedanei del latte. Se si utilizzano attacchi, questi non devono provocare lesioni al vitello e debbono essere regolarmente esaminati ed eventualmente aggiustati in modo da assicurare una posizione confortevole agli animali. Ogni attacco deve essere concepito in modo tale da evitare il rischio di strangolamento o ferimento e da consentire ai vitelli di muoversi secondo quanto disposto al punto 7." 146/2001 All. Libertà di movimento punto 7; "La libertà di movimento propria dell''animale, in funzione della sua specie e secondo l''esperienza acquisita e le conoscenze scientifiche, non deve essere limitata in modo tale da causargli inutili sofferenze o lesioni. [...]" Punti da verificare: 1) L''area di stabulazione degli animali deve essere correttamente dimensionata al fine di garantire il massimo comfort ed igiene, nonché il completo utilizzo da parte di tutti gli animali, senza causar loro inutili sofferenze o lesioni. Lo "spazio libero disponibile" è la superficie accessibile e fruibile a disposizione di ciascun vitello sufficiente a consentirgli di voltarsi e di sdraiarsi senza alcun impedimento; da tale definizione si evince che sono da escludere dal computo della superficie disponibile tutti gli ostacoli che rendono tale spazio non fruibile. 2) Non devono essere presenti vitelli legati, ad eccezione di quelli stabulati in gruppo che possono essere legati per un periodo massimo di un''ora al momento della somministrazione di latte e succedanei del latte. L''osservazione deve essere eseguita su tutti gli animali e basta riscontrare la presenza di un singolo soggetto in condizione non conforme al requisito di legge per assegnare il giudizio non conforme. Qualora fossero utilizzati attacchi nelle modalità consentite dalla legge, è necessario osservarne tipologia e conformazione, in quanto non devono essere nocivi o causare strangolamenti, non devono causare lesioni agli animali e devono consentirgli di alzarsi, coricarsi e alimentarsi senza difficoltà. Si considera non adeguato: Presenza di uno o più animali che non possono coricarsi, giacere, alzarsi ed accudire a sé stessi senza difficoltà / Presenza di uno o più animali costantemente legati o liberi ma catturati durante le fasi di alimentazione per più di 1h / Utilizzo di attacchi nocivi o non idonei Livello della non conformita'': NO Si considera adeguato: Tutti gli animali possono coricarsi, giacere, alzarsi ed accudire a sé stessi senza difficoltà / Animali liberi ed eventualmente catturati (con attacchi idonei) solo durante le fasi di alimentazione per meno di 1h Livello della non conformita'': SI Il requisito superiore (ottimale) prevede, oltre ai criteri per l''adeguatezza, animali liberi e non catturati durante le fasi di alimentazione Livello della non conformita'': Ottimale','9) Lo spazio a disposizione di ogni animale è sufficiente per consentirgli un''adeguata libertà di movimenti ed è tale da non causargli inutili sofferenze o lesioni. I locali di stabulazione sono costruiti in modo da permettere agli animali di coricarsi, giacere in decubito, alzarsi ed accudire a se stessi senza difficoltà. I vitelli non vengono legati ad eccezione di quelli allevati in gruppo al momento della somministrazione del latte o suoi succedanei per un periodo massimo di 1 ora. Gli attacchi eventualmente utilizzati non provocano lesioni e consentono ai vitelli di assumere una posizione confortevole durante l''assunzione dell''alimento, di giacere ed alzarsi, non provocano strangolamenti o ferite, sono regolarmente esaminati, aggiustati o sostituiti se danneggiati. Per assegnare giudizio conforme tutti i requisiti devono essere soddisfatti','S, NC, -, O','(*) Descrivere eventuali condizioni che limitano la libertà di movimento degli animali e, se del caso, per quanto tempo e con che dispositivo i vitelli vengono legati. In caso di vitelli mantenuti permanentemente legati, riportare - se possibile - le marche auricolari dei soggetti coinvoltie i box/ricoveri dove sono alloggiati',9);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,168,1083,'SPAZIO DISPONIBILE','Spazio disponibile nel recinto individuale','126/2011 Art. 3 comma 1 lettera a) e comma 2 "Requisiti minimi di allevamento di vitelli: 1. Le aziende devono rispettare le seguenti prescrizioni: a) nessun vitello di età superiore alle otto settimane deve essere rinchiuso in un recinto individuale, a meno che il medico veterinario abbia certificato che il suo stato di salute o il suo comportamento richiedano l''isolamento dal gruppo, al fine del trattamento diagnostico e terapeutico. La larghezza del recinto individuale deve essere almeno pari all''altezza al garrese del vitello, misurata quando l''animale è in posizione eretta, e la lunghezza deve essere almeno pari alla lunghezza del vitello, misurata dalla punta del naso all''estremità caudale della tuberosità ischiatica e moltiplicata per 1,1. [omissis..] 2. Le disposizioni di cui al comma 1 non si applicano alle aziende con meno di sei vitelli e ai vitelli mantenuti presso la madre ai fini dell''allattamento." Una volta accertato che nessun vitello è stabulato permanentemente legato, è necessario procedere a verificare che lo spazio disponibile per ciascun animale sia conforme ai requisiti di legge, misurando i recinti individuali che ospitano i vitelli. I vitelli bovini possono essere stabulati in recinti individuali fino a 8 settimane d''età e i vitelli bufalini fino a 90 giorni d''età, a meno che debbano essere mantenuti isolati dal gruppo, esclusivamente per il periodo necessario, ai fini terapeutici o diagnostici giustificati dalle loro particolari condizioni sanitarie o comportamentali, che devono essere certificate da un medico veterinario. In ogni caso, tali recinti devono rispettare quanto previsto dal D. Lgs. 126/2011 Art. 3 comma 1 lettera a). Si ricorda che se si riscontrano vitelli bovini di età inferiore alle 8 settimane allevati in coppia nei recinti individuali, si deve fare riferimento a quanto specificato dalla nota del Ministero della Salute n. 0014777 del 19/06/2017 denominata "Chiarimenti per stabulazione di vitelli in coppia di età inferiore alle otto settimane". La nota è riportata in maniera dettagliata nel manuale esplicativo. Nel caso,invece, gli animali siano allevati in gruppo (in recinti collettivi) anche prima delle 8 settimane d''età nel caso dei bovini, o prima dei 90 giorni d''età nel caso dei bufalini, occorre verificare le dimensioni dello spazio disponibile come indicato dal D. Lgs. 126/2011 Art. 3 comma 1 lettera b), (vedasi requisito n.13). Per semplificare la verifica dell''idoneità del recinto individuale, a titolo indicativo, si riportano come misure di riferimento una lunghezza di 130 cm e una larghezza di 80 cm, considerando come valori medi di riferimento un''altezza al garrese di 0,80 m ed una lunghezza dell''animale di 1,18 - 1,20 m, misurata a partire dalla punta del naso all''estremità caudale della tuberosità ischiatica. Nel caso in cui le dimensioni del recinto siano inferiori a quelle indicate - prima di assegnare la risposta insufficiente - è necessario verificare che esse siano conformi o meno a quanto previsto dalla normativa, in relazione alle dimensioni dell''animale realmente ospitato. Infatti, alcune gabbiette con misure inferiori a quelle proposte potrebbero essere comunque idonee, qualora ospitino animali molto giovani o di dimensioni minori rispetto alla media, purché la larghezza sia superiore all''altezza al garrese del vitello e la lunghezza sia superiore del 10% della distanza fra la punta del naso e l''estremità caudale della tuberosità ischiatica dell''animale. Da ricordare che i limiti di superficie del box individuale non si applicano agli allevamenti con meno di 6 vitelli (ovvero 5 animali tra 0-6 mesi d''età) presenti al momento della visita, e ai vitelli mantenuti presso la madre ai fini dell''allattamento. Per assegnare il giudizio ottimale, lo spazio disponibile per ciascun vitello deve essere superiore di almeno il 10% rispetto alle dimensioni minime previste. Basta riscontrare la presenza di un singolo soggetto in condizione tale da non soddisfare il requisito di legge per assegnare il giudizio non conforme. Si considera non adeguato: Spazio disponibile per ciascun vitello presente inferiore ai limiti di legge Livello della non conformita'': no; NO Si considera adeguato: Spazio disponibile per ciascun vitello presente conforme ai limiti di legge Livello della non conformita'': SI Si considera ottimale: Spazio disponibile per ciascun vitello presente superiore ai limiti di legge (almeno più del 10%) Livello della non conformita'': Ottimale','10) I recinti individuali hanno dimensioni adeguate e conformi alle disposizioni vigenti','S, NA, NB, NC, -, O','(*) Descrivere le dimensioni dei recinti individuali in relazione alle dimensioni dei soggetti ospitati. In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate, riportando - se possibile - le marche auricolari dei soggetti coinvolti e i box/ricoveri dove sono alloggiati',10);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,169,1084,'SPAZIO DISPONIBILE','Possibilità di contatto (visivo e tattile)','126/2011 Art. 3 comma 1 lettera a) e comma 2 "Requisiti minimi di allevamento di vitelli: 1.[...] Ogni recinto individuale per vitelli, salvo quelli destinati ad isolare gli animali malati, non deve avere muri compatti ma pareti divisorie traforate che consentano un contatto diretto, visivo e tattile tra i vitelli. [omissis] 2. Le disposizioni di cui al comma 1 non si applicano alle aziende con meno di sei vitelli e ai vitelli mantenuti presso la madre ai fini dell''allattamento." I muri che dividono i recinti individuali di stabulazione dei vitelli non devono essere compatti ma avere divisorie traforate; per questo, il contatto che potrebbe verificarsi tramite il fronte mangiatoia non soddisfa il requisito normativo. I vitelli devono avere contatto diretto, visivo e tattile contemporaneamente, ad eccezione dei soggetti isolati per motivi sanitari o comportamentali opportunamente documentati da certificazioni rilasciate dal medico veterinario (es. diagnosi di malattia, trattamenti farmacologici, ecc. indicando - se possibile - le marche auricolari e i box/ricoveri dei soggetti coinvolti). L''osservazione deve essere eseguita su tutti gli animali. Fanno eccezione gli allevamenti con meno di 6 vitelli (ovvero 5 animali tra 0-6 mesi di vita) presenti al momento della visita e i vitelli mantenuti presso la madre ai fini dell''allattamento. Basta riscontrare la presenza di un singolo soggetto in condizione tale da non soddisfare il requisito di legge per assegnare il giudizio non conforme. Si considera non adeguato: Impossibilità di contatto (visivo e tattile) tra gli animali Livello della non conformita'': no; NO Si considera adeguato: Possibilità di contatto (visivo e tattile) tra gli animali / Impossibilità di contatto solo per i soggetti isolati in recinti individuali con idonea certificazione veterinaria Livello della non conformita'': SI','11) Le pareti divisorie dei recinti individuali dei vitelli non sono costituite da muri compatti, ma sono traforate, salvo nel caso in cui sia necessario isolare i vitelli','S, NA, NB, NC, -','(*) Descrivere come i vitelli possono entrare in contatto tra loro e, se ci sono animali isolati, per quale motivazione. In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate, riportando - se possibile - le marche auricolari dei soggetti coinvolti e i box/ricoveri dove sono alloggiati.',11);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,170,1085,'SPAZIO DISPONIBILE','Assenza di vitelli bovini oltre le 8 settimane d''età (o vitelli bufalini oltre i 90 giorni d''età) stabulati in recinti individuali','126/2011 Art. 3 comma 1 lettera a) e comma 2 "Requisiti minimi di allevamento di vitelli: 1. Le aziende devono rispettare le seguenti prescrizioni: a) nessun vitello di età superiore alle otto settimane deve essere rinchiuso in un recinto individuale, a meno che il medico veterinario abbia certificato che il suo stato di salute o il suo comportamento richiedano l''isolamento dal gruppo, al fine del trattamento diagnostico e terapeutico.[omissis..] 2. Le disposizioni di cui al comma 1 non si applicano alle aziende con meno di sei vitelli e ai vitelli mantenuti presso la madre ai fini dell''allattamento." Dare la possibilità ai vitelli, anche molto giovani, di interagire tra di loro è assai importante per far sì che sviluppino migliori comportamenti sociali, minor stress da svezzamento, facilità di apprendimento e tassi di crescita maggiori. Per questo è obbligatorio che i vitelli bovini con più di 8 settimane d''età, e i vitelli bufalini con più di 90 giorni d''età, siano allevati in gruppo. Esistono due sole eccezioni a questo requisito: 1) presenza di vitelli bovini con più di 8 settimane d''età (e vitelli bufalini con più di 90 giorni d''età) mantenuti isolati dal gruppo, esclusivamente per il periodo necessario, ai fini terapeutici o diagnostici giustificati dalle loro particolari condizioni sanitarie o comportamentali, che devono essere certificate da un medico veterinario. Tali recinti individuali hanno comunque dimensioni adeguate e conformi alle disposizioni vigenti (126/2011 Art. 3 comma 1 lettera a); vedasi item n. 9 e 10); 2) allevamenti nei quali, al momento dell''ispezione, siano presenti meno di 6 vitelli (ovvero 5 animali tra 0-6 mesi d''età) o vitelli stabulati con la madre ai fini dell''allattamento. Ammesse le eccezioni citate, basta riscontrare la presenza di un singolo soggetto in condizione tale da non soddisfare il requisito di legge per assegnare il giudizio non conforme. Si considera non adeguato: presenza di uno o più vitelli bovini oltre le 8 settimane d''età (e vitelli bufalini oltre i 90 giorni d''età) isolati in recinti individuali senza idonea certificazione veterinaria Livello della non conformita'': no; NO Si considera adeguato: presenza di uno o più vitelli bovini oltre le 8 settimane d''età (e vitelli bufalini oltre i 90 giorni d''età) isolati in recinti individuali con idonea certificazione veterinaria / tutti i vitelli bovini oltre le 8 settimane d''età (e tutti i vitelli bufalini oltre i 90 giorni d''età) sono allevati in recinti collettivi Livello della non conformita'':SI','12) I vitelli bovini di età superiore alle 8 settimane e i vitelli bufalini di età superiore ai 90 giorni non sono allevati in recinti individuali. Sono presenti vitelli bovini di età superiore alle 8 settimane e vitelli bufalini di età superiore ai 90 giorni rinchiusi in recinti individuali per motivi sanitari o comportamentali certificati da un medico veterinario esclusivamente per il periodo necessario','S, NA, NB, NC, -','(*) Riportare gli estremi di eventuale certificazione veterinaria. In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate, riportando - se possibile - le marche auricolari dei soggetti coinvolti e i box/ricoveri dove sono alloggiati.',12);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,171,1086,'SPAZIO DISPONIBILE','Spazio disponibile per vitelli allevati in gruppo (box collettivo)','126/2011 Art. 3 comma 1 lettera b) e comma 2 "Requisiti minimi di allevamento di vitelli: 1. Le aziende devono rispettare le seguenti prescrizioni: [omissis...] b) per i vitelli allevati in gruppo, lo spazio libero disponibile per ciascun vitello deve essere pari ad almeno 1,5 metri quadrati per ogni vitello di peso vivo inferiore a 150 chilogrammi, ad almeno 1,7 metri quadrati per ogni vitello di peso vivo pari o superiore a 150 chilogrammi, ma inferiore a 220 chilogrammi e ad almeno 1,8 metri quadrati per ogni vitello di peso vivo pari o superiore a 220 chilogrammi. 2. Le disposizioni di cui al comma 1 non si applicano alle aziende con meno di sei vitelli e ai vitelli mantenuti presso la madre ai fini dell''allattamento." Per i vitelli allevati in gruppo (fino a 6 mesi d''età), lo spazio disponibile per ciascun soggetto deve essere pari ad almeno: - 1,5 m2/ capo per vitelli di p.v. < 150 kg; - 1,7 m2/ capo per vitelli di p.v. tra 150 e 220 kg; - 1,8 m2/ capo per vitelli di p.v. > 220 kg. La superficie è quella totale del box, esclusi eventuali ostacoli che rendano lo spazio non fruibile agli animali. L''osservazione deve essere eseguita su tutti i gruppi di animali. La presente disposizione non si applica agli allevamenti con meno di 6 vitelli (ovvero 5 animali tra 0-6 mesi di vita) presenti al momento della visita e ai vitelli mantenuti presso la madre ai fini dell''allattamento. Basta riscontrare la presenza di un singolo soggetto in condizione tale da non soddisfare il requisito di legge per assegnare il giudizio non conforme. Si considera non adeguato: Spazio disponibile per ciascun vitello presente inferiore ai limiti di legge Livello della non conformita'': no; NO Si considera adeguato: Spazio disponibile per ciascun vitello presente conforme ai limiti di legge Livello della non conformita'': SI Si considera ottimale: Spazio disponibile per ciascun vitello presente superiore ai limiti di legge (almeno più del 10%) Livello della non conformita'': Ottimale','13) Lo spazio libero disponibile per ciascun vitello è adeguato e conforme alle disposizioni vigenti.','S, NA, NB, NC, -, O','(*)In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate, riportando - se possibile - le marche auricolari dei soggetti coinvolti e i box/ricoveri dove sono alloggiati.',13);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,172,1087,'EDIFICI E LOCALI DI STABULAZIONE','Assenza di fabbricati e locali di stabulazione nocivi','126/2011 All. 1 Punto 1 "I materiali utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature coni quali i vitelli possono venire a contatto non devono essere nocivi per i vitelli e devono poter essere accuratamente puliti e disinfettati." 146/2001 All. Fabbricati e locali di stabulazione punto 8 e 9. "8. I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati. 9. I locali di stabulazione [omissis] degli animali devono essere costruiti e mantenuti in modo che non vi siano spigoli taglienti o sporgenze tali da provocare lesioni agli animali." Tutti i materiali e le attrezzature utilizzati nei locali di stabulazione, nonché le superfici stesse,con cui i vitelli possono venire a contatto, non devono essere nocivi per gli animali e non devono avere spigoli taglienti o sporgenze in grado di provocare lesioni o traumi. Si considera non adeguato: Presenza di locali, ambienti o attrezzature nocivi per gli animali: es. lettiera dannosa, percorsi, accessi, delimitazioni, ecc. che causano lesioni agli animali Livello della non conformita'': no; NO Si considera adeguato: Presenza di locali, ambienti e attrezzature idonei e non nocivi per gli animali Livello della non conformita'': SI','14) I materiali di costruzione, i recinti e le attrezzature con i quali gli animali possono venire a contatto non sono nocivi per gli animali stessi, non vi sono spigoli taglienti o sporgenze, tutte le superfici sono facilmente lavabili e disinfettabili','S, NA, NB, NC, -','(*) In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. superficie di decubito o lettiera dannosa e gravemente lesiva per gli animali; presenza di spigoli, rastrelliere, passaggi, ecc. taglienti e/o lesivi, ecc., evidenza di lesioni cutanee, ecc.). Le valutazioni ABM in calce alla checklist (es. pulizia, condizione corporea, zoppie, ecc.) possono supportare e dare un''evidenza della presenza di un''eventuale non conformità o confermare la conformità',14);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,173,1088,'EDIFICI E LOCALI DI STABULAZIONE','Igiene, pulizia e gestione degli ambienti di stabulazione e della lettiera','126/2011 All. 1 Punto 9 "La stalla, i recinti, le attrezzature e gli utensili devono essere puliti e disinfettati regolarmente in modo da prevenire infezioni incrociate o lo sviluppo di organismi infettivi. Gli escrementi, l''urina e i foraggi che non sono stati mangiati o che sono caduti sul pavimento devono essere rimossi con la dovuta regolarità per ridurre al minimo gli odori e la presenza di mosche o roditori." 146/2001 All. Fabbricati e locali di stabulazione punto 8. "I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati." Considerare l''igiene, la pulizia e la gestione degli ambienti di stabulazione, delle pavimentazioni e della lettiera e la frequenza degli interventi di ripristino e ricambio del materiale. Eccezion fatta per i vitelli con meno di 2 settimane d''età, dove è obbligatorio usare la lettiera, nel caso di grigliato pulito, usato come area di decubito, la valutazione è al massimo adeguata. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: ambienti di stabulazione e lettiera sporchi, non gestiti e/o dannosi per gli animali no; NO Si considera adeguato: ambienti di stabulazione e/o lettiera discretamente puliti e gestiti sufficientemente e/o grigliato pulito SI Si considera ottimale: ambienti di stabulazione e lettiera puliti, asciutti e gestiti in modo ottimale con ricambio frequente del materiale Ottimale','15) I fabbricati, i recinti, le attrezzature e gli utensili sono puliti e disinfettati regolarmente. Gli escrementi, l''urina e i foraggi non mangiati o caduti sono rimossi con regolarità La zona in cui i vitelli si coricano è confortevole, pulita e ben drenata.','S, NA, NB, NC, -, O','(*) In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. condizioni igieniche dei locali e dei materiali di stabulazione, frequenza di ripristino e ricambio, condizioni igieniche degli animali ed eventuale presenza di lesioni cutanee). Le valutazioni ABM in calce alla checklist (pulizia animali, zoppie, ecc.) possono supportare e dare un''evidenza della presenza di un''eventuale non conformità o confermare la conformità',15);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,174,1089,'EDIFICI E LOCALI DI STABULAZIONE','Igiene, pulizia e gestione delle attrezzature per l''alimentazione (alimenti e acqua)','126/2011 All. 1 Punto 9 "La stalla, i recinti, le attrezzature e gli utensili devono essere puliti e disinfetti regolarmente in modo da prevenire infezioni incrociate o lo sviluppo di organismi infettivi. Gli escrementi, l''urina e i foraggi che non sono stati mangiati o che sono caduti sul pavimento devono essere rimossi con la dovuta regolarità per ridurre al minimo gli odori e la presenza di mosche o roditori." 126/2011 All. 1 Punto 14 "Le attrezzature per la somministrazione di mangimi e di acqua devono essere concepite, costruite, installate e mantenute in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua destinati ai vitelli." I secchi, i poppatoi, le mangiatoie e gli abbeveratoi devono essere sottoposti a regolare pulizia e periodica disinfezione; ogni alimento avanzato deve essere rimosso. Se utilizzate, le attrezzature per l''alimentazione automatica (es. lupa/allattatrice automatica) devono essere pulite regolarmente e frequentemente, se del caso, smontando le parti in cui si depositano i residui di alimento. Il grado di pulizia della mangiatoia può essere giudicato tramite osservazione di eventuali incrostazioni sulla parte alta della stessa e sul corrente d''acciaio più vicino al trogolo. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: presenza di attrezzature, mangiatoie e abbeveratoi sporchi con presenza di evidenti ed abbondanti incrostazioni, o presenza di materiali difficilmente igienizzabili Livello della non conformita'': no; NO Si considera adeguato: presenza di attrezzature, mangiatoie e abbeveratoi adeguatamente puliti ed evidenza di minime incrostazioni Livello della non conformita'': SI Si considera ottimale: presenza di attrezzature, mangiatoie e abbeveratoi puliti, senza residui o incrostazioni, lavati dopo ogni pasto o prima del successivo Livello della non conformita'': Ottimale','16) I secchi, i poppatoi, le mangiatoie, gli abbeveratoi sono sottoposti a regolare pulizia e periodica disinfezione. Ogni alimento avanzato viene rimosso con regolarità. Le attrezzature per l''alimentazione automatica sono pulite regolarmente e frequentemente, smontando le parti in cui si depositano residui di alimento','S, NA, NB, NC, -, O','(*) Indicare le condizioni igieniche, di pulizia e gestione delle attrezzature per l''alimentazione e l''abbeverata. In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate',16);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,175,1090,'EDIFICI E LOCALI DI STABULAZIONE','Igiene, pulizia e gestione dei locali e delle attrezzature per la preparazione del latte','126/2011 All. 1 Punto 9 "La stalla, i recinti, le attrezzature e gli utensili devono essere puliti e disinfetti regolarmente in modo da prevenire infezioni incrociate o lo sviluppo di organismi infettivi. Gli escrementi, l''urina e i foraggi che non sono stati mangiati o che sono caduti sul pavimento devono essere rimossi con la dovuta regolarità per ridurre al minimo gli odori e la presenza di mosche o roditori." 126/2011 All. 1 Punto 14 "Le attrezzature per la somministrazione di mangimi e di acqua devono essere concepite, costruite, installate e mantenute in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua destinati ai vitelli." I locali e le attrezzature per la preparazione e somministrazione del pasto a base di latte devono essere costruiti, mantenuti e puliti regolarmente, in modo da ridurre al minimo le possibilità di contaminazione e prevenire infezioni incrociate o lo sviluppo di organismi infettivi. Per facilitare tale operazione, le superfici delle attrezzature e le pareti dei locali devono essere facilmente igienizzabili (es. superfici lisce, pareti rivestite da piastrelle o da vernice lavabile, senza rugosità o punti in cui si possano formare incrostazioni). È condizione ottimale la presenza di istruzioni/procedure scritte per il controllo, la pulizia e la gestione dei locali e delle attrezzature adibite alla preparazione del latte. Si considera non adeguato: presenza di locali ed attrezzature sporchi, con presenza di evidenti ed abbondanti incrostazioni, o presenza di materiali difficilmente igienizzabili Livello della non conformita'': no; NO Si considera adeguato: presenza di locali ed attrezzature adeguatamente pulitecon evidenza di minime incrostazioni, e facilmente igienizzabili Livello della non conformita'': SI Si considera ottimale: presenza di locali ed attrezzature pulite, senza residui o incrostazioni, facilmente igienizzabili ed evidenza di istruzioni/procedure scritte Livello della non conformita'': Ottimale','17) I locali adibiti alla preparazione/conservazione degli alimenti sono adeguatamente separati, regolarmente puliti e disinfettati e soddisfano i requisiti minimi dal punto di vista igienico- sanitario','S, NA, NB, NC, -, O','(*)Indicare le condizioni igieniche, di pulizia e gestione delle attrezzature per la preparazione e somministrazione del pasto di latte',17);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,176,1091,'EDIFICI E LOCALI DI STABULAZIONE','Pavimentazione e superficie di decubito','126/2011 All. 1 Punto 10 "I pavimenti devono essere non sdrucciolevoli e senza asperità per evitare lesioni ai vitelli e devono essere costruiti in modo da non causare lesioni o sofferenza ai vitelli in piedi o coricati. Essi devono essere adeguati alle dimensioni ed al peso dei vitelli e costituire una superficie rigida, piana e stabile. La zona in cui si coricano i vitelli deve essere confortevole, pulita, adeguatamente prosciugata e non dannosa per i vitelli." 146/2001 All. Fabbricati e locali di stabulazione punto 8 e 9. "8. I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati. 9. I locali di stabulazione [omissis] degli animali devono essere costruiti e mantenuti in modo che non vi siano spigoli taglienti o sporgenze tali da provocare lesioni agli animali." MINSAN 2006, Nota esplicativa Prot. N. 27232 del 25/07/2006, pag.8 "Le norme vigenti non forniscono indicazioni circa la dimensione dei travetti e delle fessure del grigliato, ma al fine del benessere degli animali si dovrà garantire che le distanze tra i travetti o i diametri dei fori siano sempre inferiori al diametro del piede dei vitelli stabulati, inoltre i bordi dei travetti non devono essere taglienti per evitare lesioni agli arti." - "La lettiera in paglia fornisce ai vitelli maggiore comfort e una buona protezione contro il freddo invernale, inoltre, assorbendo il contenuto liquido delle deiezioni, mantiene il recinto asciutto e poco sdrucciolevole." Valutare le superfici di camminamento (corridoi, passaggi, accessi, corsia di alimentazione, aree di esercizio, ecc.) e di decubito che devono essere sufficientemente ruvide, sicure e libere da ostacoli consentendo agli animali di alzarsi, coricarsi e muoversi, anche rapidamente, senza scivolare. Una pavimentazione fessurata idonea dovrebbe avere le fessure (ovvero le distanze tra i travetti) di dimensioni indicativamente non superiori a 3 cm, o comunque dovrebbe essere in grado di sostenere i piedi dei vitelli e non causare lesioni all''unghia. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: Presenza di pavimento fessurato non idoneo oppure pavimento pieno senza materiale da lettiera, liscio e scivoloso o che impedisce i movimenti Livello della non conformita'': no; NO Si considera adeguato: Presenza di pavimento - pieno o fessurato - idoneo e rugoso (es. presenza di idonea rigatura, o rivestimento in gomma o rivestimento in legno, o quantità lieve di lettiera, ecc.) per almeno la maggior parte delle superfici su cui camminano e decubitano gli animali Livello della non conformita'': SI Si considera ottimale: Presenza di pavimento pieno idoneo e rugoso su tutte le superfici su cui camminano e decubitano gli animali e presenza di lettiera permanente con materiale organico adeguato (perché abbondante, non abrasivo, ben conservato, assorbente) Livello della non conformita'': Ottimale','18) I pavimenti non sono sdrucciolevoli e non hanno asperità tali da provocare lesioni, sono costruiti e mantenuti in maniera tale da non arrecare sofferenza o lesioni agli arti e ai piedi e sono adeguati alle dimensioni e al peso dei vitelli','S, NA, NB, NC, -, O','(*) Descrivere le condizioni della pavimentazione e della zona di decubito e riportare eventuale presenza di eventuali lesioni cutanee. Le valutazioni ABM in calce alla checklist (es. zoppie) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',18);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,177,1092,'EDIFICI E LOCALI DI STABULAZIONE','Lettiera dei vitelli con meno di 2 settimane d''età','126/2011 All. 1 Punto 10 "La zona in cui si coricano i vitelli deve essere confortevole, pulita, adeguatamente prosciugata e non dannosa per i vitelli. Per tutti i vitelli di età inferiore a due settimane deve essere prevista una lettiera adeguata." MINSAN 2006, Nota esplicativa Prot. N. 27232 del 25/07/2006, pag. 8 "La lettiera in paglia fornisce ai vitelli maggiore comfort e una buona protezione contro il freddo invernale, inoltre, assorbendo il contenuto liquido delle deiezioni, mantiene il recinto asciutto e poco sdrucciolevole." L''evidenza di anche un singolo soggetto con meno di 2 settimane d''età in evidenti condizioni non conformi (assenza di lettiera per il vitello) è sufficiente per assegnare il giudizio non conforme. Si considera non adeguato: Assenza di lettiera in uno o più vitelli con meno di 2 settimane d''età Livello della non conformita'': no; NO Si considera adeguato: Presenza di lettiera idonea e in quantità adeguata per tutti i vitelli con meno di 2 settimane d''età Livello della non conformita'': SI','19) È presente la lettiera, obbligatoria per vitelli con meno di 2 settimane d''età','S, NA, NB, NC, -','(*) In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate, riportando - se possibile - le marche auricolari dei soggetti coinvolti e i box/ricoveri dove sono alloggiati.',19);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,178,1093,'EDIFICI E LOCALI DI STABULAZIONE','Infermeria','126/2011, All. 1 punto 6 "I vitelli che presentano sintomi di malattie o ferite debbono ricevere immediatamente le opportune cure e, qualora un vitello non reagisca al trattamento dell''allevatore, deve essereconsultato al più presto un veterinario. Se necessario, i vitelli malati o feriti debbono essere isolati in locali appropriati con lettiera asciutta e confortevole." 126/2011, All. 1 punto 13 "[...] I vitelli malati e sottoposti a condizioni atmosferiche di grande calore devono poter disporre di acqua fresca in ogni momento." 146/2001, All. punto 4 "Ove necessario gli animali malati o feriti vengono isolati in appositi locali muniti, se del caso, di lettiere asciutte o confortevoli" MINSAN 2006, Nota esplicativa Prot. N. 27232 del 25/07/2006, pag. 7 "Deve essere previsto un locale o recinto infermeria chiaramente identificato, ma non necessariamente fisso, nel quale devono essere presenti in permanenza una lettiera asciutta ed acqua fresca in quantità sufficiente per gli animali malati ivi ospitati" Per curare i soggetti con problemi sanitari, comportamentali e/o lesioni traumatiche, è opportuno che ogni allevamento abbia a disposizione un''area di isolamento-infermeria o, in caso di necessità e in base alle caratteristiche dell''azienda,possa servirsene. Nel reparto infermeria devono essere presenti solo animali con patologie ben identificate. Le dimensioni adeguate di quest''area devono essere: - - per il recinto di isolamento individuale, almeno pari a quanto già previsto dall''Art. 3 comma 1 lettera a) del D. Lgs. 126/2011 (si veda anche item n. 10 della checklist); - - per il recinto di isolamento collettivo, almeno pari a quanto già previsto dall''Art. 3 comma 1 lettera b) del D. Lgs. 126/2011 (si Veda anche item n.13 della checklist); - permettendo così all''animale di girarsi facilmente, di avere contatti visivi ed olfattivi con gli altri animali, salvo nel caso in cui ciò non sia in contraddizione con specifiche prescrizioni veterinarie. Gli animali in isolamento-infermeria devono poter disporre di alimento ed acqua fresca ad libitum e la zona ad essi dedicata deve essere fornita di lettiera confortevole, paglia o materiali similari, tappetini di gomma morbida e/o altre pavimentazioni che possano migliorare le condizioni degli animali nel caso in cui la condizione clinica lo richieda, come ad esempio per animali in stato di decubito prolungato o zoppi. Per gli animali di età inferiore a 2 settimane,la lettiera è obbligatoria (All. 1 Punto 10 del D. Lgs. 126/2011). In caso di allevamenti di piccole dimensioni per "infermeria identificata" si intende che: - in caso di presenza di animali malati, feriti o che necessitano isolamento, il recinto o il box o altro settore deve essere identificato; - in caso non ci siano animali malati, feriti o bisognosi di isolamento, il recinto o il box o altro settore può essere solo "identificabile", ma l''allevatore deve dare prova della possibilità di ricavare tale zona in qualunque momento, in caso di necessità. Si considera requisito ottimale l''esistenza di un''infermeria identificata e circoscritta adibita a tale funzione. Quest''area deve essere segnalata tramite apposita cartellonistica o altra indicazione scritta (se è soggetta a rotazione) o segnalata sulla planimetria aziendale (se è a localizzazione fissa). Per motivazioni sanitarie, si considera ottimale la presenza di una zona di isolamento-infermeria con capienza complessiva non inferiore al 3% del numero medio di animali quotidianamente presenti in allevamento e con spazi disponibili molto abbondanti (es. più di 2-3m2/capo), facili da ispezionare da parte dell''operatore, eventualmente suddivisi su più box, con lettiera permanente ottimamente gestita, abbeveratoi e facile accesso al cibo. Si considera non adeguato: Assenza di qualsiasi locale specifico e identificato nel quale, in caso di necessità, possano essere isolati animali feriti o malati Livello della non conformita'': no; NO Si considera adeguato: Presenza di locale identificato ed appositamente preparato per accogliere animali malati o feriti munito di lettiera asciutta o tappetino confortevole, ove la condizione clinica lo richieda Livello della non conformita'': SI Si considera ottimale: Presenza di locale specifico ed identificato, con lettiera permanente asciutta e confortevole, in grado di ospitare almeno il 3% del numero medio di animali quotidianamente presenti in allevamento e con spazi disponibili molto abbondanti (es. più di 2-3m2/capo) Livello della non conformita'': Ottimale','20) In caso di necessità, gli animali possono essere isolati in appositi locali/recinti muniti, se del caso, di lettiere asciutte o confortevoli e dispongono di acqua fresca in ogni momento','S, NA, NB, NC, -, O','(*) Descrivere, se del caso, le caratteristiche del recinto di isolamento-infermeria',20);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,179,1094,'EDIFICI E LOCALI DI STABULAZIONE','Temperatura ed umidità','126/2011 All. 1 Punto 3 "L''isolamento termico, il riscaldamento e la ventilazione devono consentire di mantenere entro limiti non dannosi per i vitelli, la circolazione dell''aria, la quantità di polvere, la temperatura,l''umidità relativa dell''aria e le concentrazioni di gas." 146/2001 All. Fabbricati e locali di stabulazione Punto 10 "10. La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas devono essere mantenute entro limiti non dannosi per gli animali." MINSAN 2006, Nota esplicativa Prot. N. 27232 del 25/07/2006, pag. 9 "Deve essere tenuto sotto controllo il microclima: si consiglia la valutazione dei parametri ambientali utilizzando apparecchiature per il rilevamento di temperatura ed umidità relativa(termo-igrometro)." Si valutano la tipologia di stalla e le attrezzature per il mantenimento di condizioni non dannose per i vitelli. L''isolamento termico della struttura, il riscaldamento e la ventilazione devono consentire di mantenere entro limiti non dannosi per i vitelli, la circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa e le concentrazioni di gas (anidride carbonica, ammoniaca, ecc.). Nel caso di allevamenti che prevedano grandi concentrazioni di animali (es. vitelli a carne bianca o grandi vitellaie da latte), è essenziale garantire una buona ventilazione per mantenere sia una corretta temperatura all''interno degli edifici sia una buona qualità dell''aria. Il livello di ventilazione adeguato deve permettere da un lato la rimozione del calore quando eccessivo (es. estate - inizio autunno) e dall''altro la rimozione dell''umidità in eccesso nelle stagioni più fredde (es. tardo autunno - inverno) mantenendo però una temperatura confortevole (protezione dal freddo eccessivo). La ventilazione naturale può essere garantita da ampie finestre, la cui apertura è regolata in base alla temperatura interna ed esterna, a cui si associa un buon isolamento termico della copertura e la presenza di un camino. Si considera "stalla aperta" quando non presenta muri su nessuno dei 4 lati oppure quando è presente un solo lato chiuso, senza che questo limiti una buona aerazione della stalla. Nel caso di altre modalità di allevamento dei vitelli (es. produzione latte di piccole-medie dimensioni o linea vacca-vitello),considerare come le varie strutture di stabulazione siano in grado di proteggere gli animali dal caldo e dal freddo (es. coibentazione gabbiette, teloni ombreggianti o antivento, ecc.). Si considera non adeguato: presenza di condizioni microclimatiche dannose per gli animali: es. ambienti chiusi o polverosi oppure stalle semi-aperte senza adeguata aerazione oppure insufficiente protezione dal caldo e dal freddo Livello della non conformita'': no; NO Si considera adeguato: presenza di condizioni microclimatiche idonee per gli animali:es. ventilazione naturale (stalla aperta) oppure impianti di ventilazione/aerazione idonei (senza sistemi di controllo) oppure possibilità di proteggere gli animali dal caldo e dal freddo in funzione delle condizioni atmosferiche (es. coibentazione delle gabbiette, teloni ombreggianti o antivento, ecc.) Livello della non conformita'': SI Si considera ottimale: presenza di condizioni microclimatiche ottimali per gli animali: es. presenza di impianti di condizionamento del microclima con sistemi automatici di controllo; oppure evidenza di tipologie di ricoveri nonché di procedure ottimali per la costante protezione degli animali dalle variazioni di temperatura, umidità e qualità dell''aria; oppure pascolo idoneo dei vitelli insieme agli adulti Livello della non conformita'': Ottimale','21) La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria sono mantenute entro limiti non dannosi per gli animali. All''atto dell''ispezione, T° e UR sono adeguate alle esigenze etologiche della specie e all''età degli animali','S, NA, NB, NC, -, O','(*)Descrivere come vengono garantite idonee condizioni microclimatiche',21);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,180,1095,'EDIFICI E LOCALI DI STABULAZIONE','Presenza di gas nocivi','126/2011 All. 1 Punto 3 "L''isolamento termico, il riscaldamento e la ventilazione devono consentire di mantenere entro limiti non dannosi per i vitelli, la circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas." 146/2001 All. Fabbricati e locali di stabulazione Punto 10 "10. La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas devono essere mantenute entro limiti non dannosi per gli animali." L''isolamento termico della struttura, il riscaldamento e la ventilazione devono consentire di mantenere entro limiti non dannosi per i vitelli, la circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa e le concentrazioni di gas (anidride carbonica, ammoniaca, ecc.). In particolare, durante la valutazione dei vitelli (diversamente dagli animali adulti), si considerano accettabili tenori di ammoniaca inferiori a 10 ppm e tenori di anidride carbonica inferiori a 3.000 ppm. La presenza di concentrazioni di gas non adeguate(es. NH3>10 ppm),potrebbe essere rilevata negli animali per comparsa di rossore delle congiuntive e lacrimazione. In caso di sospetto di condizione inadeguata, è necessario confermare la condizione, misurando la concentrazione dei gas nocivi in allevamento mediante un rilevatore di gas portatile da posizionare al centro della stalla all''altezza degli animali. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato l''evidenza di una concentrazione di gas dannosa per gli animali (indicazioni di riferimento dei limiti: NH3>10 ppm; CO2 > 3000 ppm) Livello della non conformita'': no; NO Si considera adeguato l''evidenza di una concentrazione dei gas non dannosa per gli animali (indicazioni di riferimento dei limiti: NH3<10 ppm; CO2< 3000 ppm) Livello della non conformita'': SI','22) Le concentrazioni di gas sono mantenute entro limiti non dannosi per gli animali','S, NA, NB, NC, -','(*)Se del caso, registrare i valori misurati NH3... CO2... a campione in tutti i settori e tipologie, per confermare un sospetto di condizione inadeguata',22);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,181,1096,'EDIFICI E LOCALI DI STABULAZIONE','Presenza di ripari nelle aree esterne per gli animali custoditi al di fuori dei fabbricati','146/2001 All. Animali custoditi al di fuori dei fabbricati Punto 12 "12. Agli animali custoditi al di fuori dei fabbricati deve essere fornito, in funzione delle necessità e delle possibilità, un riparo adeguato dalle intemperie, dai predatori e da rischi per la salute." Si considerano i ripari sia di tipo naturale (alberi, anfratti, grotte, ecc.) che di tipo artificiale (tettoie, ricoveri, ecc.) a condizione che offrano un riparo adeguato, in relazione alla stagione e alla località. Se nell''allevamento non ci sono animali custoditi al di fuori dei fabbricati, il requisito si considera non applicabile. Si considera non adeguato: In funzione della valutazione del rischio, delle necessità e delle possibilità, l''assenza, insufficienza o presenza di ripari non adeguati anche per un solo gruppo di animali no; NO Si considera adeguato: Presenza di ripari sufficienti ed adeguati per tutti gli animali Livello della non conformita'': SI Si considera ottimale: Presenza di ripari di tipo artificiale facilmente accessibili, sufficienti ed idonei a proteggere tutti gli animali dai pericoli ambientali Livello della non conformita'': Ottimale','23) Gli animali custoditi al di fuori dei fabbricati hanno ripari adeguati e sufficienti','S, NA, NB, NC, -, O','(*) Descrivere i ripari, la loro localizzazione e le modalità con le quali gli animali li raggiungono',23);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,182,1097,'ILLUMINAZIONE MINIMA','Illuminazione minima - ciclo di luce per gli animali','126/2011 All. 1 Punto 5 "I vitelli non devono restare continuamente al buio. A tal fine, onde soddisfare le loro esigenze comportamentali e fisiologiche, è opportuno prevedere, date le diverse condizioni climatiche degli Stati membri, una illuminazione adeguata naturale o artificiale che, in quest''ultimo caso, dovrà essere almeno equivalente alla durata di illuminazione naturale normalmente disponibile tra le ore9.00 e le ore 17.00." 146/2001 All. Fabbricati e locali di stabulazione Punto 11 "Gli animali custoditi nei fabbricati non devono essere tenuti costantemente al buio o esposti ad illuminazione artificiale senza un adeguato periodo di riposo. Se la luce naturale disponibile è insufficiente a soddisfare esigenze comportamentali e fisiologiche degli animali, occorre prevedere un''adeguata illuminazione artificiale." Gli animali stabulati che non hanno accesso alla luce naturale dispongono di un periodo di luce ininterrotto non inferiore alle 8 ore (con intensità minima di almeno 40 lux) e di un periodo di buio ininterrotto (o debole illuminazione notturna) non inferiore alle 8 ore. Indicativamente, in condizioni di routine, si può considerare come riferimento per i 40 lux, una luce che permette di leggere la checklist senza sforzo. Le fonti di luce artificiale dovrebbero essere installate in modo da non causare disagio agli animali. In caso di sospetto di condizione inadeguata, è necessario confermare con luxometro. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: Assenza o insufficienza di illuminazione naturale o artificiale / assente o insufficiente periodo di riposo Livello della non conformita'': no; NO Si considera adeguato: Presenza di illuminazione naturale o artificiale adeguata per almeno 8 ore al giorno / presenza di un periodo di riposo adeguato per almeno 8 ore al giorno Livello della non conformita'': SI','24) Gli animali custoditi nei fabbricati non sono tenuti costantemente al buio, ad essi sono garantiti un adeguato periodo di luce (naturale o artificiale) ed un adeguato periodo di riposo. È garantita un''illuminazione adeguata, naturale o artificiale, tra le ore 9.00 e le ore 17.00','S, NA, NB, NC, -','(*) Registrare il tipo di illuminazione naturale/artificiale/assente e in caso di rilievo non conforme, confermare con luxometro',24);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,183,1098,'ATTREZZATURA AUTOMATICA E MECCANICA','Ispezione delle attrezzature automatiche e meccaniche','126/2011 All. 1 Punto 4 "Ogni impianto automatico o meccanico indispensabile per la salute ed il benessere dei vitelli deve essere ispezionato almeno una volta al giorno. Gli eventuali difetti riscontrati devono esseree liminati immediatamente; se ciò non fosse possibile, occorre prendere le misure adeguate per salvaguardare la salute ed il benessere dei vitelli fino a che non sia effettuata la riparazione,ricorrendo in particolare a metodi alternativi disponibili di alimentazione e provvedendo a mantenere condizioni ambientali soddisfacenti." 146/2001 All. Impianti automatici o meccanici Punto 13 "13. Ogni impianto automatico o meccanico indispensabile per la salute ed il benessere degli animali deve essere ispezionato almeno una volta al giorno. Gli eventuali difetti riscontrati devono essere eliminati immediatamente; se ciò non è possibile, occorre prendere le misure adeguate per salvaguardare la salute ed il benessere degli animali." I sistemi automatici di alimentazione, di ventilazione, ecc. possono essere considerati impianti automatici o meccanici indispensabili per la salute degli animali; per questo devono essere ispezionati una volta al giorno e i difetti riscontrati eliminati immediatamente. Se ciò non è possibile occorre prendere misure adeguate per salvaguardare la salute ed il benessere degli animali (es. possedere un generatore di corrente). Si considera non adeguato: mancanza di regolari ispezioni giornaliere e/o rilievo di una mancanza di interventi tempestivi Livello della non conformita'': no; NO Si considera adeguato: Almeno 1 ispezione al giorno Livello della non conformita'': SI Si considera ottimale: Presenza di procedure o manuali di buone pratiche comprensivi di istruzioni operative per gli operatori, che garantiscano il controllo giornaliero e la conservazione delle relative registrazioni Livello della non conformita'': Ottimale','25) Gli impianti automatici o meccanici sono ispezionati almeno una volta al giorno Sono prese misure adeguate per salvaguardare la salute e il benessere degli animali in caso di non funzionamento degli impianti (es. metodi alternativi di alimentazione; generatore di corrente; ecc.)?','S, NA, NB, NC, -, O','(*)Raccolta di informazioni tramite interviste agli operatori, consultazione di manuali di buone pratiche e relative registrazioni',25);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,184,1099,'ATTREZZATURA AUTOMATICA E MECCANICA','Allarme impianto di ventilazione e impianto di riserva','126/2011 All. 1 Punto 4 "[...] Se si utilizza un impianto di ventilazione artificiale, occorre prevedere un opportuno sistema sostitutivo che permetta un ricambio di aria sufficiente per preservare la salute e il benessere dei vitelli in caso di guasti all''impianto, nonché un sistema di allarme che segnali i guasti all''allevatore. Il sistema di allarme deve essere verificato regolarmente." 146/2001 All. Impianti automatici o meccanici Punto 13 "[...] Se la salute ed il benessere degli animali dipendono da un impianto di ventilazione artificiale, deve essere previsto un adeguato impianto di riserva per garantire un ricambio di aria sufficiente a salvaguardare la salute e il benessere degli animali. In caso di guasto all''impianto deve essere previsto un sistema di allarme che segnali il guasto. Detto sistema d''allarme deve essere sottoposto a Pagina 22 di 36 controlli regolari." Qualora per la salute ed il benessere degli animali sia presente e necessario un sistema di aerazione artificiale con ventilazione unicamente forzata, occorre prevedere un sistema di allarme che segnali eventuali guasti all''allevatore, nonché un opportuno sistema sostitutivo che all''occorrenza consenta un ricambio di aria sufficiente per gli animali presenti, in attesa che il guasto venga corretto (es. presenza di finestre che possono essere aperte, presenza di ventilatori trasportabili a carrello, presenza di generatore di corrente per ripristinare la ventilazione, ecc.). Il sistema di allarme e il sistema sostitutivo devono essere verificati regolarmente, soprattutto se il loro malfunzionamento metterebbe in serio rischio la salute ed il benessere degli animali. Se l''allevamento non necessita di un impianto di ventilazione forzata (es. idonea circolazione naturale dell''aria, pascolo, ecc.) il requisito si considera non applicabile. Si considera non adeguato: Assenza di un sistema di allarme e di sostituzione all''impianto di ventilazione artificiale e/o assenza di controlli regolari del sistema di allarme e del sistema sostitutivo Livello della non conformita'': no; NO Si considera adeguato: Presenza di un sistema di allarme e di sostituzione all''impianto di ventilazione artificiale regolarmente controllato Livello della non conformita'': SI','26) Se la salute e il benessere degli animali dipendono da un impianto di ventilazione artificiale, è previsto un adeguato impianto di riserva per garantire un ricambio di aria sufficiente a salvaguardare la salute e il benessere degli animali in caso di guasto all''impianto stesso. È previsto un sistema di allarme che segnali eventuali guasti','S, NA, NB, NC, -','(*)Registrare la verifica del corretto funzionamento del sistema di allarme e del sistema sostitutivo effettuata durante il controllo ufficiale; annotare la frequenza degli interventi di verifica e manutenzione effettuati dall''allevatore',26);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,185,1100,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIO NE DI ALTRE SOSTANZE','Gestione degli alimenti e della razione giornaliera','126/2011, All. 1, punto 11 "Ai vitelli deve essere somministrata un''alimentazione adeguata alla loro età e al loro peso e conforme alle loro esigenze comportamentali e fisiologiche, onde favorire buone condizioni di salute e di benessere.[...]" 146/2001 All. Mangimi, acqua e altre sostanze Punto 14 "Agli animali deve essere fornita un''alimentazione sana adatta alla loro età e specie e in quantità sufficiente a mantenerli in buona salute e a soddisfare le loro esigenze nutrizionali. Gli alimenti o i liquidi sono somministrati agli animali in modo da non causare loro inutili sofferenze o lesioni e non contengono sostanze che possano causare inutili sofferenze o lesioni." I sistemi di alimentazione dovrebbero permettere ad ogni individuo di soddisfare i propri fabbisogni per quantità e qualità degli alimenti. La dieta dovrebbe fornire energia sufficiente, nutrienti e fibra alimentare tali da soddisfare i requisiti nutrizionali e rispettare la fisiologia digestiva e metabolica degli animali in giovane età (0-6 mesi). Per poter soddisfare le esigenze nutrizionali degli animali e far sì che l''alimentazione sia adeguata al loro sviluppo corporeo e al loro stato fisiologico è necessario che, almeno ognuno dei gruppi base previsti disponga di una razione specifica. Tale razione specifica può essere scritta o registrata su pesa o comunque, anche se non scritta, nota all''allevatore o al personale che si occupa della preparazione degli alimenti per gli animali, ed è buona prassi che sia mantenuta il più possibile stabile nel tempo. La corretta alimentazione degli animali è anche collegata alla qualità degli alimenti e dell''acqua che la compongono, che devono essere di origine conosciuta e conservati in ambienti idonei (es. silos, magazzini, fienili, sistema di distribuzione idrica) per evitare alterazioni e contaminazioni con sostanze tossico-nocive. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: Presenza di una razione non adatta agli animali perché non adeguata alle loro esigenze, mai stata calcolata, non in rapporto ai fabbisogni o composta da alimenti non sani Livello della non conformita'': no; NO Si considera adeguato: Presenza di una razione adatta agli animali, perché specifica per ogni gruppo di base (es. vitelli non svezzati in box singolo; in svezzamento; svezzati; in accrescimento) e composta da alimenti sani Livello della non conformita'': SI Si considera ottimale: Presenza di una razione ottimale per gli animali, perché calcolata da un alimentarista, revisionata di frequente o comunque ad ogni cambio di alimenti, e composta da alimenti sani Livello della non conformita'': Ottimale','27) L''alimentazione è adeguata in rapporto all''età, al peso e alle esigenze comportamentali e fisiologiche degli animali. Gli alimenti o i liquidi sono somministrati agli animali in modo da non causare loro inutili sofferenze o lesioni e non contengono sostanze che possano causare inutili sofferenze o lesioni','S, NA, NB, NC, -, O','(*) Descrivere eventualmente la razione o le razioni (se specifiche per i diversi gruppi di animali), le modalità di calcolo, il nominativo dell''alimentarista o del veterinario che l''ha calcolata, ecc. Le valutazioni ABM (body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',27);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,186,1101,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIO NE DI ALTRE SOSTANZE','Frequenza di somministrazione dell''alimento (latte e fibra)','126/2011, All. 1, Punto 12 "Tutti i vitelli devono essere nutriti almeno due volte al giorno. Se i vitelli sono stabulati in gruppo e non sono alimentati ad libitum o mediante un sistema automatico di alimentazione, ciascun vitello deve avere accesso agli alimenti contemporaneamente agli altri vitelli del gruppo". 146/2001 All. Mangimi, acqua e altre sostanze Punto 15 "15. Tutti gli animali devono avere accesso ai mangimi ad intervalli adeguati alle loro necessità fisiologiche." Tutti i vitelli devono essere nutriti almeno due volte al giorno. Nella fase in cui l''apporto energetico e nutritivo per la corretta crescita del vitello è fornito esclusivamente o prevalentemente dal latte o suoi sostituti, gli animali devono ricevere almeno due pasti di latte al giorno, 7 giorni su 7 per un totale di 14 pasti settimanali. Solo nelle fasi in cui la quota energetica e nutritiva fornita dal latte (e suoi sostituti) cala progressivamente(<50%) in ragione dell''aumento della quota fornita dagli alimenti solidi tipici dell''alimentazione dei ruminanti (es. cereali e foraggi), la frequenza di somministrazione di ciascuna tipologia di alimento (latte o sostituti vs alimento solido) può variare, fermo restando l''obbligo di nutrire tutti i vitelli almeno due volte al giorno. Il requisito ottimale è soddisfatto quando tutti i vitelli (nella fase di alimentazione prevalentemente lattea) ricevono il pasto di latte (o suoi sostituti) 3 o più volte/giorno, regolarmente tutti i giorni della settimana e/o mediante sistema automatizzato e controllato di distribuzione del latte correttamente gestito (es. lupa o allattatrice automatica). Anche nella linea vacca-vitello, accertarsi che i vitelli possano essere nutriti almeno due volte al giorno. Si considera non adeguato: Somministrazione dell''alimento 1 volta/giorno no; NO Si considera adeguato: Somministrazione dell''alimento 2 volte/giorno, regolarmente tutti i giorni della settimana SI Si considera ottimale: Per tutti i vitelli, somministrazione dell''alimento 3 o più volte/giorno, regolarmente tutti i giorni della settimana e/o mediante sistema automatizzato e controllato Ottimale','28) I vitelli sono nutriti almeno 2 volte al giorno','S, NA, NB, NC, -, O','(*) Descrivere le modalità e frequenze di somministrazione dei pasti',28);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,187,1102,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIO NE DI ALTRE SOSTANZE','Numero di posti disponibili in mangiatoia','126/2011, All. 1, Punto 12 "Tutti i vitelli devono essere nutriti almeno due volte al giorno. Se i vitelli sono stabulati in gruppo e non sono alimentati ad libitum o mediante un sistema automatico di alimentazione,ciascuno vitello deve avere accesso agli alimenti contemporaneamente agli altri vitelli del gruppo." 146/2001,All. Mangimi, acqua e altre sostanze Punto 17. "Le attrezzature per la somministrazione di mangimi e di acqua devono essere concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua e le conseguenze negative derivanti da rivalità tra gli animali." Se i vitelli sono stabulati in gruppo e non sono alimentati ad libitum o mediante un sistema automatico di alimentazione, ciascun vitello Deve avere accesso agli alimenti (latte e alimento solido) contemporaneamente agli altri vitelli del gruppo. In questi casi, verificare quanti animali possono accedere contemporaneamente alla mangiatoia (latte/alimento solido),considerando che per ogni capo sono indicati i seguenti spazi lineari: - per animali sotto 200 kg p.v. >= 0,4 m/capo; - per animali tra 200 e 300 kg p.v. >= 0,5 m/capo. Pertanto, durante l''ispezione, il primo aspetto da valutare è la possibilità che tutti gli animali accedano contemporaneamente alla mangiatoia in caso di alimentazione non ad libitum (latte e/o alimento solido). Se l''alimento solido è somministrato ad libitum, è sufficiente che i posti in mangiatoia per il latte siano almeno pari al numero di vitelli presenti nel box. Il requisito è ottimale, se oltre ai criteri per assegnare un giudizio conforme, i posti in mangiatoia sono più ampi rispetto al previsto (almeno 0,5 - 0,6 m/capo) e, in caso di fibra ad libitum, esiste la possibilità di accesso contemporaneo ad essa per almeno 2 soggetti. Nel caso l''alimento sia somministrato mediante un sistema automatico di alimentazione per assegnare il giudizio ottimale è necessario che lo stesso sia correttamente gestito e controllato. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera accesso agli alimenti non adeguato: posti in mangiatoia inferiori al numero di vitelli presenti, in caso di alimentazione non ad libitum per latte e fibra Livello della non conformita'': no; NO Si considera accesso agli alimenti adeguato: posti in mangiatoia pari al numero di vitelli presenti, in caso di alimentazione non ad libitum per latte e fibra oppure solo per il latte se la fibra è distribuita ad libitum Livello della non conformita'': SI Si considera accesso agli alimenti ottimale:posti in mangiatoia più ampi rispetto al previsto (almeno 0,5 - 0,6 m/capo) / presenza di un sistema automatico di alimentazione correttamente gestito e in caso di fibra ad libitum accesso contemporaneo per almeno 2 soggetti Livello della non conformita'': Ottimale','29) Se non viene praticata l''alimentazione ad libitum o con sistemi automatici è assicurato l''accesso agli alimenti a tutti i vitelli del gruppo contemporaneamente','S, NA, NB, NC, -, O','(*)Descrivere le modalità di accesso all''alimento, riportare gli spazi disponibili per l''alimentazione. Le valutazioni ABM (body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',29);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,188,1103,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIO NE DI ALTRE SOSTANZE','Disponibilità di acqua e numero di abbeveratoi','126/2011, All.1, Punto 13 "A partire dalla seconda settimana di età, ogni vitello deve poter disporre di acqua fresca adeguata in quantità sufficiente oppure poter soddisfare il proprio fabbisogno in liquidi bevendo altre bevande, tuttavia i vitelli malati e sottoposti a condizioni atmosferiche di grande calore devono poter disporre di acqua fresca in ogni momento." 126/2011, All. 1, Punto 14 "Le attrezzature per la somministrazione di mangimi e di acqua devono essere concepite, costruite,installate e mantenute in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua destinati ai vitelli." 146/2001 All. Mangimi, acqua e altre sostanze Punti 16 "Tutti gli animali devono avere accesso ad un''appropriata quantità di acqua, di qualità adeguata, o devono poter soddisfare le loro esigenze di assorbimento di liquidi in altri modi." Tutti i vitelli, in particolare dopo la seconda settimana d''età, devono poter soddisfare le loro esigenze d''idratazione. In presenza di animali sani e non sottoposti a condizioni di grande calore, è ragionevole attendersi che il soddisfacimento del fabbisogno idrico sia garantito (quantomeno fino allo svezzamento)dalla somministrazione giornaliera di latte in quantità proporzionale al peso vivo (si considera indicativamente un rapporto litri/die = 10% del peso vivo). Se gli animali sono malati o sottoposti a condizioni di grande calore, devono poter disporre permanentemente di acqua di qualità adeguata. Per questo motivo, considerando che nei nostri climi si verificano sovente periodi di intenso calore, è necessario - in tutto il periodo dell''anno - che sussista chiara evidenza della possibilità di ottemperare a questo requisito nel momento della necessità (es. presenza o possibilità di instaurare idonei dispositivi per la somministrazione esclusiva di acqua da bere). Nel caso di animali che ricevono alimentazione prevalentemente solida, l''accesso all''acqua deve essere permanentemente garantito e si considera adeguato almeno 1 abbeveratoio ogni 14 animali o se a vasca 4-5 cm/capo (per stabulazioni collettive). I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio Per quanto riguarda la qualità dell''acqua si considera adeguato che gli abbeveratoi siano mantenuti in buone condizioni di pulizia, liberi da feci e residui di alimento inveterato; in aggiunta al requisito adeguato si considera ottimale, l''esecuzione su base almeno annuale di esami per la potabilità dell''acqua. Basta riscontrare la presenza di un singolo soggetto in condizione tale da non soddisfare il requisito di legge per assegnare il giudizio non conforme. Si considera non adeguato: Evidenza di mancato soddisfacimento del fabbisogno idrico per: -nel caso di animali con alimentazione prevalentemente a base di latte: somministrazione di una quantità di latte non adatta a Soddisfare il fabbisogno idrico; -nel caso di animali con alimentazione prevalentemente solida: acqua non disponibile in maniera permanente o di qualità non adeguata; -presenza di vitelli malati o sottoposti a condizioni atmosferiche di grande calore che non ricevono acqua fresca ad libitum -mancata evidenza della possibilità di predisporre idonei sistemi di idratazione nei periodi di grande calore; -presenza di attrezzature non idonee per la somministrazione di acqua (es. lesive o palesemente insufficienti) Livello della non conformita'': no; NO Si considera adeguato:Evidenza di corretto soddisfacimento del fabbisogno idrico per: -somministrazione di un''adeguata quantità di latte o suoi sostituti agli animali non ancora svezzati; -acqua disponibile in maniera permanente e di qualità adeguata agli animali malati, a quelli sottoposti a condizioni di grande calore e a quelli nutriti prevalentemente con alimenti solidi; -evidenza della possibilità di predisporre idonei sistemi di idratazione nei periodi di grande calore; -presenza di attrezzature idonee per la somministrazione di acqua (es. non lesive e in numero adeguato) Livello della non conformita'': SI Il requisito superiore prevede, oltre ai criteri per l''adeguatezza,acqua ad libitum per tutti i vitelli; presenza di abbeveratoi regolabili in altezza e in numero superiore al minimo previsto e desecuzione di esami annuali per la potabilità dell''acqua o approvvigionamento dall''acquedotto in maniera diretta (senza vasche di raccolta intermedia) Livello della non conformita'': Ottimale','30) A partire dalla seconda settimana d''età, ogni vitello dispone di acqua fresca di qualità e in quantità sufficiente o può soddisfare il proprio fabbisogno di liquidi con altre bevande. La modalità di somministrazione dell''acqua consente un''adeguata idratazione degli animali anche nei periodi di intenso calore. Le attrezzature per la somministrazione di mangimi e di acqua sono concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua e le conseguenze negative derivanti da rivalità tra gli animali?','S, NA, NB, NC, -, O','(*) Registrare modalità di accesso all''acqua e numero di abbeveratoi, descrivere la frequenza di pulizia diabbeveratoi, riportare gli estremi di eventuali esami dell''acqua, ecc. Le valutazioni ABM (body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',30);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,189,1104,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIO NE DI ALTRE SOSTANZE','Colostratura dei vitelli','126/2011, All. 1, Punto 15 "Ogni vitello deve ricevere colostro bovino quanto prima possibile dopo la nascita e comunque entro le prime sei ore di vita." Poiché il vitello nasce pressoché privo della copertura anticorpale materna, il trasferimento dell''immunità passiva deve avvenire necessariamente attraverso l''assunzione di colostro, in adeguate quantità e qualità, entro le prime 6 ore dal parto. Il colostro può essere somministrato lasciando il vitello con la madre oppure, se il vitello è sottratto dalla madre, l''allevatore deve somministrare il colostro tramite biberon o (se necessario) sonda esofagea, per maggiore garanzia sulla quantità effettivamente assunta (ottimale 4 litri). Poiché non tutte le bovine producono un colostro di qualità (con un tenore di gamma-globuline di almeno 60 g/litro) è da considerarsi ottimale la presenza di una banca del colostro aziendale proveniente dalle bovine migliori. Analogamente se, in determinate situazioni, è necessario evitare cross-infezioni tra madre e vitello, è opportuno raccogliere il colostro di bovine sane o sottoporlo a trattamenti di risanamento. Infine, il requisito si considera ottimale se le procedure di somministrazione del colostro, nonché la tenuta della banca del colostro, sono descritte e ben documentate in manuali di buone pratiche aziendali. Si considera non adeguato: I vitelli non ricevono colostro bovino/bufalino quanto prima possibile dopo la nascita o comunque entro le prime 6 ore di vita Livello della non conformita'': no; NO Si considera adeguato: I vitelli ricevono colostro bovino/bufalino quanto prima possibile dopo la nascita o comunque entro le prime 6 ore di vita Livello della non conformita'': SI Si considera ottimale: I vitelli ricevono colostro bovino quanto prima possibile dopo la nascita o comunque entro le prime 6 ore di vita e, in caso di insufficiente stato sanitario delle bovine presenti in azienda, il colostro è sottoposto a trattamento di risanamento, oppure proviene da bovine sane della stessa azienda o esiste una banca del colostro o vengono fatte verifiche sul grado di colostratura e sulla qualità del colostro stesso. Livello della non conformita'': Ottimale','31) I vitelli ricevono colostro entro le prime 6 ore di vita','S, NA, NB, NC, -, O','(*) Descrivere le modalità di colostratura dei vitelli, l''eventuale presenza di una banca del colostro e evidenza che le procedure di colostratura sono descritte e documentate in un manuale di buone pratiche aziendali',31);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,190,1105,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIO NE DI ALTRE SOSTANZE','Somministrazione di sostanze illecite','146/2001 All. Mangimi, acqua e altre sostanze punto 18 "18. Nessuna altra sostanza, ad eccezione di quelle somministrate a fini terapeutici o profilattici o in vista di trattamenti zootecnici come previsto nell''art. 1, paragrafo 2, lettera c) della direttiva 96/22/CE, deve essere somministrata ad un animale, a meno che gli studi scientifici sul benessere degli animali e l''esperienza acquisita ne abbiano dimostrato l''innocuità per la sua salute e il suo benessere." Verificare il registro o le registrazioni elettroniche dei trattamenti e l''armadietto o eventuali confezioni o giacenze di farmaci. Si considera non adeguato: evidenza di somministrazione di sostanze illecite Livello della non conformita'': NO Si considera adeguato: nessuna evidenza di somministrazione di sostanze illecite Livello della non conformita'': SÌ','32) Non viene somministrata alcuna sostanza, ad eccezione di quelle somministrate a fini terapeutici o profilattici o in vista di trattamenti zootecnici come previsto dalla normativa vigente I trattamenti terapeutici e profilattici sono regolarmente prescritti da un medico veterinario','S, NC, -','(*)Descrivere le verifiche condotte',32);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,191,1106,'TASSO DI EMOGLOBINA','Livelli di emoglobina','126/2011,All. 1, Punto 11 "Ai vitelli deve essere somministrata un''alimentazione adeguata alla loro età e al loro peso e conforme alle loro esigenze comportamentali e fisiologiche, onde favorire buone condizioni di salute e di benessere. A tal fine gli alimenti devono avere un tenore di ferro sufficiente per raggiungere un tasso di emoglobina di almeno 4,5 mmol/litro [...]" MINSAN 2006, Nota esplicativa Prot. N. 27232 del 25/07/2006, pag. 15. "I Servizi Veterinari delle ASL competenti per territorio devono effettuare i controlli ufficiali per la determinazione dei livelli di emoglobina e, qualora l''allevatore li metta in atto, verificare l''adeguatezza e l''efficacia dei piani di autocontrollo/buone pratiche di allevamento. I controlli ufficiali, compatibilmente con le risorse disponibili, devono aver luogo preferibilmente in azienda dopo i 70 giorni/stalla ed in particolare nella fase di finissaggio che rappresenta il periodo più a rischio. Nel controllo ufficiale si ritiene che il livello minimo per considerare l''allevamento nella norma corrisponda ad una media di gruppo accertata uguale o superiore al livello soglia previsto dalla normativa vigente (7,25 g/dl) ed il numero dei soggetti da testare debba garantire una sufficiente rappresentatività delle variabili genetiche ed ambientali, a tal fine si ritiene adeguato un campionamento del 20% dell''intera partita con un massimo di 20 animali. Inoltre, deve essere incoraggiato il raggiungimento di un secondo livello, qualitativamente più elevato, che prevede una percentuale di vitelli fuori norma inferiore al 20% calcolata con criteri statistici. Al secondo livello di conformità, che al momento ha unicamente lo scopo di testare la qualità dell''allevamento,dovranno tendere con il tempo tutte le aziende; i servizi veterinari delle ASL, che hanno un ruolo fondamentale nella formazione degli allevatori devono, a tal fine, svolgere un''attività di consulenza e di verifica dei piani di autocontrollo/buone pratiche aziendali.'' "L''allevatore, anche in regime di autocontrollo, al fine di ottenere risultati attendibili relativamente al livello di emoglobina dovrà avvalersi di laboratori di analisi ematochimiche autorizzati dall''autorità sanitaria competente nei quali si utilizza la metodica analitica ufficiale(valutazione dell''emoglobina)" Pagina 27 di 36 Richiedere all''allevatore i referti analitici del prelievo individuale eseguito in autocontrollo a 70-90 giorni di allevamento, in un gruppo significativo di animali per la titolazione dell''emoglobina. Dai risultati di tali prelievi deve essere estrapolato il livello medio di emoglobina del gruppo ed il numero di capi che presentano livelli di emoglobina inferiori a 4,5 mmol/L. Il valore medio minimo che deve essere riscontrato è pari a 7,25 g/dL corrispondente al limite imposto dal D.Lgs. 126/2011 di 4,5 mmol/L. Negli allevamenti dove l''alimentazione dei vitelli è prevalentemente a base di latte o suoi succedanei (es. vitello a carne bianca), si considera non conforme la mancanza di controlli dei livelli di emoglobina degli animali o il riscontro di valori medi del gruppo inferiori a 7,25 g/dL (4,5 mmol/L). Sono invece valutati con giudizio ottimale gli allevamenti in cui il valore medio di emoglobina del gruppo è superiore a 9 g/dL (5,6 mmol/L) e nessun animale ha valori inferiori a 7,25 g/dL, unitamente all''adozione di piani di autocontrollo/buone pratiche di allevamento che prendano in considerazione il rischio d''insorgenza di anemia nei vitelli e la relativa gestione. Negli allevamenti in cui i vitelli ricevono un''alimentazione prevalentemente a base fibrosa (es. produzione latte o linea vacca-vitello) poiché la probabilità d''insorgenza di anemia ferro-priva è molto bassa, non è necessario eseguire verifiche ematiche sul tenore di emoglobina plasmatica. Pertanto, salvo evidenze di anomalie nel razionamento alimentare (vedi successivi punti 34-35), il requisito si considera soddisfatto. Si considera non adeguato: Assenza di referti analitici o valori medi < 7,25 g/dL (4,5 mmol/L) Livello della non conformita'': no; NO Si considera adeguato: Valori medi >= 7,25 g/dL (4,5 mmol/L) Livello della non conformita'': SI Si considera ottimale: Valori medi superiori a 9 g/dL e nessun animale con valore < 7,25 g/dL (4,5 mmol/L) Livello della non conformita'': Ottimale','33) La razione alimentare ha un contenuto in ferro sufficiente ad assicurare un tenore in emoglobina di almeno 4,5 mmol/L (pari a 7,25 g/dl). È tenuta una registrazione dei prelievi per il dosaggio di emoglobina','S, NA, NB, NC, -, O','(*) Descrivere la cadenza delle analisi in autocontrollo e i risultati riscontrati. Se presenti, riportare gli estremi di eventuali controlli ufficiali di verifica, che possono essere eseguiti con frequenze decise discrezionalmente dalle Autorità Competenti',33);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,192,1107,'ALIMENTI CONTENENTI FIBRE','Somministrazione di alimento fibroso (vitelli tra 2 e 8 settimane d''età)','126/2011, All. 1, Punto 11 "[...] Una dose giornaliera di alimenti fibrosi deve essere somministrata ad ogni vitello dopo la seconda settimana di età [...]" È necessario che i vitelli, a partire dalla seconda settimana di vita, ricevano giornalmente quantità crescenti di alimenti fibrosi, per consentire una buona stimolazione dei prestomaci e il loro corretto sviluppo, riducendo il rischio di disturbi digerenti e comportamenti anormali, come le stereotipie orali. Si considera non adeguato: Alimento fibroso assente dalla 2ª fino all''8ª settimana d''età Livello della non conformita'': no; NO Si considera adeguato: Alimento fibroso (paglia, fieno, insilato di mais, ecc.) presente in quantità adeguata e accessibile a tutti gli animali dalla 2° fino all''8° settimana d''età Livello della non conformita'': SI Si considera ottimale: Alimento fibroso (paglia, fieno, insilato di mais, ecc.) presente in quantità adeguata, accessibile a tutti gli animali dalla 2° fino all''8° settimana d''età e di lunghezza compresa tra 0,4 - 1 cm Livello della non conformita'': Ottimale','34) A partire dalla seconda settimana d''età, ogni vitello dispone quotidianamente di una quantità adeguata di alimenti fibrosi','S, NA, NB, NC, -, O','(*) Descrivere che alimento viene somministrato ai vitelli tra 2 e 8 settimane d''età',34);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,193,1108,'ALIMENTI CONTENENTI FIBRE','Quantità di alimento solido somministrato ai vitelli tra 8 e 20 settimane d''età','126/2011,All. 1, Punto 11 "[...] Una dose giornaliera di alimenti fibrosi deve essere somministrata ad ogni vitello dopo la seconda settimana di età e il quantitativo deve essere portato da 50 a 250 grammi al giorno per i vitelli di età compresa fra le 8 e le 20 settimane." A partire dall''8° settimana d''età, la quantità di alimenti fibrosi (es. paglia, fieno, insilato di mais, ecc.) somministrata agli animali deve essere pari ad almeno 50 gr/giorno/capo, fino ad arrivare ad un minimo di 250 gr/giorno/capo alla 20° settimana d''età. Si considera non adeguato: Somministrazione di alimenti fibrosi inferiore al limite di legge Livello della non conformita'': no; NO Si considera adeguato: Somministrazione di alimenti fibrosi conforme o leggermente superiore (10%) al limite di legge Livello della non conformita'': SI Si considera ottimale: Somministrazione di alimenti fibrosi superiore al limite di legge (almeno più del 10%) Livello della non conformita'': Ottimale','35) I vitelli di età compresa tra 8 e 20 settimane ricevono quotidianamente una quantità adeguata di alimenti fibrosi portata da 50 a 250 g/capo/die','S, NA, NB, NC, -, O','(*)Descrivere che alimento viene somministrato ai vitelli dopo le 8 settimane d''età',35);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,194,1109,'MUTILAZIONI','Mutilazioni e altre pratiche','146/2001 All. Mutilazioni Punto 19 "19. È vietato [...] il taglio [...] di code per i bovini se non a fini terapeutici certificati. La cauterizzazione dell''abbozzo corneale è ammessa al di sotto delle tre settimane di vita. [...] La castrazione è consentita per mantenere la qualità dei prodotti e le pratiche tradizionali di produzione a condizione che tali operazioni siano effettuate prima del raggiungimento della matura sessuale da personale qualificato, riducendo al minimo ogni sofferenza per gli animali. [...] Le pratiche di cui al presente punto sono effettuate sotto il controllo del medico veterinario dell''azienda." Per mutilazione si intende una pratica non effettuata per fini terapeutici o diagnostici, che si manifesta quale danno o perdita di una parte sensibile del corpo o quale alterazione della struttura dell''osso. Se praticata, verificare che la rimozione dell''abbozzo corneale sia eseguita al di sotto dei 21 giorni d''età, ad esempio tramite cauterizzazione termica o causticazione (applicazione di una pasta/matita caustica) della gemma corneale, e sotto il controllo del medico veterinario dell''azienda. Se la rimozione dell''abbozzo corneale avviene dopo i 21 giorni d''età o se si riscontrano altre mutilazioni (es. taglio della coda, castrazione) riconducibili al periodo di permanenza dell''animale nell''azienda ispezionata,deve essere verificato il registro dei trattamenti (cartaceo o elettronico) per controllare se contestualmente è stato effettuato/prescritto dal veterinario un trattamento anestetico e analgesico. Il taglio della coda è ammesso solo a fini terapeutici certificati e adeguatamente registrati. Tutti i trattamenti, che prevedono operazioni cruente, devono essere eseguiti con materiali sterili o a perdere ed espletati in modo da evitare all''animale dolore o sofferenza prolungata o non necessaria. Si considera ottimale la mancanza di qualsiasi mutilazione e/o castrazione su tutti gli animali. Si considera non adeguato: Presenza di animali con incisioni o con mutilazioni/castrazioni eseguite senza il rispetto dei tempi e delle modalità previste dalla normativa Livello della non conformita'': NO Si considera adeguato: Presenza di animali con mutilazioni/castrazioni eseguite nel rispetto dei tempi e delle modalità previste dalla normativa Livello della non conformita'': SI Si considera ottimale: Presenza di tutti gli animali integri e che non presentano incisioni o mutilazioni/castrazioni Livello della non conformita'': Ottimale','36) Sono rispettate le disposizioni pertinenti in caso di mutilazioni/castrazioni effettuate sugli animali? Sono praticate: -la cauterizzazione dell''abbozzo corneale entro le tre settimane di vita sotto controllo veterinario -il taglio della coda se necessario è eseguito da un medico veterinario esclusivamente a fini terapeutici dei quali esiste idonea documentazione','S, NC, -, O','(*)Descrivere l''eventuale tipo di mutilazione/incisione/castrazione, le categorie e - se possibile - le marche auricolari dei soggetti coinvoltie i box/ricoveri dove sono alloggiati. Descrivere le modalità ed i medicinali utilizzati, riportare il nominativo del veterinario, ecc.',36);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,195,1110,'PROCEDURE D''ALLEVAMENTO','Utilizzo della museruola','126/2011, All. 1, Punto 11 "[...] Ai vitelli non deve essere messa la museruola." L''osservazione deve essere eseguita su tutti gli animali. Basta riscontrare la presenza di un singolo soggetto a cui è stata posta la museruola (a meno di particolari e documentate prescrizioni veterinarie), per assegnare il giudizio non conforme. Si considera non adeguato: Utilizzo di museruola per uno o più animali in assenza di prescrizione documentata del veterinario Livello della non conformita'': no; NO Si considera adeguato: Non utilizzo di museruola / utilizzo di museruola in seguito a prescrizione documentata del veterinario Livello della non conformita'': SI','37) È rispettato il divieto di mettere la museruola ai vitelli','S, NA, NB, NC, -','(*) In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate, le categorie e - se possibile - le marche auricolari dei soggetti coinvolti e i box/ricoveri dove sono alloggiati.',37);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,196,1111,'PROCEDURE D''ALLEVAMENTO','Disposizioni generali','146/2001 Art. 2 Comma 1 Obblighi dei proprietari, dei custodi dei detentori degli animali 1. Il proprietario o il custode ovvero il detentore deve: a) adottare misure adeguate per garantire il benessere dei propri animali e affinché nonvengano loro provocati dolore, sofferenze o lesioni inutili; b) allevare e custodire gli animali [...] in conformità alledisposizioni di cui all''allegato. 146/2001 All. Procedure di allevamento punto 20 "Non devono essere praticati l''allevamento naturale o artificiale o procedimenti di allevamento che provochino o possano provocare agli animali in questione sofferenze o lesioni. Questa disposizione non impedisce il ricorso a taluni procedimenti che possono causare sofferenze o ferite minime o momentanee o richiedere interventi che non causano lesioni durevoli, se consentiti dalle disposizioni Nazionali". Valutare se vengono praticate procedure d''allevamento in contrasto con una o più delle 5 libertà, tali da causare evidenze negative sullo stato degli animali. Si considera non adeguato: la gestione delle fasi produttive in allevamento ha evidenze negative sullo stato degli animali Livello della non conformita'': no;NO Si considera adeguato: la gestione delle fasi produttive in allevamento non evidenzia alcun effetto negativo sullo stato degli animali Livello della non conformita'': SI','38) Non sono praticati procedimenti di allevamento che provocano o possano provocare agli animali sofferenze o lesioni. Nel caso si ricorra a taluni procedimenti che possono causare sofferenze o ferite minime o momentanee o richiedere interventi che non causano lesioni durevoli, sono consentiti dalle disposizioni vigenti','S, NA, NB, NC, -','(*) In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate. Le valutazioni ABM possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità.',38);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,197,1112,'PROCEDURE D''ALLEVAMENTO','Trasporto di vitelli neonati','Reg. 1/2005 All I, capo I, punto 2 lett. d) ed e) "2. Gli animali che presentino lesioni o problemi fisiologici ovvero patologie non vanno considerati idonei al trasporto,in particolare se: d) sono mammiferi neonati il cui ombelico non è ancora completamente cicatrizzato; e) sono [...] vitelli di meno di dieci giorni, a meno che non siano trasportati per percorsi inferiori a 100 km. Reg. 1/2005 All I, capo VI, punto 1.9 Criteri minimi per talune specie durante i lunghi viaggi: "1.9 Salvo se accompagnati dalla madre, i lunghi viaggi sono consentiti [...] per gli animali domestici delle specie bovina [...] soltanto se: - i vitelli hanno più di quattordici giorni di età." È vietato il trasporto di vitelli bovini/bufalini neonati prima che l''orifizio (anello)ombelicale sia completamente cicatrizzato. Poiché le strutture ombelicali sono molteplici e la loro cicatrizzazione avviene in tempi diversi, è necessario constatare che l''orifizio ombelicale esterno (prima porta d''ingresso di eventuali agenti patogeni e unica struttura valutabile ad un esame ispettivo) sia completamene cicatrizzato; evento che avviene di norma non prima di 10 giorni d''età. Pertanto, i vitelli all''arrivo in azienda o in partenza da essa devono avere più di 10 giorni di vita e l''ombelico esterno completamente cicatrizzato. Infine, in caso di viaggi superiori a 100 km, è vietato trasportare vitelli di età inferiore ai 10 giorni. Si considera non adeguato: Presenza di uno o più vitelli trasportati prima che la cicatrizzazione dell''ombelico sia completa e, in caso di viaggi superiori ai 100 km, presenza di uno o più vitelli di età inferiore ai 10 giorni Livello della non conformita'': NO Si considera adeguato: Tutti i vitelli sono trasportati dopo che la cicatrizzazione dell''ombelico è avvenuta completamente e, in caso di viaggi superiori ai 100 km, non prima dei 10 giorni d''età Livello della non conformita'': SI','39) I vitelli all''arrivo in azienda o in partenza da essa hanno la cicatrizzazione dell''ombelico esterno completa e, in caso di viaggi superiori ai 100 km, non hanno meno di 10 giorni d''età','S, NC, -','(*) In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate, le categorie e le marche auricolari dei soggetti coinvolti ed eventualmente i box/ricoveri dove sono alloggiati. L''ispezione del cordone ombelicale esterno può supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',39);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,198,1113,'PROCEDURE D''ALLEVAMENTO','Biosicurezza - Lotta agli infestanti','Reg. CE 852/2004 All. I parte A cap. II punto 4 f) "Gli operatori del settore alimentare che allevano, raccolgono o cacciano animali o producono prodotti primari di origine animale devono, se del caso, adottare misure adeguate per evitare la contaminazione da parte di animali e altri insetti nocivi." Verificare la presenza di azioni volte al controllo degli infestanti (mosche, roditori e parassiti).Se l''ambiente in cui vivono gli animali è ritenuto tale da non richiedere alcuna azione di controllo, il requisito si considera non applicabile . I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Si considera non adeguato: Completa insussistenza di qualsiasi azione volta al controllo degli infestanti o piani approssimativi (es. dichiarata la presenza di gatti come unico metodo di lotta ai roditori) Livello della non conformita'': no; NO Si considera adeguato: Presenza di procedure approssimative e non formalizzate (assenza di piani scritti) ma giudicate idonee al contenimento dei principali infestanti Livello della non conformita'': SI Si considera ottimale: Presenza di azioni strutturate volte al controllo degli infestanti (es. piani di derattizzazione con descrizione dei principi attivi utilizzati, schede di sicurezza e planimetria con localizzazione delle esche, definizione dei limiti critici e delle azioni conseguenti al loro superamento, la registrazione dei trattamenti e delle azioni correttive) Livello della non conformita'': Ottimale','40) Vengono messe in atto azioni preventive e vengono eseguiti interventi contro mosche, roditori e parassiti','S, NA, NB, NC, - , O','(*) Descrivere le condizioni rilevate (es. presenza di mosche o evidenza della presenza di roditori o loro tracce). Descrivere, nel caso siano presenti perché ritenute necessarie, le modalità e periodicità di disinfestazione, derattizzazione e pulizia dell''allevamento (presenza di una ditta esterna o procedure interne aziendali), annotare gli estremi di alcune registrazioni e delle postazioni ispezionate. In caso N.A. specificare il motivo.',40);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,199,1114,'ABM','Stato di nutrizione misurato tramite body condition score (BCS)','Valutare la condizione corporea degli animali, utilizzando una scala di body condition score (BCS) da 1 a 5 (1 = molto magro; 5 = molto grasso). Sono da registrare gli animali molto magri con BCS inferiore a 2, ovvero in evidente stato di dimagramento: prominenza delle tuberosità ossee,termine dei processi trasversi delle vertebre lombari ben visibile, legamenti sacro-iliaco e sacro-ischiatico privi di copertura adiposa ed assenza di grasso nell''incavo della coda. La valutazione del BCS è maggiormente informativa se eseguita su vitelli di età superiore alle 8 settimane, ma questo non esclude l''osservazione dello stato di nutrizione anche dei soggetti più giovani. Più dell''8% di animali con BCS minore di 2 Livello della non conformita'': Insufficiente Tra il 3% e l''8% di animali con BCS minore di 2 Livello della non conformita'': Adeguato Meno del 3% di animali con BCS minore di 2 Livello della non conformita'': Ottimale','41) Animali con BCS oltre i limiti','A, I, -, O','(*) Registrare il numero di animali con BCS oltre i limiti e il numero di animali controllati',41);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,200,1115,'ABM','Pulizia degli animali','Valutare la condizione di pulizia degli animali; per ogni animale considerare l''osservazione di un solo fianco. Più del 20% di animali sporchi Livello della non conformita'': Insufficiente Tra il 10% e il 20% di animali sporchi Livello della non conformita'': Adeguato Meno del 10% di animali sporchi Livello della non conformita'': Ottimale','42) Grado di pulizia del corpo degli animali','A, I, -, O','(*) Registrare il numero di animali sporchi e il numero di animali controllati',42);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,201,1116,'ABM','Patologie respiratorie negli animali arrivati da almeno 8 giorni','Valutare il numero di animali, tra quelli arrivati in allevamento da almeno 8 giorni (fino alla macellazione), che presentano patologia respiratoria lieve (scolo oculo-congiuntivale di almeno 3 cm, espettorato di tipo mucoso, lievi alterazioni dei caratteri del respiro, tosse frequente) o grave (alterazione dei caratteri del respiro, dispnea, depressione del sensorio). Un animale con forma respiratoria grave viene considerato come 3 animali con forma lieve. Più del 25% di animali con patologie respiratorie lievi Insufficiente Tra il 10% e il 25% di animali con patologie respiratorie lievi Adeguato Meno del 10% di animali con patologie respiratorie lievi Ottimale','43) Animali (arrivati in allevamento da almeno 8 giorni) con evidenti segni di patologia respiratoria','A, I, -, O','(*) Registrare il numero di animali con patologia in forma grave o lieve, il gruppo e il numero di animali controllati',43);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,202,1117,'ABM','Patologie gastro- enteriche negli animali arrivati da almeno 8 giorni','Valutare il numero di animali, tra quelli arrivati in allevamento da almeno 8 giorni (fino alla macellazione), chepresentano regione perineale sporca per diarrea o meteorismo. La consistenza delle feci e la zona perianale imbrattata sonoindicatori della presenza di diarree, mentre il rumine prominente ed il ventre teso sono indicatoridella presenza di meteorismo. Più del 15% di animali con patologie gastro-enteriche Livello della non conformita'': Insufficiente Tra il 5% e il 15% di animali con patologie gastro-enteriche Livello della non conformita'': Adeguato Meno del 5% di animali con patologie gastro-enteriche Livello della non conformita'': Ottimale','44) Animali con evidenti segni di patologia gastro-enterica','A, I, -, O','(*) Registrare il numero di animali con patologia in forma grave o lieve, il gruppo e il numero di animali controllati.',44);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,203,1118,'ABM','Zoppie','Gli animali possono essere considerati affetti da zoppia se presentano almeno uno degli indicatori seguenti: - indicatori da rilevare con l''animale in movimento: riluttanza a caricare il peso su un piede, irregolare ritmo di appoggio dei piedi nella falcata, peso non caricato in maniera uniforme sui 4 arti; - indicatori da rilevare con l''animale fermo: presenza di un piede in riposo (minore o nessun peso caricato), spostamento continuo del peso da un piede all''altro (stepping) o movimenti ripetuti sullo stesso piede, appoggio sul bordo di un gradino. Più del 5% di animali zoppi Livello della non conformita'': Insufficiente Tra 2% e 5% di animali zoppi Livello della non conformita'': Adeguato Meno del 2% di animali zoppi Livello della non conformita'': Ottimale','45) Animali con evidenti segni di zoppia','A, I, -, O','(*) Registrare il numero di animali con zoppia, il gruppo e il numero di animali controllati.',45);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (44,204,1119,'ABM','Mortalità annuale dei vitelli (0-6 mesi di vita)','Considerare il numero di vitelli bovini/bufalini morti spontaneamente in allevamento, macellati d''urgenza (MSU) e eutanasizzati tra il 2° e il 180° giorno di vita e il numero totale di vitelli circolanti* negli ultimi 12 mesi (escludendo quindi i vitelli nati morti o morti nelle prime 24 ore). *Per capi circolanti si intendono i soggetti con età compresa tra 0 e 6 mesi presenti almeno 1 giorno in allevamento negli ultimi 12 mesi (esclusi i nati morti o morti nelle prime 24 ore). Nel caso di allevamenti da latte e linea vacca-vitello (senza ingresso di vitelli provenienti da altri allevamenti), il denominatore equivale al numero di vitelli nati vivi e vitali negli ultimi 12 mesi. Se necessario, il controllore può eseguire ulteriori approfondimenti in allevamento, per verificare che il risultato di mortalità estratto da BDN sia attendibile. Suggerimenti sulle informazioni da raccogliere/verificare sono i seguenti: n. vitelli nati negli ultimi 12 mesi (ovvero n. parti singoli e gemellari) .......... interparto medio in allevamento ........... stima della capacità riproduttiva dell''allevamento (in base al numero di bovine/bufale adulte, manze a termine di gravidanza, produzione latte/capo/gg, ecc.).......... n. vitelli nati morti o morti nelle prime 24 ore........... . n. vitelli morti dopo il secondo giorno ma prima di essere identificati VITELLI A CARNE BIANCA/INGRASSO Più del 7% ALLEVAMENTI DA LATTE (BOVINI/BUFALINI) Più del 10% LINEA VACCA-VITELLO Più del 12% Livello della non conformita'': Insufficiente VITELLI A CARNE BIANCA/DA INGRASSO Tra il 3% e 7% ALLEVAMENTI DA LATTE BOVINI Tra il 4% e 10% ALLEVAMENTI DA LATTE BUFALINI Tra il 5% e 10% LINEA VACCA-VITELLO Tra il 6% e 12% Livello della non conformita'': Adeguato VITELLI A CARNE BIANCA/DA INGRASSO Meno del 3% ALLEVAMENTI DA LATTE BOVINI Meno del 4% ALLEVAMENTI DA LATTE BUFALINI Meno del 5% LINEA VACCA-VITELLO Meno del 6%  Livello della non conformita'': Ottimale','46) Mortalità annuale animali adulti','A, I, -, O','(*) Registrare il numero di animali morti e la tipologia di morte',46);


