-- FUNCTION: public.get_chiamata_ws_insert_ba_v7_gallus(text, text, integer)

-- DROP FUNCTION IF EXISTS public.get_chiamata_ws_insert_ba_v7_gallus(text, text, integer);

CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_gallus(
	_username_ws_bdn text,
	_password_ws_bdn text,
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE STRICT PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
BEGIN

  msg := concat('<?xml version="1.0" encoding="UTF-8"?>
<S:Envelope xmlns:S="http://schemas.xmlsoap.org/soap/envelope/">','
',
(select get_chiamata_ws_insert_ba_sa_header from get_chiamata_ws_insert_ba_sa_header(_username_ws_bdn, _password_ws_bdn)),'
',
'<S:Body>
      <ns2:insertControlliallevamentiBA xmlns:ns2="http://ws.izs.it">','
      ',(select get_chiamata_ws_insert_ba_v7_gallus_controlli_allevamenti from get_chiamata_ws_insert_ba_v7_gallus_controlli_allevamenti(_id)),'
      ',(select get_chiamata_ws_insert_ba_v7_gallus_dettaglio_checklist from get_chiamata_ws_insert_ba_v7_gallus_dettaglio_checklist(_id)),'
      </ns2:insertControlliallevamentiBA>
      </S:Body> 
      </S:Envelope>');
    
    
 RETURN msg;
 END;
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_gallus(text, text, integer)
    OWNER TO postgres;


CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_gallus_controlli_allevamenti(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
tag_preavviso text;
BEGIN
select 
concat('<controlliallevamenti>
	  <caId>0</caId>
           <dtControllo>', to_char(data_controllo, 'YYYY-MM-DD"T"00:00:00.000'),'</dtControllo>
            <aslCodice>',codice_asl,'</aslCodice>
            <aziendaCodice>',codice_azienda,'</aziendaCodice>
            <speCodice>',bdn.codice_specie_chk_bns,'</speCodice>
            <allevIdFiscale>',id_fiscale_allevamento,'</allevIdFiscale>
	    <tipoProdCodice>',tipo_produzione_ba,'</tipoProdCodice>
	    <tipoAllevCodice>',tipo_allevamento_codice,'</tipoAllevCodice>
	    <orientamentoCodice>',codice_orientamento_ba,'</orientamentoCodice>
	    <flagFaseProduttiva>',galline.fase_produttiva,'</flagFaseProduttiva>	    
            <ocCodice>',bdn.occodice,'</ocCodice>
            <flagEsito>',galline.esito_controllo,'</flagEsito>
            <flagVitelli>N</flagVitelli> 
            <flagExtrapiano>',galline.extrapiano,'</flagExtrapiano>
            <detenIdFiscale>',id_fiscale_detentore,'</detenIdFiscale>
            <flagPreavviso>',bdn.flag_preavviso,'</flagPreavviso>
	    ',case when bdn.flag_preavviso <> 'N' then concat('<dataPreavviso>',to_char(bdn.data_preavviso, 'YYYY-MM-DD"T"00:00:00.000'),'</dataPreavviso>') else '' end,'            
            <primoControllore>',galline.nome_controllore,'</primoControllore>
            <tipoControllo>BA</tipoControllo>
            <flagCopiaCheckList>',bdn.flag_checklist,'</flagCopiaCheckList>
	    ',case when galline.esito_controllo = 'N' then concat(
	    '<sanzBloccoMov>',galline.sanz_blocco,'</sanzBloccoMov>
            <sanzAmministrativa>',galline.sanz_amministrativa,'</sanzAmministrativa>
            <sanzSequestro>',galline.sanz_sequestro,'</sanzSequestro>
            <sanzAbbattimentoCapi>',galline.sanz_abbattimento,'</sanzAbbattimentoCapi>
            <sanzInformativa>',galline.sanz_informativa,'</sanzInformativa>
            <sanzAltro>',galline.sanz_altro,'</sanzAltro>
            ',case when galline.sanz_altro <> '0' then concat('<sanzAltroDesc>',galline.sanz_altro_desc,'</sanzAltroDesc>')
	    else '' end,'
            <noteControllore>',galline.note_controllore,'</noteControllore>
            <noteDetentore>',galline.note_proprietario,'</noteDetentore>')
	    else '' end,'             
	    ',case when galline.esito_controllo <> 'A' then 
	    concat('<flagEvidenze>',galline.evidenze,'</flagEvidenze>')
	    else '' end,
	    case when galline.evidenze = 'S' then 
	    concat('<evidenzaIr>',galline.evidenze_ir,'</evidenzaIr>
            <evidenzaSv>',galline.evidenze_sv,'</evidenzaSv>
            <evidenzaSa>',galline.evidenze_tse,'</evidenzaSa>')
            else '' end,'
            <flagCondizionalita>',galline.appartenente_condizionalita,'</flagCondizionalita>
             ',case when galline.data_chiusura_relazione <> '' then concat('<dataChiusura>',concat(galline.data_chiusura_relazione,'T00:00.000'),'</dataChiusura>')
	    else '' end,'    
         </controlliallevamenti>') into msg
from bdn_cu_chiusi_benessere_animale bdn
left join bdn_checklist_v7_galline galline on galline.id_chk_bns_mod_ist = bdn.id_chk_bns_mod_ist
where bdn.id_chk_bns_mod_ist = _id;

     
    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_gallus_controlli_allevamenti(integer)
    OWNER TO postgres;

-- FUNCTION: public.get_chiamata_ws_insert_ba_v7_gallus_dettaglio_checklist(integer)

-- DROP FUNCTION IF EXISTS public.get_chiamata_ws_insert_ba_v7_gallus_dettaglio_checklist(integer);

CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_gallus_dettaglio_checklist(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
BEGIN

select 
concat(
'<dettagliochecklist>
	<clbId>0</clbId>
	<flagVitelli>N</flagVitelli>
	<critCodice>',galline.criteri_utilizzati,'</critCodice>
	',
	case when galline.criteri_utilizzati = 'CF13' then concat('<criterioControlloAltro>',galline.criteri_utilizzati_altro_descrizione,'</criterioControlloAltro>
	') else '' end,
	case when galline.esito_controllo = 'N' then concat('<flagIntenzionalita>',galline.intenzionalita,'</flagIntenzionalita>
	') else '' end,
	'<nomeRappresentante>',galline.nome_proprietario,'</nomeRappresentante>
	',
	'<tipoAllegato>',bdn.tipo_allegato,'</tipoAllegato>
	',
	'<numUovaAnno>',galline.num_uova_anno,'</numUovaAnno>
	',
	case when galline.esito_controllo <> 'A' then 
		concat('<flagSelImbal>',galline.selezione_imballaggio,'</flagSelImbal>
	') else '' end, 

	case when galline.selezione_imballaggio = 'S' then 
		concat('<indirizzoSelImbal >',galline.selezione_imballaggio_destinazione,'</indirizzoSelImbal>
		') else '' end,
	'<mutaInAllev>',galline.muta,'</mutaInAllev>
	',
	case when galline.esito_controllo = 'N' then 
		concat('<prescrizioni>',galline.prescrizioni_descrizione,'</prescrizioni>
		') else '' end, 
		case when galline.prescrizioni_descrizione <> '' then 
			concat('<dataScadPrescrizioni>',concat(galline.data_prescrizioni,'T00:00:00.000'),'</dataScadPrescrizioni>
			        <flagPrescrizioneEsitoBa>',galline.eseguite_prescrizioni,'</flagPrescrizioneEsitoBa>
			        ',	
				case when galline.data_verifica <> '' then 
					concat('<dataVerificaBa>',concat(galline.data_verifica,'T00:00:00.000'),'</dataVerificaBa>
						<secondoControllore>',galline.nome_controllore,'</secondoControllore>
						<nomeRappresentanteVer>',galline.nome_proprietario_prescrizioni_eseguite,'</nomeRappresentanteVer>
						'
					       ) else '' end
		               --) end
		      )else '' end,
	'<parametro>2024</parametro>
	<numClb>',galline.num_checklist,'</numClb>
	',
	case when galline.esito_controllo <> 'A' then concat('<ibridoRazza>',galline.ibrido_razza,'</ibridoRazza>
	') else '' end, 
	case when galline.esito_controllo <> 'A' then concat('<veterinario>',galline.veterinario_ispettore,'</veterinario>
	') else '' end, 
	case when galline.esito_controllo <> 'A' then concat('<presenzaManuale>',galline.presenza_manuale,'</presenzaManuale>
	') else '' end,
	case when galline.esito_controllo <> 'A' then (select get_chiamata_ws_insert_ba_v7_requisiti from public.get_chiamata_ws_insert_ba_v7_requisiti(_id)) else '' 
	END,'
            </dettagliochecklist>
            ') into msg
from bdn_cu_chiusi_benessere_animale bdn
left join bdn_checklist_v7_galline galline on galline.id_chk_bns_mod_ist = bdn.id_chk_bns_mod_ist
where bdn.id_chk_bns_mod_ist = _id;
   

    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_gallus_dettaglio_checklist(integer)
    OWNER TO postgres;

    
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,205,1218,'PERSONALE','Numero di addetti che si occupano degli animali','146/2001 All. Personale Punto 1 "Gli animali devono essere accuditi da un numero sufficiente di addetti" Il numero di addetti deve essere sufficiente per garantire la salute e il benessere di tutti gli animali in allevamento: verificare rapporto n. addetti - n. animali. Vista la variabilità dei sistemi di allevamento e del livello di automazione degli impianti (gabbie modificate, sistemi alternativi in voliera o a terra), la valutazione dovrà essere fatta considerando i singoli casi. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Numero non sufficiente di addetti: SIST. ALTERNATIVI: indicativamente un operatore per più di 35.000 animali GABBIE MODIFICATE: indicativamente un operatore per più di 50.000 animali Livello di non conformità: no; NO Numero sufficiente di addetti: SIST. ALTERNATIVI: indicativamente un operatore per 15.000 - 35.000 animali GABBIE MODIFICATE: indicativamente un operatore per 30.000 - 50.000 animali Livello di non conformità: SI Numero ottimale di addetti: SIST. ALTERNATIVI: indicativamente un operatore per meno di 15.000 animali GABBIE MODIFICATE: indicativamente un operatore per meno di 30.000 animali Livello di non conformità: Ottimale','1) Gli animali sono accuditi da un numero sufficiente di addetti','S, NA, NB, NC, -,O','(*) Indicare il numero degli addetti rapportato al numero di animali presenti',1);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,206,1219,'PERSONALE','Formazione degli addetti','146/2001 All. Personale Punto 1 "Gli addetti devono avere adeguate capacità, conoscenze e competenze professionali" Valutare le competenze complessive del personale addetto agli animali, che possono essere sia di origine pratica (perché fornite dall''esperienza) sia di origine teorica (es. corso di formazione/titolo di studio). Si considera accettabile una lunga esperienza pratica nel settore oppure, in assenza di essa (es. giovani allevatori), il conseguimento di titoli di studio inerenti (diploma o laurea in agraria, veterinaria e lauree brevi simili) o la partecipazione a corsi di formazione specifici sul benessere animale. Per assegnare un giudizio ottimale è necessario possedere entrambi i requisiti: esperienza prolungata e titolo di studio/formazione specifica. I corsi di formazione o aggiornamento dovrebbero essere ripetuti con cadenza regolare durante il periodo lavorativo (almeno 1 corso ogni 3 anni). Se l''allevamento è seguito da più operatori, si considera sufficiente la partecipazione ai corsi di formazione anche di un solo addetto (sia esso il titolare o il dipendente assunto). I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Capacità e conoscenze non adeguate: esperienza indicativamente minore di 10 anni e nessun corso di formazione sul benessere animale Livello di non conformità: no; NO Capacità e conoscenze adeguate: esperienza indicativamente di almeno 10 anni e nessun corso di formazione sul benes animale/titolo di studio (o combinazione opposta) Livello di non conformità: SI Capacità e conoscenze ottimali: esperienza indicativamente di almeno 10 anni con titolo di studio o corso di formazione attinente, seguito negli ultimi 3 anni Livello di non conformità: Ottimale','2) Il personale addetto agli animali dispone di capacità e conoscenze adeguate e ha ricevuto istruzioni pratiche sulle pertinenti diposizioni normative.','S, NA, NB, NC, -,O',' (*) Indicare, presenza di istruzioni pratiche scritte correttamente comunicate ed estremi dei corsi di formazione frequentati, con periodicità e ricaduta a livello aziendale, ecc',2);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,207,1220,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Numero di ispezioni','146/2001 All. Controllo Punto 2 "Tutti gli animali tenuti in sistemi di allevamento, il cui benessere richieda un''assistenza frequente dell''uomo, sono ispezionati almeno una volta al giorno. Gli animali allevati o custoditi in altri sistemi sono ispezionati a intervalli sufficienti al fine di evitare sofferenze." D. L. vo 267/2003, allegato A, punto 1 "Tutte le galline ovaiole devono essere ispezionate dal proprietario o detentore almeno una volta al giorno" Il numero di ispezioni che gli addetti compiono giornalmente è importante da valutare in quanto indicativo dell''attenzione che essi prestano al gruppo di animali e quindi della tempestività con cui eventuali problemi (anche di benessere) possono essere individuati. Il detentore o gli addetti dovrebbero effettuare un''attenta osservazione di tutti gli animali almeno una volta al giorno. L''operazione dovrebbe essere svolta esclusivamente con questo intento al fine di porre la massima attenzione nell''individuare possibili pericoli per il benessere e qualsiasi anomalia sanitaria, comportamentale o fisiologica degli animali, consentendo un rapido intervento risolutivo. La situazione ottimale si ha quando, oltre ad osservare, il proprietario o detentore scrive e registra i segni clinici o le lesioni riscontrate, oppure verifica sistematicamente i dati forniti dai sistemi automatici di misurazione delle attività dell''animale registrati su supporto informatico. Per assegnare il giudizio conforme, il veterinario ufficiale potrà avvalersi di quanto riportato dalle Procedure Operative Standar aziendali o di quanto riferito dall''allevatore. Il giudizio ottimale verrà invece assegnato in presenza di scritte/computerizzate delle problematiche eventualmente riscontrate durante le ispezioni. Meno di 1 ispezione/giorno Livello di non conformità: no; NO 1 o più ispezioni/giorno Livello di non conformità: SI 2 o più ispezioni/giorno e registrazione scritta o computerizzata delle eventuali problematiche riscontrate Livello di non conformità: Ottimale','3) Gli animali presenti nello stabilimento sono ispezionati almeno una volta al giorno','S, NA, NB, NC, -,O','(*) Indicare il numero di ispezioni e le modalità con le quali il dato viene raccolto.',3);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,208,1221,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Illuminazione per l''ispezione','146/2001 All. Controllo Punto 3 "Per consentire l''ispezione completa degli animali in qualsiasi momento, deve essere disponibile un''adeguata illuminazione fissa o mobile" L''intensità luminosa e la durata del periodo di luce dovrebbero permettere agli operatori di ispezionare adeguatamente tutti gli animali nell''arco della giornata. In aggiunta un''illuminazione fissa o mobile dovrebbe essere presente per consentire l''ispezione degli animali in qualsiasi momento, anche di notte, cosicché gli addetti possano intervenire in modo attento e senza rischi. Assenza di illuminazione artificiale per l''ispezione Livello di non conformità: no; NO Presenza di illuminazione artificiale corretta e funzionante Livello di non conformità: SI','4)È disponibile un''adeguata illuminazione che consente l''ispezione completa degli animali.','S, NA, NB, NC, -','(*) Descrivere le modalità di illuminazione durante le ore diurne o notturne',4);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,209,1222,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Trattamento degli animali malati o feriti e abbattimento','146/2001 All. Controllo Punto 4 "Gli animali malati o feriti devono ricevere immediatamente un trattamento appropriato e, qualora un animale non reagisca alle cure in questione, deve essere consultato un medico veterinario. Ove necessario gli animali malati o feriti vengono isolati in appositi locali muniti, se del caso, di lettiere asciutte o confortevoli." Reg. 1099/2009 Considerando 12 "La soppressione di animali da reddito che versino in condizioni di grave sofferenza, in assenza di soluzioni economicamente valide atte ad alleviare il dolore, è un dovere morale" Per avere evidenza che gli animali malati o feriti ricevano un trattamento adeguato (non necessariamente di tipo farmacologico) il veterinario ufficiale deve verificare, tramite intervista, che gli addetti siano stati adeguatamente istruiti su come gestire i casi d malattia e la presenza di ferite. La verifica dell''immediato trattamento si assolve con due possibili condizioni, la prima è che l''animale malato o ferito possa essere spostato o sia già stato spostato in un reparto infermeria (eventualmente isolato), la seconda è che l''animale malato rimasto all''interno del gruppo abbia ricevuto un trattamento appropriato (ad esempio di tipo farmacologico, verificabile attraverso le prescrizioni e le relative registrazioni, o di tipo eutanasico). La verifica del corretto trattamento dell''animale (compreso l''abbattimento) può avvenire in via indiretta mediante il controllo dell''eventuale presenza di animali morti da lungo tempo (es. mummificati). Tale rilievo è indicativo di un mancato controllo giornaliero degli animali, al fine di verificare la necessità di sottoporli ad un trattamento appropriato. Presenza di animali malati o feriti gestiti in maniera non adeguata Livello di non conformità: no; NO Presenza di animali malati o feriti gestiti in maniera adeguata Livello di non conformità: SI','5)Gli animali malati o feriti ricevono immediatamente un trattamento appropriato, anche in caso sia necessario ricorrere all''abbattimento','S, NA, NB, NC, -','(*) Indicare se sono presenti animali mal gestiti, ammalati o feriti e le modalità di detenzione; se l''infermeria o altro settore sono, all''occorrenza, adattabili ad accogliere ed isolare animali deboli, malati o feriti. Indicare se all''interno delle gabbie sono presenti animali morti da lungo tempo.',5);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,210,1223,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Consultazione del Medico Veterinario','146/2001 All. Controllo Punto 4 "[...]qualora un animale non reagisca alle cure in questione, deve essere consultato un medico veterinario." Nello specifico dell''allevamento avicolo, tale riferimento legislativo dev''essere inteso come problematica sanitaria di gruppo e non del singolo animale Il veterinario ufficiale potrà verificare la rispondenza al requisito attraverso l''ispezione documentale di: - registro delle presenze - ricette veterinarie e registro dei trattamenti o registrazioni in Vetinfo - eventuali verbali di intervento rilasciati dal veterinario Non viene consultato un medico veterinario in caso di necessità Livello di non conformità: no; NO Viene consultato un medico veterinario in caso di necessità Livello di non conformità: SI','6)Un medico veterinario viene consultato in caso di necessità','S, NA, NB, NC, -',' (*) Annotare l''eventuale documentazione visionata, in grado o meno di confermare la conformità al requisito (registro presenze, verbali di intervento etc.)',6);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,211,1224,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Dispositivi di ispezione','267/2003 All. A Punto 6 "Gli impianti che comportano più piani di gabbie devono essere provvisti di dispositivi o di misure adeguate che consentano ispezionare direttamente e agevolmente tutti i piani, e che facilitino il ritiro delle galline." In caso di locali di allevamento che ospitano più piani di gabbie, il veterinario ufficiale deve verificare che siano presenti dei dispositivi adeguati per permettere l''ispezione di ogni parte dell''allevamento, incluse le gabbie collocate sui piani più alti. Non sono presenti dispositivi o misure adeguate per ispezionare direttamente e agevolmente tutti i piani e che facilitino il ritiro delle galline Livello di non conformità: no; NO Sono presenti dispositivi o misure adeguate per ispezionare direttamente e agevolmente tutti i piani e che facilitino il ritiro delle galline Livello di non conformità: SI','7)Sono presenti adeguati dispositivi che consentono di ispezionare tutti i piani','S, NA, NB, NC, -','(*) Annotare le caratteristiche degli eventuali dispositivi utilizzati',7);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,212,1225,'TENUTA DEI REGISTRI (Registrazione dei dati)','Tenuta delle registrazioni dei dati','146/2001 All. Registrazione Punti 5-6. (i riferimenti al D.P.R. 8 febbraio 1954, n. 320, attualmente abrogato, si intendono fatti ai corrispondenti articoli dei decreti legislativi 5 agosto 2022, n.134 e n.136 ) "Le mortalità sono denunciate ai sensi del D.P.R. 8 febbraio 1954 n. 320. I registri sono conservati per un periodo di almeno tre anni e sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta." Dlgs 134 art. 9 comma 10 "L''operatore provvede alla registrazione in BDN di tutte le informazioni inerenti agli animali identificati singolarmente, alle partite, agli insiemi e ai gruppi di animali, nonché agli eventi che li riguardano, con i tempi di cui al presente articolo e con i modi previsti nel manuale operativo anche ai fini della generazione del registro della propria attività nella stessa BDN. Tale registro sostituisce qualsiasi altro registro aziendale cartaceo o su altro supporto concernente l''identificazione e registrazione degli animali." D. Lgs. 136/2022, art. 6 "Notifica e comunicazione delle malattie all''Autorità competente"; art. 11 comma 4 "gli operatori nel caso in cui non siano ancora disponibili nel sistema «ClassyFarm.it»,le apposite funzionalità informatiche per la tipologia di stabilimento di cui s responsabili, adempiono agli obblighi del presente articolo tenendo in modalità elettronica o cartacea, per cinque anni documentazione riguardante almeno le informazioni di cui all''allegato 3 del presente decreto"; Allegato 3 "...individuazione registrazione e segnalazione delle mortalità anomale" Verificare in BDN la presenza del registro di carico e scarico e la corretta registrazione delle informazioni, al fine di valutare se si siano verificate eventuali mortalità anomale. Nel caso si siano verificate mortalità anomale, verificare che siano state correttamente individuate, registrate e segnalate. Si considera non conforme: assenza in BDN del registro di carico e scarico o mancata o non corretta registrazione delle informazioni e/ o evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': NO Si considera conforme: presenza in BDN del registro di carico e scarico e corretta registrazione delle informazioni e mancata evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': SI','8) In BDN è presente il registro di carico e scarico, le informazioni vengono correttamente registrate e non sono presenti episodi di mortalità anomale non registrate','S, NC, -','(*) Annotare eventuali rilievi di mortalità anomale non registrate o di mancanza delle registrazioni delle informazioni in BDN',8);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,213,1226,'TENUTA DEI REGISTRI (Registrazione dei dati)','Tenuta del registro dei trattamenti farmacologici','146/2001 All. Registrazione Punti 5-6. "Il proprietario o il custode ovvero il detentore degli animali tiene un registro dei trattamenti terapeutici effettuati. La registrazione e le relative modalità di conservazione sono effettuate secondo quanto previsto dal decreto legislativo 27 gennaio 1992, n. 119 successive modificazioni ed integrazioni e dal decreto legislativo 4 agosto 1999, n. 336. I registri sono conservati per un periodo di almeno tre anni e sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta" Sono presenti le registrazioni informatizzate dei trattamenti su Vetinfo Farmacosorveglianza, ai sensi della Legge 20 novembre 2017, n. 167 e s.m.i. Assenza delle registrazioni in Vetinfo farmacosorveglianza o non corretta registrazione dei trattamenti. Livello della non conformita'': NO Corretta registrazione dei trattamenti in Vetinfo farmacosorveglianza. Livello della non conformita'': SI','9) Le registrazioni in Vetinfo farmacosorveglianza vengono effettuate nei tempi stabiliti e i trattamenti sono regolarmente prescritti da un medico veterinario.','S, NC, -','(*) Segnalare i trattamenti controllati a campione',9);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,214,1227,'LIBERTA'' DI MOVIMENTO','Spazio disponibile','146/2001 All. Libertà di movimento punto 7; "La libertà di movimento propria dell''animale, in funzione della sua specie e secondo l''esperienza acquisita e le conos scientifiche, non deve essere limitata in modo tale da causargli inutili sofferenze o lesioni. Allorché continuamente o regolarmente legato, incatenato o trattenuto, l''animale deve poter disporre di uno spazio adeguato alle sue esigenze fisiologiche ed etologiche, secondo l''esperienza acquisita e le conoscenze scientifiche." 267/2003 All. B, Punto 1, c)"Disposizioni applicabili ai sistemi alternativi. [...] c) avere un coefficiente di densità non superiore a 9 galline ovaiole per metro quadrato di zona utilizzabile. 267/2003 All. D, punto 1, a), 1"Disposizioni applicabili all''allevamento in gabbie modificate. 1. A decorrere dalla data di entrata in vigore del presente decreto, tutte le gabbie di cui al presente allegato devono: a) consentire alle galline ovaiole di disporre: 1) di almeno 750 centimetri quadrati di superficie della gabbia per ogni gallina ovaiola, di cui 600 centimetri quadrati di superficie utilizzabile, fermo restando che l''altezza della gabbia, diversa dall''altezza al di sopra della superficie utilizzabile, non deve essere inferiore a 20 cm in ogni punto e che la superficie totale di ogni gabbia non può essere inferiore a 2000 centime quadrati;" Lo spazio a disposizione di ogni gallina deve essere sufficiente per consentirle un''adeguata libertà di movimento e deve essere tale da non causarle inutili sofferenze o lesioni, in tutte le tipologie di allevamento, ovvero: GABBIE MODIFICATE - ogni gallina ovaiola dispone di almeno 750 cm2 di superficie della gabbia, di cui 600 cm2 di supe utilizzabile, fermo restando che l''altezza della gabbia diversa dall''altezza al di sopra della superficie utilizzabile non deve esser inferiore a 20 cm in ogni punto e che la superficie totale di ogni gabbia non può essere inferiore a 2000 cm2 SISTEMI ALTERNATIVI - il coefficiente di densità non è superiore a 9 galline ovaiole per m2 di zona utilizzabile Superficie disponibile per ogni gallina inadeguata / Densità superiore ai limiti previsti Livello di non conformità: no; NO; Superficie disponibile per ogni gallina adeguata / Densità pari o inferiore ai limiti previsti<br>Livello di non conformità: SI','10) Lo spazio a disposizione di ogni animale è sufficiente a consentirgli un''adeguata libertà di movimento ed è tale da non causargli inutili sofferenze o lesioni','S, NA, NB, NC, -','(*)in caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. densità non conforme ai limiti previsti; animali senza adeguato spazio). Riportare la metratura del capannone rispetto al numero di animali presenti.',10);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,215,1228,'EDIFICI E LOCALI DI STABULAZIONE','Rimozione degli animali morti','267/2003All. APunto 4. "Occorre eliminare quotidianamente le galline morte" La rispondenza al requisito può essere verificata dal veterinario ufficiale attraverso l''osservazione di animali morti da tempo (più di un giorno) all''interno dell''allevamento Le galline morte non sono rimosse quotidianamente Livello di non conformità: no; NO Le galline morte sono rimosse quotidianamente Livello di non conformità: SI','11) Le galline morte sono rimosse quotidianamente.','S, NA, NB,','(*)In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. numero e stato di decomposizione di animali morti da tempo all''interno del capannone)',11);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,216,1229,'EDIFICI E LOCALI DI STABULAZIONE','Rimozione delle deiezioni','267/2003All. A punto 4. "Occorre eliminare con la necessaria frequenza le deiezioni" La rimozione non regolare delle deiezioni può essere un pericolo per il benessere dell''animale, in grado di peggiorare la qualità dell''aria e favorire la diffusione di patologie. La normativa definisce in modo generale la necessità di eliminare con la necessaria frequenza le deiezioni: tale prescrizione deve essere interpretata sulla base della tecnologia di allevamento presente. E'' necessario chieder all''allevatore quante volte effettui la rimozione delle feci mediante l''utilizzo del nastro o del raschiatore e confrontarlo con la quantità di feci effettivamente presenti e soprattutto con la qualità dell''aria. In caso di allevamenti su lettiera permanente, es normalmente rimossa a fine ciclo, è necessario valutare le condizioni ambientali (qualità dell''aria) e la gestione della stessa (e lettiera eccessivamente umida) al momento della valutazione. Prima di assegnare il giudizio, il valutatore dovrà considerare tecnologia di allevamento (lettiera, nastri, raschiatori ...) in uso nel capannone e le condizioni ambientali al momento della valutazione (es. odore di ammoniaca, lettiera eccessivamente bagnata ...) Le deiezioni non sono rimosse/gestite con la frequenza necessaria Livello di non conformità: no; NO Le deiezioni sono rimosse con la frequenza necessaria / Lettiera permanente Livello di non conformità: SI','12) Le deiezioni sono rimosse/gestite con regolarità','S, NA, NB, NC, -','(*)Descrivere la tecnologia presente in allevamento. In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. frequenza di rimozione della lettiera, condizioni igieniche degli animali, qualità dell''aria, condizioni della lettiera)',12);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,217,1230,'EDIFICI E LOCALI DI STABULAZIONE','Condizioni generali di pulizia','267/2003All. A punto 4 "[...] Quando i locali sono occupati, tutte le superfici e le attrezzature devono essere mantenute in condizioni di pulizia soddisfacenti." Il veterinario ufficiale dovrà osservare le condizioni di pulizia generali del capannone e delle strutture e attrezzature eventualmente presenti (mangiatoie, abbeveratoi, posatoi, nidi ...) Le superfici e le attrezzature non presentano uno stato di pulizia soddisfacente Livello di non conformità: no; NO Le superfici e le attrezzature presentano uno stato di pulizia soddisfacente Livello di non conformità: SI','13) Le superfici e le attrezzature sono mantenute in condizioni di pulizia soddisfacenti','S, NA, NB, NC, -','(*)In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. condizioni igieniche dei locali e dei materiali di stabulazione). Le valutazioni ABM (pulizia animali) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',13);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,218,1231,'EDIFICI E LOCALI DI STABULAZIONE','Fabbricati e locali di stabulazione','146/2001 All. Fabbricati e locali di stabulazione punto 8-9. "8. I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati. 9. I locali di stabulazione e i dispositivi di attacco degli animali devono essere costruiti e mantenuti in modo che non vi siano spigoli taglienti o sporgenze tali da provocare lesioni agli animali." Tutti i materiali e le attrezzature utilizzati nei locali di stabulazione, nonché le superfici stesse, con cui gli animali possono venire a contatto non devono essere nocivi per gli animali e non devono avere spigoli taglienti o sporgenze in grado di provocare traumi o lesioni. Per verificare ciò, il veterinario ufficiale dovrà osservare e verificare l''eventuale presenza di superfici o attrezzature taglienti possibilmente nocive, oltre che di animali con lesioni compatibili con superfici taglienti o sporgenti. Contemporaneamente tutte queste attrezzature devono essere concepite, costruite e mantenute in modo tale da poter esse accuratamente pulite e disinfettate. Presenza di un solo requisitonon sufficiente Livello di non conformità: no; NO Tutti i requisitisono sufficienti Livello di non conformità: SI','14) Le superfici e le attrezzature con cui gli animali possono entrare in contatto non devono determinare traumi o lesioni e possono essere pulite e disinfettate','S, NA, NB, NC, -','(*) In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. spigoli taglienti o sporgenze potenzialmente lesivi per gli animali). Le valutazioni ABM (lesioni, ferite compatibili con le caratteristiche nocive delle strutture/attrezzature) possono supportare e dare un''evidenza della presenza di una eventuale non conformità',14);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,219,1232,'EDIFICI E LOCALI DI STABULAZIONE','Sistemi di allevamento','267/2003All. A punto 5. "I sistemi di allevamento devono essere concepiti in modo da evitare che le galline possano scappare." A seconda della tipologia di allevamento, devono essere prese misure efficaci per evitare che gli animali scappino. A tal proposito, il valutatore dovrà assicurarsi che non vi siano gabbie con parti rotte o porte aperte e che le recinzioni siano integre. I sistemi di allevamento consentono alle galline di scappare Livello di non conformità: no; NO I sistemi di allevamento non consentono alle galline di scappare Livello di non conformità: SI','15) I sistemi di allevamento sono concepiti in modo tale che le galline non possano scappare','S, NA, NB, NC, -','(*)In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es.gabbie con parti rotte o danneggiate, recinzioni non integre )',15);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,220,1233,'EDIFICI E LOCALI DI STABULAZIONE','Dimensioni delle gabbie e loro aperture','267/2003All. A punto 7. "La gabbia e le dimensioni della relativa apertura devono essere concepite in modo tale che una gallina adulta possa essere ritirata senza inutili sofferenze o senza essere ferita." Per verificare la rispondenza al requisito è necessario, in caso di dubbio, fare estrarre qualche animale dalla gabbia, controllando inoltre che le aperture non presentino superfici taglienti o sporgenti in grado di ferire gli animali.Come ausilio si possono osservare le galline all''interno delle gabbie, verificando che non presentino segni o lesioni compatibili con una difficile estrazione dalla gabbia stessa. La gabbia e la relativa apertura non consentono di ritirare facilmente una gallina senza ferirla e/o causarle sofferenza Livello di non conformità: no; NO La gabbia e la relativa apertura consentono di ritirare facilmente una gallina senza ferirla e/o causarle sofferenza Livello di non conformità: S','16) L''apertura delle gabbie è concepita in modo tale che una gallina possa essere ritirata senza essere ferita e senza sofferenze','S, NA, NB, NC, -','(*)In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. aperture delle gabbie inadeguate per dimensioni o per presenza di parti che possano ferire gli animali). Le valutazioni ABM (lesioni cutanee) possono supportare e dare un''evidenza della presenza di una eventuale non conformità',16);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,221,1234,'EDIFICI E LOCALI DI STABULAZIONE','Infermeria','146/2001 All. Controllo punto 4 "Ove necessario gli animali malati o feriti vengono isolati in appositi locali muniti, se del caso, di lettiere asciutte o confortevoli." Devono essere presenti settori dedicati agli animali malati o feriti, facilmente raggiungibili o allestibili nell''immediato in caso necessità; essi devono essere chiaramente identificati o identificabili, con presenza se del caso di lettiera asciutta e acqua e alimento in quantità sufficiente (possibilmente ad libitum). Per garantire un adeguato comfort agli animali, all''interno di questi locali la densità dev''essere ridotta. Nessuno spazio facilmente allestibile e identificabile Livello di non conformità: no; NO Spazio facilmente allestibile ed identificabile Livello di non conformità: SI','17) In caso di necessità, gli animali possono essere isolati in appositi locali muniti, se del caso, di lettiera asciutta','S, NA, NB, NC, -','(*) Descrivere, se del caso, le caratteristiche dello spazio di isolamento-infermerià',17);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,222,1235,'EDIFICI E LOCALI DI STABULAZIONE','Temperatura e umidità relativa','146/2001 All. Fabbricati e locali di stabulazione Punto 10 "10. La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas devono essere mantenute entro limiti non dannosi per gli animali." Temperatura e umidità relativa sono parametri strettamente correlati al sistema di ventilazione, pertanto il veterinario ufficiale dovrà considerare la presenza e l''adeguatezza di quest''ultimo nell''esprimere il giudizio La ventilazione di un ricovero può essere naturale o forzata; nel primo caso la circolazione dell''aria all''interno della struttura assicurata dai movimenti convettivi di aria calda. Le aperture possono essere collocate sul tetto (cupolini, camini) o direttamente nelle pareti. Detto sistema di ventilazione può essere utilizzato convenientemente solo nei climi temperati e in presenza di basse densità di animali. Con la ventilazione forzata e il condizionamento ambientale è possibile controllare in modo più efficace le condi microclimatiche, anche in presenza di elevate densità e di temperature esterne non perfettamente conformi alle necessità deg animali. Il giudizio sarà insufficiente in caso di mancanza di un sistema di ventilazione adeguato di qualsiasi tipo; si ritiene invece accettabile la presenza di sistemi di ventilazione naturale che si avvalgono eventualmente di agitatori, o ventole, oppure sistemi di ventilazione forzata con estrattori longitudinali: sistemi a tunnel, giudicata ottimale se coadiuvata da specifici sistemi di raffrescamento ( esempio il cooling) e di riscaldamento (ad esempio i bruciatori a gas). Assenza di ventilazione adeguata Livello di non conformità: no; NO Ventilazione adeguata, naturale o forzata Livello di non conformità: SI Ventilazione meccanica (tunnel) e presenza di sistemi di raffrescamento e riscaldamento Livello di non conformità: Ottimale','18) Il sistema di ventilazione è adeguato a mantenere idonee condizioni microclimatiche','S, NA, NB, NC, -,O','(*) Descrivere come vengono garantite idonee condizioni microclimatiche',18);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,223,1236,'EDIFICI E LOCALI DI STABULAZIONE','Presenza di gas nocivi','146/2001 All. Fabbricati e locali di stabulazione Punto 10 "10. La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas devono essere mantenute entro limiti non dannosi per gli animali." Durante la valutazione dell''allevamento, sono considerati accettabili tenori di ammoniaca inferiori a 20 ppm e tenori di anidrid carbonica inferiori a 3.000 ppm. Nel caso di concentrazioni di gas non adeguate (es. NH3>20 ppm) può essere rilevata la seguente sintomatologia negli anima rossore delle congiuntive e lacrimazione; inoltre tali sintomi possono essere percepiti anche dal veterinario ufficiale, con il forte tipico odore pungente negli ambienti di stabulazione. In caso di sospetto di condizione inadeguata, è necessario confermare la condizione, misurando la concentrazione dei gas nocivi in allevamento mediante un rilevatore di gas portatile da posizionare al centro capannone all''altezza degli animali. Qualora non si abbia a disposizione questo strumento, nel caso di allevamenti che non presentano particolari odori o segnali che possano indicare una cattiva qualità dell''aria, si ritiene possibile assegnare il giudizio "accettabile" senza necessariamente provvedere alla misurazione dei gas. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. La concentrazione dei gas è dannosa per gli animali (indicazioni di riferimento: NH3> 20 ppm; CO2> 3000 ppm) Livello di non conformità: no; NO La concentrazione dei gas non è dannosa per gli animali (indicazioni di riferimento: NH3< 20 ppm; CO2< 3000 ppm) Livello di non conformità: SI','19) Le concentrazioni di gas sono mantenute entro limiti non dannosi per gli animali','S, NA, NB, NC, -','(*) Se del caso, registrare i valori misurati NH3 ... CO2... a campione, per confermare un sospetto di condizione inadeguatà',19);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,224,1237,'EDIFICI E LOCALI DI STABULAZIONE','Polverosità dell''aria','146/2001 All. Fabbricati e locali di stabulazione Punto 10 "10. La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas devono essere mantenute entro limiti non dannosi per gli animali." Per valutare la polverulenza dell''aria si consiglia, in caso di dubbio, l''utilizzo del "Test della polvere sul foglio di carta", descritto nel protocollo di valutazione del benessere degli avicoli Welfare Quality® (2009). Il metodo prevede l''utilizzo di un foglio formato A4 nero, che verrà posizionato al momento dell''ingresso nel capannone, su una superficie orizzontale sufficientemente alta da evitare contatto con gli animali, preferibilmente lontano dalla catena di alimentazione. Alla fine della valutazione, il foglio di carta va rimosso e va fatto scorrere un dito sulla sua superficie, per avere l''impressione della quantità di polvere che si è depositata nel frattempo. Il livello di polvere viene poi classificato come segue: a. Assenza di polvere b. Lieve presenza di polvere c. Leggera copertura d. Molta polvere e. Il colore della carta è nascosto dalla polvere Il giudizio insufficiente è assegnato per condizioni di polverosità d ed e; giudizio accettabile per c e b; ottimale per a. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Quantità di polvere dannosa per gli animali Livello di non conformità: no; NO Quantità di polvere non dannosa per gli animali Livello di non conformità: SI Assenza di polvere Livello di non conformità: Ottimale','20) La quantità di polvere è mantenuta entro limiti non dannosi per gli animali','S, NA, NB, NC, -,O','(*)Descrivere il livello di polverulenza riscontrato',20);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,225,1238,'EDIFICI E LOCALI DI STABULAZIONE','Nido (Gabbie Modificate)','267/2003Art.1 Punto 1.d "Nido: uno spazio separato, i cui componenti escludono per il pavimento qualsiasi utilizzo di rete metallica o plastificata che possa entrare in contatto con i volatili, previsto per la deposizione delle uova di una singola gallina o di un gruppo di galline, così detto nido di gruppo" 267/2003 All. D, punto 1, a),2 1 "Le galline ovaiole dispongono di un nido (la cui area non entra a far parte della superficie utilizzabile)". La normativa verticale non fornisce un numero specifico di nidi per gabbia; tuttavia affinché le galline possano manifestar comportamento naturale di cova senza essere disturbate si suggerisce di verificare, come linea guida, che sia presente almeno 1 nido ogni 5 galline.Per valutare l''adeguatezza del nido, è consigliabile utilizzare le indicazioni riportate nel Welfare Quality® (200 secondo le quali è necessario osservare che i nidi siano distribuiti uniformemente nello spazio disponibile e che le uova siano ripartite omogeneamente nella barra di raccolta di fronte al posatoio. Si considera quindi accettabile la presenza di almeno un nido per gabbia, coperto su almeno 1 lato, mentre potrà essere assegnato il giudizio migliorativo se il nido risulta essere ben separato ed efficacemente utilizzato. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Nido assente e/o non idoneo Livello di non conformità: no; NO Nido presente e idoneo Livello di non conformità: SI Nido presente e ben separato Livello di non conformità: Ottimale','21) Gli animali dispongono di un nido, la cui superficie non entra a far parte della superficie utilizzabile','S, NA, NB, NC, -,O','(*) Descrivere il numero di nidi e le loro caratteristiche',21);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,226,1239,'EDIFICI E LOCALI DI STABULAZIONE','Lettiera (Gabbie Modificate)','"Le galline ovaiole dispongono di una lettiera che consente loro di becchettare e razzolare". L''allevamento in gabbie modificate presenta delle peculiarità strutturali e gestionali che rendono impossibile la presenza uniforme di una lettiera intesa in senso classico. Il Dlgs 267/2003 definisce come lettiera "il materiale allo stato friabile che permette alle ovaiole di soddisfare le loro esigenze etologiche". Ciò che agli animali è effettivamente fornito nella quasi totalità dei casi è un tappetino o una piccola superficie in gomma o plastica su cui possa cadere una piccola quantità di mangime attraverso un foro praticato s mangiatoia. Tale mangime costituisce un materiale manipolabile e razzolabile che gli animali possono sfruttare. Quando gli animali non hanno accesso ad alcuna lettiera, questi comportamenti possono essere ridiretti verso i conspecifici, con atteggiamenti di feather- pecking (situazione in cui le galline beccano il piumaggio delle altre) e di aggressività. Per valutare la rispondenza al requisito si osservi la presenza di dispositivi che permettano agli animali di becchettare e razzolare. Assenza di lettiera o lettiera non idonea Livello di non conformità: no; NO Presenza di lettiera idonea Livello di non conformità: SI','22) Gli animali dispongono di una lettiera che consenta loro di becchettare e razzolare','S, NA, NB, NC, -','(*) Descrivere le caratteristiche della lettiera presente',22);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,227,1240,'EDIFICI E LOCALI DI STABULAZIONE','Posatoi (Gabbie Modificate)','267/2003 All. D, punto 1, a),4"Le galline ovaiole dispongono di posatoi appropriati che offrono almeno 15 cm di spazio per ovaiola". Per poter assicurare un adeguato livello di benessere è fondamentale che i posatoi garantiscano alle galline stabilità nella seduta e nel riposo, quindi non devono essere scivolosi ma sufficientemente larghi e devono avere una forma tale da essere ben afferrati dai piedi degli animali (EFSA, 2015). La normativa definisce lo spazio minimo a disposizione per ogni gallina ovaiola, ma è import considerare anche altri parametri. Le indicazioni fornite da EFSA (2015) per quanto riguarda la larghezza dei posatoi sono le seguenti: - all''interno di un range compreso tra 1,5 cm e 10, 5 cm il benessere è garantito, pertanto si può considerare accettabile; - un''ampiezza compresa tra 3 e 6 cm è considerabile Ottimale, in quanto è preferibile rispetto ad altre ampiezze. Per valutare la rispondenza al requisito si consideri la lunghezza (in cm) dei posatoi all''interno della gabbia in rapporto al numero di animali presenti e le indicazioni riguardanti l''ampiezza come ausilio per formulare una valutazione più completa. Per "posatoi appropriati" si intendono i posatoi che offrono alla gallina una percezione di elevazione, progettati in modo tale da ridurre al minimo le lesioni e massimizzarne l''uso. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Assenza di posatoi o presenza di posatoi non appropriati (indicativamente larghezza < 1,5 cm o >10,5 cm), che non offrono almeno 15 cm di spazio per ovaiola Livello di non conformità: no; NO Presenza di posatoi appropriati (indicativamente larghezza compresa tra 1,5 e 3 cm o tra 6 e 10,5 cm) e che offrono almeno 15 cm di spazio per ovaiola Livello di non conformità: SI Presenza di posatoi appropriati (indicativamente larghezza compresa tra 3 e 6 cm) e che offrono almeno 15 cm di spazio per ovaiola. Livello di non conformità: Ottimale','23) Gli animali dispongono di posatoi appropriati che offrono almeno 15 cm di spazio per ovaiola','S, NA, NB, NC, -,O','(*)In caso di non conformità descrivere i cm di posatoio a disposizione di ciascuna ovaiolà',23);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,228,1241,'EDIFICI E LOCALI DI STABULAZIONE','Dimensione degli spazi tra gabbie e tra gabbie e pavimento (Gabbie Modificate)','267/2003 All. D, punto 1,d) "Le file di gabbie (per agevolare l''ispezione, la sistemazione e l''evacuazione dei volatili), sono separate da passaggi aventi u larghezza minima di 90 cm e tra il pavimento dell''edificio e le gabbie delle file inferiori lo spazio è di almeno 35 cm". Per garantire una pratica ispezione ed estrazione dei volatili è necessario che l''addetto possa muoversi con facilità all''interno del capannone. Per questo motivo le file di gabbie devono essere separate da uno spazio avente una larghezza non inferiore ai 90 cm. Inoltre, le gabbie nella fila più bassa non devono essere direttamente a contatto con il pavimento, ma ad un''altezza di almeno 35 cm. Per valutare la rispondenza al requisito è necessario misurare la larghezza tra le file di gabbie e lo spazio tra il pavimento e le file inferiori di gabbie. Presenza anche solo di un parametro non sufficiente Livello di non conformità: no; NO Tutti i parametri sono sufficienti Livello di non conformità: SI','24) Le file di gabbie sono separate da passaggi aventi una larghezza minima di 90 cm e tra il pavimento e le gabbie inferiori lo spazio è di almeno 35 cm','S, NA, NB, NC, -','(*) Riportare la larghezza minima dei passaggi e lo spazio tra pavimento e gabbie inferiori',24);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,229,1242,'EDIFICI E LOCALI DI STABULAZIONE','Dispositivi di accorciamento unghie (Gabbie Modificate)','267/2003 All. D, punto 1,e) "Le gabbie sono provviste di adeguati dispositivi per accorciare le unghie". Come riporta l''EFSA (2005), esistono diverse tipologie di dispositivi di accorciamento delle unghie attualmente in uso, generalmente posizionati dietro le mangiatoie: pannelli perforati costituiti da diverso materiale, come ceramica o metallo, oppure paste e strisce abrasive. Per valutare la rispondenza al requisito è necessario innanzitutto verificare la presenza di tali dispositivi nelle gabbie, e poi valutarne l''adeguatezza attraverso l''osservazione degli animali. Per evitare una crescita eccessiva delle unghie, che potrebbe portare ad un intrappolamento e/o rottura dell''artiglio o essere un rischio per le altre galline all''interno della gabbia in caso di graffi, le gabbie devono essere dotate di dispositivi per l''accorciamento delle unghie. Il valutatore potrà verificare l''adeguatezza e l''efficacia di questi dispositivi osservando la presenza di unghie eccessivamente lunghe / rotte direttamente sugli animali. Dispositivi di accorciamento unghie assenti o non adeguati Livello di non conformità: no; NO Dispositivi di accorciamento unghie presenti e adeguati Livello di non conformità: SI','25) Le gabbie sono provviste di adeguati dispositivi per accorciare le unghie','S, NA, NB, NC, -','(*) Descrivere il tipo di dispositivo utilizzato e, in caso di rilievi non conformi il numero di animali con unghie molto lunghe o rotte',25);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,230,1243,'EDIFICI E LOCALI DI STABULAZIONE','Nido (Sistemi alternativi)','267/2003 All. B, punto 1, a), 3) "Gli impianti di allevamento di cui al presente allegato, nuovi, ristrutturati o messi in funzione per la prima volta, devono: essere attrezzati in modo da garantire che tutte le galline ovaiole dispongano di [...]: almeno un nido per 7 ovaiole. Se sono utilizzati nidi di gruppo deve essere presente una superficie di almeno 1 m² per un massimo di 120 ovaiole". Nell''ambito dei sistemi alternativi di allevamento, i nidi possono essere individuali oppure di gruppo (anche chiamati "familiari"). I primi, come definito dalla normativa vigente, devono essere in numero di massimo 1 ogni 7 galline, mentre i secondi, che possono presentare una sola apertura oppure numerose aperture che consentono l''accesso ad una superficie più ampia, devono essere coperti almeno su due lati. La situazione ideale per il benessere dell''ovaiola si ha quando i nidi individuali siano in un numero non inferiore a 1 ogni 5 galline o quando i nidi di gruppo siano coperti sui quattro lati con tende frontali e possibilmente situati al centro del capannone. Per valutare la rispondenza al requisito è necessario verificare la congruità del numero di nidi rispetto agli animali presenti. Numero/dimensione dei nidi non sufficiente Livello di non conformità: no; NO Tutti i parametri sono sufficienti Livello di non conformità: SI Presenza di un nido ogni 5 ovaiole o 1 m² per meno di 120 ovaiole Livello di non conformità: Ottimale','26) Gli animali dispongono di un nido ogni 7 ovaiole, se sono nidi di gruppo è presente una superficie di di almeno 1 m2 per un massimo di 120 ovaiole','S, NA, NB, NC, -,O','(*) Descrivere il numero/dimensione dei nidi e le loro caratteristiche',26);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,231,1244,'EDIFICI E LOCALI DI STABULAZIONE','Posatoi (Sistemi alternativi)','267/2003 All. B, punto 1, a), 4) "Gli impianti di allevamento di cui al presente allegato, nuovi, ristrutturati o messi in funzione per la prima volta, devono: essere attrezzati in modo da garantire che tutte le galline ovaiole dispongano di [...]: posatoi appropriati, privi di bordi aguzzi e che offrono almeno 15 cm di spazio per ovaiola. I posatoi non sovrastano le zone coperte di lettiera: la distanza orizzontale fra posatoi non è inferiore a 30 cm e quella tra i posatoi e le pareti non è inferiore a 20 cm." La normativa prescrive che i posatoi siano adeguati, privi bordi aguzzi e che vi siano almeno 15 centimetri di spazio per ciascuna ovaiola. È stato dimostrato che fornire 15 centimetri di posatoio rialzato riduce paura e aggressività degli animali e migliora condizione corporea. Per i sistemi alternativi è preferibile avere più di 15 centimetri di spazio per ovaiola, poiché vi è maggio competizione tra animali rispetto alle gabbie modificate. La conformazione e il posizionamento del posatoio sono importanti per prevenire l''ammassamento delle galline, che può portare a problemi di discesa dal posatoio stesso e a lesioni conseguenti. Per questo motivo i posatoi non devono sovrastare le zone coperte da lettiera e devono distare almeno 30 centimetri l''uno dall''altro e almeno 20 centimetri dalle pareti. Sono da preferirsi posatoi costituiti da materiali morbidi (es. legno tenero) o ricoperti di gomma e quelli a sezione rotonda rispetto a quelli a sezione rettangolare. EFSA (2015) raccomanda una larghezza del posatoio compresa tra i 3 e i 6 centimetri per garantire il comfort del piede e del cuscinetto plantare in particolare. Per valutare la rispondenza al requisito è necessario verificare che lo spazio di posatoio a disposizione di ciascuna ovaiola sufficiente e che le caratteristiche del posatoio stesso lo rendano sicuro per gli animali e adatto all''espletamento del comportamento naturale di "perching" o appollaiamento. Per "posatoi appropriati" si intendono i posatoi progettati in modo tale da ridurre al minimo le lesioni e massimizzarne l''uso. Assenza di posatoi o presenza di posatoi non appropriati (indicativamente larghezza < 1,5 cm o >10,5 cm), che non offrono almeno 15 cm di spazio per ovaiola Livello di non conformità: no; NO Presenza di posatoi appropriati (indicativamente larghezza compresa tra 1,5 e 3 cm o tra 6 e 10,5 cm) e che offrono almeno 15 cm di spazio per ovaiola Livello di non conformità: SI Presenza di posatoi appropriati (indicativamente larghezza compresa tra 3 e 6 cm) e che offrono almeno 15 cm di spazio per ovaiola Livello di non conformità: Ottimale','27) Gli animali dispongono di posatoi appropriati, privi di bordi aguzzi, e che offrono almeno 15 cm di spazio per ovaiola I posatoi non sovrastano le zone coperte di lettiera La distanza orizzontale tra posatoi non è inferiore a 30 cm e quella tra i posatoi e le pareti non è inferiore a 30 cm','S, NA, NB, NC, -,O','(*) Descrivere lo spazio a disposizione sul posatoio per ogni ovaiola e in caso di non conformità le caratteristiche non conformi',27);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,232,1245,'EDIFICI E LOCALI DI STABULAZIONE','Lettiera (Sistemi alternativi)','267/2003 All. B, punto 1, a), 5) "Gli impianti di allevamento di cui al presente allegato, nuovi, ristrutturati o messi in funzione per la prima volta, devono: a) essere attrezzati in modo da garantire che tutte le galline ovaiole dispongano di: [...] 5) una superficie di lettiera di almeno 250 cm quadrati per gallina ovaiola; la lettiera deve occupare almeno un terzo de superficie al suolo;" La presenza di lettiera nell''allevamento della gallina ovaiola è essenziale affinché gli animali esprimano comportamenti specie-specifici quali: dust-bathing (bagni di polvere), razzolamento e becchettamento (pecking). Quando gli animali non hanno accesso ad alcuna lettiera (o quando le condizioni della lettiera sono molto scadenti), questi comportamenti possono essere rediretti verso i cospecifici, con atteggiamenti di aggressività e di feather-pecking (situazione in cui le galline beccano il piumaggio delle altre). Il veterinario ufficiale deve verificare che la superficie di lettiera presente sia adeguata rispetto al numero di animali, e coperta da uno strato sufficiente di materiale razzolabile e friabile. La superficie di lettiera è inferiore ai 250cm²/ovaiola e/o occupa meno di un terzo della superficie al suolo Livello di non conformità: no; NO La superficie di lettiera è pari o superiore a 250cm²/ovaiola e occupa almeno un terzo della superficie al suolo Livello di non conformità: SI','28) La lettiera è presente in quantità sufficiente rispetto al numero di animali accasati e alla superficie al suolo','S, NA, NB, NC, -','(*) Riportare i dati raccolti per verificare la conformità (superficie coperta da lettiera e numero di animali accasati)',28);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,233,1246,'EDIFICI E LOCALI DI STABULAZIONE','Pavimentazione (Sistemi alternativi)','267/2003 All. B, punto 1, b) "1. Gli impianti di allevamento di cui al presente allegato, nuovi, ristrutturati o messi in funzione per la prima volta, devono: [...] b) essere dotati di pavimento che sostenga adeguatamente ciascuna delle unghie anteriori di ciascuna zampa;" Il veterinario ufficiale consideri se le dimensioni del fessurato calpestabile dagli animali siano di dimensioni adeguate, tenendo in considerazione le dimensioni delle loro zampe. La pavimentazione fessurata dovrebbe essere in grado sia di permettere il passaggio delle deiezioni al di sotto della superficie calpestabile, sia di sostenere adeguatamente le unghie anteriori delle galline. Pavimento non idoneo a sostenere adeguatamente ciascuna unghia anteriore di ciascuna zampa Livello di non conformità: no; NO Pavimento idoneo a sostenere adeguatamente ciascuna unghia anteriore di ciascuna zampa Livello di non conformità: SI','29) la pavimentazione sostiene adeguatamente le unghie anteriori di ciascuna zampa','S, NA, NB, NC, -','(*) Descrivere le caratteristiche della pavimentazione',29);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,234,1247,'EDIFICI E LOCALI DI STABULAZIONE','Numero di livelli sovrapposti (Sistemi alternativi VOLIERE)','267/2003 All. B, punto 2, a), 1) "2. Oltre alle prescrizioni di cui al numero 1: a) nei sistemi di allevamento che consentono alle galline ovaiole di muoversi liberamente fra diversi livelli: 1) il numero massimo di livelli sovrapposti deve essere pari a 4;". I sistemi a voliera prevedono diversi livelli di pavimentazione perforata (in rete metallica o plastica). Le piattaforme sono spess connesse tra loro attraverso rampe o scale.Per garantire una buona gestione di questi sistemi di allevamento (soprattutto per quanto riguarda lo smaltimento delle deiezioni), la normativa stabilisce che il numero massimo di livelli sovrapposti sia pari a quattro; veterinario ufficiale dovrà, con eventualmente l''ausilio della planimetria dell''allevamento, verificare che tale requisito sia rispettato Voliere con 5 o più livelli sovrapposti Livello di non conformità: no; NO Voliere con un numero massimo di livelli sovrapposti pari a 4 Livello di non conformità: SI','30) Ci sono al massimo 4 livelli sovrapposti','S, NA, NB, NC, -','(*) Riportare il numero di livelli presenti',30);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,235,1248,'EDIFICI E LOCALI DI STABULAZIONE','Altezza dei livelli (Sistemi alternativi VOLIERE)','267/2003 All. B, punto 2, a), 2) "2. Oltre alle prescrizioni di cui al numero 1: a) nei sistemi di allevamento che consentono alle galline ovaiole di muoversi liberamente fra diversi livelli: [...] 2) l''altezza libera minima fra i vari livelli deve essere di 45 cm;". L''altezza minima prevista per ciascun livello è di 45 centimetri, tale da consentire agli animali di stare in posizione eretta o appollaiarsi sui posatoi. Voliera con altezza dei livelli minore di 45 cm Livello di non conformità: no; NO Voliera con altezza dei livelli pari o maggiore di 45 cm Livello di non conformità: SI','31) Tra i livelli vi è uno spazio minimo di 45 cm','S, NA, NB, NC, -','(*) Riportare l''altezza dei livelli',31);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,236,1249,'EDIFICI E LOCALI DI STABULAZIONE','Mangiatoie e abbeveratoi (Sistemi alternativi VOLIERE)','267/2003 All. B, punto 2, a), 3) "2. Oltre alle prescrizioni di cui al numero 1: a) nei sistemi di allevamento che consentono alle galline ovaiole di muoversi liberamente fra diversi livelli: [...] 3) le mangiatoie e gli abbeveratoi devono essere ripartiti in modo da permettere a tutte le galline ovaiole un a uniforme;". I dispositivi di alimentazione e abbeverata possono essere presenti su tutti i piani o solo su alcuni, ma è importante che ogni gallina abbia facile accesso ad alimento e acqua a qualsiasi livello. Talvolta si preferisce lasciare soltanto l''ultimo livello privo di tali dispositivi, poiché si è visto che gli animali tendono a raggiungere i piani più alti per riposare durante le ore notturne. Saranno perciò conformi sia gli allevamenti che presentano mangiatoie ed abbeveratoi su ogni piano, sia quelli che non presentano queste strutture sull''ultimo piano (purché siano uniformemente distribuite nei piani restanti). Impossibilità di garantire agli animali un accesso uniforme ad abbeveratoi e mangiatoie Livello di non conformità: no; NO E'' garantito agli animali un accesso uniforme ad abbeveratoi e mangiatoie Livello di non conformità: SI','32) Abbeveratoi e mangiatoie sono correttamente distribuiti tra i piani','S, NA, NB, NC, -','(*) Riportare la localizzazione di abbeveratoi e mangiatoie',32);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,237,1250,'EDIFICI E LOCALI DI STABULAZIONE','Protezione dalle deiezioni (Sistemi alternativi VOLIERE)','267/2003 All. B, punto 2, a), 4) "2. Oltre alle prescrizioni di cui al numero 1: a) nei sistemi di allevamento che consentono alle galline ovaiole di muoversi liberamente fra diversi livelli: [...] 4) i livelli devono essere installati in modo da impedire alle deiezioni di cadere sui livelli inferiori;". I livelli della voliera possono essere disposti in modo sfalsato, tale da far sì che le deiezioni cadano in uno spazio sottostante privo di animali, oppure può essere presente al di sotto di ciascun piano un dispositivo di rimozione delle deiezioni. Il veterinario ufficiale deve osservare la disposizione dei piani del sistema a voliera e può aiutarsi nella valutazione osservando lo stato di pulizia generale degli animali. Voliere con sistemi che causano la caduta di deiezioni nei livelli inferiori Livello di non conformità: no; NO Voliere con sistemi che evitano la caduta di deiezioni nei livelli inferiori Livello di non conformità: SI','33) Sono impedite le cadute di deiezioni dai livelli superiori a quelli inferiori','S, NA, NB, NC, -','(*) Riportare l''eventuale presenza di dispositivi che evitano la caduta di deiezioni sui piani sottostanti (es. sistemi di rimozione a rullo). Gli ABM (es. pulizia degli animali a livello dorsale) possono essere usati per confermare la conformità o stabilire la non conformità.',33);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,238,1251,'EDIFICI E LOCALI DI STABULAZIONE','Dimensione degli accessi agli spazi esterni (Sistemi alternativi ALL''APERTO)','267/2003 All. B, punto 2, b), 1) D.M. 20/04/2006 All. I Punto 2.b.1 "2. Oltre alle prescrizioni di cui al numero 1: b) se le galline ovaiole dispongono di un passaggio che consente loro di uscire all''aperto: 1) le diverse aperture del passaggio devono dare direttamente accesso allo spazio all''aperto, avere un''altezza minima di 35 cm, una larghezza di 40 cm ed essere distribuite su tutta la lunghezza dell''edificio; per ogni 1000 galline ovaiole deve ess comunque disponibile un''apertura totale di 2 m;". In caso di possibilità di accesso ad uno spazio esterno, esso deve essere garantito in egual misura a ciascun animale, in qualunque punto del capannone esso si trovi. Le aperture poi devono avere dimensioni tali da non ostacolare l''uscita delle galline né ferirl durante il passaggio. Presenza anche di un solo parametro non sufficiente Livello di non conformità: no; NO Tutti i parametri sono sufficienti Livello di non conformità: SI','34) Le aperture che conducono all''esterno sono in numero sufficiente e di dimensioni adeguate','S, NA, NB, NC, -','(*) Descrivere il numero di aperture e le loro caratteristiche',34);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,239,1252,'EDIFICI E LOCALI DI STABULAZIONE','Disponibilità di spazio esterno (Sistemi alternativi ALL''APERTO)','Reg. Delegato (UE) 2168/2017 All. Punto 1.c "1. Le «uova da allevamento all''aperto» devono essere prodotte in aziende che soddisfino almeno le condizioni di cui all''articolo 4 della direttiva 1999/74/CE del Consiglio. In particolare, devono essere soddisfatte le seguenti condizioni: c) la densità massima di carico degli spazi all''aperto non deve mai superare 2 500 galline per ettaro di terreno disponibile per le galline oppure una gallina per 4 m2. Tuttavia, ove siano disponibili almeno 10 m2 per gallina e si pratichi la rotazione, cosicché alle galline sia consentito l''accesso a tutto il recinto durante l''intero ciclo di vita del branco, ciascun recinto utilizzato deve garantire in ogni momento almeno 2,5 m2 per gallina;" 267/2003 All. B, punto 2, b).2.a)) "2. Oltre alle prescrizioni di cui al numero 1: b) se le galline ovaiole dispongono di un passaggio che consente loro di uscire all''aperto: 2) gli spazi all''aperto devono: a) avere una superficie adeguata alla densità di galline ovaiole allevate e alla natura del suolo al fine di prevenire qualsiasi contaminazione;" Il veterinario ufficiale deve calcolare, attraverso l''esame della planimetria dell''allevamento e la conoscenza del numero di animali accasati, se la superficie esterna destinata alle ovaiole sia sufficiente e rispondente al requisito normativo. La superficie esterna non garantisce la corretta densità di animali prevista dalla normativa Livello di non conformità: no; NO La superficie esterna garantisce la corretta densità di animali prevista dalla normativa Livello di non conformità: SI','35) Gli spazi all''aperto hanno una superficie tale da garantire la densità corretta di animali','S, NA, NB, NC, -','(*) Riportare le dimensioni di spazio aperto e il numero di animali accasati',35);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,240,1253,'EDIFICI E LOCALI DI STABULAZIONE','Ripari esterni (Sistemi alternativi ALL''APERTO)','Reg. Delegato (UE) 2168/2017 All. Punto 1.d "1. Le «uova da allevamento all''aperto» devono essere prodotte in aziende che soddisfino almeno le condizioni di cui all''articolo 4 della direttiva 1999/74/CE del Consiglio. In particolare, devono essere soddisfatte le seguenti condizioni: d) gli spazi all''aperto non si estendono oltre un raggio di 150 m dall''apertura più vicina del fabbricato. Può essere tuttavia ammessa una distanza maggiore, fino a 350 m di raggio dall''apertura più vicina dell''edificio, purché vi sia un numero sufficiente di ripari, ai sensi dell''articolo 4, paragrafo 1, punto 3), lettera b), punto ii), della direttiva 1999/74/CE, uniformemente distribuiti nell''intero spazi all''aperto, con una densità di almeno quattro ripari per ettaro." 146/2001 All. Animali custoditi al di fuori dei fabbricati Punto 12 "12. Agli animali custoditi al di fuori dei fabbricati deve essere fornito, in funzione delle necessità e delle possibilità, un riparo adeguato dalle intemperie, dai predatori e da rischi per la salute." 267/2003 All. B, punto 2, b).2.b)2. Oltre alle prescrizioni di cui al numero 1: b) se le galline ovaiole dispongono di un passaggio che consente loro di uscire all''aperto: 2) gli spazi all''aperto devono: b) essere provvisti di riparo dalle intemperie e dai predatori e di abbeveratoi appropriati." Lo spazio all''aperto va correttamente gestito per incoraggiare le galline ad utilizzarlo appieno. Esse infatti manifesta atteggiamento di timore (nei confronti di possibili predatori), che può essere attenuato dalla presenza di ripari sotto cui possano ripararsi. Quindi, per incoraggiare l''uso delle aree esterne, è necessario fornirle di ripari e zone di ombra (sia naturali che artificiali). Tuttavia, i predatori costituiscono effettivamente un rischio per le galline allevate; è perciò importante adottare appropriate misure a loro tutela (ad esempio una recinzione contro i predatori da terra o una rete sospesa al di sopra degli animali in certe aree contro i grossi rapaci). In funzione della valutazione del rischio, delle necessità e delle possibilità, il veterinario ufficiale deve verificare la presenza l''adeguatezza ai fini della protezione da predatori dei ripari forniti dall''allevatore, nonché se il numero di abbeveratoi presenti sufficiente e la loro disposizione uniforme. Presenza anche di un solo parametro non sufficiente Livello di non conformità: no; NO Tutti i parametri sono sufficienti Livello di non conformità: SI','36) Gli spazi all''aperto sono provvisti di adeguati ripari da intemperie e predatori, e dotati di abbeveratoi in numero adeguato','S, NA, NB, NC, -','(*) Descrivere i ripari e le loro caratteristiche, riportare il numero di abbeveratoi esterni',36);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,241,1254,'EDIFICI E LOCALI DI STABULAZIONE','Utilizzi ulteriori dello spazio esterno (Sistemi alternativi ALL''APERTO)','Reg. delegato (UE) 2168/2017 All. Punto 1.b "1. Le «uova da allevamento all''aperto» devono essere prodotte in aziende che soddisfino almeno le condizioni di cui all''articolo 4 della direttiva 1999/74/CE del Consiglio. In particolare, devono essere soddisfatte le seguenti condizioni: b) gli spazi all''aperto ai quali hanno accesso le galline devono essere coperti prevalentemente di vegetazione e possono esser utilizzati solo come frutteto, bosco o pascolo, se quest''ultima utilizzazione è autorizzata dalle competenti autorità" Per stimolare l''utilizzo dello spazio esterno da parte delle galline, esso deve essere coperto prevalentemente di vegetazione (che non dovrebbe però essere eccessivamente fitta per non ostacolare il movimento degli animali). Tale spazio può essere contestualmente utilizzato a scopo agricolo ma esclusivamente in qualità di frutteto, bosco o pascolo. Spazi all''aperto non idonei Livello di non conformità: no; NO Spazi all''aperto idonei Livello di non conformità: SI','37) Gli spazi all''aperto sono coperti prevalentemente di vegetazione e, se utilizzati anche ad altro scopo, non sono adibiti ad usi diversi da: frutteto, bosco o pascolo','S, NA, NB, NC, -','(*) Descrivere gli eventuali usi ulteriori dello spazio esterno',37);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,242,1255,'ILLUMINAZIONE MINIMA','Illuminazione','146/2001 All. Fabbricati e locali di stabulazione Punto 11 "11. Gli animali custoditi nei fabbricati non devono essere tenuti costantemente al buio o esposti ad illuminazione artificiale senza un adeguato periodo di riposo. Se la luce naturale disponibile è insufficiente a soddisfare esigenze comportamentali e fisiologiche degli animali, occorre prevedere un''adeguata illuminazione artificiale." 267/2003 All. A Punto 3 "3. Tutti gli edifici devono essere dotati di un''illuminazione sufficiente per consentire alle galline di vedersi e di essere chiaramente, di guardarsi intorno e di muoversi normalmente. In caso di illuminazione naturale le aperture per la luce devono essere disposte in modo da ripartirla uniformemente nei locali." Per "illuminazione sufficiente" si intende un''intensità luminosa di almeno 20 lux misurata a livello degli occhi degli animali e in diversi punti (al di sopra della linea di mangiatoia, della lettiera, vicino alla linea di abbeveratoi...) In caso di sospetto di condizione inadeguata, è necessario confermare con luxometro. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Assenza o insufficienza di illuminazione naturale o artificiale adeguata Livello di non conformità: no; NO Presenza di illuminazione naturale o artificiale adeguata Livello di non conformità: S','38) Gli edifici sono dotati di un''illuminazione adeguata','S, NA, NB, NC, -','(*) Registrare il tipo di illuminazione naturale/artificiale/assente e in caso di rilievo non conforme, confermare con luxometro',38);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,243,1256,'ILLUMINAZIONE MINIMA','Programma di luce','146/2001 All. Fabbricati e locali di stabulazione Punto 11 "11. Gli animali custoditi nei fabbricati non devono essere tenuti costantemente al buio o esposti ad illuminazione artificiale senza un adeguato periodo di riposo. Se la luce naturale disponibile è insufficiente a soddisfare esigenze comportamentali e fisiologiche degli animali, occorre prevedere un''adeguata illuminazione artificiale." 267/2003 All. A Punto 3 "3. [...] Dopo i primi giorni di adattamento, al fine di evitare problemi di salute e di comportamento, deve seguire un cic ventiquattro ore comprensivo di un periodo di oscurità sufficiente e ininterrotto, a titolo indicativo pari a circa un terzo della giornata, per consentire alle galline di riposarsi ed evitare problemi quali immunodepressione e anomalie oculari." Periodi di luce prolungata sono pericolosi per il benessere della gallina non solo dal punto di vista fisico, ma anche comportamentale; programmi di luce di tipo continuo rendono gli animali più paurosi (Campo e Davila, 2002). Diventa quindi fondamentale assicurare agli animali un ciclo di luce artificiale il più vicino possibile a quello naturale, con alternanza di luce e buio nell''arco di 24 ore, che permetta alle galline di riposare, ridurre lo stress e incentivare il ritmo circadiano (Malleau et al., 2007). Il programma di luce non è adeguato Livello di non conformità: no; NO Il programma di luce è adeguato Livello di non conformità: SI','39) Gli animali custoditi nei fabbricati non sono tenuti costantemente al buio, ad essi sono garantiti un adeguato periodo di luce (naturale o artificiale) ed un adeguato periodo di riposo','S, NA, NB, NC, -','(*) Registrare il programma di luce messo in atto',39);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,244,1257,'ILLUMINAZIONE MINIMA','Periodo di penombra','146/2001 All. Fabbricati e locali di stabulazione Punto 11 "11. Gli animali custoditi nei fabbricati non devono essere tenuti costantemente al buio o esposti ad illuminazione artificiale senza un adeguato periodo di riposo. Se la luce naturale disponibile è insufficiente a soddisfare esigenze comportamentali e fisiologiche degli animali, occorre prevedere un''adeguata illuminazione artificiale." 267/2003 All. A Punto 3 "[...] In concomitanza con la diminuzione della luce deve essere rispettato un periodo di penombra di durata sufficiente per consentire alle galline di sistemarsi senza confusione o ferite." Nei sistemi ad illuminazione artificiale deve essere previsto un periodo di crepuscolo sufficiente prima del ciclo di buio, in l''illuminazione viene gradualmente attenuata, permettendo agli uccelli di individuare i punti di appostamento per il riposo appropriati, senza provocarsi ferite o disturbarsi a vicenda: si considera sufficiente un periodo di penombra di 15-30 minuti. Questo sistem permette di stimolare anche il comportamento alimentare durante le ore diurne per prevenire la fame durante il periodo notturno. In aggiunta, è importante anche la presenza di un periodo di graduale aumento dell''intensità luminosa prima che si accendano le luci, per ridurre la quantità di uova deposte sul pavimento invece che nel nido (ICFAW, 2017). Per "durata sufficiente" si intende un periodo di penombra di almeno 15 minuti. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Il periodo di penombra è assente o di durata insufficiente Livello di non conformità: no; NO Il periodo di penombra è presente e di durata sufficiente Livello di non conformità: SI','40) E'' rispettato un periodo di penombra di durata sufficiente','S, NA, NB, NC, -','(*) Riportare orari e durata del periodo di penombra applicato dall''allevatore',40);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,245,1258,'ILLUMINAZIONE MINIMA','Uniformità di illuminazione (Allevamenti ad illuminazione naturale)','267/2003 All. A Punto 3 "[...] In caso di illuminazione naturale le aperture per la luce devono essere disposte in modo da ripartirla uniformemente nei locali. Le aperture per l''ingresso della luce naturale devono essere presenti in numero tale e con una disposizione adeguata a garantire che non vi siano zone perpetuamente in ombra (con luminosità inferiore a 5 lux). I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Illuminazione naturale non ripartita uniformemente nei locali di allevamento Livello di non conformità: no; NO Illuminazione naturale ripartita uniformemente nei locali di allevamento Livello di non conformità: SI','41) Le aperture per la luce naturale consentono una distribuzione uniforme della luce nei locali','S, NA, NB, NC, -','(*) In caso di rilievo non conforme, descrivere in che modo la disposizione delle aperture non consente la distribuzione uniforme della luce (con formazione di zone d''ombra)',41);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,246,1259,'ATTREZZATURA AUTOMATICA E MECCANICA','Ispezione degli impianti automatici e meccanici e gestione delle situazioni di emergenza',' 146/2001 All. Impianti automatici o meccanici Punto 13 "13. Ogni impianto automatico o meccanico indispensabile per la salute ed il benessere degli animali deve essere ispezionato almeno una volta al giorno. Gli eventuali difetti riscontrati devono essere eliminati immediatamente; se ciò non é possibile, occorre prendere le misure adeguate per salvaguardare la salute ed il benessere degli animali. [...]" Gli impianti automatici o meccanici che possono influenzare il benessere animale (sistemi automatici di alimentazione, ventilazione, ecc.) possono essere considerati indispensabili per la salute degli animali; per questo devono essere ispezionati una volta al giorno.Il giudizio migliorativo può essere dato in presenza di un piano documentato della gestione di situazioni d''emergenza o di incidenti che possono minacciare il benessere degli animali (danno agli impianti di alimentazione/abbeveraggio, incendi, condizioni climatiche estreme) Impianti automatici o meccanici ispezionati meno di 1 volta/giorno Livello di non conformità: no; NO Impianti automatici o meccanici ispezionati almeno 1 volta/giorno Livello di non conformità: SI Impianti automatici o meccanici ispezionati 1 o più volte/giorno e presenza di un piano documentato della gestione delle situazioni di emergenza Livello di non conformità: Ottimale','42) Gli impianti automatici o meccanici sono ispezionati almeno una volta al giorno','S, NA, NB, NC, -,O','(*) Raccolta di informazioni tramite interviste agli operatori, consultazione di manuali di buone pratiche e relative registrazioni',42);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,247,1260,'ATTREZZATURA AUTOMATICA E MECCANICA','Sistema di allarme','146/2001 All. Impianti automatici o meccanici Punto 13 "In caso di guasto all''impianto [di ventilazione] deve essere previsto un sistema di allarme che segnali il guasto. Detto sistem d''allarme deve essere sottoposto a controlli regolari". Gli impianti fondamentali per la salute ed il benessere animale, come l''impianto di ventilazione, devono essere provvisti di un sistema di allarme; detto sistema di allarme deve essere sottoposto a controlli e manutenzioni regolari. Per verificare la rispondenza requisito è necessario che il detentore possegga un registro dove segnare le date dei controlli al sistema di allarme. Se l''allevamento nonnecessita di un impianto di ventilazione artificiale il requisito si considera non applicabile. Il giudizio migliorativo può essere assegnato in presenza di un sistema di allarme che riguardi anche altri impianti (oltre a quello di ventilazione) indispensabili per la sopravvivenza ed il benessere degli animali (es. sistema di alimentazione/abbeveraggio) Assenza di un sistema di allarme all''impianto di ventilazione artificiale e/o assenza di controlli regolari Livello di non conformità: no; NO Presenza di un sistema di allarme all''impianto di ventilazione artificiale controllato regolarmente Livello di non conformità: SI Presenza aggiuntiva di un sistema di allarme riguardante altri impianti indispensabili per la sopravvivenza ed il benessere degli animali controllato regolarmente Livello di non conformità: Ottimale','43) E'' previsto un sistema di allarme per l''impianto di ventilazione Detto sistema è sottoposto a controlli regolari','S, NA, NB, NC, -,O','(*) Descrivere la data dell''ultimo controllo al sistema di allarme',43);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,248,1261,'ATTREZZATURA AUTOMATICA E MECCANICA','Presenza di un impianto di riserva','146/2001 All. Impianti automatici o meccanici Punto 13 "13. Se la salute ed il benessere degli animali dipendono da un impianto di ventilazione artificiale, deve essere previsto un adeguato impianto di riserva per garantire un ricambio di aria sufficiente a salvaguardare la salute e il benessere degli animali." Qualora per la salute ed il benessere degli animali sia presente e necessario un sistema di aerazione artificiale con ventilazion unicamente forzata, occorre prevedere un opportuno sistema sostitutivo che all''occorrenza consenta un ricambio d''aria sufficiente per gli animali presenti, in attesa che il guasto venga corretto (es. apertura d''emergenza di finestre/generatore di corrente, ecc.) e che lo stesso sia sottoposto a periodici interventi di verifica e manutenzione..Se l''allevamento non necessita di un impianto di ventilazione forzata (es. idonea circolazione naturale dell''aria, pascolo, ecc.) il requisito si considera non applicabile. Il giudizio migliorativo può essere dato in presenza di un piano documentato della gestione delle situazioni d''emergenza o di incidenti che possono minacciare il benessere degli animali (guasto degli impianti di ventilazione artificiale), descritto nei Manuali di corretta prassi operativa. Assenza / inadeguatezza dell''impianto di riserva, in caso di ventilazione artificiale Livello di non conformità: no; NO Presenza di un impianto di riserva adeguato Livello di non conformità: SI Presenza aggiuntiva di un piano di emergenza formalizzato Livello di non conformità: Ottimale','44) Se la salute e il benessere degli animali dipendono da un impianto di ventilazione artificiale, è previsto un adeguato impianto di riserva per garantire un ricambio di aria sufficiente a salvaguardare la salute e il benessere degli animali in caso di guasto all''impianto stesso','S, NA, NB, NC, -,O','(*)Registrare la verifica del corretto funzionamento del sistema di allarme e del sistema sostitutivo effettuata durante il controllo ufficiale; annotare la frequenza degli interventi di verifica e manutenzione effettuati dall''allevatore',44);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,249,1262,'ATTREZZATURA AUTOMATICA E MECCANICA','Rumore','267/2003 Art.1 Punto 2 "2) Il livello sonoro deve essere ridotto al minimo possibile e si devono evitare rumori di fondo o improvvisi. La costru l''installazione, la manutenzione e il funzionamento dei ventilatori, dei dispositivi di alimentazione e di altre attrezzature devono essere tali da provocare il minimo rumore possibile." Bright and Johnson (2001) riportano che, nonostante le galline ovaiole si adattino e sopportino vari gradi e diverse tipologie di rumore, l''esposizione degli animali a rumori improvvisi e ad alto volume dovrebbe essere ridotta al minimo, per prevenire lo stress paura- indotto e le sue reazioni, come l''ammassamento degli animali in un''unica area. I macchinari devono pertanto essere progetta collocati e mantenuti in modo da produrre il minimo rumore possibile (Chlouplek et al., 2009). Il veterinario ufficiale deve quind verificare l''assenza di rumori che possano stressare gli animali e provocarne diminuzione del benessere. Rumorosità eccessiva Livello di non conformità: no; NO Rumorosità accettabile Livello di non conformità: S','45) La rumorosità determinata dalle attrezzature non è tale da arrecare danno agli animali','S, NA, NB, NC, -','(*) Riportare le principali fonti di rumorosità e se gli animali ne sono spaventati o stressati (es. animali ammassati o che si spaventano all''azionamento di un particolare macchinario)',45);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,250,1263,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Gestione dell''alimentazione','146/2001 All. Mangimi, acqua e altre sostanze Punto 14 "14. Agli animali deve essere fornita un''alimentazione sana adatta alla loro età e specie e in quantità sufficiente a mantenerli in buona salute e a soddisfare le loro esigenze nutrizionali.Gli alimenti o i liquidi sono somministrati agli animali in modo da non causare loro inutili sofferenze o lesioni e non contengono sostanze che possano causare inutili sofferenze o lesioni." I sistemi di alimentazione devono permettere ad ogni individuo di soddisfare i propri fabbisogni per quantità e qualità degli alimenti. La dieta deve fornire energia sufficiente, nutrienti e fibra alimentare tali da soddisfare i requisiti nutrizionali e rispettare la fisiologia digestiva e metabolica degli animali. Per poter soddisfare le esigenze nutrizionali degli animali e far sì che l''alimentazione sia adeguata al loro sviluppo corporeo, allo stato fisiologico e al livello produttivo è necessario che, in funzione della fase produttiva in cui le galline si trovano, dispongano di una razione specifica. Tale razione specifica può essere scritta o registrata o comunque, anche se non scritta, nota all''allevatore o a personale che si occupa della preparazione degli alimenti per gli animali, ed è buona prassi che sia mantenuta il più possibile stabile nel tempo. La corretta alimentazione degli animali è anche collegata alla qualità degli alimenti e dell''acqua che la compongono, che devono essere di origine conosciuta e conservati in ambienti idonei (es. silos, magazzini, fienili, sistema di distribuzione idrica) per evitare alterazioni e contaminazioni con sostanze tossico-nocive. Il veterinario ufficiale deve quindi verificare la qualità e la salubrità degli alimenti che vengono somministrati agli animali, tramite l''osservazione dei cartellini posti sui silos e la valutazione dell''idoneità degli ambienti deputati alla conservazione dei mangimi, per poi verificare l''adeguatezza della razione somministrata ai fabbisogni dell''animale in quello specifico periodo di deposizione, sia pe composizione che per quantità e intervalli di somministrazione, tenendo in considerazione il sistema di allevamento; come ausilio si può utilizzare il piano di razionamento utilizzato in azienda. Oltre a questo è consigliabile, per effettuare una valutazione più completa e accurata, ispezionare visivamente l''alimento contenuto all''interno delle mangiatoie, per verificarne sia la granulometria che l''eventuale presenza di mangime vecchio o contaminato. L''alimentazione non è adeguata ai fabbisogni e/o non è composta da alimenti sani Livello di non conformità: no; NO L''alimentazione è adeguata ai fabbisogni e composta da alimenti sani Livello di non conformità: SI','46) L''alimentazione è adeguata in rapporto all''età, al peso e alle esigenze comportamentali e fisiologiche degli animali Gli alimenti o i liquidi sono somministrati agli animali in modo da non causare loro inutili sofferenze o lesioni e non contengono sostanze che possano causare inutili sofferenze o lesioni','S, NA, NB, NC, -','(*) Descrivere eventualmente la razione, le modalità di calcolo, il nominativo dell''alimentarista o del veterinario che l''ha calcolata, ecc.',46);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,251,1264,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Tipologia di alimentazione','146/2001 All. Mangimi, acqua e altre sostanze Punto 15 "15. Tutti gli animali devono avere accesso ai mangimi ad intervalli adeguati alle loro necessità fisiologiche." L''alimentazione dovrebbe essere somministrata ad libitum per garantire ad ogni animale di alimentarsi adeguatamente, secondo le esigenze.Nel caso di alimentazione frazionata gli alimenti devono essere uniformemente distribuitinell''arco delle ore diurne. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Accesso all''alimento ad intervalli non adeguati: indicativamente alimentazione non garantita uniformemente nelle ore diurne e/o distribuita con intervalli non adeguati alle necessità fisiologiche degli animali Livello di non conformità: no; NO Accesso all''alimento ad intervalli adeguati: indicativamente alimentazione garantita uniformemente nelle ore diurne e distribuita correttamente con intervalli adeguati alle necessità fisiologiche degli animali Livello di non conformità: SI','47) Se non viene praticata l''alimentazione ad libitum o con sistemi automatici è assicurato l''accesso agli alimenti a tutti gli animali contemporaneamente per evitare competizioni a intervalli adeguati alle loro necessità fisiologiche','S, NA, NB, NC, -','(*) Descrivere le modalità di somministrazione dell''alimento ed eventualmente gli intervalli tra le varie somministrazioni.',47);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,252,1265,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Qualità dell''acqua','146/2001 All. Mangimi, acqua e altre sostanze Punto 16 "16. Tutti gli animali devono avere accesso ad un''appropriata quantità di acqua, di qualità adeguata [...]" In campo zootecnico non esistono norme specifiche relative alle caratteristiche qualitative delle acque destinate all''abbeverata degli animali, ma è buona pratica accertarsi che l''acqua per abbeverata sia di buona qualità e pulita. È necessario valutare l''approvvigionamento dell''acqua: - acqua di acquedotto: si può considerare di qualità adeguata. Verificare l''effettivo consumo dai contatori e/o dalle bollette p assicurarsi che si tratti effettivamente solo di acqua di acquedotto e non sia mescolata con acqua di diverse provenienze - acqua di pozzo o acque di superficie (es. lago): è necessario condurre verifiche su eventuali trattamenti (clorazione, filtrazione depurazione) ed eventualmente sulla qualità di tipo microbiologico e chimico (si consiglia una frequenza almeno biennale, salv eventuali problemi particolari) Per valutare la rispondenza al requisito, il Veterinario Ufficiale può considerare: - la tipologia di approvvigionamento - gli eventuali trattamenti dell''acqua e la loro tipologia - le caratteristiche chimiche e microbiologiche valutate mediante analisi periodiche - l''ispezione visiva dell''acqua che fuoriesce dagli abbeveratoi; - Si considera ottimale la presenza di un controllo annuo sia chimico che microbiologicoe la presenza e la completezza di una POS relativa alla gestione della qualità delle acque. Si valuti la qualità dell''acqua di abbeverata. Per la valutazione della quantità di acqua disponibile, si rimanda all''item "Disponibilità di abbeveratoi". I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. - Acqua di pozzo o di superficie non sottoposta a idonei trattamenti o a controlli analitici Livello di non conformità: no; NO - Acqua di pozzo o di superficie sottoposta a idonei trattamenti o a controlli analitici Livello di non conformità: SI - Acqua di acquedotto oppure acqua di pozzo o di superficie sottoposta ad almeno un controllo microbiologico e chimico annuo e presenza di una procedura operativa relativa alla gestione dei trattamenti e degli esami Livello di non conformità: Ottimale','48) L''acqua di abbeverata è di buona qualità','S, NA, NB, NC, -,O','(*) Specificare le modalità di approvvigionamento dell''acqua. Descriveregli eventuali trattamenti eseguiti sull''acqua e gli eventuali controlli analitici, con relative date di esecuzione',48);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,253,1266,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Disponibilità di mangiatoie','146/2001 All. Mangimi, acqua e altre sostanze Punto 17 "17. Le attrezzature per la somministrazione di mangimi e di acqua devono essere concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua e le conseguenze negative derivanti da rivalità tra gli animali." 267/2003 All. D, punto 1, b) GABBIE MODIFICATE - "Le galline ovaiole dispongono di mangiatoia utilizzabile senza limitazioni con lunghezza minima di 12 cm moltiplicata per il numero di ovaiole nella gabbia" 267/2003 All. B, punto 1, a).1) SISTEMI ALTERNATIVI - "Gli impianti di allevamento sono attrezzati in modo da garantire che tutte le galline ovaiole dispongano: di mangiatoie lineari che offrono almeno 10 cm di lunghezza per volatile o circolari che offrono almeno 4 cm di lunghezza per volatile" Verificare se lo spazio disponibile in mangiatoia per ciascun animale è conforme o meno alla normativa vigente Le mangiatoie non sono strutturate in maniera adeguata Livello di non conformità: no; NO Le mangiatoie sono strutturate in maniera adeguata Livello di non conformità: SI','49) Tutti gli animali hanno accesso all''alimento con una frequenza adeguata alle loro necessità riducendo al minimo le competizioni derivanti dalla rivalità tra animali','S, NA, NB, NC, -','(*) Descrivere le modalità di accesso all''alimento, riportare gli spazi disponibili per l''alimentazione',49);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,254,1267,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Disponibilità di abbeveratoi','146/2001 All. Mangimi, acqua e altre sostanze Punto 17 "Gli abbeveratoi sono strutturati per evitare la competizione e ridurre al minimo la contaminazione dell''acqua" 267/2003 All. D, punto 1, c) GABBIE MODIFICATE -"Le galline ovaiole dispongono di un sistema di abbeveraggio appropriato tenuto conto in particolare dell dimensione del gruppo - nel caso di abbeveraggio a raccordo, almeno due tettarelle o coppette sono raggiungibili da ciascuna ovaiola" 267/2003 All. B, punto 1, a). 2) SISTEMI ALTERNATIVI - "Abbeveratoi continui che offrono 2,5 cm di lunghezza per ovaiola o circolari che offrono 1 cm di lunghezza per ovaiola, in caso di utilizzazione di abbeveratoio a tettarella o a coppetta è prevista almeno una tettarella o una coppetta ogni 10 ovaiole. Nel caso di abbeveratoio a raccordo, almeno due tettarelle o due coppette devono essere raggiungibili da ciascuna ovaiola" Verificare se lo spazio disponibile di abbeverata per ciascun animale è conforme o meno alla normativa vigente. Gli abbeveratoi non sono strutturati in maniera adeguata Livello di non conformità: no; NO Gli abbeveratoi sono strutturati in maniera adeguata Livello di non conformità: SI','50) Tutti gli animali hanno accesso all''acqua con una frequenza adeguata alle loro necessità riducendo al minimo le competizioni derivanti dalla rivalità tra animali','S, NA, NB, NC, -',' (*) Descrivere la tipologia di abbeveratoio utilizzata (continui, niplles, circolari) ed in caso di non conformità lo spazio a disposizione di ciascun animale',50);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,255,1268,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Somministrazione di sostanze illecite','146/2001 All. Mangimi, acqua e altre sostanze punto 18. "18. Nessuna altra sostanza, ad eccezione di quelle somministrate a fini terapeutici o profilattici o in vista di trattamenti zootecnici come previsto nell''art. 1, paragrafo 2, lettera c) della direttiva 96/22/CE, deve essere somministrata ad un animale, a meno che gli studi scientifici sul benessere degli animali e l''esperienza acquisita ne abbiano dimostrato l''innocuità per la sua salute e il benessere". Verificare il registro o le registrazioni elettroniche dei trattamenti e l''armadietto o eventuali confezioni o giacenze di farmaci. Somministrazione di sostanze non consentite Livello di non conformità: NO Sostanze non consentite non somministrate Livello di non conformità: SI','51) Non viene somministrata alcuna sostanza, ad eccezione di quelle somministrate a fini terapeutici o profilattici o in vista di trattamenti zootecnici come previsto dalla normativa vigente I trattamenti terapeutici e profilattici','S, NC, -','(*) Descrivere le verifiche condotte',51);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,256,1269,'MUTILAZIONI','Mutilazioni','146/2001 All. Mutilazioni Punto 19 È vietata la bruciatura dei tendini ed il taglio di ali per i volatili se non a fini terapeutici certificati. Il taglio del becco deve esser effettuato nei primi giorni di vita con il solo uso di apparecchiature che riducano al minimo le sofferenze degli animali. Le pratiche di cui al presente punto sono effettuate sotto il controllo del medico veterinario dell''azienda" 267/2003 All.A Punto 8 "Fatte salve le disposizioni di cui al numero 19 dell''allegato al decreto legislativo 26 marzo 2001,n.146, è vietato qualsiasi tipo di mutilazione. Tuttavia, al fine di prevenire plumofagia e cannibalismo, è consentito il taglio del becco, a condizione che sia effettuata da personale qualificato su pulcini di età inferiore a dieci giorni destinati alla deposizione di uova sotto la responsabilità del veterinario" Per mutilazione si intende una pratica non effettuata per fini terapeutici o diagnostici, che si manifesta quale danno o perdita di una parte sensibile del corpo o quale alterazione della struttura dell''osso. Verificare se sono presenti animali con mutilazioni (es. taglio del becco). Se queste sono eseguite e sono riconducibili al periodo di permanenza dell''animale nell''azienda ispezionata, deve essere verificato il registro dei trattamenti (cartaceo o elettronico) per controllare se contestualmente è stato effettuato/prescritto dal veterinario un trattamento anestetico e analgesico. Tutti i trattamenti, che prevedono operazioni cruente, devono essere eseguiti con materiali sterili o a perdere ed espletati in modo da evitare all''animale dolore o sofferenza prolungata o non necessaria. Se sono presenti animali con debeccaggio effettuato in n azienda diversa da quella sottoposta ad ispezione il requisito si considera applicabile solo per la presenza/assenza di mutilazioni consentite/vietate. Si considera ottimale la mancanza di qualsiasi mutilazione su tutti gli animali. Presenza di animali con mutilazioni vietate o mutilazioni consentite che non rispettano i requisiti richiesti dalla normativa Livello di non conformità: NO Presenza di animali con mutilazioni che rispettano i requisiti richiesti dalla normativa Livello di non conformità: SI Tutti gli animali sono integri e non presentano mutilazioni Livello di non conformità: Ottimale','52) Sono rispettate le disposizioni pertinenti in caso di mutilazioni/castrazioni effettuate sugli animali?','S, NC, -,O',' (*) Descrivere l''eventuale tipo di mutilazione/incisione/castrazione. Descrivere le modalità ed i medicinali utilizzati, riportare il nominativo del veterinario, ecc.',52);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,257,1270,'PROCEDURE D''ALLEVAMENTO','Disposizioni generali','146/2001 Art. 2 Comma 1 Obblighi dei proprietari, dei custodi dei detentori degli animali 1. Il proprietario o il custode ovvero il detentore deve: a) adottare misure adeguate per garantire il benessere dei propri animali e affinché non vengano loro provocati dolore, sofferenze o lesioni inutili; b) allevare e custodire gli animali [omissis] in conformità alle disposizioni di cui all''allegato. 146/2001 All. Procedure di allevamento punto 20 "Non devono essere praticati l''allevamento naturale o artificiale o procedimenti di allevamento che provochino o possano provocare agli animali in questione sofferenze o lesioni. Questa disposizione non impedisce il ricorso a taluni procedimenti che possono causare sofferenze o ferite minime o momentanee o richiedere interventi che non causano lesioni durevoli, se consentiti dalle disposizioni nazionali". N. prot. DGSAF 0023052-03/12/2013 "Muta forzata: muta indotta attraverso la totale sospensione di alimento (mangime/acqua) e/o mancata applicazione dei programmi luce previsti dalle norme vigenti in materia di benessere animale. Tale pratica è vietata. Muta non forzata: indotta senza ricorrere alla sospensione totale di alimento e nel rispetto dei programmi luce previsti dalle norme vigenti in materia di benessere animale." La muta non forzata se applicata deve essere conforme alle disposizioni contenute nelle check list come da nota ministeriale N. prot. DGSAF 23052 del 03/12/2013 e 14833 del 19/06/2017 Valutare se vengono praticate procedure d''allevamento in contrasto con una o più delle 5 libertà, tali da causare evidenze negative sullo stato degli animali, in particolare se viene praticata la muta forzata o non forzata e in quest''ultimo caso se è stata invia comunicazione al Servizio Veterinario. N. prot. DGSAF 0023052-03/12/2013 Si considera non adeguato: la gestione delle fasi produttive in allevamento ha evidenze negative sullo stato degli animali Livello di non conformità: NO Si considera adeguato: la gestione delle fasi produttive in allevamento non evidenzia alcun effetto negativo sullo stato degli animali Livello di non conformità: SI','53) Non sono praticati procedimenti di allevamento, inclusa la muta forzata, che provocano o possano provocare agli animali sofferenze o lesioni. Nel caso si ricorra a taluni procedimenti che possono causare sofferenze o ferite minime o momentanee o richiedere interventi che non causano lesioni durevoli, sono consentiti dalle disposizioni vigenti','S, NC, -','(*) In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate. Le valutazioni ABM possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità. In caso di muta non forzata citare, se presente, gli estremi della comunicazione al servizio veterinario. In caso di muta forzata o non forzata in assenza di comunicazione, descrivere le evidenze sugli animali e sulle produzioni che hanno portato a confermare la non conformità (es. morrtalità, diminuzione del consumo di mangime o di acqua, diminuzione o sospensione della deposizione)',53);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (45,258,1271,'ABM','Mortalità settimanale','La mortalità settimanale media si ottiene sommando le mortalità settimanali diviso il numero di settimane dall''accasamento. La mortalità settimanale è definita come il numero di morti (inclusi i soppressi) registrati nell''arco di una settimana, diviso il numero di animali presenti il settimo giorno precedente, espresso in percentuale. È equivalente ricorrere al dato di mortalità cumulativa più recente da dividere per il numero delle settimane intercorse dall''accasamento. Il dato è attendibile se valutato su un ciclo di produzione che abbia superato almeno la 45° settimana dall''accasamento delle pollastre, diversamente prendere in considerazione anche il dato di mortalità settimanale media del ciclo precedente. Mortalità settimanale media superiore a 0,20% Livello di non conformità: Insufficiente Mortalità settimanale media compresa tra 0,10 % e 0,20% Livello di non conformità: Adeguato Mortalità settimanale media inferiore a 0,10% Livello di non conformità: Ottimale','54) Mortalità oltre i limiti','A, I, -, O','(*) Registrare la mortalità osservata e le possibili cause di mortalità elevata',54);

