CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_tacchini(
	_username_ws_bdn text,
	_password_ws_bdn text,
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE STRICT PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
BEGIN

  msg := concat('<?xml version="1.0" encoding="UTF-8"?>
<S:Envelope xmlns:S="http://schemas.xmlsoap.org/soap/envelope/">','
',
(select get_chiamata_ws_insert_ba_sa_header from get_chiamata_ws_insert_ba_sa_header(_username_ws_bdn, _password_ws_bdn)),'
',
'<S:Body>
      <ns2:insertControlliallevamentiBA xmlns:ns2="http://ws.izs.it">','
      ',(select get_chiamata_ws_insert_ba_v7_tacchini_controlli_allevamenti from get_chiamata_ws_insert_ba_v7_tacchini_controlli_allevamenti(_id)),'
      ',(select get_chiamata_ws_insert_ba_v7_tacchini_dettaglio_checklist from get_chiamata_ws_insert_ba_v7_tacchini_dettaglio_checklist(_id)),'
      </ns2:insertControlliallevamentiBA>
      </S:Body> 
      </S:Envelope>');
    
 RETURN msg;
 END;
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_tacchini(text, text, integer)
    OWNER TO postgres;

insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,404,1002,'PERSONALE','Numero di addetti che si occupano degli animali','146/2001 All. Personale Punto 1 "Gli animali sono accuditi da un numero sufficiente di addetti" Il numero di addetti deve essere sufficiente per garantire la salute e il benessere di tutti gli animali in allevamento: verificare il rapporto n. addetti - superficie allevabile. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Numero non sufficiente di addetti: indicativamente un operatore per più di 20000 maschi o 35000 femmine Livello della non conformita'': no; NO Numero accettabile di addetti: indicativamente un operatore per 15000-20000 maschi o 25000-35000 femmine Livello della non conformita'': SI Numero ottimale di addetti: indicativamente un operatore per meno di 15000 maschi o 25000 femmine Livello della non conformita'': Ottimale','1) Gli animali sono accuditi da un numero sufficiente di addetti','S, NA, NB, NC, - , O','(*) Indicare il numero degli addetti',1);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,405,1003,'PERSONALE','Formazione degli addetti','146/2001 All. Personale Punto 1 "Gli addetti devono avere adeguate capacità, conoscenze e competenze professionali" Valutare le competenze complessive del personale addetto agli animali, che possono essere sia di origine pratica (perché fornite dall''esperienza) sia di origine teorica (es. corso di formazione/titolo di studio). Si considera accettabile una lunga esperienza pratica nel settore oppure, in assenza di essa (es. giovani allevatori) il conseguimento di titoli di studio inerenti (diploma o laurea in agraria, veterinaria e lauree brevi simili) o la partecipazione a corsi di formazione specifici sul benessere animale. Per assegnare un giudizio ottimale è necessario possedere entrambi i requisiti: esperienza prolungata e titolo di studio/formazione specifica. I corsi di formazione o aggiornamento dovrebbero essere ripetuti con cadenza regolare durante il periodo lavorativo (almeno 1 corso ogni 3 anni). Se l''allevamento è seguito da più operatori, si considera sufficiente la partecipazione ai corsi di formazione anche di un solo addetto (sia esso il titolare o il dipendente assunto). I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Esperienza indicativamente minore di 10 anni e nessun corso di formazione sul benessere animale Livello della non conformita'': no; NO Esperienza indicativamente di almeno 10 anni e nessun corso di formazione sul benessere animale/titolo di studio Livello della non conformita'': SI Esperienza indicativamente di almeno 10 anni con titolo di studio o corso di formazione attinente, seguito negli ultimi 3 anni Livello della non conformita'': Ottimale','2) Il personale addetto agli animali dispone di capacità e conoscenze adeguate e ha ricevuto istruzioni pratiche sulle pertinenti diposizioni normative','S, NA, NB, NC, - , O','(*) Indicare, presenza di istruzioni pratiche scritte correttamente comunicate ed estremi dei corsi di formazione frequentati, con periodicità e ricaduta a livello aziendale, ecc.',2);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,406,1004,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Numero di ispezioni','146/2001 All. Controllo Punto 2 "Tutti gli animali tenuti in sistemi di allevamento, il cui benessere richieda un''assistenza frequente dell''uomo, sono ispezionati almeno una volta al giorno. Gli animali allevati o custoditi in altri sistemi sono ispezionati a intervalli sufficienti al fine di evitare loro sofferenze." Tutti i tacchini presenti nello stabilimento devono essere ispezionati, prestando particolare attenzione ai segni che rivelano un abbassamento del livello di benessere e/o di salute degli animali. Qualora sussista una registrazione scritta/computerizzata delle problematiche riscontrate durante le ispezioni, può essere assegnato il giudizio ottimale. Meno di 1 ispezione/giorno. Livello della non conformita'': no; NO Almeno 1 ispezione/giorno Livello della non conformita'': SI 2 o più ispezioni al giorno e registrazione scritta o computerizzata delle eventuali problematiche riscontrate Livello della non conformita'': Ottimale','3) Gli animali stabulati sono ispezionati almeno una volta al giorno.','S, NA, NB, NC, - , O','(*) Indicare il numero di ispezioni e le modalità con le quali il dato viene raccolto.',3);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,407,1005,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Illuminazione per l''ispezione','146/2001 All. Controllo Punto 3 "Per consentire l''ispezione completa degli animali in qualsiasi momento, deve essere disponibile un''adeguata illuminazione fissa o mobile" L''intensità luminosa e la durata del periodo di luce dovrebbero permettere agli operatori di ispezionare adeguatamente tutti gli animali nell''arco della giornata. In aggiunta dovrebbe essere presente un''illuminazione fissa o mobile per consentire l''ispezione degli animali in qualsiasi momento, anche di notte, cosicché gli addetti possano intervenire in modo attento e senza rischi. Assenza di illuminazione artificiale per l''ispezione. Livello della non conformita'': no; NO Presenza di illuminazione artificiale corretta e funzionante Livello della non conformita'': SI','4) È disponibile un''adeguata illuminazione che consente l''ispezione completa degli animali.','S, NA, NB, NC, -','(*) Descrivere le modalità di illuminazione durante le ore diurne o notturne',4);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,408,1006,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Trattamento degli animali malati o feriti','146/2001 All. Controllo Punto 4 "Gli animali malati o feriti devono ricevere immediatamente un trattamento appropriato. [...]. Ove necessario gli animali malati o feriti vengono isolati in appositi locali muniti, se del caso, di lettiere asciutte o confortevoli." Per avere evidenza che gli animali malati o feriti ricevano un trattamento adeguato, verificare, tramite intervista agli addetti, se siano state impartite precise istruzioni, anche di tipo orale. Punti da verificare: 1. La verifica dell''immediato trattamento e/o isolamento degli animali malati: si attua verificando che gli animali malati o feriti siano stati identificati, abbiano ricevuto un trattamento appropriato e che siano stati spostati in un ambiente adibito ad infermeria. Valutare le dichiarazioni e/o la presenza di trattamenti farmacologici in atto nelle registrazioni in Vetinfo farmacosorveglianza; 2. Il consulto con un veterinario: verificare se è dichiarata o documentata la presenza del veterinario aziendale o di un veterinario libero professionista o privato che segue l''azienda. Per assegnare il giudizio conforme tutte le condizioni devono essere soddisfatte. Si considera non adeguato: evidenza di personale non istruito e/o presenza di animali che necessitano di un trattamento e non lo hanno ancora ricevuto (n.b. valutare con attenzione la possibile insorgenza iperacuta dei casi di malattia) e/o assenza di un veterinario che segue l''azienda. Si considera adeguato: presenza di personale istruito ed evidenza che i due punti di verifica sopra enunciati siano soddisfatti. Il requisito ottimale prevede, oltre ai criteri per l''adeguatezza, la presenza di relative procedure scritte per il trattamento degli animali. Evidenza di personale non istruito e/o presenza di animali che necessitano di un trattamento e non lo hanno ancora ricevuto e/o assenza di un veterinario che segue l''azienda Livello della non conformita'': no; NO Presenza di personale istruito ed evidenza che i due punti di verifica sopra enunciati siano soddisfatti Livello della non conformita'': SI Oltre ai criteri per l''adeguatezza, presenza di relative procedure scritte per il trattamento degli animali Livello della non conformita'': Ottimale','5) Gli animali feriti o malati ricevono immediatamente un trattamento appropriato.','S, NA, NB, NC, - , O','(*) Indicare se sono presenti animali mal gestiti, malati o feriti e le modalità di detenzione, se l''infermeria o altro settore sono, all''occorrenza, adattabili ad accogliere ed isolare animali deboli, malati o feriti.',5);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,409,1007,'ISPEZIONE E CONTROLLO DEGLI ANIMALI','Abbattimento','146/2001 All. Punto 4 "Gli animali malati o feriti devono ricevere immediatamente un trattamento appropriato (...)" Reg CE 1099/2009, Capo II Articolo 7 Livello di competenze e certificato di idoneità 1. L''abbattimento e le operazioni correlate sono effettuati esclusivamente da persone che abbiano un adeguato livello di competenze per l''esecuzione di dette operazioni senza causare agli animali dolori, ansia o sofferenze evitabili. Reg CE 1099/2009, Capo IV Articolo 19 "Nel caso di abbattimenti di emergenza, la persona che ha in custodia gli animali interessati adotta tutti i provvedimenti necessari per abbattere gli animali nel più breve tempo possibile." Se i tacchini sono malati o feriti a tal punto che non è più eseguibile un trattamento e il trasporto verso il macello causerebbe sofferenze supplementari, essi devono essere abbattuti in azienda seguendo metodi in accordo con il Regolamento CE 1099/2009. Valutare che tali pratiche siano svolte da personale competente (e. proprietario/detentore adeguatamente formato, eutanasia effettuata da un veterinario oppure ricorso a personale provvisto del certificato d''idoneità alla macellazione) e che siano presenti delle istruzioni (es. materiale didattico del corso frequentato dal proprietario/detentore, numeri da contattare in caso d''emergenza, modalità di manutenzione degli strumenti, ecc.). È tuttavia possibile, in casi eccezionali, effettuare l''abbattimento di animali che presentino patologie gravi, causa di sofferenze acute, anche senza possesso di un certificato di idoneità né di un''adeguata competenza. In tali situazioni, infatti, l''intervento di personale competente dotato di attrezzature idonee, non essendo immediato, potrebbe determinare un prolungamento delle sofferenze dell''animale. Questi eventi non devono avere tuttavia caratteristiche di routinarietà. Mancato ricorso a personale competente e/o assenza di istruzioni nel caso in cui l''abbattimento sia eseguito da parte di personale aziendale e/o uso di strumentazione non adeguata o non sottoposta a regolare manutenzione. Livello della non conformita'': no; NO Abbattimento effettuato da un veterinario, oppure da personale provvisto del certificato d''idoneità alla macellazione e/o da personale aziendale, in presenza di istruzioni o formato al riguardo (es. presenza di un corso di formazione con tematica specifica trattata) e/o uso di strumentazione adeguata e sottoposta a regolare manutenzione. Livello della non conformita'': SI Presenza di procedure scritte per il corretto abbattimento e distinte per le diverse categorie animali, indicando responsabilità, strumenti e verifiche periodiche per agevolare una corretta gestione dell''emergenza. Livello della non conformita'': Ottimale','6) Gli animali malati o feriti ricevono immediatamente un trattamento appropriato in caso di abbattimento...','S, NA, NB, NC, - , O','(*) Fare riferimento a procedure o istruzioni sull''abbattimento e ai piani di emergenza e alla formazione degli operatori. Indicare se ci sono evidenze di animali ammalati o feriti che non hanno ricevuto in maniera tempestiva un trattamento adeguato in caso di abbattimento. Le valutazioni ABM (lesioni, body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità.',6);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,410,1008,'TENUTA DEI REGISTRI (Registrazione dei dati)','Tenuta delle registrazioni dei dat','146/2001 All. Registrazione Punti 5-6. (i riferimenti al D.P.R. 8 febbraio 1954, n. 320, attualmente abrogato, si intendono fatti ai corrispondenti articoli dei decreti legislativi 5 agosto 2022, n.134 e n. 136) (i riferimenti al D.P.R. 8 febbraio 1954, n. 320, attualmente abrogato, si intendono fatti ai corrispondenti articoli dei decreti legislativi 5 agosto 2022, n.134 e n. 136) "Le mortalità sono denunciate ai sensi del D.P.R. 8 febbraio 1954 n. 320. I registri sono conservati per un periodo di almeno tre anni e sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta." Dlgs 134 art. 9 comma 10 "L''operatore provvede alla registrazione in BDN di tutte le informazioni inerenti agli animali identificati singolarmente, alle partite, agli insiemi e ai gruppi di animali, nonché agli eventi che li riguardano, con i tempi di cui al presente articolo e con i modi previsti nel manuale operativo anche ai fini della generazione del registro della propria attività nella stessa BDN. Tale registro sostituisce qualsiasi altro registro aziendale cartaceo o su altro supporto concernente l''identificazione e registrazione degli animali." D. Lgs. 136/2022, art. 6 "Notifica e comunicazione delle malattie all''Autorità competente"; art. 11 comma 4 "gli operatori nel caso in cui non siano ancora disponibili nel sistema «ClassyFarm.it»,le apposite funzionalità informatiche per la tipologia di stabilimento di cui sono responsabili, adempiono agli obblighi del presente articolo tenendo in modalità elettronica o cartacea, per cinque anni, la documentazione riguardante almeno le informazioni di cui all''allegato 3 del presente decreto"; Allegato 3 "...individuazione e registrazione e segnalazione delle mortalità anomale" Verificare in BDN la presenza del registro di carico e scarico e la corretta registrazione delle informazioni, al fine di valutare se si siano verificate eventuali mortalità anomale. Nel caso si siano verificate mortalità anomale, verificare che siano state correttamente individuate, registrate e segnalate. Assenza in BDN del registro di carico e scarico o mancata o non corretta registrazione delle informazioni e/ o evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': NO Presenza in BDN del registro di carico e scarico e corretta registrazione delle informazioni e mancata evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': SI','7) In BDN è presente il registro di carico e scarico, le informazioni vengono correttamente registrate e non sono presenti episodi di mortalità anomale non registrate','S, NC, -','(*) Annotare eventuali rilievi di mortalità anomale non registrate o di mancanza delle registrazioni delle informazioni in BDN',7);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,411,1009,'TENUTA DEI REGISTRI (Registrazione dei dati)','Tenuta del registro dei trattamenti farmacologici','146/2001 All. Registrazione Punti 5-6. "Il proprietario o il custode ovvero il detentore degli animali tiene un registro dei trattamenti terapeutici effettuati. La registrazione e le relative modalità di conservazione sono effettuate secondo quanto previsto dal decreto legislativo 27 gennaio 1992, n. 119, e successive modificazioni ed integrazioni e dal decreto legislativo 4 agosto 1999, n. 336. I registri sono conservati per un periodo di almeno tre anni e sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta" Sono presenti le registrazioni informatizzate dei trattamenti su Vetinfo Farmacosorveglianza, ai sensi della Legge 20 novembre 2017, n. 167 e s.m.i. Assenza delle registrazioni in Vetinfo farmacosorveglianza o non corretta registrazione dei trattamenti. Livello della non conformita'': NO Corretta registrazione dei trattamenti in Vetinfo farmacosorveglianza. Livello della non conformita'': SI','8) Le registrazioni in Vetinfo farmacosorveglianza vengono effettuate nei tempi stabiliti e i trattamenti sono regolarmente prescritti da un medico veterinario','S, NC, -','(*) Segnalare i trattamenti controllati a campione',8);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,412,1010,'LIBERTÀ DI MOVIMENTO','Spazio disponibile','146/2001 All. Libertà di movimento punto 7 "La libertà di movimento propria dell''animale, in funzione della sua specie e secondo l''esperienza acquisita e le conoscenze scientifiche, non deve essere limitata in modo tale da causargli inutili sofferenze o lesioni." Lo spazio disponibile per ciascun animale è strettamente connesso ad altri fattori, come la ventilazione, la temperatura ambientale e la qualità della lettiera e, se questi fattori non sono modificati in funzione dell''aumento di densità, il benessere animale può venir seriamente compromesso. Se l''animale non ha un sufficiente spazio a disposizione, si muoverà con maggiore difficoltà, non riuscirà ad evitare fenomeni aggressivi da altri conspecifici e non riuscirà a riposare tranquillamente. La densità all''interno del capannone necessaria per ciascun animale deve essere calcolata utilizzando lo spazio calpestabile disponibile per gli animali, che deve essere gradualmente adattato a seconda dell''età degli animali, al peso e al livello di gestione. Si dovrà verificare il numero degli animali presenti al momento della valutazione, moltiplicarlo per il peso medio e dividerlo per la superficie a disposizione degli animali. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Densità superiore a 60 kg di peso vivo per mq di superficie utile per i maschi, 56 kg di peso vivo per mq di superficie utile per le femmine Livello della non conformita'': no; NO Densità fino a 60 kg di peso vivo per mq di superficie utile per i maschi, 56 kg di peso vivo per mq di superficie utile per le femmine Livello della non conformita'': SI Densità pari o inferiore a 50 kg di peso vivo per mq di superficie utile per i maschi, 45 kg di peso vivo per mq di superficie utile per le femmine Livello della non conformita'': Ottimale','"9) Lo spazio a disposizione di ogni animale è sufficiente a consentirgli un''adeguata libertà di movimento ed è tale da non causargli inutili sofferenze o lesioni.','S, NA, NB, NC, - , O','(*) in caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. animali senza adeguato spazio; presenza di lesioni cutanee, ecc.). Le valutazioni ABM (lesioni, feather pecking, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',9);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,413,1011,'EDIFICI E LOCALI DI STABULAZIONE','Fabbricati e locali di stabulazione','146/2001 All. Fabbricati e locali di stabulazione punto 8-9 "I materiali che devono essere utilizzati per la costruzione dei locali di stabulazione e, in particolare, dei recinti e delle attrezzature con i quali gli animali possono venire a contatto, non devono essere nocivi per gli animali e devono poter essere accuratamente puliti e disinfettati. "I locali di stabulazione degli animali devono essere costruiti e mantenuti in modo che non vi siano spigoli taglienti o sporgenze tali da provocare lesioni agli animali." Tutti i materiali, le lettiere e le attrezzature utilizzati nei locali di stabulazione, nonché le superfici stesse, con cui gli animali possono venire a contatto, non devono essere nocivi per gli animali e non devono avere spigoli taglienti o sporgenze in grado di provocare traumi o lesioni. Parallelamente tutte queste attrezzature devono essere concepite, costruite e mantenute in modo tale da poter essere accuratamente pulite e disinfettate. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Ambienti di stabulazione sporchi, non gestiti e/o dannosi per gli animali. Livello della non conformita'': no; NO Ambienti di stabulazione puliti e gestiti sufficientemente Livello della non conformita'': SI','10) I materiali utilizzati per la costruzione dei locali di stabulazione che possono venire a contatto con gli animali non sono nocivi e devono poter essere puliti e disinfettati.','S, NA, NB, NC, -','(*) In caso di rilievi non conformi descrivere o documentare la o le condizioni rilevate (es. condizioni igieniche dei locali e dei materiali di stabulazione, frequenza di ripristino e ricambio della lettiera, condizioni igieniche degli animali). Le valutazioni ABM (pulizia animali, zoppie, lesioni cutanee) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la Conformità',10);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,414,1012,'EDIFICI E LOCALI DI STABULAZIONE','Infermeria','146/2001 All. Controllo punto 4 "Ove necessario gli animali malati o feriti vengono isolati in appositi spazi muniti, se del caso, di lettiere asciutte o confortevoli." Devono essere presenti settori dedicati agli animali malati o feriti, facilmente raggiungibili o allestibili nell''immediato in caso di necessità; essi devono essere chiaramente identificati, con presenza di lettiera asciutta e acqua e alimento in quantità sufficiente. Per garantire un adeguato confort agli animali, all''interno di questi locali la densità dev''essere ridotta. Nel reparto infermeria ci devono essere solo animali che presentino patologie o lesioni. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Nessuno spazio facilmente allestibile ed identificabile. Livello della non conformita'': no; NO Almeno 1 spazio facilmente allestibile e identificabile per capannone o spazio già allestito e con strutture adeguate se presenti degli animali. Livello della non conformita'': SI"','11) In caso di necessità, gli animali possono essere isolati in appositi spazi muniti, se del caso, di lettiera asciutta o confortevole','S, NA, NB, NC, -','(*) Descrivere, se del caso, le caratteristiche dell''infermeria',11);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,415,1013,'EDIFICI E LOCALI DI STABULAZIONE','Temperatura e umidità relativa','146/2001 All. Fabbricati e locali di stabulazione punto 10 "10. La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas devono essere mantenute entro limiti non dannosi per gli animali." Temperatura e umidità relativa sono parametri strettamente correlati al sistema di ventilazione, pertanto il valutatore dovrà considerare sia la presenza e l''adeguatezza di quest''ultimo, sia le condizioni microclimatiche rilevate al momento dell''ispezione nell''esprimere il giudizio. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. L''assenza di ventilazione meccanica e/o condizioni microclimatiche dannose per gli animali al momento dell''ispezione Livello della non conformita'': no; NO La presenza di ventilazione meccanica (agitatori o tunnel) e condizioni microclimatiche idonee per gli animali al momento dell''ispezione Livello della non conformita'': SI Presenza di condizioni microclimatiche ottimali per gli animali ottenute attraverso una ventilazione meccanica (tunnel) con sistemi di raffrescamento Livello della non conformita'': Ottimale','12) La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria sono mantenute entro limiti non dannosi per gli animali. All''atto dell''ispezione, T° e UR sono adeguate alle esigenze etologiche della specie e all''età degli animali','S, NA, NB, NC, - , O','(*) Descrivere come vengono garantite idonee condizioni microclimatiche',12);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,416,1014,'EDIFICI E LOCALI DI STABULAZIONE','Presenza di gas nocivi','146/2001 All. Fabbricati e locali di stabulazione Punto 10 "10. La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas devono essere mantenute entro limiti non dannosi per gli animali." Durante la valutazione dell''allevamento, sono considerati accettabili tenori di ammoniaca inferiori a 20 ppm e tenori di anidride carbonica inferiori a 3.000 ppm. Nel caso di concentrazioni di gas non adeguate (es. NH3>20 ppm) può essere rilevata la seguente sintomatologia negli animali: rossore delle congiuntive e lacrimazione; inoltre tali sintomi possono essere percepiti anche dal valutatore, con il forte tipico odore pungente negli ambienti di stabulazione. In caso di sospetto di condizione inadeguata, è necessario confermare la condizione, misurando la concentrazione dei gas nocivi in allevamento mediante un rilevatore di gas portatile da posizionare al centro del capannone all''altezza degli animali. Qualora non si abbia a disposizione questo strumento, nel caso di allevamenti che non presentano particolari odori o segnali che possano indicare una cattiva qualità dell''aria, si ritiene possibile assegnare il giudizio "conforme" senza necessariamente provvedere alla misurazione dei gas. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Evidenza di concentrazioni di gas dannose per gli animali (indicazioni di riferimento dei limiti: NH3 > 20 ppm; CO2 > 3000 ppm) Livello della non conformita'': no; NO Evidenza di concentrazioni di gas non dannose per gli animali (indicazioni di riferimento dei limiti: NH3 < 20 ppm; CO2 < 3000 ppm) Livello della non conformita'': SI','13) Le concentrazioni di gas sono mantenute entro limiti non dannosi per gli animali','S, NA, NB, NC, -','(*) Se del caso, registrare i valori misurati NH3 .... CO2.... a campione in tutti i capannoni, per confermare un sospetto di condizione Inadeguata',13);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,417,1015,'EDIFICI E LOCALI DI STABULAZIONE','Polverosità dell''aria','146/2001 All. Fabbricati e locali di stabulazione Punto 10 "10. La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas devono essere mantenute entro limiti non dannosi per gli animali." Negli allevamenti avicoli, la polverosità dell''aria è generalmente provocata da particelle di pelle, di mangime, di lettiera e di penne. Nella pratica, per valutare la polverosità dell''aria in modo semplice e veloce, si consiglia l''utilizzo del "Test della polvere sul foglio di carta", descritto nel protocollo di valutazione del benessere degli avicoli Welfare Quality® (2009). Il metodo prevede l''utilizzo di un foglio A4 nero, che dovrà essere posizionato al momento dell''ingresso nel capannone, su una superficie orizzontale sufficientemente alta da evitare il contatto con gli animali, preferibilmente lontano dalla catena di alimentazione. Alla fine della valutazione rimuovere il foglio di carta e passare un dito sulla superficie per avere l''impressione della quantità di polvere che si è depositata nel frattempo. Classificare il livello di polvere come segue: a. Assenza di polvere, b. Lieve presenza di polvere; c. Leggera copertura; d. Molta polvere; e. Il colore della carta è nascosto dalla polvere. Il giudizio insufficiente è assegnato per condizioni di polverosità d ed e; giudizio accettabile per c e b; ottimale per a. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Quantità di polvere dannosa per gli animali Livello della non conformita'': no; NO Quantità di polvere non dannosa per gli animali Livello della non conformita'': SI Assenza di polvere Livello della non conformita'': Ottimale','14) La quantità di polvere è mantenuta entro limiti non dannosi per gli animali','S, NA, NB, NC, - , O','(*) Descrivere il livello di polverosità riscontrato',14);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,418,1016,'EDIFICI E LOCALI DI STABULAZIONE','Illuminazione','146/2001 All. Fabbricati e locali di stabulazione Punto 11 "11. [...] Se la luce naturale disponibile è insufficiente a soddisfare esigenze comportamentali e fisiologiche degli animali, occorre prevedere un''adeguata illuminazione artificiale." Le fonti di luce artificiale dovrebbero essere installate in modo da non causare disagio agli animali. In caso di sospetto di condizione inadeguata, è necessario confermare con luxometro. Assenza o insufficienza di illuminazione naturale o artificiale. Livello della non conformita'': no; NO Presenza di illuminazione naturale o artificiale adeguata Livello della non conformita'': SI','15) E'' presente un''adeguata illuminazione naturale o, se insufficiente, è prevista un''adeguata illuminazione artificiale','S, NA, NB, NC, -','(*) Registrare il tipo di illuminazione naturale/artificiale/assente e in caso di rilievo non conforme, confermare con luxometro.',15);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,419,1017,'EDIFICI E LOCALI DI STABULAZIONE','Programma di luce','146/2001 All. Fabbricati e locali di stabulazione Punto 11 "11. "Gli animali non devono essere tenuti costantemente al buio o esposti ad illuminazione artificiale senza un adeguato periodo di riposo. Se la luce naturale disponibile è insufficiente a soddisfare esigenze comportamentali e fisiologiche degli animali, occorre prevedere un''adeguata illuminazione artificiale." La luce deve seguire indicativamente un ciclo di 24 ore e comprendere un periodo di oscurità sufficiente e ininterrotto, a titolo indicativo pari a circa un terzo della giornata. Per verificare la conformità al requisito, il valutatore potrà intervistare l''allevatore o, se presente, controllare il programma di luce impostato sulla centralina. Dopo aver considerato tutti i fattori sopra riportati, il valutatore potrà assegnare un giudizio migliorativo se nell''allevamento sono stati inseriti dei sistemi di controllo dell''illuminazione, i cosiddetti "dimmers", in grado di ricreare l''alba e il tramonto con un graduale passaggio dalla luce al buio in circa un''ora. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Il programma di luce non è adeguato Livello della non conformita'': no; NO Il programma di luce è adeguato Livello della non conformita'': SI Il programma di luce è adeguato e sono presenti dei sistemi di dimmeraggio della luce Livello della non conformita'': Ottimale','16) Il programma previsto è adeguato','S, NA, NB, NC, - , O','(*) Raccolta di informazioni tramite interviste agli operatori, controllo di eventuale apparecchiatura elettronica di controllo automatico delle luci.',16);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,420,1018,'EDIFICI E LOCALI DI STABULAZIONE','Presenza di ripari nelle aree esterne per gli animali custoditi al di fuori dei fabbricati','146/2001 All. Animali custoditi al di fuori dei fabbricati Punto 12 "12. Agli animali custoditi al di fuori dei fabbricati deve essere fornito, in funzione delle necessità e delle possibilità, un riparo adeguato dalle intemperie, dai predatori e da rischi per la salute." Si considerano i ripari sia di tipo naturale (alberi, anfratti, grotte, ecc.) che di tipo artificiale (tettoie, ricoveri, ecc.) a condizione che offrano un riparo adeguato, in relazione alla stagione e alla località. Assenza, insufficienza o presenza di ripari non adeguati anche per un solo gruppo di animali Livello della non conformita'': no; NO Presenza di ripari sufficienti ed adeguati per tutti gli animali Livello della non conformita'': SI Presenza di ripari di tipo artificiale facilmente accessibili, sufficienti ed idonei a proteggere tutti gli animali dai pericoli ambientali Livello della non conformita'': Ottimale','17) Gli animali custoditi al di fuori dei fabbricati hanno ripari adeguati e sufficienti, o gli animali sono stabulati al chiuso','S, NA, NB, NC, - , O','(*) Descrivere i ripari, la loro localizzazione e le modalità con le quali gli animali li raggiungono',17);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,421,1019,'ATTREZZATURA AUTOMATICA E MECCANICA','Ispezione degli impianti automatici e meccanici','146/2001 All. Impianti automatici o meccanici Punto 13 "13. Ogni impianto automatico o meccanico indispensabile per la salute ed il benessere degli animali deve essere ispezionato almeno una volta al giorno." Il sistema di ventilazione (sistemi di sgancio finestre, agitatori etc.) e il sistema di distribuzione del mangime (coclea, sistema distributivo etc.) possono essere considerati impianti automatici o meccanici indispensabili per la salute degli animali; per questo devono essere ispezionati una volta al giorno e i difetti riscontrati eliminati immediatamente. Impianti automatici o meccanici ispezionati meno di 1 volta al giorno Livello della non conformita'': no; NO Impianti automatici o meccanici ispezionati almeno 1 volta al giorno Livello della non conformita'': SI Impianti automatici o meccanici ispezionati 2 o più volte al giorno e registrazione scritta o computerizzata dell''avvenuta ispezione (es. numero di ispezioni, malfunzionamenti, rettifiche) Livello della non conformita'': Ottimale','18) Gli impianti automatici o meccanici sono ispezionati almeno una volta al giorno','S, NA, NB, NC, - , O','(*) Raccolta di informazioni tramite interviste agli operatori, consultazione di manuali di buone pratiche e relative registrazioni',18);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,422,1020,'ATTREZZATURA AUTOMATICA E MECCANICA','Ispezione degli impianti automatici e meccanici','146/2001 All. Impianti automatici o meccanici Punto 13 "13. [...] Gli eventuali difetti riscontrati devono essere eliminati immediatamente; se cio'' non e'' possibile, occorre prendere le misure adeguate per salvaguardare la salute ed il benessere degli animali. [...]" Eventuali problemi riscontrati nel corso delle quotidiane ispezioni agli impianti automatici e meccanici devono essere immediatamente risolti. Il veterinario ufficiale dovrà verificare il regolare funzionamento di tali impianti o verificare tramite intervista quali sono le azioni che l''allevatore intraprende nell''immediato a seguito del verificarsi del problema. Il giudizio migliorativo può essere dato in presenza di un piano documentato della gestione di situazioni d''emergenza o di incidenti che possono minacciare il benessere degli animali (danno agli impianti di alimentazione/abbeveraggio, incendi, condizioni climatiche estreme) Rilievo di un difetto agli impianti automatici e/o meccanici non eliminato immediatamente / misure inadeguate a salvaguardare la salute degli animali Livello della non conformita'': no; NO Presenza di registrazioni che documentino la tempestiva risoluzione delle emergenze e/o misure adeguate a salvaguardare la salute degli animali Livello della non conformita'': SI Presenza di registrazioni che documentino la tempestiva risoluzione delle emergenze e/o misure adeguate a salvaguardare la salute degli animali e presenza di un piano documentato della gestione delle situazioni di emergenza Livello della non conformita'': Ottimale','19) Eventuali problemi a impianti automatici e meccanici sono gestiti tempestivamente e in modo corretto','S, NA, NB, NC, - , O','(*) Descrivere come viene garantita la gestione di eventuali problemi e con quali tempistiche',19);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,423,1021,'ATTREZZATURA AUTOMATICA E MECCANICA','Ispezione degli impianti automatici e meccanici','146/2001 All. Impianti automatici o meccanici Punto 13 "13. [...] Occorre prendere le misure adeguate per salvaguardare la salute ed il benessere degli animali. Se la salute ed il benessere degli animali dipendono da un impianto di ventilazione artificiale, deve essere previsto un adeguato impianto di riserva per garantire un ricambio d''aria sufficiente a salvaguardare salute e benessere degli animali" La presenza di un impianto di riserva funzionante in caso di emergenza è fondamentale per garantire un adeguato ricambio di aria (ad esempio sistema meccanico di apertura delle finestre); a tal fine è importante verificarne la funzionalità e la manutenzione costante. Se l''allevamento non necessita di un impianto di ventilazione forzata (es. idonea circolazione naturale dell''aria) il requisito si considera non applicabile. Il giudizio migliorativo può essere dato in presenza di un piano documentato della gestione di situazioni d''emergenza o di incidenti che possono minacciare il benessere degli animali (guasto degli impianti di ventilazione artificiale), descritto nei Manuali di corretta prassi operativa. Assenza / inadeguatezza dell''impianto di riserva, in caso di ventilazione artificiale. Livello della non conformita'': no; NO Presenza di un impianto di riserva adeguato Livello della non conformita'': SI Presenza aggiuntiva di un piano di emergenza formalizzato Livello della non conformita'': Ottimale','20) È previsto un adeguato impianto di riserva per garantire un ricambio d''aria sufficiente a salvaguardare la salute e il benessere degli animali','S, NA, NB, NC, - , O','(*) Descrivere che impianto di riserva viene utilizzato e le sue condizioni di manutenzione',20);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,424,1022,'ATTREZZATURA AUTOMATICA E MECCANICA','Sistema d''allarme','146/2001 All. Impianti automatici o meccanici Punto 13 "13. Se la salute ed il benessere degli animali dipendono da un impianto di ventilazione artificiale [...] In caso di guasto all''impianto deve essere previsto un sistema di allarme che segnali il guasto. Detto sistema d''allarme deve essere sottoposto a controlli regolari." Qualora per la salute ed il benessere degli animali sia presente e necessario un sistema di aerazione artificiale con ventilazione unicamente forzata, occorre prevedere un sistema di allarme che segnali eventuali guasti all''allevatore. Il sistema di allarme deve essere verificato regolarmente, soprattutto se il suo malfunzionamento metterebbe in serio rischio la salute ed il benessere degli animali. Se l''allevamento non necessita di un impianto di ventilazione forzata (es. idonea circolazione naturale dell''aria) il requisito si considera non applicabile. Assenza di un sistema di allarme all''impianto di ventilazione artificiale se presente Livello della non conformita'': no; NO Presenza di un sistema di allarme all''impianto di ventilazione artificiale Livello della non conformita'': SI Presenza aggiuntiva di un sistema di allarme riguardante altri impianti indispensabili per la sopravvivenza ed il benessere degli animali Livello della non conformita'': Ottimale','21) Se la salute e il benessere degli animali dipendono da un impianto di ventilazione artificiale, è previsto un sistema di allarme che segnali eventuali guasti','S, NA, NB, NC, - , O','(*) Registrare la verifica del corretto funzionamento del sistema di allarme e del sistema sostitutivo effettuata durante il controllo ufficiale; annotare la frequenza degli interventi di verifica e manutenzione effettuati dall''allevatore',21);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,425,1023,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Gestione dell''alimentazione','146/2001 All. Mangimi, acqua e altre sostanze Punto 14 "14. Agli animali deve essere fornita un''alimentazione sana adatta alla loro età e specie e in quantità sufficiente a mantenerli in buona salute e a soddisfare le loro esigenze nutrizionali. Gli alimenti o i liquidi sono somministrati agli animali in modo da non causare loro inutili sofferenze o lesioni e non contengono sostanze che possano causare inutili sofferenze o lesioni." I sistemi di alimentazione dovrebbero permettere ad ogni individuo di soddisfare i propri fabbisogni per quantità e qualità degli alimenti. La dieta dovrebbe fornire energia sufficiente, nutrienti e fibra alimentare tali da soddisfare i requisiti nutrizionali e rispettare la fisiologia digestiva e metabolica degli animali. Per poter soddisfare le esigenze nutrizionali degli animali e far sì che l''alimentazione sia adeguata al loro sviluppo corporeo, allo stato fisiologico e al livello produttivo è necessario che, per ogni fase del ciclo, sia presente una razione specificatamente calcolata. La corretta alimentazione degli animali è anche collegata alla qualità degli alimenti e dell''acqua che la compongono, che devono essere di origine conosciuta e conservati in ambienti idonei (es. silos, magazzini, sistema di distribuzione idrica) per evitare alterazioni e contaminazioni con sostanze tossico-nocive. L''alimentazione non è adeguata ai fabbisogni e/o non è composta da alimenti sani Livello della non conformita'': no; NO L''alimentazione è adeguata ai fabbisogni e composta da alimenti sani Livello della non conformita'': SI','22) L''alimentazione è adeguata in rapporto all''età, al peso e alle esigenze comportamentali e fisiologiche degli animali. Gli alimenti o i liquidi sono somministrati agli animali in modo da non causare loro inutili sofferenze o lesioni e non contengono sostanze che possano causare inutili sofferenze o lesioni','S, NA, NB, NC, -','(*) Descrivere eventualmente le caratteristiche dell''alimento somministrato. Le valutazioni ABM (condizione corporea, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',22);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,426,1024,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Tipologia di alimentazione','146/2001 All. Mangimi, acqua e altre sostanze Punto 15 "15. Tutti gli animali devono avere accesso ai mangimi ad intervalli adeguati alle loro necessità fisiologiche." L''alimentazione dovrebbe essere somministrata ad libitum per garantire ad ogni animale di alimentarsi secondo esigenza durante le 24 h. Nel caso di alimentazione frazionata, gli alimenti dovrebbero essere garantiti quasi costantemente nell''arco delle 24 h, e preferibilmente somministrati in almeno due occasioni. Accesso all''alimento ad intervalli non adeguati: indicativamente alimentazione non fornita per più di 12 ore e/o distribuita con intervalli non adeguati alle necessità fisiologiche degli animali Livello della non conformita'': no; NO Accesso all''alimento ad intervalli adeguati: indicativamente alimentazione fornita almeno ogni 12 ore e distribuita correttamente con intervalli adeguati alle necessità fisiologiche degli animali Livello della non conformita'': SI','23) Se non viene praticata l''alimentazione ad libitum o con sistemi automatici è assicurato l''accesso agli alimenti a tutti gli animali contemporaneamente ad intervalli adeguati alle loro necessità fisiologiche','S, NA, NB, NC, -','(*) Descrivere le modalità di somministrazione dell''alimento ed eventualmente gli intervalli tra le varie somministrazioni. Le valutazioni ABM (body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la conformità',23);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,427,1025,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Qualità dell''acqua','146/2001 All. Mangimi, acqua e altre sostanze Punto 16 "16. Tutti gli animali devono avere accesso ad un''appropriata quantità di acqua, di qualità adeguata, o devono poter soddisfare le loro esigenze di assorbimento di liquidi in altri modi." In campo zootecnico non esistono norme specifiche relative alle caratteristiche qualitative delle acque destinate all''abbeverata degli animali, ma è buona pratica accertarsi che l''acqua per abbeverata sia di buona qualità e pulita. È necessario valutare l''approvvigionamento dell''acqua: - acqua di acquedotto: si può considerare di qualità adeguata. Verificare l''effettivo consumo dai contatori e/o dalle bollette per assicurarsi che si tratti effettivamente solo di acqua di acquedotto e non sia mescolata con acqua di diverse provenienze - acqua di pozzo o acque di superficie (es. lago): è necessario condurre verifiche su eventuali trattamenti (clorazione, filtrazione, depurazione) ed eventualmente sulla qualità di tipo microbiologico (si consiglia una frequenza almeno biennale, salvo eventuali problemi particolari). Per valutare la rispondenza al requisito, il Veterinario Ufficiale può considerare: - la tipologia di approvvigionamento - gli eventuali trattamenti dell''acqua e la loro tipologia - le caratteristiche analitiche valutate mediante analisi periodiche - l''ispezione visiva dell''acqua che fuoriesce dagli abbeveratoi - Si considera ottimale un controllo analitico annuo e la presenza e la completezza di una POS relativa alla gestione della qualità delle acque. Si valuti la qualità dell''acqua di abbeverata. Per la valutazione della quantità di acqua disponibile, si rimanda all''item "Disponibilità di abbeveratoi". I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Acqua di pozzo o di superficie non sottoposta a idonei trattamenti o a controlli analitici Livello della non conformita'': no; NO Acqua di acquedotto o di pozzo/di superficie sottoposta a idonei trattamenti o a controlli analitici almeno biennale Livello della non conformita'': SI Acqua di acquedotto oppure di pozzo/di superficie sottoposta ad almeno un controllo microbiologico annuo e presenza di una procedura operativa relativa alla gestione dei trattamenti e degli esami Livello della non conformita'': Ottimale','24) La qualità dell''acqua fornita agli animali è adeguata','S, NA, NB, NC, - , O','(*) Descrivere le caratteristiche visive dell''acqua che fuoriesce dagli abbeveratoi e la documentazione visionata (analisi precedenti, eventuale POS relativa alla gestione della qualità delle acque)',24);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,428,1026,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Disponibilità di mangiatoie','146/2001 All. Mangimi, acqua e altre sostanze Punti 17 "17. Le attrezzature per la somministrazione di mangimi e di acqua devono essere concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua e le conseguenze negative derivanti da rivalità tra gli animali." Per la somministrazione del mangime sono utilizzate diverse tipologie di mangiatoie, generalmente circolari o lineari. Di seguito sono forniti gli spazi per ogni tipologia di mangiatoia (in cm) ed età (settimane): 0-22 settimane (Lineare: 1,9; Circolare: 1,52); 12-22 settimane (Lineare 3,8; Circolare 3,04). In caso di mangiatoia lineare, lo spazio necessario per tacchino è calcolato come spazio lineare mangiatoia per tacchino quando entrambi i lati della mangiatoia sono disponibili. Se solo un lato della mangiatoia è disponibile lo spazio necessario indicato deve essere raddoppiato. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio Le attrezzature per la somministrazione di mangimi non sono strutturate in maniera adeguata Livello della non conformita'': no; NO Le attrezzature per la somministrazione di mangimi sono strutturate in maniera adeguata Livello della non conformita'': SI','25) Tutti gli animali hanno accesso all''alimento con una frequenza adeguata alle loro necessità riducendo al minimo le competizioni derivanti dalla rivalità tra animali. Le attrezzature per la somministrazione di mangimi sono concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti e le conseguenze negative derivanti da rivalità tra gli animali.','S, NA, NB, NC, -','(*) Descrivere le modalità di accesso all''alimento, riportare gli spazi disponibili per l''alimentazione ... Le valutazioni ABM (body condition score, ecc.) possono supportare e dare un''evidenza della presenza di una eventuale non conformità o confermare la Conformità',25);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,429,1027,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Disponibilità di abbeveratoi','146/2001 All. Mangimi, acqua e altre sostanze Punto 16 e 17 16."Tutti gli animali devono avere accesso ad un''appropriata quantità di acqua, di qualità adeguata" [...] 17. "Le attrezzature per la somministrazione di mangimi e di acqua devono essere concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua e le conseguenze negative derivanti da rivalità tra gli animali." Per la somministrazione di acqua sono utilizzate diverse tipologie di abbeveratoi, generalmente a campana, a tazza o lineari. Di seguito sono forniti gli spazi per ogni tipologia di abbeveratoio (in cm) ed età (settimane): FEMMINE 0-16,5 settimane (Lineare: 1,27; A campana: 1,02; A tazza: 1 ogni 10 tacchini); MASCHI 0-8 settimane (Lineare 1,27; A campana: 1,02; A tazza: 1 ogni 20 tacchini); 8-16 settimane (Lineare 1,91; A campana: 1,53; A tazza: 1 ogni 10 tacchini); 16-20 settimane (Lineare 2,54; A campana: 2,03; A tazza: 1 ogni 10 tacchini). In caso di abbeveratoio lineare, lo spazio minimo necessario per gli abbeveratoi è calcolato come spazio lineare abbeveratoio per tacchino. Se solo un lato dell''abbeveratoio è disponibile lo spazio necessario indicato dev''essere raddoppiato. I limiti indicati sono solamente un ausilio per il valutatore che dovrà comunque considerare tutti i fattori di rischio dell''allevamento prima di esprimere il giudizio. Le attrezzature per la somministrazione di acqua non sono strutturate in maniera adeguata Livello della non conformita'': no; NO Le attrezzature per la somministrazione di acqua sono strutturate in maniera adeguata Livello della non conformita'': SI','26) Il numero di punti di abbeverata è in linea con le indicazioni fornite. È garantito per tutti gli animali l''accesso ad una appropriata quantità di acqua di qualità adeguata. Le attrezzature per la somministrazione di mangimi e di acqua sono concepite, costruite e installate in modo da ridurre al minimo le possibilità di contaminazione degli alimenti o dell''acqua e le conseguenze negative derivanti da rivalità tra gli animali','S, NA, NB, NC, -','(*) Registrare tipologia e numero degli abbeveratoi presenti in rapporto agli animali accasati',26);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,430,1028,'ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZIONE DI ALTRE SOSTANZE','Somministrazione di sostanze illecite','146/2001 All. Mangimi, acqua e altre sostanze punto 18 "18. Nessuna altra sostanza, ad eccezione di quelle somministrate a fini terapeutici o profilattici o in vista di trattamenti zootecnici come previsto nell''art. 1, paragrafo 2, lettera c) della direttiva 96/22/CE, deve essere somministrata ad un animale, a meno che gli studi scientifici sul benessere degli animali e l''esperienza acquisita ne abbiano dimostrato l''innocuità per la sua salute e il suo benessere". 158/2006 Art. 1 Comma 3 Lettera g. "3. Si intende [...] per: [...] g) trattamento illecito: l''utilizzazione di sostanze o prodotti non autorizzati, ovvero di sostanze o prodotti autorizzati, a fini o a condizioni diversi da quelli previsti dalle disposizioni vigenti;" Verificare il registro o le registrazioni elettroniche dei trattamenti e l''armadietto dei farmaci. Somministrazione di sostanze non consentite Livello della non conformita'': NO Sostanze non consentite non somministrate Livello della non conformita'': SI','27) Non viene somministrata alcuna sostanza, ad eccezione di quelle somministrate a fini terapeutici o profilattici o in vista di trattamenti zootecnici come previsto dalla normativa vigente I trattamenti terapeutici e profilattici sono regolarmente prescritti da un medico Veterinario','S, NC, -','(*) Descrivere le verifiche condotte',27);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,431,1029,'MUTILAZIONI','Mutilazioni e altre pratiche','146/2001 All. Mutilazioni Punto 19 "19. È vietata la bruciatura dei tendini ed il taglio delle ali per i volatili [...] se non a fini terapeutici certificati. [...] Il taglio del becco deve essere effettuato nei primi giorni di vita con il solo uso di apparecchiature che riducano al minimo le sofferenze degli animali. [...] Le pratiche di cui al presente punto sono effettuate sotto il controllo del medico veterinario dell''azienda" Per mutilazione si intende una pratica non effettuata per fini terapeutici o diagnostici, che si manifesta quale danno o perdita di una parte sensibile del corpo o quale alterazione della struttura dell''osso. Nell''allegato 1 punto 19 del D. L. vo 146/2001 è riportato il divieto del taglio delle ali, se non a fini terapeutici certificati e adeguatamente registrati; pertanto, il riscontro di tacchini con tali mutilazioni in assenza di documentazione redatta dal medico veterinario dell''allevamento che, sotto la propria responsabilità, giustifichi tale misura, deve essere valutato negativamente. Stessa valutazione può essere assegnata qualora la documentazione sia giudicata non idonea o non sufficiente a motivare il riscontro di ali tagliate. Il taglio del becco è vietato, se non effettuato nei primi giorni di vita e solo con l''utilizzo di apparecchiature che riducano al minimo la sofferenza degli animali. Si considera ottimale la mancanza di qualsiasi mutilazione e/o castrazione su tutti gli animali. Presenza di animali con mutilazioni vietate o mutilazioni consentite che non rispettano i requisiti richiesti dalla normativa Livello della non conformita'': NO Presenza di animali con mutilazioni che rispettano i requisiti richiesti dalla normativa Livello della non conformita'': SI Tutti gli animali sono integri e non presentano mutilazioni Livello della non conformita'': Ottimale','28) Sono rispettate le disposizioni pertinenti in caso di mutilazioni/castrazioni effettuate sugli animali','S, NC, -, O','(*) Descrivere l''eventuale tipo di mutilazione/incisione/castrazione e i capannoni dove sono alloggiati gli animali. Descrivere le modalità ed i medicinali utilizzati, riportare il nominativo del veterinario, ecc.',28);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,432,1030,'PROCEDURE DI ALLEVAMENTO','Disposizioni generali','146/2001 Art. 2 Comma 1 2. "Obblighi di proprietari, dei custodi dei detentori degli animali" "Il proprietario o il custode ovvero il detentore deve: a) Adottare misure adeguate per garantire il benessere dei propri animali e affinché non vengano loro provocati dolore, sofferenze o lesioni inutili; b) Allevare e custodire gli animali [omissis] in conformità alle disposizioni di cui all''allegato." Dlgs 146/2001 Allegato - Procedure di allevamento punto 20 "Non devono essere praticati l''allevamento naturale o artificiale o procedimenti di allevamento che provochino o possano provocare agli animali in questione sofferenze o lesioni. Questa disposizione non impedisce il ricorso a taluni procedimenti che possono causare sofferenze o ferite minime o momentanee o richiedere interventi che non causano lesioni durevoli, se consentiti dalle disposizioni nazionali." Valutare se vengono praticate procedure d''allevamento in contrasto con una o più delle 5 libertà, tali da causare evidenze negative sullo stato degli animali. Allevamento non consono alla specie valutata con evidenti limitazioni a livello di 5 libertà Livello della non conformita'': no; NO Allevamento consono alla specie valutata senza limitazioni delle 5 libertà Livello della non conformita'': SI','29) Non sono praticati procedimenti di allevamento che provocano o possano provocare agli animali sofferenze o lesioni. Nel caso si ricorra a taluni procedimenti che possono causare sofferenze o ferite minime o momentanee o richiedere interventi che non causano lesioni durevoli, sono consentiti dalle disposizioni vigenti','S, NA, NB, NC, -','(*) Descrivere, se del caso, le modalità di gestione degli animali in periodi critici della loro vita (es. pulcinaia, ultimi giorni di ingrasso, Ecc.)',29);
insert into chk_bns_domande_v7 (idmod, irr_id, dettirrid, descrizione, titolo, sottotitolo, quesito, esito, evidenze, level) values (46,433,1031,'ABM','Mortalità settimanale media','La mortalità settimanale media si ottiene sommando le mortalità settimanali diviso il numero di settimane dall''accasamento. La mortalità settimanale è definita come il numero di morti (inclusi i soppressi) registrati nell''arco di una settimana, diviso il numero di animali presenti il settimo giorno precedente, espresso in percentuale. Il dato può essere fornito dall''allevatore, in quanto deducibile dalle tabelle di mortalità giornaliera di ogni singolo capannone e sarà considerato adeguato qualora la percentuale di mortalità sia compresa tra lo 0,5 e lo 0,6%; mentre sarà considerato ottimale se inferiore allo 0,5%. Qualora l''allevatore non registri il dato per ogni singolo capannone, ma in forma cumulativa per tutto l''allevamento, il giudizio sarà insufficiente. % mortalità settimanale media> 0,6% Livello della non conformita'': Insufficiente % mortalità settimanale media tra 0,5% e 0,6% Livello della non conformita'': Adeguato % mortalità settimanale media< 0,5% Livello della non conformita'': Ottimale','30) Mortalita settimanale','A, I, -, O','(*) Registrare la mortalità osservata e le possibili cause di mortalità elevata.',30);



CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_tacchini_controlli_allevamenti(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
tag_preavviso text;
BEGIN

select 
concat('<controlliallevamenti>
	  <caId>0</caId>
             <dtControllo>', to_char(data_controllo, 'YYYY-MM-DD"T"00:00:00.000'),'</dtControllo>
            <aslCodice>',codice_asl,'</aslCodice>
            <aziendaCodice>',codice_azienda,'</aziendaCodice>
            <speCodice>',bdn.codice_specie_chk_bns,'</speCodice>
            <allevIdFiscale>',id_fiscale_allevamento,'</allevIdFiscale>
            <ocCodice>',bdn.occodice,'</ocCodice>
            <flagEsito>',tacchini.esito_controllo,'</flagEsito>
            <flagVitelli>',bdn.flag_vitelli,'</flagVitelli>
            <flagExtrapiano>',tacchini.extrapiano,'</flagExtrapiano>
            <detenIdFiscale>',id_fiscale_detentore,'</detenIdFiscale>
            <flagPreavviso>',bdn.flag_preavviso,'</flagPreavviso>
	    ',case when bdn.flag_preavviso <> 'N' then concat('<dataPreavviso>',to_char(bdn.data_preavviso, 'YYYY-MM-DD"T"00:00:00.000'),'</dataPreavviso>') else '' end,' 
	    ',case when bdn.flag_preavviso = 'A' then concat('<descAltroPreavviso>', bdn.descrizione_preavviso,'</descAltroPreavviso>') else '' end,'
            <primoControllore>',tacchini.nome_controllore,'</primoControllore>
            <tipoControllo>BA</tipoControllo>
            <flagCopiaCheckList>',bdn.flag_checklist,'</flagCopiaCheckList>
	    ',case when tacchini.esito_controllo = 'N' then concat(
	    '<sanzBloccoMov>',tacchini.sanz_blocco,'</sanzBloccoMov>
            <sanzAmministrativa>',tacchini.sanz_amministrativa,'</sanzAmministrativa>
            <sanzSequestro>',tacchini.sanz_sequestro,'</sanzSequestro>
            <sanzAbbattimentoCapi>',tacchini.sanz_abbattimento,'</sanzAbbattimentoCapi>
            <sanzInformativa>',tacchini.sanz_informativa,'</sanzInformativa>
            <sanzAltro>',tacchini.sanz_altro,'</sanzAltro>
            ',case when tacchini.sanz_altro <> '0' then concat('<sanzAltroDesc>',tacchini.sanz_altro_desc,'</sanzAltroDesc>')
	    else '' end,'
            <noteControllore>',tacchini.note_controllore,'</noteControllore>
            <noteDetentore>',tacchini.note_proprietario,'</noteDetentore>')
	    else '' end,'             
	    ',case when tacchini.esito_controllo <> 'A' then 
	    concat('<flagEvidenze>',tacchini.evidenze,'</flagEvidenze>')
	    else '' end,
	    case when tacchini.evidenze = 'S' then 
	    concat('<evidenzaIr>',tacchini.evidenze_ir,'</evidenzaIr>
            <evidenzaSv>',tacchini.evidenze_sv,'</evidenzaSv>
            <evidenzaSa>',tacchini.evidenze_tse,'</evidenzaSa>')
            else '' end,'
            <flagCondizionalita>',tacchini.appartenente_condizionalita,'</flagCondizionalita>
             ',case when tacchini.data_chiusura_relazione <> '' then concat('<dataChiusura>',concat(tacchini.data_chiusura_relazione,'T00:00.000'),'</dataChiusura>')
	    else '' end,'    
         </controlliallevamenti>') into msg
from bdn_cu_chiusi_benessere_animale bdn
left join bdn_checklist_v7_tacchini tacchini on tacchini.id_chk_bns_mod_ist = bdn.id_chk_bns_mod_ist
where bdn.id_chk_bns_mod_ist = _id;
     
    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_tacchini_controlli_allevamenti(integer)
    OWNER TO postgres;


CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v7_tacchini_dettaglio_checklist(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
BEGIN
select 
concat(
'<dettagliochecklist>
	<clbId>0</clbId>
	<flagVitelli>',bdn.flag_vitelli,'</flagVitelli>
	<critCodice>',tacchini.criteri_utilizzati,'</critCodice>
	<flagSessoAllevato>',tacchini.sesso_allevato,'</flagSessoAllevato>
	',
	case when tacchini.criteri_utilizzati = 'CF13' then concat('<criterioControlloAltro>',tacchini.criteri_utilizzati_altro_descrizione,'</criterioControlloAltro>
	') else '' end,
	case when tacchini.esito_controllo = 'N' then concat('<flagIntenzionalita>',tacchini.intenzionalita,'</flagIntenzionalita>
	') else '' end,
	'<nomeRappresentante>',tacchini.nome_proprietario,'</nomeRappresentante>
	',
	'<tipoAllegato>',bdn.tipo_allegato,'</tipoAllegato>
	',
	case when tacchini.esito_controllo = 'N' then 
		concat('<prescrizioni>',tacchini.prescrizioni_descrizione,'</prescrizioni>
		', 
		case when tacchini.prescrizioni_descrizione <> '' then 
			concat('<dataScadPrescrizioni>',concat(tacchini.data_prescrizioni,'T00:00:00.000'),'</dataScadPrescrizioni>
			        <flagPrescrizioneEsitoBa>',tacchini.eseguite_prescrizioni,'</flagPrescrizioneEsitoBa>
			        ',	
				case when tacchini.data_verifica <> '' then 
					concat('<dataVerificaBa>',concat(tacchini.data_verifica,'T00:00:00.000'),'</dataVerificaBa>
						<secondoControllore>',tacchini.nome_controllore,'</secondoControllore>
						<nomeRappresentanteVer>',tacchini.nome_proprietario_prescrizioni_eseguite,'</nomeRappresentanteVer>
						'
					       ) else '' end
		               ) end
		      )else '' end,
	'<parametro>2024</parametro>
	<numClb>',tacchini.num_checklist,'</numClb>
	',
	case when tacchini.esito_controllo <> 'A' then concat('<veterinario>',tacchini.veterinario_ispettore,'</veterinario>
	') else '' end, 
	case when tacchini.esito_controllo <> 'A' then concat('<presenzaManuale>',tacchini.presenza_manuale,'</presenzaManuale>
	') else '' end,
	case when tacchini.esito_controllo <> 'A' then concat('<ibridoRazza>',tacchini.ibrido_razza_allevata,'</ibridoRazza>
	') else '' end,
	case when tacchini.esito_controllo <> 'A' then (select get_chiamata_ws_insert_ba_v7_requisiti from public.get_chiamata_ws_insert_ba_v7_requisiti(_id)) else '' 
	END,'
            </dettagliochecklist>
            ') into msg
from bdn_cu_chiusi_benessere_animale bdn
left join bdn_checklist_v7_tacchini tacchini on tacchini.id_chk_bns_mod_ist = bdn.id_chk_bns_mod_ist
where bdn.id_chk_bns_mod_ist = _id;
   
    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v7_tacchini_dettaglio_checklist(integer)
    OWNER TO postgres;


-- View: public.bdn_cu_chiusi_benessere_animale

-- DROP VIEW public.bdn_cu_chiusi_benessere_animale;

CREATE OR REPLACE VIEW public.bdn_cu_chiusi_benessere_animale
 AS
 SELECT DISTINCT ist.id_alleg,
    ist.id_bdn,
    specie.short_description_ba AS codice_specie_chk_bns,
    o.name AS ragione_sociale,
    o.org_id,
    ticket.ticketid AS id_controllo,
    ticket.assigned_date AS data_controllo,
    concat('R', ticket.site_id) AS codice_asl,
    upper(o.account_number::text) AS codice_azienda,
    COALESCE(btrim(o.partita_iva::text), btrim(o.codice_fiscale_rappresentante::text)) AS id_fiscale_allevamento,
        CASE
            WHEN ROW(nucleo.nucleo_ispettivo, nucleo.nucleo_ispettivo_due, nucleo.nucleo_ispettivo_tre, nucleo.nucleo_ispettivo_quattro, nucleo.nucleo_ispettivo_cinque, nucleo.nucleo_ispettivo_sei, nucleo.nucleo_ispettivo_sette, nucleo.nucleo_ispettivo_otto, nucleo.nucleo_ispettivo_nove, nucleo.nucleo_ispettivo_dieci)::text ~~* '%nas_%'::text THEN '001'::text
            WHEN ROW(nucleo.nucleo_ispettivo, nucleo.nucleo_ispettivo_due, nucleo.nucleo_ispettivo_tre, nucleo.nucleo_ispettivo_quattro, nucleo.nucleo_ispettivo_cinque, nucleo.nucleo_ispettivo_sei, nucleo.nucleo_ispettivo_sette, nucleo.nucleo_ispettivo_otto, nucleo.nucleo_ispettivo_nove, nucleo.nucleo_ispettivo_dieci)::text ~~* '%forestale%'::text THEN '002'::text
            ELSE '003'::text
        END AS occodice,
    false AS flag_cee,
        CASE
            WHEN ticket.closed IS NULL THEN 'APERTO'::text
            WHEN ticket.closed_nolista THEN 'CHIUSO SENZA LISTA PER ASSENZA DI NON CONFORMITA'''::text
            ELSE 'CHIUSO'::text
        END AS stato,
    o.cf_correntista::text AS id_fiscale_detentore,
    op.tipo_produzione_ba,
        CASE
            WHEN op.codice_orientamento_ba IS NOT NULL THEN op.codice_orientamento_ba
            ELSE ''::text
        END AS codice_orientamento_ba,
    ist.data_import,
    ist.esito_import,
    ist.descrizione_errore,
    o.specie_allev,
    lcm.tipo_allegato_bdn AS tipo_allegato,
        CASE
            WHEN o.specie_allev::text !~~* 'pesci'::text THEN '0'::text || o.specie_allevamento::text
            ELSE 'PES'::text
        END AS specie_allevamento,
        CASE
            WHEN lcm.codice_checklist = 'isVitelli'::text THEN 'S'::text
            ELSE 'N'::text
        END AS flag_vitelli,
        CASE
            WHEN (o.specie_allev::text ~~* 'gallus'::text OR o.specie_allev::text ~~* 'oche'::text OR o.specie_allev::text ~~* 'fagiani'::text OR o.specie_allev::text ~~* 'conigli'::text OR o.specie_allev::text ~~* 'quaglie'::text) AND o.codice_tipo_allevamento IS NOT NULL THEN o.codice_tipo_allevamento
            ELSE '11'::text
        END AS tipo_allevamento_codice,
        CASE
            WHEN ticket.flag_preavviso IS NULL OR ticket.flag_preavviso = '-1'::text THEN 'N'::text
            ELSE ticket.flag_preavviso
        END AS flag_preavviso,
    ticket.data_preavviso_ba AS data_preavviso,
    ticket.descrizione_preavviso_ba AS descrizione_preavviso,
    ist.id AS id_chk_bns_mod_ist,
    ticket.flag_checklist,
    lcm.description AS nome_checklist,
    o.codice_fiscale_rappresentante AS id_fiscale_proprietario,
    lcm.codice_checklist
   FROM ticket
     JOIN organization o ON ticket.org_id = o.org_id AND o.tipologia = 2 AND o.account_number IS NOT NULL AND o.trashed_date IS NULL
     LEFT JOIN lookup_site_id asl ON asl.code = o.asl_old
     LEFT JOIN orientamenti_produttivi op ON op.specie_allevata = o.specie_allev::text AND op.descrizione_tipo_produzione = o.tipologia_strutt AND o.orientamento_prod = op.descrizione_codice_orientamento
     LEFT JOIN lookup_specie_allevata specie ON specie.description::text = o.specie_allev::text
     JOIN tipocontrolloufficialeimprese tipi ON ticket.ticketid = tipi.idcontrollo AND tipi.enabled AND tipi.pianomonitoraggio > 0
     JOIN lookup_piano_monitoraggio lp ON lp.code = tipi.pianomonitoraggio
     JOIN rel_motivi_eventi_cu rel ON rel.cod_raggrup_ind = lp.codice_interno_univoco
     JOIN lookup_eventi_motivi_cu ev ON ev.code = rel.id_evento_cu AND ev.codice_evento::text = 'isBenessereAnimale'::text
     LEFT JOIN nucleo_ispettivo_mv nucleo ON nucleo.id_controllo_ufficiale = ticket.ticketid
     JOIN chk_bns_mod_ist ist ON ist.idcu = ticket.ticketid AND ist.trashed_date IS NULL AND ist.bozza IS FALSE AND (ist.esito_import IS NULL OR ist.esito_import = ''::text OR ist.esito_import ~~* '%KO%'::text)
     JOIN lookup_chk_bns_mod lcm ON lcm.code = ist.id_alleg AND (lcm.codice_checklist IS NULL OR (lcm.codice_checklist = ANY (ARRAY['isBoviniBufalini'::text, 'isBroiler'::text, 'isVitelli'::text, 'isSuini'::text, 'isGallus'::text, 'isOviCaprini'::text, 'isConigli'::text, 'isAltreSpecie'::text, 'isTacchini'::text])))
  WHERE ticket.tipologia = 3 AND (ticket.closed IS NOT NULL OR ticket.closed_nolista) AND ticket.trashed_date IS NULL AND ticket.assigned_date > (now() - '2 years'::interval);

ALTER TABLE public.bdn_cu_chiusi_benessere_animale
    OWNER TO postgres;


    
    
-- FUNCTION: public.get_chiamata_ws_insert_ba_sa(text, text, integer)

-- DROP FUNCTION IF EXISTS public.get_chiamata_ws_insert_ba_sa(text, text, integer);

CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_sa(
	_username_ws_bdn text,
	_password_ws_bdn text,
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
id_tipo_chk integer;
result text;
BEGIN

     select id_alleg into id_tipo_chk from chk_bns_mod_ist where id = _id;
     raise info '%', id_tipo_chk; 
     IF id_tipo_chk = 24  THEN
	select get_chiamata_ws_insert_ba_v5_suini into result from get_chiamata_ws_insert_ba_v5_suini(_username_ws_bdn, _password_ws_bdn, _id);
     ELSIF id_tipo_chk = 23 THEN
	select get_chiamata_ws_insert_sa_v4 into result from get_chiamata_ws_insert_sa_v4(_username_ws_bdn, _password_ws_bdn, _id);
     ELSIF id_tipo_chk = 29 THEN
	select get_chiamata_ws_insert_ba_v5_bovini into result from public.get_chiamata_ws_insert_ba_v5_bovini(_username_ws_bdn, _password_ws_bdn, _id);	
     ELSIF id_tipo_chk = 30 THEN	
	select get_chiamata_ws_insert_ba_v5_vitelli into result from public.get_chiamata_ws_insert_ba_v5_vitelli(_username_ws_bdn, _password_ws_bdn, _id);	
     ELSIF id_tipo_chk in (25, 27, 28) THEN 
     	select get_chiamata_ws_insert_ba_v4 into result from public.get_chiamata_ws_insert_ba_v4(_username_ws_bdn, _password_ws_bdn, _id);
     ELSIF id_tipo_chk in (31) THEN 
     	select get_chiamata_ws_insert_ba_v5_gallus into result from public.get_chiamata_ws_insert_ba_v5_gallus(_username_ws_bdn, _password_ws_bdn, _id);
     ELSIF id_tipo_chk in (32) THEN 
     	select get_chiamata_ws_insert_ba_v6_ovicaprini into result from public.get_chiamata_ws_insert_ba_v6_ovicaprini(_username_ws_bdn, _password_ws_bdn, _id);
     ELSIF id_tipo_chk in (33) THEN 
     	select get_chiamata_ws_insert_ba_v6_bovini into result from public.get_chiamata_ws_insert_ba_v6_bovini(_username_ws_bdn, _password_ws_bdn, _id);
     ELSIF id_tipo_chk in (34) THEN 
     	select get_chiamata_ws_insert_ba_v6_suini into result from public.get_chiamata_ws_insert_ba_v6_suini(_username_ws_bdn, _password_ws_bdn, _id);
     ELSIF id_tipo_chk in (35) THEN 
     	select get_chiamata_ws_insert_ba_v6_vitelli into result from public.get_chiamata_ws_insert_ba_v6_vitelli(_username_ws_bdn, _password_ws_bdn, _id);
     ELSIF id_tipo_chk in (36) THEN 
     	select get_chiamata_ws_insert_ba_v6_gallus into result from public.get_chiamata_ws_insert_ba_v6_gallus(_username_ws_bdn, _password_ws_bdn, _id);
     ELSIF id_tipo_chk in (37, 38) THEN 
     	select get_chiamata_ws_insert_ba_v3 into result from public.get_chiamata_ws_insert_ba_v3(_username_ws_bdn, _password_ws_bdn, _id);
		 ELSIF id_tipo_chk in (39) THEN 
     	select get_chiamata_ws_insert_ba_v6_broiler into result from public.get_chiamata_ws_insert_ba_v6_broiler(_username_ws_bdn, _password_ws_bdn, _id);
		 ELSIF id_tipo_chk in (40) THEN 
     	select get_chiamata_ws_insert_ba_v6_conigli into result from public.get_chiamata_ws_insert_ba_v6_conigli(_username_ws_bdn, _password_ws_bdn, _id);
		ELSIF id_tipo_chk in (41) THEN 
     	select get_chiamata_ws_insert_ba_v7_bovini into result from public.get_chiamata_ws_insert_ba_v7_bovini(_username_ws_bdn, _password_ws_bdn, _id);
		ELSIF id_tipo_chk in (42) THEN 
     	select get_chiamata_ws_insert_ba_v7_ovicaprini into result from public.get_chiamata_ws_insert_ba_v7_ovicaprini(_username_ws_bdn, _password_ws_bdn, _id);
		ELSIF id_tipo_chk in (43) THEN 
     	select get_chiamata_ws_insert_ba_v7_suini into result from public.get_chiamata_ws_insert_ba_v7_suini(_username_ws_bdn, _password_ws_bdn, _id);
		ELSIF id_tipo_chk in (44) THEN 
     	select get_chiamata_ws_insert_ba_v7_vitelli into result from public.get_chiamata_ws_insert_ba_v7_vitelli(_username_ws_bdn, _password_ws_bdn, _id);
     	ELSIF id_tipo_chk in (45) THEN 
     	select get_chiamata_ws_insert_ba_v7_gallus into result from public.get_chiamata_ws_insert_ba_v7_gallus(_username_ws_bdn, _password_ws_bdn, _id);
		ELSIF id_tipo_chk in (46) THEN 
     	select get_chiamata_ws_insert_ba_v7_tacchini into result from public.get_chiamata_ws_insert_ba_v7_tacchini(_username_ws_bdn, _password_ws_bdn, _id);
		ELSIF id_tipo_chk in (47) THEN 
		select get_chiamata_ws_insert_ba_v8_altre_specie into result from public.get_chiamata_ws_insert_ba_v8_altre_specie(_username_ws_bdn, _password_ws_bdn, _id);

     ELSE 
	result :='';
     END IF;
     
    RETURN result;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_sa(text, text, integer)
    OWNER TO postgres;


