TABLESPACE pg_default;

ALTER TABLE IF EXISTS public.chk_bns_domande_v8
    OWNER to postgres;

insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,39,1272,'1. PERSONALE','Gli animali sono accuditi da un numero sufficiente di addetti. Indicare il n. addetti.',1);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,39,1273,'1. PERSONALE','Il personale addetto agli animali, ha ricevuto istruzioni pratiche sulle pertinenti disposizioni normative.',2);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,39,1274,'1. PERSONALE','Sono stati tenuti corsi di formazione specifici in materia di benessere degli animali per il personale addetto agli animali. Indicare la frequenza dei corsi (una volta l''anno, ogni sei mesi ecc). Indicare da chi sono stati organizzati i corsi (Regione, ASL, Associazioni di categoria ecc).',3);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,40,1275,'2. ISPEZIONE E CONTROLLO DEGLI ANIMALI','Gli animali sono ispezionati almeno una volta al giorno.',4);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,40,1276,'2. ISPEZIONE E CONTROLLO DEGLI ANIMALI','E'' disponibile un''adeguata illuminazione che consente l''ispezione completa degli animali.',5);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,40,1277,'2. ISPEZIONE E CONTROLLO DEGLI ANIMALI','Sono presenti recinti/locali di isolamento con lettiera asciutta e confortevole.',6);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,40,1278,'2. ISPEZIONE E CONTROLLO DEGLI ANIMALI','Gli animali malati o feriti vengono isolati e ricevono immediatamente un trattamento appropriato.',7);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,40,1279,'2. ISPEZIONE E CONTROLLO DEGLI ANIMALI','In caso di necessità viene consultato un medico veterinario.',8);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,40,1280,'2. ISPEZIONE E CONTROLLO DEGLI ANIMALI','Il recinto individuale di isolamento ha dimensioni adeguate e permette all''animale di girarsi facilmente e di avere contatti visivi ed olfattivi con gli altri animali salvo nel caso in cui ciò non sia in contraddizione con specifiche prescrizioni veterinarie.',9);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,41,1281,'3. TENUTA DEI REGISTRI (Registrazione dei dati)','"Registrazione dei dati 146/2001 All. Registrazione Punti 5-6. (i riferimenti al D.P.R. 8 febbraio 1954, n. 320, attualmente abrogato, si intendono fatti ai corrispondenti articoli dei decreti legislativi 5 agosto 2022, n.134 e n.136 ) "Le mortalità sono denunciate ai sensi del D.P.R. 8 febbraio 1954 n. 320. I registri sono conservati per un periodo di almeno tre anni e sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta." Dlgs 134 art. 9 comma 10 "L''operatore provvede alla registrazione in BDN di tutte le informazioni inerenti agli animali identificati singolarmente, alle partite, agli insiemi e ai gruppi di animali, nonché agli eventi che li riguardano, con i tempi di cui al presente articolo e con i modi previsti nel manuale operativo anche ai fini della generazione del registro della propria attività nella stessa BDN. Tale registro sostituisce qualsiasi altro registro aziendale cartaceo o su altro supporto concernente l''identificazione e registrazione degli animali." D. Lgs. 136/2022, art. 6 "Notifica e comunicazione delle malattie all''Autorità competente"; art. 11 comma 4 "gli operatori nel caso in cui non siano ancora disponibili nel sistema «ClassyFarm.it»,le apposite funzionalità informatiche per la tipologia di stabilimento di cui sono responsabili, adempiono agli obblighi del presente articolo tenendo in modalità elettronica o cartacea, per cinque anni, la documentazione riguardante almeno le informazioni di cui all''allegato 3 del presente decreto"; Allegato 3 "...individuazione e registrazione e segnalazione delle mortalità anomale" Verificare in BDN la presenza del registro di carico e scarico e la corretta registrazione delle informazioni, al fine di valutare se si siano verificate eventuali mortalità anomale. Nel caso si siano verificate mortalità anomale, verificare che siano state correttamente individuate, registrate e segnalate. Si considera non conforme: assenza in BDN del registro di carico e scarico o mancata o non corretta registrazione delle informazioni e/ o evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': NO Si considera conforme: presenza in BDN del registro di carico e scarico e corretta registrazione delle informazioni e mancata evidenza di mortalità anomale non registrate ai sensi del D. Lgs. 5 agosto 2022, n. 136 Livello della non conformita'': SI".',10);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,41,1311,'3. TENUTA DEI REGISTRI (Registrazione dei dati)','"Tenuta del registro dei trattamenti farmacologici 146/2001 All. Registrazione Punti 5-6. "Il proprietario o il custode ovvero il detentore degli animali tiene un registro dei trattamenti terapeutici effettuati. La registrazione e le relative modalità di conservazione sono effettuate secondo quanto previsto dal decreto legislativo 27 gennaio 1992, n. 119, e successive modificazioni ed integrazioni e dal decreto legislativo 4 agosto 1999, n. 336. I registri sono conservati per un periodo di almeno tre anni e sono messi a disposizione dell''autorità competente al momento delle ispezioni o su richiesta" Sono presenti le registrazioni informatizzate dei trattamenti su Vetinfo Farmacosorveglianza, ai sensi della Legge 20 novembre 2017, n. 167 e s.m.i. Assenza delle registrazioni in Vetinfo farmacosorveglianza o non corretta registrazione dei trattamenti. Livello della non conformita'': NO Corretta registrazione dei trattamenti in Vetinfo farmacosorveglianza. Livello della non conformita'': SI".',11);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,42,1282,'4. LIBERTÀ DI MOVIMENTO','Lo spazio a disposizione di ogni animale è sufficiente per consentirgli un''adeguata libertà di movimenti ed è tale da non causargli inutili sofferenze o lesioni.',12);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,42,1283,'4. LIBERTÀ DI MOVIMENTO','I locali di stabulazione sono costruiti in modo di permettere agli animali di coricarsi, giacere in decubito, alzarsi ed accudire se stessi senza difficoltà.',13);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,42,1284,'4. LIBERTÀ DI MOVIMENTO','Gli attacchi eventualmente utilizzati non provocano lesioni e consentono agli animali di assumere una posizione confortevole, di giacere ed alzarsi, non provocano strangolamenti o ferite, sono regolarmente esaminati, riparati o sostituiti se danneggiati.',14);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,42,1285,'4. LIBERTÀ DI MOVIMENTO','I recinti di isolamento hanno dimensioni adeguate e conformi alle disposizioni vigenti.',15);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,44,1286,'5. EDIFICI E LOCALI DI STABULAZIONE','I materiali di costruzione, i recinti e le attrezzature con i quali gli animali possono venire a contatto non sono nocivi per gli animali stessi, non vi sono spigoli taglienti o sporgenze, tutte le superfici sono facilmente lavabili e disinfettabili.',16);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,44,1287,'5. EDIFICI E LOCALI DI STABULAZIONE','Gli animali stabulati all''aperto dispongono di un riparo adeguato.',17);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,44,1288,'5. EDIFICI E LOCALI DI STABULAZIONE','Le apparecchiature e gli impianti elettrici sono costruiti in modo da evitare scosse elettriche e sono conformi alle norme vigenti in materia.',18);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,44,1289,'5. EDIFICI E LOCALI DI STABULAZIONE','La circolazione dell''aria, la quantità di polvere, la temperatura, l''umidità relativa dell''aria e le concentrazioni di gas sono mantenute entro limiti non dannosi per gli animali. All''atto dell''ispezione T° e UR sono adeguate alle esigenze etologiche della specie e all''età degli animali.',19);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,44,1290,'5. EDIFICI E LOCALI DI STABULAZIONE','Le attrezzature per l''alimentazione automatica sono pulite regolarmente e frequentemente, smontando le parti in cui si depositano residui di alimento.',20);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,44,1291,'5. EDIFICI E LOCALI DI STABULAZIONE','Gli escrementi, l''urina i foraggi non mangiati o caduti sono rimossi con regolarità.',21);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,44,1292,'5. EDIFICI E LOCALI DI STABULAZIONE','È presente un locale/recinto infermeria chiaramente identificato e con presenza permanente di lettiera asciutta e acqua fresca in quantità sufficiente.',22);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,44,1293,'5. EDIFICI E LOCALI DI STABULAZIONE','I locali adibiti alla preparazione/conservazione degli alimenti sono adeguatamente separati e soddisfano i requisiti minimi dal punto di vista igienico-sanitario.',23);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,44,1294,'5. EDIFICI E LOCALI DI STABULAZIONE','Gli animali custoditi nei fabbricati non sono tenuti costantemente al buio, ad essi sono garantiti un adeguato periodo di luce (naturale o artificiale) ed un adeguato periodo di riposo.',24);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,46,1295,'6. ATTREZZATURA AUTOMATICA E MECCANICA','Gli impianti automatici o meccanici sono ispezionati almeno 1 volta al giorno.',25);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,46,1296,'6. ATTREZZATURA AUTOMATICA E MECCANICA','Sono presenti idonei dispositivi per la somministrazione di acqua nei periodi di intenso calore.',26);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,46,1297,'6. ATTREZZATURA AUTOMATICA E MECCANICA','Sono presenti impianti automatici per la somministrazione del mangime.',27);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,46,1298,'6. ATTREZZATURA AUTOMATICA E MECCANICA','Sono prese misure adeguate per salvaguardare la salute ed il benessere degli animali in caso di non funzionamento degli impianti (es. metodi alternativi di alimentazione).',28);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,46,1299,'6. ATTREZZATURA AUTOMATICA E MECCANICA','Se la salute e il benessere degli animali dipendono da un impianto di ventilazione artificiale, è previsto un adeguato impianto di riserva per garantire un ricambio d''aria sufficiente a salvaguardare la salute e il benessere degli animali in caso di guasto all''impianto stesso.',29);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,46,1300,'6. ATTREZZATURA AUTOMATICA E MECCANICA','E'' previsto un sistema di allarme che segnali eventuali guasti.',30);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,46,1301,'6. ATTREZZATURA AUTOMATICA E MECCANICA','Sono presenti apparecchiature per il rilevamento della T° e dell''UR.',31);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,47,1302,'7. ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZION E DI ALTRE SOSTANZE','Non viene somministrata alcuna sostanza, ad eccezione di quelle somministrate a fini terapeutici o profilattici o in vista di trattamenti zootecnici come previsto dalla normativa vigente.',32);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,47,1303,'8. ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZION E DI ALTRE SOSTANZE','I trattamenti terapeutici e profilattici sono regolarmente prescritti da un medico veterinario.',33);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,47,1304,'9. ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZION E DI ALTRE SOSTANZE','L''alimentazione è adeguata in rapporto all''età, al peso e alle esigenze comportamentali e fisiologiche dei vitelli.',34);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,47,1305,'10. ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZION E DI ALTRE SOSTANZE','Se non viene praticata l''alimentazione ad libitum o con sistemi automatici è assicurato l''accesso agli alimenti a tutti gli animali contemporaneamente per evitare competizioni.',35);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,47,1306,'11. ALIMENTAZIONE, ABBEVERAGGIO E SOMMINISTRAZION E DI ALTRE SOSTANZE','La modalità di somministrazione dell''acqua consente una adeguata idratazione degli animali anche nei periodi di intenso calore.',36);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,48,1307,'8. MUTILAZIONI','Rispetto delle pertinenti disposizioni di cui all''allegato al D.Lgs. 146/2001, punto 19.',37);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,49,1308,'9. PROCEDURE D''ALLEVAMENTO','Non sono praticati l''allevamento naturale o artificiale o procedimenti di allevamento che provocano o possano provocare agli animali sofferenze o lesioni.',38);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,49,1309,'9. PROCEDURE D''ALLEVAMENTO','I fabbricati, i recinti, le attrezzature e gli utensili sono puliti e disinfettati regolarmente.',39);
insert into chk_bns_domande_v8 (idmod, irr_id, dettirrid, requisito, quesito, level) values (47,49,1310,'9. PROCEDURE D''ALLEVAMENTO','Vengono messe in atto azioni preventive e vengono eseguiti interventi contro mosche, roditori e parassiti.',40);

