CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v8_altre_specie(
	_username_ws_bdn text,
	_password_ws_bdn text,
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE STRICT PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
BEGIN
          
  msg := concat('<?xml version="1.0" encoding="UTF-8"?>
<S:Envelope xmlns:S="http://schemas.xmlsoap.org/soap/envelope/">','
',
(select get_chiamata_ws_insert_ba_sa_header from get_chiamata_ws_insert_ba_sa_header(_username_ws_bdn, _password_ws_bdn)),'
',
'<S:Body>
      <ns2:insertControlliallevamentiBA xmlns:ns2="http://ws.izs.it">','
      ',(select get_chiamata_ws_insert_ba_v8_altre_specie_controlli_allevamenti from get_chiamata_ws_insert_ba_v8_altre_specie_controlli_allevamenti(_id)),'
      ',(select get_chiamata_ws_insert_ba_v8_altre_specie_dettaglio_checklist from get_chiamata_ws_insert_ba_v8_altre_specie_dettaglio_checklist(_id)),'
      </ns2:insertControlliallevamentiBA>
      </S:Body> 
      </S:Envelope>');
      
 RETURN msg;
 END;
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v8_altre_specie(text, text, integer)
    OWNER TO postgres;
    
CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v8_altre_specie_capannoni(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
tag_preavviso text;
BEGIN

select string_agg(concat('<capannoni>
               <clBoxId>0</clBoxId>
               <clbId>0</clbId>
               <identificativo>',numero,'</identificativo>
               <capacita>',capacita,'</capacita>
               <animaliPresenti>',animali,'</animaliPresenti>
               <numeroBox>',num_totale_box,'</numeroBox>
               <numeroBoxAttivi>',num_totale_box_attivi,'</numeroBoxAttivi>
               <flagIspezionato>',ispezionato,'</flagIspezionato>
            </capannoni>'),'
            ') into msg 
   from
  chk_bns_capannoni_v8 where trashed_date is null and id_chk_bns_mod_ist = _id;
    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v8_altre_specie_capannoni(integer)
    OWNER TO postgres;


CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v8_altre_specie_controlli_allevamenti(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
tag_preavviso text;
BEGIN

select 
concat('<controlliallevamenti>
          <caId>0</caId>
             <dtControllo>', to_char(data_controllo, 'YYYY-MM-DD"T"00:00:00.000'),'</dtControllo>
            <aslCodice>',codice_asl,'</aslCodice>
            <aziendaCodice>',codice_azienda,'</aziendaCodice>
            <speCodice>',bdn.codice_specie_chk_bns,'</speCodice>
            <allevIdFiscale>',id_fiscale_allevamento,'</allevIdFiscale>
            ', case when bdn.codice_specie_chk_bns in ('0128','0155','0186','0187', '0171') then concat('<propIdFiscale>',id_fiscale_allevamento,'</propIdFiscale>
            ') else '' end,'            
            <ocCodice>',bdn.occodice,'</ocCodice>
            <flagEsito>',v8.esito_controllo,'</flagEsito>
            <flagVitelli>',bdn.flag_vitelli,'</flagVitelli>
            <flagExtrapiano>',v8.extrapiano,'</flagExtrapiano>
            <detenIdFiscale>',id_fiscale_detentore,'</detenIdFiscale>
            ', case when bdn.codice_specie_chk_bns in ('0144','30','32','33','34','35','36','37','38','40','41','42','46','0128') then concat('<tipoProdCodice>',bdn.tipo_produzione_ba,'</tipoProdCodice>
            <tipoAllevCodice>',bdn.tipo_allevamento_codice,'</tipoAllevCodice>
            <orientamentoCodice>',bdn.codice_orientamento_ba,'</orientamentoCodice>') else '' end,
            case when bdn.codice_specie_chk_bns in ('0186','0187', '0171') then concat('<orientamentoCodice>',bdn.codice_orientamento_ba,'</orientamentoCodice>
            ') else '' end,        
            case when bdn.codice_specie_chk_bns in ('0144','30','32','33','34','35','36','37','38','40','41','42','46') then 
	   		concat('<flagFaseProduttiva>',v8.flag_fase_produttiva,'</flagFaseProduttiva>
            ') else '' end,
            '<flagPreavviso>',bdn.flag_preavviso,'</flagPreavviso>
            ',case when bdn.flag_preavviso <> 'N' then concat('<dataPreavviso>',to_char(bdn.data_preavviso, 'YYYY-MM-DD"T"00:00:00.000'),'</dataPreavviso>
            ') else '' end,'     
	   	    ',case when bdn.flag_preavviso = 'A' then concat('<descAltroPreavviso>', bdn.descrizione_preavviso,'</descAltroPreavviso>') else '' end,'
            <primoControllore>',v8.nome_controllore,'</primoControllore>
            <tipoControllo>BA</tipoControllo>
            <flagCopiaCheckList>',bdn.flag_checklist,'</flagCopiaCheckList>
            ',case when v8.esito_controllo = 'N' then concat(
            '<sanzBloccoMov>',v8.sanz_blocco,'</sanzBloccoMov>
            <sanzAmministrativa>',v8.sanz_amministrativa,'</sanzAmministrativa>
            <sanzSequestro>',v8.sanz_sequestro,'</sanzSequestro>
            <sanzAbbattimentoCapi>',v8.sanz_abbattimento,'</sanzAbbattimentoCapi>
            <sanzAltro>',v8.sanz_altro,'</sanzAltro>
            ',case when v8.sanz_altro <> '0' then concat('<sanzAltroDesc>',v8.sanz_altro_desc,'</sanzAltroDesc>
            ')
            else '' end,'
            <noteControllore>',v8.note_controllore,'</noteControllore>
            <noteDetentore>',v8.note_proprietario,'</noteDetentore>
            <flagEvidenze>',v8.evidenze,'</flagEvidenze>
            ',case when v8.evidenze = 'S' then 
                                                concat('<evidenzaIr>',v8.evidenze_ir,'</evidenzaIr>
                                                <evidenzaSv>',v8.evidenze_sv,'</evidenzaSv>
                                                <evidenzaSa>',v8.evidenze_tse,'</evidenzaSa>'
                                                )
                                                else '' end) 
            else '' end,'
            <flagCondizionalita>',v8.appartenente_condizionalita,'</flagCondizionalita>
             ',case when v8.data_chiusura_relazione <> '' then concat('<dataChiusura>',concat(v8.data_chiusura_relazione,'T00:00.000'),'</dataChiusura>
             ')
            else '' end,'    
         </controlliallevamenti>
         ') into msg
from bdn_cu_chiusi_benessere_animale bdn
left join bdn_checklist_v8_altre_specie v8 on v8.id_chk_bns_mod_ist = bdn.id_chk_bns_mod_ist
where bdn.id_chk_bns_mod_ist = _id;

    RETURN msg;
END;        
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v8_altre_specie_controlli_allevamenti(integer)
    OWNER TO postgres;
    
    
-- FUNCTION: public.get_chiamata_ws_insert_ba_v8_altre_specie_dettaglio_checklist(integer)

-- DROP FUNCTION IF EXISTS public.get_chiamata_ws_insert_ba_v8_altre_specie_dettaglio_checklist(integer);

CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v8_altre_specie_dettaglio_checklist(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
tag_preavviso text;
BEGIN
select 
concat(
'<dettagliochecklist>
	<clbId>0</clbId>
	<flagVitelli>',bdn.flag_vitelli,'</flagVitelli>
	<critCodice>',v8.criteri_utilizzati,'</critCodice>
	',
	case when v8.criteri_utilizzati = 'CF13' then concat('<criterioControlloAltro>',v8.criteri_utilizzati_altro_descrizione,'</criterioControlloAltro>
	') else '' end,
	case when v8.esito_controllo = 'N' then concat('<flagIntenzionalita>',v8.intenzionalita,'</flagIntenzionalita>
	') else '' end,
	'<nomeRappresentante>',v8.nome_proprietario,'</nomeRappresentante>
	',
	'<tipoAllegato>',bdn.tipo_allegato,'</tipoAllegato>
	',
	case when v8.esito_controllo <> 'A' then concat('<numCapannoni>',v8.num_capannoni,'</numCapannoni>
	<numCapannoniAttivi>',v8.num_capannoni_attivi,'</numCapannoniAttivi>',
		case when bdn.tipo_allegato = '1' then concat('<numUovaAnno>',v8.num_uova_anno,'</numUovaAnno>
		<flagSelImbal>',v8.selezione_imballaggio,'</flagSelImbal>
		<indirizzoSelImbal>',v8.destinazione_imballaggio,'</indirizzoSelImbal>
		<mutaInAllev>',v8.muta,'</mutaInAllev>') else '' end,
		case when bdn.tipo_allegato = '3' then concat('<numVitelliMax>',v8.num_vitelli_max,'</numVitelliMax>
		<numVitelliPresenti>',v8.num_vitelli,'</numVitelliPresenti>
		<numVitelli8Sett>',v8.num_vitelli_8_sett,'</numVitelli8Sett>') else '' end
						    ) else '' end,
	case when v8.esito_controllo <> 'A' then concat( 
	case when bdn.tipo_allegato = '5' then concat('<flagCapMaxAut>','','</flagCapMaxAut>') else '' end) else '' 
	end,
	case when v8.esito_controllo = 'N' then 
		concat('<prescrizioni>',v8.prescrizioni_descrizione,'</prescrizioni>
		', 
		case when v8.prescrizioni_descrizione <> '' then 
			concat('<dataScadPrescrizioni>',concat(v8.data_prescrizioni,'T00:00:00.000'),'</dataScadPrescrizioni>
			        <flagPrescrizioneEsitoBa>',v8.eseguite_prescrizioni,'</flagPrescrizioneEsitoBa>
			        ',	
				case when v8.data_verifica <> '' then 
					concat('<dataVerificaBa>',concat(v8.data_verifica,'T00:00:00.000'),'</dataVerificaBa>
						<secondoControllore>',v8.nome_controllore,'</secondoControllore>
						<nomeRappresentanteVer>',v8.nome_proprietario_prescrizioni_eseguite,'</nomeRappresentanteVer>
						'
					       ) else '' end
		               ) end
		      )else '' end,
	'<parametro>2024</parametro>',
	case when v8.esito_controllo = 'N' then (select get_chiamata_ws_insert_ba_v8_altre_specie_requisiti from public.get_chiamata_ws_insert_ba_v8_altre_specie_requisiti(_id)) else '' 
	END,
	case when v8.esito_controllo = 'N' then (select get_chiamata_ws_insert_ba_v8_altre_specie_capannoni from public.get_chiamata_ws_insert_ba_v8_altre_specie_capannoni(_id)) else '' 
	END,'
            </dettagliochecklist>
            ') into msg
from bdn_cu_chiusi_benessere_animale bdn
left join bdn_checklist_v8_altre_specie v8 on v8.id_chk_bns_mod_ist = bdn.id_chk_bns_mod_ist
where bdn.id_chk_bns_mod_ist = _id;
   

    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v8_altre_specie_dettaglio_checklist(integer)
    OWNER TO postgres;



CREATE OR REPLACE FUNCTION public.get_chiamata_ws_insert_ba_v8_altre_specie_requisiti(
	_id integer)
    RETURNS text
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$

   DECLARE
msg text ;
id_tipo_chk integer;
result text;
tag_preavviso text;
BEGIN

select string_agg(concat('<requisiti>
               <cldbId>0</cldbId>
               <irrId>',dom.irr_id,'</irrId>
               <dettirrId>',dom.dettirrid,'</dettirrId>
               <flagEsitoDett>',risp.risposta,'</flagEsitoDett>
	       ',case when risp.risposta = 'N' then concat('<numIrregolarita>',risp.num_irregolarita,'</numIrregolarita>
               <numProvvCatA>',risp.num_provv_a,'</numProvvCatA>
               <numProvvCatB>',risp.num_provv_b,'</numProvvCatB>
               <numProvvCatC>',risp.num_provv_c,'</numProvvCatC>') else '' end,'               
               <osservazioni>',risp.evidenze,'</osservazioni>
               <operation>insert</operation>
            </requisiti>'),'
            ')
into msg
from
chk_bns_risposte_v8 risp  
left join chk_bns_domande_v8 dom on risp.id_domanda = dom.id
where risp.trashed_date is null and risp.id_chk_bns_mod_ist = _id;   

    RETURN msg;
END;	
$BODY$;

ALTER FUNCTION public.get_chiamata_ws_insert_ba_v8_altre_specie_requisiti(integer)
    OWNER TO postgres;

CREATE OR REPLACE FUNCTION public.chk_bns_get_domande_v8(
	_codicespecie integer,
	_numallegato integer,
	_versione integer,
	_idcontrollo integer)
    RETURNS TABLE(id integer, level integer, irr_id integer, dettirrid integer, idmod integer, requisito text, quesito text) 
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE STRICT PARALLEL UNSAFE
    ROWS 1000

AS $BODY$

DECLARE
BEGIN
	FOR id, level, irr_id , dettirrid , idmod , requisito , quesito 
	in

	select distinct c.id, c.level, c.irr_id , c.dettirrid , c.idmod , c.requisito , c.quesito 

from chk_bns_domande_v8 c where c.idmod in (select code from lookup_chk_bns_mod where codice_specie = _codiceSpecie and versione = _versione and num_allegato = _numAllegato and start_date < (select assigned_date from ticket where ticketid = _idcontrollo) and end_date >(select assigned_date from ticket where ticketid = _idcontrollo) ) order by c.level asc

LOOP
        RETURN NEXT;
     END LOOP;
     RETURN;
 END;
$BODY$;

ALTER FUNCTION public.chk_bns_get_domande_v8(integer, integer, integer, integer)
    OWNER TO postgres;
    

CREATE TABLE IF NOT EXISTS public.chk_bns_risposte_v8
(
    id serial,
    id_chk_bns_mod_ist integer,
    id_domanda integer,
    risposta text COLLATE pg_catalog."default",
    evidenze text COLLATE pg_catalog."default",
    num_irregolarita text COLLATE pg_catalog."default",
    num_provv_a text COLLATE pg_catalog."default",
    num_provv_b text COLLATE pg_catalog."default",
    num_provv_c text COLLATE pg_catalog."default",
    trashed_date timestamp without time zone,
    entered_by integer,
    entered timestamp without time zone DEFAULT now()
)

TABLESPACE pg_default;

ALTER TABLE IF EXISTS public.chk_bns_risposte_v8
    OWNER to postgres;
